INSERT INTO `script_texts` VALUES (-1000000, '<ScriptDev2 Text Entry Missing!>', NULL, '<Texte ScriptDev2 introuvable !>', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DEFAULT_TEXT');
INSERT INTO `script_texts` VALUES (-1000005, '%s goes into a frenzy!', NULL, '%s est pris de frénésie !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'EMOTE_BOSS_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES (-1000111, 'Our house is this way, through the thicket.', NULL, 'Notre maison est par ici, à travers le bosquet.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'magwin SAY_START');
INSERT INTO `script_texts` VALUES (-1000112, 'Help me!', NULL, 'Aidez-moi !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'magwin SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000113, 'My poor family. Everything has been destroyed.', NULL, 'Ma pauvre famille. Tout a été détruit.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'magwin SAY_PROGRESS');
INSERT INTO `script_texts` VALUES (-1000114, 'Father! Father! You\'re alive!', NULL, 'Père ! Père ! Tu es en vie !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'magwin SAY_END1');
INSERT INTO `script_texts` VALUES (-1000115, 'You can thank $N for getting me back here safely, father.', NULL, 'Tu peux remercier $N de m\'avoir ramené ici saint et sauf, père.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'magwin SAY_END2');
INSERT INTO `script_texts` VALUES (-1000116, '%s hugs her father.', NULL, '%s serre son père dans ses bras.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 7, 0, 'magwin EMOTE_HUG');
INSERT INTO `script_texts` VALUES (-1000117, 'Thank you for agreeing to help. Now, let\'s get out of here $N.', NULL, 'Merci d\'avoir accepter de nous aider. Maintenant sortons d\'ici $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_START');
INSERT INTO `script_texts` VALUES (-1000118, 'Over there! They\'re following us!', NULL, 'Par ici ! Ils nous suivent !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `script_texts` VALUES (-1000119, 'Allow me a moment to rest. The journey taxes what little strength I have.', NULL, 'Accordez-moi un moment de repos. La journée a été difficile et j\'ai peu de force.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 16, 'wounded elf SAY_ELF_RESTING');
INSERT INTO `script_texts` VALUES (-1000120, 'Did you hear something?', NULL, 'Vous avez entendu quelque chose ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `script_texts` VALUES (-1000121, 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has -', NULL, 'Le Guet de l\'Épervier, enfin ! Maintenant, où est mon... Oh non ! Mon paquet a disparu ! Où est...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000122, 'You won\'t keep me from getting to Falcon Watch!', NULL, 'Vous ne m\'empêcherez pas d\'arriver au Guet de l\'Épervier !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `script_texts` VALUES (-1000128, 'We need you to send reinforcements to Manaforge Duro, Ardonis. This is not a request, it\'s an order.', NULL, 'Nous avons besoin de vous pour envoyer des renforts à la Manaforge Duro, Ardonis. Il ne s\'agit pas d\'une demande, c\'est un ordre.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `script_texts` VALUES (-1000129, 'You cannot be serious! We are severely understaffed and can barely keep this manaforge functional!', NULL, 'Vous ne pouvez pas être sérieux ! Nous sommes déjà à court de personnel et nous pouvons à peine garder la Manaforge fonctionnelle !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT INTO `script_texts` VALUES (-1000130, 'You will do as ordered. Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control. Failure to comply will not be tolerated!', NULL, 'Vous ferez comme on vous l\'a ordonné. La Manaforge Duro subit déjà une lourde attaque par les créatures de mana et la situation est hors de contrôle. L\'échec ne sera pas toléré !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `script_texts` VALUES (-1000131, 'Indeed, it is not a request.', NULL, 'En effet, ce n\'est pas une demande.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT INTO `script_texts` VALUES (-1000132, 'My lord!', NULL, 'Mon seigneur !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `script_texts` VALUES (-1000133, 'Duro will be reinforced! Ultris was a complete disaster. I will NOT have that mistake repeated!', NULL, 'Duro recevra des renforts ! Ultris a été un désastre complet. Je ne veux pas répéter cette erreur !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge PATHALEON_CULATOR_IMAGE_2');
INSERT INTO `script_texts` VALUES (-1000134, 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point... Prince Kael\'thas will tolerate no further delays. I will tolerate nothing other than complete success!', NULL, 'Nous avons eu un trop grand nombre de revers tout au long de notre route : la citadelle des Flammes infernales, la Crête Tombeciel, la Halte Aile-de-Feu... Le Prince Kael\'thas ne tolérera pas d\'autres retards. Je ne tolèrerai rien d\'autre qu\'une réussite totale !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge PATHALEON_CULATOR_IMAGE_2_1');
INSERT INTO `script_texts` VALUES (-1000135, 'I am returning to Tempest Keep. See to it that I do not have reason to return!', NULL, 'Je vais retourner au Donjon de la Tempête. Veillez à ce que je n\'ai aucune raison de revenir !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge PATHALEON_CULATOR_IMAGE_2_2');
INSERT INTO `script_texts` VALUES (-1000136, 'Yes, my lord.', NULL, 'Oui, mon seigneur !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge COMMANDER_DAWNFORGE_4 SAY_ARCANIST_ARDONIS_2');
INSERT INTO `script_texts` VALUES (-1000137, 'See to it, Ardonis!', NULL, 'Nous nous reverrons la-bas, Ardonis !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'dawnforge COMMANDER_DAWNFORGE_5');
INSERT INTO `script_texts` VALUES (-1000138, 'Avruu\'s magic... it still controls me. You must fight me, mortal. It\'s the only way to break the spell!', NULL, 'La magie d\'Avruu... elle me contrôle toujours. Vous devez me combattre, mortel. C\'est la seule façon de briser le charme !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'aeranas SAY_SUMMON');
INSERT INTO `script_texts` VALUES (-1000139, 'Avruu\'s magic is broken! I\'m free once again!', NULL, 'La magie d\'Avruu est cassée ! Je suis de nouveau libre !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'aeranas SAY_FREE');
INSERT INTO `script_texts` VALUES (-1000140, 'Let\'s go.', NULL, 'Allons-y.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_START');
INSERT INTO `script_texts` VALUES (-1000141, '$N, let\'s use the antechamber to the right.', NULL, '$N, nous allons utiliser l\'antichambre sur la droite.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_PROGRESS1');
INSERT INTO `script_texts` VALUES (-1000142, 'I can see the light at the end of the tunnel!', NULL, 'Je peux voir la lumière au bout du tunnel !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_PROGRESS2');
INSERT INTO `script_texts` VALUES (-1000143, 'There\'s Farstrider Enclave now, $C. Not far to go... Look out! Troll ambush!!', NULL, 'Voici l\'Enclave des Péregrins, $C. Plus beaucoup de chemin à parcourir... Regardez ! Une embuscade de Trolls !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_PROGRESS3');
INSERT INTO `script_texts` VALUES (-1000144, 'Thank you for saving my life and bringing me back to safety, $N', NULL, 'Merci de m\'avoir sauvé la vie et de me ramener en lieu sur, $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_END1');
INSERT INTO `script_texts` VALUES (-1000145, 'Captain Helios, I\'ve been rescued from the Amani Catacombs. Reporting for duty, sir!', NULL, 'Capitaine Helios, j\'ai été sauvé des Catacombes Amani. J\'ai accompli mon devoir, Capitaine !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_END2');
INSERT INTO `script_texts` VALUES (-1000146, 'Liatha, get someone to look at those injuries. Thank you for bringing her back safely.', NULL, 'Liatha, que quelqu\'un s\'occupe de vos blessures. Merci de l\'avoir ramenée en sécurité.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lilatha CAPTAIN_ANSWER');
INSERT INTO `script_texts` VALUES (-1000169, 'Muahahahaha! You fool! You\'ve released me from my banishment in the interstices between space and time!', NULL, 'Muahahahaha ! Pauvre imbécile ! Vous m\'avez libéré de mon bannissement dans l\'interstice de l\'espace et du temps !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_1');
INSERT INTO `script_texts` VALUES (-1000170, 'All of Draenor shall quick beneath my feet! I will destroy this world and reshape it in my image!', NULL, 'Tout Draenor gisera rapidement sous mes pieds ! Je vais détruire ce monde et le remodeler à mon image !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_2');
INSERT INTO `script_texts` VALUES (-1000171, 'Where shall I begin? I cannot bother myself with a worm such as yourself. There is a world to be conquered!', NULL, 'Par où vais-je commencer ? Je ne peux pas perdre de temps avec un ver comme vous. Il y a un monde à conquérir !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_3');
INSERT INTO `script_texts` VALUES (-1000172, 'No doubt the fools that banished me are long dead. I shall take wing survey my demense. Pray to whatever gods you hold dear that we do not meet again.', NULL, 'Sans doute que les imbéciles qui m\'ont exclu sont déjà morts. Je vais surveiller ma demeure depuis les airs. Priez votre dieu afin que nous ne nous rencontrions pas à nouveau.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_4');
INSERT INTO `script_texts` VALUES (-1000173, 'NOOOOooooooo!', NULL, 'NOOOOooooooonnnn !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_INTERRUPT');
INSERT INTO `script_texts` VALUES (-1000174, 'Good $N, you are under the spell\'s influence. I must analyze it quickly, then we can talk.', NULL, 'Bien $N, vous êtes sous l\'influence du sort. Je dois l\'analyser rapidement, alors nous pourrons parler.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'daranelle SAY_SPELL_INFLUENCE');
INSERT INTO `script_texts` VALUES (-1000175, 'Thank you, mortal.', NULL, 'Je vous remercie, mortel.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 11, 0, ' SAY_JUST_EATEN');
INSERT INTO `script_texts` VALUES (-1000176, 'The last thing I remember is the ship falling and us getting into the pods. I\'ll go see how I can help. Thank you!', NULL, 'La dernière chose dont je me souviens est la chute du vaisseau et notre fuite vers les nacelles. Je vais aller voir comment je peux aider. Merci !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL1');
INSERT INTO `script_texts` VALUES (-1000177, '$C, Where am I? Who are you? Oh no! What happened to the ship?', NULL, '$C, Où suis-je ? Qui êtes-vous ? Oh non ! Que s\'est-il passé sur le vaisseau ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL2');
INSERT INTO `script_texts` VALUES (-1000178, '$C You saved me! I owe you a debt that I can never repay. I\'ll go see if I can help the others.', NULL, '$C, vous m\'avez sauvé ! J\'ai une dette envers vous que je ne pourrais pas rembourser. Je vais voir si je peux aider les autres.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL3');
INSERT INTO `script_texts` VALUES (-1000179, 'Ugh... what is this place? Is that all that\'s left of the ship over there?', NULL, 'Ugh... quel est cet endroit ? Est-ce tout ce qui reste du vaisseau là-bas ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL4');
INSERT INTO `script_texts` VALUES (-1000180, 'Oh, the pain...', NULL, 'Oh, quelle douleur...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP1');
INSERT INTO `script_texts` VALUES (-1000181, 'Everything hurts, Please make it stop...', NULL, 'J\'ai mal partout. S\'il vous plaît, faites cesser la douleur...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP2');
INSERT INTO `script_texts` VALUES (-1000182, 'Ughhh... I hurt. Can you help me?', NULL, 'Ughhh... J\'ai mal. Pouvez-vous m\'aider ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP3');
INSERT INTO `script_texts` VALUES (-1000183, 'I don\'t know if I can make it, please help me...', NULL, 'Je ne sais pas si je peux le faire, s\'il vous plaît, aidez-moi ...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP4');
INSERT INTO `script_texts` VALUES (-1000184, 'Yes Master, all goes along as planned.', NULL, 'Oui Maître, tout se passe comme prévu.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'engineer_spark SAY_TEXT');
INSERT INTO `script_texts` VALUES (-1000185, '%s puts the shell to his ear.', NULL, '%s colle le coquillage contre son oreille.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 7, 0, 'engineer_spark EMOTE_SHELL');
INSERT INTO `script_texts` VALUES (-1000186, 'Now I cut you!', NULL, 'Maintenant, je vais vous découper !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 7, 0, 'engineer_spark SAY_ATTACK');
INSERT INTO `script_texts` VALUES (-1000187, 'Thank you, dear $C, you just saved my life.', NULL, 'Merci, cher $C, vous venez de me sauver ma vie.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'faulk SAY_HEAL');
INSERT INTO `script_texts` VALUES (-1000188, 'Deployment sucessful. Trespassers will be neutralized.', NULL, 'Déploiement terminé. Les intrus vont être neutralisés.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'converted_sentry SAY_CONVERTED_1');
INSERT INTO `script_texts` VALUES (-1000189, 'Objective acquired. Initiating security routines.', NULL, 'Cible repérée. Lancement des mesures de sécurité.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'converted_sentry SAY_CONVERTED_2');
INSERT INTO `script_texts` VALUES (-1000190, 'In Nagrand, food hunt ogre!', NULL, 'En Nagrand, la nourriture chasse les ogres !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, ' SAY_LUMP_0');
INSERT INTO `script_texts` VALUES (-1000191, 'You taste good with maybe a little salt and pepper.', NULL, 'Vous avez peut-être un bon goût avec un peu de sel et de poivre.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, ' SAY_LUMP_1');
INSERT INTO `script_texts` VALUES (-1000192, 'OK, OK! Lump give up!', NULL, 'OK, OK ! Lump renonce !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, ' SAY_LUMP_DEFEAT');
INSERT INTO `script_texts` VALUES (-1000193, 'Thank you, dear $C, you just saved my life.', NULL, 'Merci, cher $C, vous venez de me sauver ma vie.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'stillblade SAY_HEAL');
INSERT INTO `script_texts` VALUES (-1000194, 'I give up! Please don\'t kill me!', NULL, 'Je renonce ! Ne me tuez pas s\'il vous plaît !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'unkor SAY_SUBMIT');
INSERT INTO `script_texts` VALUES (-1000195, 'I choose the third option: KILLING YOU!', NULL, 'Je choisis la troisième option : VOUS TUER !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'floon SAY_FLOON_ATTACK');
INSERT INTO `script_texts` VALUES (-1000196, 'Belore...', NULL, 'Belore...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lady_sylvanas SAY_LAMENT_END');
INSERT INTO `script_texts` VALUES (-1000197, '%s kneels down and pick up the amulet.', NULL, '%s s\'agenouille et ramasse l\'amulette.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 1, 0, 'lady_sylvanas EMOTE_LAMENT_END');
INSERT INTO `script_texts` VALUES (-1000198, 'Taste blade, mongrel!', NULL, 'Goûte à ma lame, bâtard !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_GUARD_SIL_AGGRO1');
INSERT INTO `script_texts` VALUES (-1000199, 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', NULL, 'Pitié, dites-moi que vous n\'avez pas fait ce que je crois que vous avez fait. Dites-moi que je ne vais pas devoir vous faire de mal...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_GUARD_SIL_AGGRO2');
INSERT INTO `script_texts` VALUES (-1000200, 'As if we don\'t have enough problems, you go and create more!', NULL, 'Comme si nous n\'avons pas assez de problèmes, vous allez en créer encore plus !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_GUARD_SIL_AGGRO3');
INSERT INTO `script_texts` VALUES (-1000201, 'I\'m saved! Thank you, doctor!', NULL, 'Me voilà sur pieds ! Merci docteur !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'injured_patient SAY_DOC1');
INSERT INTO `script_texts` VALUES (-1000202, 'HOORAY! I AM SAVED!', NULL, 'HOURRA ! J\'AI SURVÉCU !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'injured_patient SAY_DOC2');
INSERT INTO `script_texts` VALUES (-1000203, 'Sweet, sweet embrace... take me...', NULL, 'Adieu monde cruel... Je m\'en vais loin de toi mourir de douleur et d\'ennui... Adieu, adieu, adieu...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'injured_patient SAY_DOC3');
INSERT INTO `script_texts` VALUES (-1000204, '%s looks up at you quizzically. Maybe you should inspect it?', NULL, '%s lève les yeux vers vous d\'un air interrogateur. Peut-être devriez vous l\'inspecter ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'cluck EMOTE_A_HELLO');
INSERT INTO `script_texts` VALUES (-1000205, '%s looks at you unexpectadly.', NULL, '%s vous regarde avec impatience.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'cluck EMOTE_H_HELLO');
INSERT INTO `script_texts` VALUES (-1000206, '%s starts pecking at the feed.', NULL, '%s commence à picorer la nourriture.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'cluck EMOTE_CLUCK_TEXT2');
INSERT INTO `script_texts` VALUES (-1000207, 'You have my blessing', NULL, 'Vous avez ma bénédiction.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ashyen_and_keleth SAY_REWARD_BLESS');
INSERT INTO `script_texts` VALUES (-1000208, 'Frenzyheart kill you if you come back. You no welcome here no more!', NULL, 'Frénécœur va vous tuer si vous revenez. On veut plus vous voir !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'vekjik SAY_TEXTID_VEKJIK1');
INSERT INTO `script_texts` VALUES (-1000209, 'Very well. Let\'s see what you have to show me, $N.', NULL, 'Très bien. Voyons voir ce que vous avez à me montrer, $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'anvilward SAY_ANVIL1');
INSERT INTO `script_texts` VALUES (-1000210, 'What manner of trick is this, $R? If you seek to ambush me, I warn you I will not go down quietly!', NULL, 'Quel est le mode d\'emploi de ce truc, $R ? Si vous cherchez à me tendre une embuscade, je vous avertis que je ne me laisserai pas descendre si facilement !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'anvilward SAY_ANVIL2');
INSERT INTO `script_texts` VALUES (-1000211, 'Warning! %s emergency shutdown process initiated by $N. Shutdown will complete in two minutes.', NULL, 'Attention ! Le processus d\'arrêt d\'urgence de la %s a été initialisé par $N. L\'arrêt sera effectif dans deux minutes.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_START');
INSERT INTO `script_texts` VALUES (-1000212, 'Emergency shutdown will complete in one minute.', NULL, 'Le processus d\'arrêt d\'urgence prendra fin dans une minute.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_60');
INSERT INTO `script_texts` VALUES (-1000213, 'Emergency shutdown will complete in thirty seconds.', NULL, 'Le processus d\'arrêt d\'urgence prendra fin dans trente secondes.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_30');
INSERT INTO `script_texts` VALUES (-1000214, 'Emergency shutdown will complete in ten seconds.', NULL, 'Le processus d\'arrêt d\'urgence prendra fin dans dix secondes.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_10');
INSERT INTO `script_texts` VALUES (-1000215, 'Emergency shutdown complete.', NULL, 'Processus d\'arrêt d\'urgence terminé.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000216, 'Emergency shutdown aborted.', NULL, 'Processus d\'arrêt d\'urgence annulé.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_ABORT');
INSERT INTO `script_texts` VALUES (-1000234, 'Follow me, stranger. This won\'t take long.', NULL, 'Suivez-moi. Ce ne sera pas long.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_SERV_0');
INSERT INTO `script_texts` VALUES (-1000235, 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', NULL, '« Shattrath fut jadis la capitale draeneï de ce monde. Son nom signifie : « demeure de lumière ». »', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_1');
INSERT INTO `script_texts` VALUES (-1000236, 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', NULL, 'Quand la Légion ardente retourna la Horde contre les draeneï, la bataille la plus terrible eut lieu ici. Les draeneï combattirent jusqu\'au dernier, mais la ville finit par tomber.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_2');
INSERT INTO `script_texts` VALUES (-1000237, 'The city was left in ruins and darkness... until the Sha\'tar arrived.', NULL, 'La ville fut laissée en ruine, en proie aux ténèbres… jusqu\'à l\'arrivée des Sha\'tar.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_3');
INSERT INTO `script_texts` VALUES (-1000238, 'Let us go into the Lower City. I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', NULL, 'Allons dans la Ville basse. Je dois vous avertir que Shattrath est l\'un des seuls havres de paix de l\'Outreterre, et qu\'elle a donc attiré des foules de réfugiés de toutes les guerres, présentes et passées.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_4');
INSERT INTO `script_texts` VALUES (-1000239, 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', NULL, '« Les Sha\'tar, ou « nés de la lumière », sont les naaru venus en Outreterre pour combattre les démons de la Légion ardente. »', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_5');
INSERT INTO `script_texts` VALUES (-1000240, 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.', NULL, 'Ils furent attirés vers les ruines de Shattrath où un petit groupe de prêtres draeneï survivants continuait à exercer ses rites dans les vestiges de l\'un des temples, ici même.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_6');
INSERT INTO `script_texts` VALUES (-1000241, 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began. The ruined temple is now used as an infirmary for injured refugees.', NULL, 'Le groupe de prêtres, appelé l\'Aldor, reprit rapidement de l\'importance quand la nouvelle du retour des naaru se répandit, et la reconstruction commença peu après. Le temple en ruine sert maintenant d\'infirmerie pour les réfugiés blessés.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_7');
INSERT INTO `script_texts` VALUES (-1000242, 'It wouldn\'t be long, however, before the city came under attack once again. This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', NULL, 'Il ne fallut cependant pas attendre longtemps avant que la ville ne soit à nouveau assaillie. Cette fois, l\'attaque venait des armées d\'Illidan. Un important régiment d\'elfes de sang avait été envoyé par l\'allié d\'Illidan, Kael\'thas Haut-soleil, pour détruire la ville.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_8');
INSERT INTO `script_texts` VALUES (-1000243, 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light. But then the unexpected happened.', NULL, 'Quand le bataillon d\'elfes de sang traversa ce pont, les exarques et redresseurs de torts de l\'Aldor prirent position pour défendre la Terrasse de la Lumière. C\'est alors que l\'inattendu arriva.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_9');
INSERT INTO `script_texts` VALUES (-1000244, 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', NULL, 'Les elfes de sang déposèrent leurs armes devant les défenseurs de la ville. Leur chef, un ancien connu sous le nom de Voren\'thal, pénétra sur la Terrasse de la Lumière et exigea de parler à A\'dal.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_10');
INSERT INTO `script_texts` VALUES (-1000245, 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', NULL, 'À l\'approche du naaru, Voren\'thal s\'agenouilla devant lui et prononça ces paroles : « Je vous ai vu en rêve, naaru. L\'unique espoir de survie de mon peuple est en vous. Mes fidèles et moi-même sommes venus pour vous servir. »', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_11');
INSERT INTO `script_texts` VALUES (-1000246, 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves. Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.', NULL, 'La défection de Voren\'thal et de ses fidèles fut la plus grande perte jamais subie par les forces de Kael. Et il ne s\'agissait pas là de n\'importe quels elfes de sang. Nombre des plus brillants érudits et magistères de Kael avaient suivi l\'influence de Voren\'thal.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_12');
INSERT INTO `script_texts` VALUES (-1000247, 'The naaru accepted the defectors, who would become known as the Scryers; their dwelling lies in the platform above. Only those initiated with the Scryers are allowed there.', NULL, 'Les naaru accueillirent les renégats, qui prirent le nom de Clairvoyants. Leur demeure se trouve là-haut, sur l\'esplanade. Seuls les initiés y sont les bienvenus.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_13');
INSERT INTO `script_texts` VALUES (-1000248, 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', NULL, 'L\'Aldor rassemble des fidèles de la Lumière, et le pardon et la rédemption sont des valeurs qu\'ils apprécient. Cependant, il fut difficile pour eux d\'oublier les exactions des elfes de sang commises sous les ordres de Kael.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_14');
INSERT INTO `script_texts` VALUES (-1000249, 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru. They were not happy to share the city with their former enemies.', NULL, 'Beaucoup de prêtres avaient été tués par les magistères qui juraient désormais de servir les naaru. Les prêtres n\'étaient pas enchantés de devoir cohabiter avec leurs anciens ennemis.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_15');
INSERT INTO `script_texts` VALUES (-1000250, 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above. As a holy site, only the initiated are welcome inside.', NULL, 'Le grand temple sacré de l\'Aldor et ses dépendances se trouvent sur la terrasse, là-haut. Il s\'agit d\'un site sacré et seuls les initiés y sont les bienvenus.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_16');
INSERT INTO `script_texts` VALUES (-1000251, 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', NULL, 'Les attaques contre Shattrath continuèrent, mais la ville ne tomba pas, comme vous pouvez le voir. Bien au contraire, le naaru appelé Xi\'ri mena avec succès une incursion dans la vallée d\'Ombrelune, l\'entrée du domaine d\'Illidan.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_17');
INSERT INTO `script_texts` VALUES (-1000252, 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers. The two factions have not given up on their old feuds, though.', NULL, 'Là-bas, il poursuit sa lutte contre Illidan avec l\'aide de l\'Aldor et des Clairvoyants. Ces deux camps n\'ont cependant pas abandonné leurs vieilles querelles.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_18');
INSERT INTO `script_texts` VALUES (-1000253, 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal. The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', NULL, 'L\'animosité entre eux est telle qu\'ils se disputent l\'honneur d\'aller aider les naaru là-bas. Chaque jour, la décision est prise ici par A\'dal. Les armées se rassemblent ici pour recevoir sa bénédiction avant de partir pour Ombrelune.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_19');
INSERT INTO `script_texts` VALUES (-1000254, 'Khadgar should be ready to see you again. Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.', NULL, 'Khadgar devrait être prêt à vous recevoir à nouveau. N\'oubliez pas que pour servir les Sha\'tar, il vous faudra vous allier à l\'Aldor ou aux Clairvoyants. Rechercher les faveurs de l\'un des deux groupes vous apportera l\'inimitié de l\'autre.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_20');
INSERT INTO `script_texts` VALUES (-1000255, 'Good luck stranger, and welcome to Shattrath City.', NULL, 'Bonne chance, étranger, et bienvenue à Shattrath.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_21');
INSERT INTO `script_texts` VALUES (-1000274, 'Time to teach you a lesson in manners, little $Gboy:girl;!', NULL, 'Il est temps de vous donnez une leçon de savoir vivre, $gpetit gars:petite fille; !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'larry SAY_START');
INSERT INTO `script_texts` VALUES (-1000275, 'Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.', NULL, 'Maintenant, je vous laisse jusqu\'à \'3\' pour déguerpir d\'ici, avant que je ne lache mes chiens sur vous.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'larry SAY_COUNT');
INSERT INTO `script_texts` VALUES (-1000276, '1...', NULL, '1...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'larry SAY_COUNT_1');
INSERT INTO `script_texts` VALUES (-1000277, '2...', NULL, '2...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'larry SAY_COUNT_2');
INSERT INTO `script_texts` VALUES (-1000278, 'Time to meet your maker!', NULL, 'Il est temps de rencontrer votre créateur !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'larry SAY_ATTACK_5');
INSERT INTO `script_texts` VALUES (-1000279, 'Alright, we give up! Don\'t hurt us!', NULL, 'D\'accord, on se rend ! Ne nous faites pas de mal !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'larry SAY_GIVEUP');
INSERT INTO `script_texts` VALUES (-1000280, 'A shadowy, sinister presence has invaded the Emerald Dream. Its power is poised to spill over into our world, $N. We must oppose it! That\'s why I cannot accompany you in person.', NULL, 'Un ténébreux, sinistre présence, a envahi le Rêve d\'Émeraude. Son pouvoir est sur le point de déborder sur notre monde, $N. Nous devons nous opposer à lui ! Raison pour laquelle je ne peux vous accompagner en personne.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'clintar SAY_START');
INSERT INTO `script_texts` VALUES (-1000281, 'The Emerald Dream will never be yours!', NULL, 'Le Rêve d\'Émeraude ne sera jamais vôtre !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'clintar SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1000282, 'Begone from this place!', NULL, 'Sortez de là !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'clintar SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1000283, 'That\'s the first relic, but there are still two more. Follow me, $N.', NULL, 'C\'est la première relique, mais il y en reste encore deux. Suivez-moi, $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'clintar SAY_RELIC1');
INSERT INTO `script_texts` VALUES (-1000284, 'I\'ve recovered the second relic. Take a moment to rest, and then we\'ll continue to the last reliquary.', NULL, 'J\'ai récupéré la deuxième relique. Reposez vous un moment, et ensuite nous continuerons jusqu\'au dernier reliquaire.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'clintar SAY_RELIC2');
INSERT INTO `script_texts` VALUES (-1000285, 'We have all three of the relics, but my energy is rapidly fading. We must make our way back to Dreamwarden Lurosa! He will let you know what to do next.', NULL, 'Nous avons les trois reliques, mais mon énergie s\'estompe rapidement. Nous devons retourner chez Garderêve Lurosa ! Il vous dira que faire maintenant.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'clintar SAY_RELIC3');
INSERT INTO `script_texts` VALUES (-1000286, 'Lurosa, I am entrusting the Relics of Aviana to $N, who will take them to Morthis Whisperwing. I must return completely to the Emerald Dream now. Do not let $N fail!', NULL, 'Lurosa, j\'ai confié les Reliques d\'Aviana à $N, qui va les emmener à Morthis Murmedaile. Je dois retourner dans le Rêve d\'Émeraude maintenant. Ne laissez pas $N faillir !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'clintar SAY_END');
INSERT INTO `script_texts` VALUES (-1000319, 'Let the trial begin, Bloodwrath, attack!', NULL, 'Que l\'épreuve commence, Hargnesang, à l\'attaque !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 1, 0, 'kelerun SayId1');
INSERT INTO `script_texts` VALUES (-1000320, 'Champion Lightrend, make me proud!', NULL, 'Champion Brise-Lumière, rends-moi fier !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 1, 0, 'kelerun SayId2');
INSERT INTO `script_texts` VALUES (-1000321, 'Show this upstart how a real Blood Knight fights, Swiftblade!', NULL, 'Montrez à cet arriviste comment un véritable Chevalier-de-Sang combat , Vivelame !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 1, 0, 'kelerun SayId3');
INSERT INTO `script_texts` VALUES (-1000322, 'Show $n the meaning of pain, Sunstriker!', NULL, 'Montrez à $N la signification du mot \"douleur\", Heurtesoleil !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 1, 0, 'kelerun SayId4');
INSERT INTO `script_texts` VALUES (-1000340, '%s howls in delight at the sight of his lunch!', NULL, '%s hurle de joie à la vue de son déjeuner !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'kyle EMOTE_SEE_LUNCH');
INSERT INTO `script_texts` VALUES (-1000341, '%s eats his lunch.', NULL, '%s mange son déjeuner.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'kyle EMOTE_EAT_LUNCH');
INSERT INTO `script_texts` VALUES (-1000342, '%s thanks you with a special dance.', NULL, '%s vous remercie par une danse spéciale.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'kyle EMOTE_DANCE');
INSERT INTO `script_texts` VALUES (-1000343, 'Is the way clear? Let\'s get out while we can, $N.', NULL, 'La voie est libre ? Sortons pendant que nous le pouvons, $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kayra SAY_START');
INSERT INTO `script_texts` VALUES (-1000344, 'Looks like we won\'t get away so easy. Get ready!', NULL, 'On dirait que nous n’arriverons pas à nous échapper si facilement. Tenez-vous prêt !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kayra SAY_AMBUSH1');
INSERT INTO `script_texts` VALUES (-1000345, 'Let\'s keep moving. We\'re not safe here!', NULL, 'Ne nous arrêtons pas. Nous ne sommes pas en sécurité ici !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kayra SAY_PROGRESS');
INSERT INTO `script_texts` VALUES (-1000346, 'Look out, $N! Enemies ahead!', NULL, 'Attention, $N ! Il y a des ennemis devant !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kayra SAY_AMBUSH2');
INSERT INTO `script_texts` VALUES (-1000347, 'We\'re almost to the refuge! Let\'s go.', NULL, 'Nous sommes presque arrivés au refuge ! Allons-y !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kayra SAY_END');
INSERT INTO `script_texts` VALUES (-1000348, 'Ah...the wondrous sound of kodos. I love the way they make the ground shake... inspect the beast for me.', NULL, 'Ah... le son merveilleux des kodos. J\'aime leur façon de remuer la terre... contrôlez cette bête pour moi.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kodo round SAY_SMEED_HOME_1');
INSERT INTO `script_texts` VALUES (-1000349, 'Hey, look out with that kodo! You had better inspect that beast before i give you credit!', NULL, 'Hey, faites attention avec ce kodo ! Vous devriez mieux contrôler cette bête avant que je ne vous fasse crédit !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kodo round SAY_SMEED_HOME_2');
INSERT INTO `script_texts` VALUES (-1000350, 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', NULL, 'Il est certain que ce kodo est une merveille. Attendez une minute, où sont mes lunettes à double foyer ? Peut-être devriez vous contrôler la bête pour moi.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kodo round SAY_SMEED_HOME_3');
INSERT INTO `script_texts` VALUES (-1000351, 'You, there! Hand over that moonstone and nobody gets hurt!', NULL, 'Vous, là ! Reposez cette Pierre de Lune et personne ne sera blessé !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'sprysprocket SAY_START');
INSERT INTO `script_texts` VALUES (-1000352, '%s takes the Southfury moonstone and escapes into the river. Follow her!', NULL, '%s s\'empare de la Pierre de lune de la Furie-du-sud et s\'enfuit en direction de la rivière. Suivez-là !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'sprysprocket EMOTE_START');
INSERT INTO `script_texts` VALUES (-1000353, 'Just chill!', NULL, 'Reste relax !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `script_texts` VALUES (-1000354, 'Stupid grenade picked a fine time to backfire! So much for high quality goblin engineering!', NULL, 'Cette stupide grenade a bien choisi son moment pour m\'ennuyer ! C\'en est fini de l\'ingénierie gobeline de haute qualité !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'sprysprocket SAY_GRENADE_FAIL');
INSERT INTO `script_texts` VALUES (-1000355, 'All right, you win! I surrender! Just don\'t hurt me!', NULL, 'Ça va, vous avez gagné ! Je me rends ! Ne me frappez plus !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'sprysprocket SAY_END');
INSERT INTO `script_texts` VALUES (-1000360, 'The strands of LIFE have been severed! The Dreamers must be avenged!', NULL, 'Les liens de VIE ont été rompus ! Les Rêveurs doivent être vengés !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, ' ysondre SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000361, 'Come forth, ye Dreamers – and claim your vengeance!', NULL, 'Venez, vous les Rêveurs.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, ' ysondre SAY_SUMMONDRUIDS');
INSERT INTO `script_texts` VALUES (-1000362, 'Let\'s go $N. I am ready to reach Whitereach Post.', NULL, 'Allons-y $N. Je suis prêt à rejoindre Blanc-relais.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'paoka SAY_START');
INSERT INTO `script_texts` VALUES (-1000363, 'Now this looks familiar. If we keep heading east, I think we can... Ahh, Wyvern on the attack!', NULL, 'Tout ça me paraît familier maintenant. Si nous continuons en direction de l\'est, je pense que nous pouvons...  Ahh, des Wyvernes nous attaquent !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'paoka SAY_WYVERN');
INSERT INTO `script_texts` VALUES (-1000364, 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', NULL, 'Merci beaucoup... Je peux retrouver le chemin du retour vers Blanc-relais à partir d\'ici. Assurez-vous de parler avec Motega Crin-de-feu. Peut-être pourrez-vous le persuader de me renvoyer à la maison.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'paoka SAY_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000365, 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_START');
INSERT INTO `script_texts` VALUES (-1000366, 'Look out, the Grimtotem are upon us!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_LOOK_OUT');
INSERT INTO `script_texts` VALUES (-1000367, 'Here they come.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_HERE_COME');
INSERT INTO `script_texts` VALUES (-1000368, 'More Grimtotems are coming this way!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_MORE');
INSERT INTO `script_texts` VALUES (-1000369, 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_END');
INSERT INTO `script_texts` VALUES (-1000381, 'I sense the tortured spirits, $n. They are this way, come quickly!', NULL, 'Je sens les esprits torturés, $N. Ils sont par ici, venez vite !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'wilda SAY_WIL_START');
INSERT INTO `script_texts` VALUES (-1000382, 'Watch out!', NULL, 'Faîtes attention !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_AGGRO1');
INSERT INTO `script_texts` VALUES (-1000383, 'Naga attackers! Defend yourself!', NULL, 'Des Nagas nous attaquent ! Défendez-vous !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_AGGRO2');
INSERT INTO `script_texts` VALUES (-1000384, 'Grant me protection $n, i must break trough their foul magic!', NULL, 'Accordez-moi votre protection $N, je dois briser leur magie répugnante !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `script_texts` VALUES (-1000385, 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', NULL, 'Les Nagas de Glissentaille sont exceptionnellement cruels envers leurs prisonniers. C\'est un miracle si j\'ai survécu aussi longtemps à l\'intérieur de cette prison aquatique. Que notre Mère la Terre soit louée.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `script_texts` VALUES (-1000386, 'Now we must find the exit.', NULL, 'Maintenant nous devons trouver la sortie.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `script_texts` VALUES (-1000387, 'Lady Vashj must answer for these atrocities. She must be brought to justice!', NULL, 'Lady Vashj doît répondre de ces atrocités. Elle doît être trainée devant la justice !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `script_texts` VALUES (-1000388, 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', NULL, 'Le caractère tumultueux des grands cours d\'eau d\'Azeroth et de Draenor est la conséquence directe des actions des esprits aquatiques tourmentés.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `script_texts` VALUES (-1000389, 'It shouldn\'t be much further, $n. The exit is just up ahead.', NULL, 'Il ne faut pas aller beaucoup plus loin, $N. La sortie est juste ici.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `script_texts` VALUES (-1000390, 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', NULL, 'Merci, $N. S\'il vous plaît, retournez chez mes frères, à l\'autel de Damnation, près de la main de Gul\'dan, et dites-leur que Wilda est en lieu sûr. Que notre Mère la Terre veille sur vous...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_END');
INSERT INTO `script_texts` VALUES (-1000399, 'Peace is but a fleeting dream! Let the NIGHTMARE reign!', NULL, 'La paix est un rêve éphémère ! Que le CAUCHEMARD règne !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'taerar SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000400, 'Children of Madness - I release you upon this world!', NULL, 'Enfant de la Folie - Je vous libère de ce monde !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'taerar SAY_SUMMONSHADE');
INSERT INTO `script_texts` VALUES (-1000401, 'Hope is a DISEASE of the soul! This land shall wither and die!', NULL, 'L\'espoir est une MALADIE de l\'âme ! Ces terres vont flétrir et mourir !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'emeriss SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000402, 'Taste your world\'s corruption!', NULL, 'Goûtez le monde de la corruption !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'emeriss SAY_CASTCORRUPTION');
INSERT INTO `script_texts` VALUES (-1000411, 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak to you back in the tower.', NULL, 'Pourquoi ne traitons-nous pas avec vous maintenant, Hendel ? Dame Portvaillant va vouloir vous parler de votre retour dans la tour.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `script_texts` VALUES (-1000412, 'Please... please... Miss Proudmore. I didn\'t mean to...', NULL, 'S\'il vous plaît... s\'il vous plaît... Mademoiselle Portvaillant. Je ne voulais pas...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `script_texts` VALUES (-1000413, 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', NULL, 'Je suis désolé d\'avoir été si long. Je désirais que Dame Portvaillant soit également présente.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `script_texts` VALUES (-1000414, 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', NULL, 'Nous devons retourner à la tour. Si vous souhaitez nous parler d\'avantage, vous devez nous retrouver là-bas.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `script_texts` VALUES (-1000415, '%s, too injured, gives up the chase.', NULL, 'Ayant subi trop de blessures, %s renonce à la poursuite.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'hendel EMOTE_SURRENDER');
INSERT INTO `script_texts` VALUES (-1000416, 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', NULL, 'Ma foi, je ne sais pas combien de temps je tiendrai dans cet état... Je me sens bien faible...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_START_1');
INSERT INTO `script_texts` VALUES (-1000417, 'Remember, if I faint again, the water that Spraggle gave you will revive me.', NULL, 'Souvenez-vous, si je m\'évanouis encore, l\'eau que Spraggle vous a donnée me rendra des forces.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_START_2');
INSERT INTO `script_texts` VALUES (-1000418, 'The heat... I can\'t take it...', NULL, 'La chaleur... Je ne la supporte pas...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_1');
INSERT INTO `script_texts` VALUES (-1000419, 'Maybe... you could carry me?', NULL, 'Peut-être... pourriez-vous me porter ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_2');
INSERT INTO `script_texts` VALUES (-1000420, 'Uuuuuuggggghhhhh....', NULL, 'Uuuuuuggggghhhhh....', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_3');
INSERT INTO `script_texts` VALUES (-1000421, 'I\'m not feeling so well...', NULL, 'Je ne me sens pas bien...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_4');
INSERT INTO `script_texts` VALUES (-1000422, 'Where... Where am I?', NULL, 'Où... Où suis-je ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_1');
INSERT INTO `script_texts` VALUES (-1000423, 'I am feeling a little better now, thank you.', NULL, 'Je me sens un peu mieux maintenant, merci.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_2');
INSERT INTO `script_texts` VALUES (-1000424, 'Yes, I must go on.', NULL, 'Oui, je dois continuer.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_3');
INSERT INTO `script_texts` VALUES (-1000425, 'How am I feeling? Quite soaked, thank you.', NULL, 'Comment je me sens ? Tout trempé, merci.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_4');
INSERT INTO `script_texts` VALUES (-1000426, 'Spraggle! I didn\'t think I\'d make it back!', NULL, 'Spraggle ! Je ne pensais pas que je m\'en sortirais !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_END_1');
INSERT INTO `script_texts` VALUES (-1000427, 'Ringo! You\'re okay!', NULL, 'Ringo ! Tu vas bien !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_SPR_END_2');
INSERT INTO `script_texts` VALUES (-1000428, 'Oh... I\'m feeling faint...', NULL, 'Oh... Je me sens si faible...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_END_3');
INSERT INTO `script_texts` VALUES (-1000429, '%s collapses onto the ground.', NULL, '%s s\'évanouit et tombe au sol.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'ringo EMOTE_RIN_END_4');
INSERT INTO `script_texts` VALUES (-1000430, '%s stands up after a short pause.', NULL, '%s se relève après une courte pause.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'ringo EMOTE_RIN_END_5');
INSERT INTO `script_texts` VALUES (-1000431, 'Ugh.', NULL, 'Ugh.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_END_6');
INSERT INTO `script_texts` VALUES (-1000432, 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', NULL, 'Ringo ? Réveille-toi ! Ne te fais pas de souci, je vais m\'occuper de toi.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ringo SAY_SPR_END_7');
INSERT INTO `script_texts` VALUES (-1000433, '%s fades away after a long pause.', NULL, '%s s\'affaiblit après une longue pause.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'ringo EMOTE_RIN_END_8');
INSERT INTO `script_texts` VALUES (-1000446, 'A-Me good. Good, A-Me. Follow... follow A-Me. Home. A-Me go home.', NULL, 'A-mi gentil. Gentil, A-mi. Suivez... suivez A-mi. Maison. A-mi retourne maison.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_START');
INSERT INTO `script_texts` VALUES (-1000447, 'Good... good, A-Me. A-Me good. Home. Find home.', NULL, 'Gentil... gentil, A-mi. A-mi gentil. Maison. Retrouver maison.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_PROGRESS');
INSERT INTO `script_texts` VALUES (-1000448, 'A-Me home! A-Me good! Good A-Me. Home. Home. Home.', NULL, 'A-mi maison ! A-mi gentil ! Gentil A-mi. Maison. Maison. Maison.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_END');
INSERT INTO `script_texts` VALUES (-1000449, '$c, no hurt A-Me. A-Me good.', NULL, '$C, pas frapper A-mi. A-mi gentil.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_AGGRO1');
INSERT INTO `script_texts` VALUES (-1000450, 'Danger. Danger! $c try hurt A-Me.', NULL, 'Danger. Danger ! $C essai de frapper A-mi.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_AGGRO2');
INSERT INTO `script_texts` VALUES (-1000451, 'Bad, $c. $c, bad!', NULL, 'Méchant, $c. $C, méchant !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_AGGRO3');
INSERT INTO `script_texts` VALUES (-1000472, '1...', NULL, '1...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_1');
INSERT INTO `script_texts` VALUES (-1000473, '2...', NULL, '2...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_2');
INSERT INTO `script_texts` VALUES (-1000474, '3...', NULL, '3...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_3');
INSERT INTO `script_texts` VALUES (-1000475, '4...', NULL, '4...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_4');
INSERT INTO `script_texts` VALUES (-1000476, '5...', NULL, '5...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_5');
INSERT INTO `script_texts` VALUES (-1000477, 'Let us leave this place. I\'ve had enough of these madmen!', NULL, 'Partons d\'ici. J\'en ai assez de ces fous !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_START');
INSERT INTO `script_texts` VALUES (-1000478, 'You\'ll go nowhere, fool!', NULL, 'Vous n\'irez nul part, abruti !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_AMBUSH_A');
INSERT INTO `script_texts` VALUES (-1000479, 'Beware! More cultists come!', NULL, 'Attention ! D\'autres cultistes arrivent !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_AMBUSH_B');
INSERT INTO `script_texts` VALUES (-1000480, 'You will not escape us so easily!', NULL, 'Tu ne t\'échapperas pas si facilement !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_AMBUSH_B_REPLY');
INSERT INTO `script_texts` VALUES (-1000481, 'I can find my way from here. My friend Mekeda will reward you for your kind actions.', NULL, 'Je trouverai mon chemin à partir d\'ici. Mekeda vous récompensera pour cette bonne action.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000482, 'Look out!', NULL, 'Faites attention !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_START');
INSERT INTO `script_texts` VALUES (-1000483, 'Don\'t let them escape! Kill the strong one first!', NULL, 'Ne les laissez pas s\'échapper ! Tuez le plus fort en premier !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_NO_ESCAPE');
INSERT INTO `script_texts` VALUES (-1000484, 'More of them coming! Watch out!', NULL, 'Plusieurs d\'entre eux arrivent ! Faites attention !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_MORE');
INSERT INTO `script_texts` VALUES (-1000485, 'Where do you think you\'re going? Kill them all!', NULL, 'Où est-ce que vous pensez aller ? Tuez-les tous !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_MORE_REPLY');
INSERT INTO `script_texts` VALUES (-1000486, 'Ride the lightning, filth!', NULL, 'Que la foudre s\'abatte sur toi, ordure !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_LIGHTNING');
INSERT INTO `script_texts` VALUES (-1000487, 'FROST SHOCK!!!', NULL, 'HORION DE GIVRE !!!', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_SHOCK');
INSERT INTO `script_texts` VALUES (-1000488, 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell stranger.', NULL, 'Il vaut mieux nous séparer maintenant, au cas où ils en envoient encore plus à nos trousses. Espérons qu\'au moins un de nous puisse revenir vers Garrosh. Adieu étranger.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000489, 'Show our guest around Shattrath, will you? Keep an eye out for pickpockets in the lower city.', NULL, 'Faites visiter Shattrath à notre invité, voulez-vous ? Faites attention aux voleurs à la tire dans la Ville basse.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_START');
INSERT INTO `script_texts` VALUES (-1000490, 'A newcomer to Shattrath! Make sure to drop by later. We can always use a hand with the injured.', NULL, 'Un nouveau venu à Shattrath ! Revenez-nous voir plus tard. Nous tendons toujours une main amicale aux blessés.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_INJURED');
INSERT INTO `script_texts` VALUES (-1000491, 'Be mindful of what you say, this one\'s being shown around by Khadgar\'s pet.', NULL, 'Surveille tes paroles, c\'est le familier de Khadgar qui lui fait visiter la ville.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_MIND_YOU');
INSERT INTO `script_texts` VALUES (-1000492, 'Are you joking? I\'m a Scryer, I always watch what i say. More enemies than allies in this city, it seems.', NULL, 'Est-ce que vous plaisantez ? Je suis un Clairvoyant, je fais toujours attention à ce que je dis. On dirait qu\'il y a plus d\'ennemis que d\'alliés dans cette ville.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_MIND_ALWAYS');
INSERT INTO `script_texts` VALUES (-1000493, 'Light be with you, $n. And welcome to Shattrath.', NULL, 'Que la Lumière soit avec vous, $N. Et bienvenue à Shattrath.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_ALDOR_GREET');
INSERT INTO `script_texts` VALUES (-1000494, 'We\'re rather selective of who we befriend, $n. You think you have what it takes?', NULL, 'Nous sommes plutôt sélectif avec ceux qui veulent être nos amis, $N. Vous croyez avoir ce qu\'il faut ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_SCRYER_GREET');
INSERT INTO `script_texts` VALUES (-1000495, 'Khadgar himself is showing you around? You must have made a good impression, $n.', NULL, 'C’est Khadgar lui-même qui vous fait la visite ? Vous devez lui avoir fait une très bonne impression, $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_HAGGARD');
INSERT INTO `script_texts` VALUES (-1000496, '%s lifts its head into the air, as if listening for something.', NULL, '%s lève la tête en l\'air, comme s\'il écoutait quelque chose.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT INTO `script_texts` VALUES (-1000497, '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', NULL, '%s pousse un hurlement qui retentit à travers les montagnes du nord et vous fait signe de le suivre.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT INTO `script_texts` VALUES (-1000498, 'Welcome, kind spirit. What has brought you to us?', NULL, 'Bienvenue, esprit bienfaisant. Qu\'est ce qui vous amène ici ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `script_texts` VALUES (-1000510, 'Please return to Zoram\'gar and report our success to the Warsong runner.', NULL, 'Je vous en prie, retournez à Zoram\'gar et faites un rapport de notre réussite au messager Chanteguerre.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'muglash SAY_MUG_RETURN');
INSERT INTO `script_texts` VALUES (-1000511, 'Aright, listen up! Form a circle around me and move out!', NULL, 'Bon, écoutez-moi ! Faites un cercle autour de moi et allez-y !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_START');
INSERT INTO `script_texts` VALUES (-1000512, 'Aright, $r, just keep us safe from harm while we work. We\'ll pay you when we return.', NULL, 'Très bien, $r, il suffira de nous protéger pendant notre travail. Nous vous paierons à votre retour.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_KEEP_SAFE');
INSERT INTO `script_texts` VALUES (-1000513, 'The dig site is just north of here.', NULL, 'Le site de fouilles se trouve au nord.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NORTH');
INSERT INTO `script_texts` VALUES (-1000514, 'We\'re here! Start diggin\'!', NULL, 'Nous y sommes ! Commencez les fouilles !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_ARRIVE');
INSERT INTO `script_texts` VALUES (-1000515, 'I think there\'s somethin\' buried here, beneath the sand!', NULL, 'Je crois qu\'il y a quelque chose d\'enterré ici, sous le sable !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_BURIED');
INSERT INTO `script_texts` VALUES (-1000516, 'Almost got it!', NULL, 'J\'y suis presque !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_ALMOST');
INSERT INTO `script_texts` VALUES (-1000517, 'By brann\'s brittle bananas! What is it!? It... It looks like a drum.', NULL, 'Par les bananes banales de Brann ! Qu\'est-ce que c\'est que ça ?! On dirait… un tambour.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DRUM');
INSERT INTO `script_texts` VALUES (-1000518, 'Wow... a drum.', NULL, 'Wahou… un tambour.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DRUM_REPLY');
INSERT INTO `script_texts` VALUES (-1000519, 'This discovery will surely rock the foundation of modern archaeology.', NULL, 'Cette découverte ébranlera sûrement les fondations de l\'archéologie moderne.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DISCOVERY');
INSERT INTO `script_texts` VALUES (-1000520, 'Yea, great. Can we leave now? This desert is giving me hives.', NULL, 'Ouais, super. On peut y aller, maintenant ? Ce désert me donne des boutons.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DISCOVERY_REPLY');
INSERT INTO `script_texts` VALUES (-1000521, 'Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir!', NULL, 'Mais qu\'est-ce que tu racontes ? Tu voudrais que j\'abandonne un tambour sans avoir tapé dessus d\'abord ? Nom d\'un fils de Forgefer, jamais ! Oh que non !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE');
INSERT INTO `script_texts` VALUES (-1000522, 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', NULL, 'Ça me rappelle la fois où tu nous as fait fouiller tout Silithus à la recherche de preuves de l\'existence des gnomes des sables.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY1');
INSERT INTO `script_texts` VALUES (-1000523, 'Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century?', NULL, 'Ou la fois où tu nous as expliqué que tu avais trouvé un remède à la peste du 20ème siècle. C\'est quoi, d\'abord, ce truc, le 20ème siècle ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY2');
INSERT INTO `script_texts` VALUES (-1000524, 'I don\'t think it can top the one time where he told us that he\'d heard that Artha\'s \"cousin\'s\" skeleton was frozen beneath a glacier in Winterspring. I\'ll never forgive you for that one, Letoll. I mean honestly... Artha\'s cousin?', NULL, 'Je crois que ça ne pourra jamais dépasser la fois où il nous a dit que le squelette du « cousin » d\'Arthas était pris dans un glacier du Berceau-de-l\'hiver. Je ne te la pardonnerai jamais, celle-là, Leglas. Non mais sérieusement… Le cousin d\'Arthas ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY3');
INSERT INTO `script_texts` VALUES (-1000525, 'I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', NULL, 'Je sais pas. Ça ne peut quand même pas battre la fois où il a essayé de nous convaincre qu\'en fait on n\'est tous que le produit de l\'imagination de quelqu\'un, et qu\'on sert juste à son amusement. Ça a été bien reçu, en plein dîner de famille.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY4');
INSERT INTO `script_texts` VALUES (-1000526, 'Shut yer yaps! I\'m gonna bang on this drum and that\'s that!', NULL, 'Fermez-la ! Je vais taper sur ce tambour un point c\'est tout !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_SHUT');
INSERT INTO `script_texts` VALUES (-1000527, 'Say, do you guys hear that?', NULL, 'Eh, les gars, vous entendez ce que j\'entends ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_REPLY_HEAR');
INSERT INTO `script_texts` VALUES (-1000528, 'IN YOUR FACE! I told you there was somethin\' here!', NULL, 'EN PLEIN DANS LES DENTS ! Je vous avais bien dit qu\'il y avait quelque chose !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_IN_YOUR_FACE');
INSERT INTO `script_texts` VALUES (-1000529, 'Don\'t just stand there! Help him out!', NULL, 'Restez pas plantés là ! Aidez-le à sortir !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_HELP_HIM');
INSERT INTO `script_texts` VALUES (-1000530, '%s picks up the drum.', NULL, '%s ramasse le tambour.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'letoll EMOTE_LE_PICK_UP');
INSERT INTO `script_texts` VALUES (-1000531, 'You\'ve been a tremendous help, $r! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably.', NULL, 'Votre aide a vraiment été très précieuse, $r ! Sortons d\'ici avant qu\'il y en ait d\'autres qui arrivent ! Vous pourrez dire à Nanowitz que vous avez admirablement rempli votre mission.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_THANKS');
INSERT INTO `script_texts` VALUES (-1000532, 'At your command, my liege...', NULL, 'À vos ordres, mon seigneur…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'torloth TORLOTH_DIALOGUE1');
INSERT INTO `script_texts` VALUES (-1000533, 'As you desire, Lord Illidan.', NULL, 'Comme vous le souhaitez, seigneur Illidan.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'torloth TORLOTH_DIALOGUE2');
INSERT INTO `script_texts` VALUES (-1000534, 'Yes, Lord Illidan, I would sacrifice to you this magnificent physique. On this day you will fall - another victim of Torloth...', NULL, 'Pour le seigneur Illidan, je sacrifierais tout, même mon physique avantageux. Ce jour verra votre perte. Une nouvelle victime pour Torloth…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'torloth TORLOTH_DIALOGUE3');
INSERT INTO `script_texts` VALUES (-1000535, 'Destroy them, Torloth. Let lose their blood like a river upon this hallowed ground.', NULL, 'Détruis-les, Torloth. Que leur sang se répande tel une rivière sur ce sol sacré.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'lordillidan ILLIDAN_DIALOGUE');
INSERT INTO `script_texts` VALUES (-1000536, 'What manner of fool dares stand before Illidan Stormrage? Soldiers, destroy these insects!', NULL, 'Quel genre d\'idiot peut donc oser s\'opposer à Illidan Hurlorage ? Soldats, détruisez ces insectes !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON1');
INSERT INTO `script_texts` VALUES (-1000537, 'You are no challenge for the Crimson Sigil. Mind breakers, end this nonsense.', NULL, 'Vous n\'êtes pas de taille contre le Cachet cramoisi. Briseurs d\'esprit, finissez-en avec ces inepties !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON2');
INSERT INTO `script_texts` VALUES (-1000538, 'Run while you still can. The highlords come soon...', NULL, 'Fuyez tant qu\'il en est encore temps. Voici qu\'arrivent les généralissimes…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON3');
INSERT INTO `script_texts` VALUES (-1000539, 'Torloth your master calls!', NULL, 'Torloth, ton maître t\'appelle !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON4');
INSERT INTO `script_texts` VALUES (-1000540, 'So you have defeated the Crimson Sigil. You now seek to challenge my rule? Not even Arthas could defeat me, yet you dare to even harbor such thoughts? Then I say to you, come! Come $N! The Black Temple awaits...', NULL, 'Vous avez donc vaincu le Cachet cramoisi. Et vous voulez maintenant défier mon autorité ? Même Arthas n\'a pas pu me terrasser, et vous osez pourtant en rêver ? Très bien, venez ! Venez, $N, le Temple noir vous attend…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'lordillidan EVENT_COMPLETED');
INSERT INTO `script_texts` VALUES (-1000543, 'Why have you come here, outsider? You will only find pain! Our fate will be yours...', NULL, 'Pourquoi êtes-vous $gvenu:venue;, $gétranger:étrangères; ? Il n\'y a que de la souffrance ici. Vous partagerez notre destin...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_1');
INSERT INTO `script_texts` VALUES (-1000544, 'It was ... terrible... the demon...', NULL, 'C\'était... terrible... le démon...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_2');
INSERT INTO `script_texts` VALUES (-1000545, 'The darkness... the corruption... they came too quickly for anyone to know...', NULL, 'Les ténèbres… la corruption… ils sont venus si vite, personne n\'a eu le temps de s\'en aperçevoir…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_3');
INSERT INTO `script_texts` VALUES (-1000546, 'The darkness will consume all... all the living...', NULL, 'Les ténèbres… vont dévorer… tous les vivants…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_4');
INSERT INTO `script_texts` VALUES (-1000547, 'It is too late for us, living one. Take yourself and your friend away from here before you both are... claimed...', NULL, 'Il est trop tard pour nous, être vivant. Partez avec votre ami avant d\'être… pris tous les deux.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_5');
INSERT INTO `script_texts` VALUES (-1000548, 'It is too late for Jarl... its hold is too strong...', NULL, 'C\'est trop tard pour Jarl... Cette cale est trop lourde...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_6');
INSERT INTO `script_texts` VALUES (-1000549, 'Go away, whoever you are! Witch Hill is mine... mine!', NULL, 'Allez-vous-en, qui que vous soyez ! La Colline des sorcières est à moi et rien qu’à moi !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_7');
INSERT INTO `script_texts` VALUES (-1000550, 'The manor... someone else... will soon be consumed...', NULL, 'Le manoir… quelqu\'un d\'autre… sera bientôt dévoré…', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_8');
INSERT INTO `script_texts` VALUES (-1033015, '%s fumbles with the rusty lock on the courtyard door.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 69, 'prisoner adamant EMOTE_UNLOCK_DOOR_AD');
INSERT INTO `script_texts` VALUES (-1036000, 'You there, check out that noise!', NULL, 'Vous là ! Occupez-vous de ce bruit bizarre.', NULL, NULL, NULL, NULL, NULL, NULL, 5775, 6, 7, 0, 'smite INST_SAY_ALARM1');
INSERT INTO `script_texts` VALUES (-1036001, 'We\'re under attack! A vast, ye swabs! Repel the invaders!', NULL, 'Nous sommes attaqués ! Repoussez les envahisseurs !', NULL, NULL, NULL, NULL, NULL, NULL, 5777, 6, 7, 0, 'smite INST_SAY_ALARM2');
INSERT INTO `script_texts` VALUES (-1109000, 'The walls of the chamber tremble. Something is happening...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'malfurion stormrage EMOTE_MALFURION');
INSERT INTO `script_texts` VALUES (-1109001, 'Be steadfast, champion. I know why it is that you are here and I know what it is that you seek. Eranikus will not give up the shard freely. He has been twisted... twisted by the same force that you seek to destroy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'malfurion stormrge SAY_MALFURION1');
INSERT INTO `script_texts` VALUES (-1109002, 'Are you really surprised? Is it hard to believe that the power of an Old God could reach even inside the Dream? It is true - Eranikus, Tyrant of the Dream, wages a battle against us all. The Nightmare follows in his wake of destruction.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'malfurion stormrge SAY_MALFURION2');
INSERT INTO `script_texts` VALUES (-1109003, 'Understand this, Eranikus wants nothing more than to be brought to Azeroth from the Dream. Once he is out, he will stop at nothing to destroy my physical manifestation. This, however, is the only way in which you could recover the scepter shard.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'malfurion stormrge SAY_MAFLURION3');
INSERT INTO `script_texts` VALUES (-1109004, 'You will bring him back into this world, champion.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'malfurion Stormrge SAY_MALFURION4');
INSERT INTO `script_texts` VALUES (-1189016, 'We hunger for vengeance.', NULL, 'Nous avons soif de vengeance !', NULL, NULL, NULL, NULL, NULL, NULL, 5844, 1, 0, 0, 'thalnos SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1189017, 'No rest, for the angry dead.', NULL, 'Pas de repos... pour les morts en colère !', NULL, NULL, NULL, NULL, NULL, NULL, 5846, 1, 0, 0, 'thalnos SAY_HEALTH');
INSERT INTO `script_texts` VALUES (-1189018, 'More... More souls.', NULL, 'Encore... encore des âmes.', NULL, NULL, NULL, NULL, NULL, NULL, 5845, 1, 0, 0, 'thalnos SAY_KILL');
INSERT INTO `script_texts` VALUES (-1230000, 'Ah, hits the spot!', NULL, 'Ah, frappe la cible !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_GOT_BEER');
INSERT INTO `script_texts` VALUES (-1230001, 'Come to aid the Throne!', NULL, 'Venez aider le Trône !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'dagran SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1230002, 'Hail to the king, baby!', NULL, 'Honneur au roi !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'dagran SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1309000, 'Let the coils of hate unfurl!', NULL, 'Que se déroulent les anneaux de la haine !', NULL, NULL, NULL, NULL, NULL, NULL, 8421, 1, 0, 0, 'venoxis SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1309001, 'Ssserenity..at lassst!', NULL, 'La ssssérénité. Enfffffin !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'venoxis SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309002, 'Lord Hir\'eek, grant me wings of vengance!', NULL, 'Seigneur Hir’eek, accorde-moi les ailes de la vengeance !', NULL, NULL, NULL, NULL, NULL, NULL, 8417, 1, 0, 0, 'jeklik SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309003, 'I command you to rain fire down upon these invaders!', NULL, 'Qu\'une pluie de feu s\'abatte sur les envahisseurs !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'jeklik SAY_RAIN_FIRE');
INSERT INTO `script_texts` VALUES (-1309004, 'Finally ...death. Curse you Hakkar! Curse you!', NULL, 'Enfin... la mort ! Sois maudit, Hakkar ! Sois maudit !', NULL, NULL, NULL, NULL, NULL, NULL, 8422, 1, 0, 0, 'jeklik SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309005, 'Draw me to your web mistress Shadra. Unleash your venom!', NULL, 'Entraine moi dans ta toile dame Shadra ! Déchaine ton venin !', NULL, NULL, NULL, NULL, NULL, NULL, 8418, 1, 0, 0, 'marli SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309006, 'Shadra, make of me your avatar!', NULL, 'Shadra, fais de moi ton avatar !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'marli SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1309007, 'Aid me my brood!', NULL, 'Aidez-moi, mes enfants !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'marli SAY_SPIDER_SPAWN');
INSERT INTO `script_texts` VALUES (-1309008, 'Bless you mortal for this release. Hakkar controls me no longer...', NULL, 'Soyez bénis, mortels, pour m\'avoir libérée… Hakkar ne me contrôle plus !', NULL, NULL, NULL, NULL, NULL, NULL, 8423, 1, 0, 0, 'marli SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309009, 'Shirvallah, fill me with your RAGE!', NULL, 'Shirvallah, que ta RAGE m’envahisse !', NULL, NULL, NULL, NULL, NULL, NULL, 8419, 1, 0, 0, 'thekal SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309010, 'Hakkar binds me no more! Peace at last!', NULL, 'Hakkar ne me domine plus ! Je connais enfin la paix !', NULL, NULL, NULL, NULL, NULL, NULL, 8424, 1, 0, 0, 'thekal SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309011, 'Bethekk, your priestess calls upon your might!', NULL, 'Bethekk, ta prêtresse en appelle à ta puissance !', NULL, NULL, NULL, NULL, NULL, NULL, 8416, 1, 0, 0, 'arlokk SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309012, 'Feast on $n, my pretties!', NULL, 'Dévorez $N, mes jolies !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'arlokk SAY_FEAST_PANTHER');
INSERT INTO `script_texts` VALUES (-1309013, 'At last, I am free of the Soulflayer!', NULL, 'Enfin ! Je suis enfin libérée de l\'Écorcheur d\'âmes !', NULL, NULL, NULL, NULL, NULL, NULL, 8412, 1, 0, 0, 'arlokk SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309015, 'I\'ll feed your souls to Hakkar himself!', NULL, 'Je jetterai vos âmes en pâture à Hakkar lui-même !', NULL, NULL, NULL, NULL, NULL, NULL, 8413, 1, 0, 0, 'mandokir SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309016, 'DING!', NULL, 'DING !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'mandokir SAY_DING_KILL');
INSERT INTO `script_texts` VALUES (-1309017, 'GRATS!', NULL, 'GRATS!', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'mandokir SAY_GRATS_JINDO');
INSERT INTO `script_texts` VALUES (-1309018, '$N! I\'m watching you!', NULL, '$N, je vous ai à l\'œil !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'mandokir SAY_WATCH');
INSERT INTO `script_texts` VALUES (-1309019, 'Don\'t make me angry. You won\'t like it when I\'m angry.', NULL, 'Ne me mettez pas en colère. Vous n\'allez pas aimer quand je suis colère.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 0, 0, 'mandokir SAY_WATCH_WHISPER');
INSERT INTO `script_texts` VALUES (-1309020, 'PRIDE HERALDS THE END OF YOUR WORLD. COME, MORTALS! FACE THE WRATH OF THE SOULFLAYER!', NULL, 'L’ORGUEIL ANNONCE LA FIN DE VOTRE MONDE. AVANCEZ, MORTELS ! AFFRONTEZ LA COLÈRE DE L’ÉCORCHEUR D’ÂMES !', NULL, NULL, NULL, NULL, NULL, NULL, 8414, 1, 0, 0, 'hakkar SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309021, 'Fleeing will do you no good, mortals!', NULL, 'Fuir ne vous servira à rien, mortels !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'hakkar SAY_FLEEING');
INSERT INTO `script_texts` VALUES (-1309022, 'You dare set foot upon Hakkari holy ground? Minions of Hakkar, destroy the infidels!', NULL, 'Vous osez poser le pied sur le sol sacré des Hakkari ? Serviteurs d’Hakkar, détruisez les infidèles !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'hakkar SAY_MINION_DESTROY');
INSERT INTO `script_texts` VALUES (-1309023, 'Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all!', NULL, 'Serviteurs d’Hakkar, écoutez votre dieu. Son temple a été profané. Des envahisseurs ont pénétré sur son sol sacré ! Protégez l’Autel de sang ! Tuez-les tous !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'hakkar SAY_PROTECT_ALTAR');
INSERT INTO `script_texts` VALUES (-1309024, '%s goes into a rage after seeing his raptor fall in battle!', NULL, '%s devient fou furieux en voyant son raptor mourir durant le combat !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'mandokir EMOTE_RAGE');
INSERT INTO `script_texts` VALUES (-1329000, 'Thanks to Egan', NULL, 'Remerciez Egan.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED0');
INSERT INTO `script_texts` VALUES (-1329001, 'Rivendare must die', NULL, 'Vaillefendre doit mourir.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED1');
INSERT INTO `script_texts` VALUES (-1329002, 'Who you gonna call?', NULL, 'Qui allez vous appeler ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED2');
INSERT INTO `script_texts` VALUES (-1329003, 'Don\'t cross those beams!', NULL, 'Ne croisez pas les faisceaux !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED3');
INSERT INTO `script_texts` VALUES (-1409000, '%s performs one last service for Ragnaros.', NULL, '%s accomplit un dernier service pour Ragnaros...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'geddon EMOTE_SERVICE');
INSERT INTO `script_texts` VALUES (-1409001, '%s forces one of his Firesworn Minions to erupt!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'garr EMOTE_MASSIVE_ERUPTION');
INSERT INTO `script_texts` VALUES (-1409002, '%s refuses to die while its master is in trouble.', NULL, '%s refuse de mourir tant que son maître est en danger !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'core rager EMOTE_LOWHP');
INSERT INTO `script_texts` VALUES (-1409003, 'Reckless mortals, none may challenge the sons of the living flame!', NULL, 'Imprudents mortels ! Nul ne défie les Fils de la Flamme vivante !', NULL, NULL, NULL, NULL, NULL, NULL, 8035, 1, 0, 0, 'majordomo SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1409004, 'The runes of warding have been destroyed! Hunt down the infidels, my brethren!', NULL, 'Les runes de sauvegarde ont été détruites ! Traquez les infidèles, mes frères !', NULL, NULL, NULL, NULL, NULL, NULL, 8039, 6, 0, 0, 'majordomo SAY_SPAWN');
INSERT INTO `script_texts` VALUES (-1409005, 'Ashes to Ashes!', NULL, 'Des cendres aux cendres.', NULL, NULL, NULL, NULL, NULL, NULL, 8037, 1, 0, 0, 'majordomo SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1409006, 'Burn mortals! Burn for this transgression!', NULL, 'Brûlez mortels ! Brûlez pour ce sacrilège !', NULL, NULL, NULL, NULL, NULL, NULL, 8036, 1, 0, 0, 'majordomo SAY_SPECIAL');
INSERT INTO `script_texts` VALUES (-1409007, 'Impossible! Stay your attack, mortals... I submit! I submit! Brashly, you have come to wrest the secrets of the Living Flame! You will soon regret the recklessness of your quest. I go now to summon the lord whose house this is. Should you seek an audience with him, your paltry lives will surely be forfeit! Nevertheless, seek out his lair, if you dare!', NULL, 'Impossible ! Arrêtez votre attaque, mortels... Je me rends ! Je me rends ! Vous avez eu l\'audace de venir affronter les secrets de la Flamme vivante ! Vous regretterez bien vite cette quête ! Je vais invoquer le seigneur dont c\'est la demeure. Si vous cherchez à obtenir une audience auprès de lui, vous y perdrez sans doute votre misérable existence ! Mais entrez donc dans son repaire, si vous l\'osez !', NULL, NULL, NULL, NULL, NULL, NULL, 8038, 1, 0, 0, 'majordomo SAY_DEFEAT');
INSERT INTO `script_texts` VALUES (-1409008, 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', NULL, 'Voyez Ragnaros, le seigneur du Feu ! Il était ancien lorsque ce monde était jeune. Inclinez-vous devant lui, mortels ! Inclinez-vous avant votre fin !', NULL, NULL, NULL, NULL, NULL, NULL, 8040, 1, 0, 0, 'ragnaros SAY_SUMMON_MAJ');
INSERT INTO `script_texts` VALUES (-1409009, 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION???', NULL, 'TROP TÔT ! VOUS M\'AVEZ RÉVEILLÉ TROP TÔT, EXECUTUS ! QUE SIGNIFIE CETTE INTRUSION ???', NULL, NULL, NULL, NULL, NULL, NULL, 8043, 1, 0, 0, 'ragnaros SAY_ARRIVAL1_RAG');
INSERT INTO `script_texts` VALUES (-1409010, 'These mortal infidels, my lord! They have invaded your sanctum and seek to steal your secrets!', NULL, 'Ces mortels infidèles, monseigneur ! Ils ont envahi votre sanctuaire et cherchent à voler vos secrets !', NULL, NULL, NULL, NULL, NULL, NULL, 8041, 1, 0, 0, 'ragnaros SAY_ARRIVAL2_MAJ');
INSERT INTO `script_texts` VALUES (-1409011, 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE, AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', NULL, 'IMBÉCILE ! VOUS AVEZ PERMIS À CES INSECTES DE RAMPER JUSQU\'AU SAINT DES SAINTS ? ET MAINTENANT VOUS LES MENEZ JUSQU\'À MON REPAIRE ? VOUS M\'AVEZ TRAHI, EXECUTUS ! JUSTICE SERA FAITE, EN VÉRITÉ !', NULL, NULL, NULL, NULL, NULL, NULL, 8044, 1, 0, 0, 'ragnaros SAY_ARRIVAL3_RAG');
INSERT INTO `script_texts` VALUES (-1409012, 'NOW FOR YOU, INSECTS! BOLDLY, YOU SOUGHT THE POWER OF RAGNAROS. NOW YOU SHALL SEE IT FIRSTHAND!', NULL, 'ET MAINTENANT À VOUS, INSECTES ! VOUS CHERCHIEZ EFFRONTÉMENT LE POUVOIR DE RAGNAROS. VOUS ALLEZ LE SENTIR DE PRÈS !', NULL, NULL, NULL, NULL, NULL, NULL, 8045, 1, 0, 0, 'ragnaros SAY_ARRIVAL5_RAG');
INSERT INTO `script_texts` VALUES (-1409013, 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', NULL, 'VENEZ ! Ô MES SERVITEURS ! VENEZ DÉFENDRE VOTRE MAÎTRE !', NULL, NULL, NULL, NULL, NULL, NULL, 8049, 1, 0, 0, 'ragnaros SAY_REINFORCEMENTS1');
INSERT INTO `script_texts` VALUES (-1409014, 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH YOU CREATURES OF HATE! YOUR MASTER CALLS!', NULL, 'VOUS NE POUVEZ PAS DÉFAIRE LA FLAMME VIVANTE. VENEZ SUPPÔTS DU FEU ! VENEZ CRÉATURES DE LA HAINE ! VOTRE MAÎTRE VOUS APPELLE !', NULL, NULL, NULL, NULL, NULL, NULL, 8050, 1, 0, 0, 'ragnaros SAY_REINFORCEMENTS2');
INSERT INTO `script_texts` VALUES (-1409015, 'BY FIRE BE PURGED!', NULL, 'PAR LE FEU, SOYEZ PURIFIÉS !', NULL, NULL, NULL, NULL, NULL, NULL, 8046, 1, 0, 0, 'ragnaros SAY_HAND');
INSERT INTO `script_texts` VALUES (-1409016, 'TASTE THE FLAMES OF SULFURON!', NULL, 'GOÛTEZ AUX FLAMMES DE SULFURON !', NULL, NULL, NULL, NULL, NULL, NULL, 8047, 1, 0, 0, 'ragnaros SAY_WRATH');
INSERT INTO `script_texts` VALUES (-1409017, 'DIE INSECT!', NULL, 'MEURS INSECTE !', NULL, NULL, NULL, NULL, NULL, NULL, 8051, 1, 0, 0, 'ragnaros SAY_KILL');
INSERT INTO `script_texts` VALUES (-1409018, 'MY PATIENCE IS DWINDLING! COME GNATS TO YOUR DEATH!', NULL, 'MA PATIENCE S\'ÉPUISE... VENEZ VOUS FAIRE TUER MOUCHERONS !', NULL, NULL, NULL, NULL, NULL, NULL, 8048, 1, 0, 0, 'ragnaros SAY_MAGMABURST');
INSERT INTO `script_texts` VALUES (-1469000, 'None of your kind should be here! You\'ve doomed only yourselves!', NULL, 'Aucun membre de votre espèce ne devrait être ici ! Vous vous êtes condamnés vous-mêmes !', NULL, NULL, NULL, NULL, NULL, NULL, 8286, 1, 0, 0, 'broodlord SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1469001, 'Clever Mortals but I am not so easily lured away from my sanctum!', NULL, 'Bien tenté, mortels, mais il n\'est pas si facile de m\'éloigner de mon sanctum !', NULL, NULL, NULL, NULL, NULL, NULL, 8287, 1, 0, 0, 'broodlord SAY_LEASH');
INSERT INTO `script_texts` VALUES (-1469002, 'REUSE ME', NULL, '%s est pris de frénésie !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'REUSE ME');
INSERT INTO `script_texts` VALUES (-1469003, '%s flinches as its skin shimmers.', NULL, '%s grimace lorsque sa peau se met à briller.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'chromaggus EMOTE_SHIMMER');
INSERT INTO `script_texts` VALUES (-1469006, 'Ah...the heroes. You are persistent, aren\'t you? Your ally here attempted to match his power against mine - and paid the price. Now he shall serve me...by slaughtering you.', NULL, 'Ah, les fameux héros. Vous êtez tenaces, n\'est-ce pas. Votre allié a tenté de m\'affronter et en a payé le prix. Il est maintenant mon serviteur, et il va vous tuer. Debout mon petit dragon rouge. Détruis les !', NULL, NULL, NULL, NULL, NULL, NULL, 8279, 1, 0, 0, 'victor_nefarius SAY_VAEL_INTRO');
INSERT INTO `script_texts` VALUES (-1469022, 'You\'ll pay for forcing me to do this!', NULL, 'Vous allez payer pour m\'avoir forcé à faire ça !', NULL, NULL, NULL, NULL, NULL, NULL, 8275, 1, 0, 0, 'razorgore SAY_EGGS_BROKEN1');
INSERT INTO `script_texts` VALUES (-1469023, 'Fools! These eggs are more precious than you know!', NULL, 'Imbéciles ! Vous n\'avez pas idée de la valeur de ces œufs !', NULL, NULL, NULL, NULL, NULL, NULL, 8276, 1, 0, 0, 'razorgore SAY_EGGS_BROKEN2');
INSERT INTO `script_texts` VALUES (-1469024, 'No - not another one! I\'ll have your heads for this atrocity!', NULL, 'Non, pas un autre ! J\'aurai vos têtes pour venger cette atrocité !', NULL, NULL, NULL, NULL, NULL, NULL, 8277, 1, 0, 0, 'razorgore SAY_EGGS_BROKEN3');
INSERT INTO `script_texts` VALUES (-1469025, 'If I fall into the abyss I\'ll take all of you mortals with me...', NULL, 'Si je tombe dans les abysses, je vous entrainerai tous avec moi mortels...', NULL, NULL, NULL, NULL, NULL, NULL, 8278, 1, 0, 0, 'razorgore SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1469026, 'Too late, friends! Nefarius\' corruption has taken hold...I cannot...control myself.', NULL, 'Trop tard, mes amis ! La corruption de Nefarius s\'empare de moi… Je ne peux plus… me contrôler.', NULL, NULL, NULL, NULL, NULL, NULL, 8281, 1, 0, 1, 'vaelastrasz SAY_LINE1');
INSERT INTO `script_texts` VALUES (-1469027, 'I beg you, mortals - FLEE! Flee before I lose all sense of control! The black fire rages within my heart! I MUST- release it!', NULL, 'Je vous en supplie, mortels, FUYEZ ! Fuyez avant que je perde tout contrôle ! Le feu noir se déchaîne dans mon cœur ! JE DOIS le libérer !', NULL, NULL, NULL, NULL, NULL, NULL, 8282, 1, 0, 1, 'vaelastrasz SAY_LINE2');
INSERT INTO `script_texts` VALUES (-1469028, 'FLAME! DEATH! DESTRUCTION! Cower, mortals before the wrath of Lord...NO - I MUST fight this! Alexstrasza help me, I MUST fight it!', NULL, 'FLAMMES ! MORT ! DESTRUCTION ! Tremblez, mortels, devant la colère du seigneur… NON ! JE DOIS lutter ! Qu\'Alexstrasza me vienne en aide, JE DOIS me battre !', NULL, NULL, NULL, NULL, NULL, NULL, 8283, 1, 0, 15, 'vaelastrasz SAY_LINE3');
INSERT INTO `script_texts` VALUES (-1469029, 'Nefarius\' hate has made me stronger than ever before! You should have fled while you could, mortals! The fury of Blackrock courses through my veins!', NULL, 'La haine de Nefarius me rend plus fort que jamais ! Vous auriez dû fuir tant qu\'il en était encore temps, mortels ! La fureur de Rochenoire coule dans mes veines !', NULL, NULL, NULL, NULL, NULL, NULL, 8285, 1, 0, 0, 'vaelastrasz SAY_HALFLIFE');
INSERT INTO `script_texts` VALUES (-1469030, 'Forgive me, $N! Your death only adds to my failure!', NULL, 'Pardonnez-moi, $N ! Votre mort ne fait qu\'ajouter à mon échec !', NULL, NULL, NULL, NULL, NULL, NULL, 8284, 1, 0, 0, 'vaelastrasz SAY_KILLTARGET');
INSERT INTO `script_texts` VALUES (-1509000, '%s senses your fear.', NULL, '%s ressent votre peur.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'moam EMOTE_AGGRO');
INSERT INTO `script_texts` VALUES (-1509001, '%s bristles with energy!', NULL, '%s luit avec énergie !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'moan EMOTE_MANA_FULL');
INSERT INTO `script_texts` VALUES (-1509018, 'I am rejuvinated!', NULL, 'Je reprends des forces !', NULL, NULL, NULL, NULL, NULL, NULL, 8593, 1, 0, 0, 'ossirian SAY_SURPREME1');
INSERT INTO `script_texts` VALUES (-1509019, 'My powers are renewed!', NULL, 'Mes pouvoirs reviennent !', NULL, NULL, NULL, NULL, NULL, NULL, 8595, 1, 0, 0, 'ossirian SAY_SURPREME2');
INSERT INTO `script_texts` VALUES (-1509020, 'My powers return!', NULL, 'Mes pouvoirs sont de retour !', NULL, NULL, NULL, NULL, NULL, NULL, 8596, 1, 0, 0, 'ossirian SAY_SURPREME3');
INSERT INTO `script_texts` VALUES (-1509025, 'Sands of the desert rise and block out the sun!', NULL, 'Sables du désert à moi ! Cachez le soleil !', NULL, NULL, NULL, NULL, NULL, NULL, 8598, 1, 0, 0, 'ossirian SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1509026, 'You are terminated.', NULL, 'Vous avez été liquidés.', NULL, NULL, NULL, NULL, NULL, NULL, 8602, 1, 0, 0, 'ossirian SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1509027, 'I...have...failed.', NULL, 'J\'ai... échoué...', NULL, NULL, NULL, NULL, NULL, NULL, 8594, 1, 0, 0, 'ossirian SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531000, 'Are you so eager to die? I would be happy to accomodate you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8615, 1, 0, 0, 'skeram SAY_AGGRO1');
INSERT INTO `script_texts` VALUES (-1531001, 'Cower mortals! The age of darkness is at hand.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8616, 1, 0, 0, 'skeram SAY_AGGRO2');
INSERT INTO `script_texts` VALUES (-1531002, 'Tremble! The end is upon you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8621, 1, 0, 0, 'skeram SAY_AGGRO3');
INSERT INTO `script_texts` VALUES (-1531003, 'Let your death serve as an example!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8617, 1, 0, 0, 'skeram SAY_SLAY1');
INSERT INTO `script_texts` VALUES (-1531004, 'Spineless wretches! You will drown in rivers of blood!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8619, 1, 0, 0, 'skeram SAY_SLAY2');
INSERT INTO `script_texts` VALUES (-1531005, 'The screams of the dying will fill the air. A symphony of terror is about to begin!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8620, 1, 0, 0, 'skeram SAY_SLAY3');
INSERT INTO `script_texts` VALUES (-1531006, 'Prepare for the return of the ancient ones!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8618, 1, 0, 0, 'skeram SAY_SPLIT');
INSERT INTO `script_texts` VALUES (-1531007, 'You only delay... the inevetable.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8622, 1, 0, 0, 'skeram SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531008, 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8646, 1, 0, 0, 'sartura SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1531009, 'I sentence you to death!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8647, 1, 0, 0, 'sartura SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1531010, 'I serve to the last!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 8648, 1, 0, 0, 'sartura SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531011, '%s is weakened!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'cthun EMOTE_WEAKENED');
INSERT INTO `script_texts` VALUES (-1531012, 'The massive floating eyeball in the center of the chamber turns its gaze upon you. You stand before a god.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'eye cthun EMOTE_INTRO');
INSERT INTO `script_texts` VALUES (-1531013, 'Only flesh and bone. Mortals are such easy prey...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'veklor SAY_INTRO_1');
INSERT INTO `script_texts` VALUES (-1531014, 'Where are your manners, brother. Let us properly welcome our guests.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'veknilash SAY_INTRO_2');
INSERT INTO `script_texts` VALUES (-1533106, 'Your petty magics are no challenge to the might of the Scourge! ', NULL, 'Vos magies insignifiantes ne sauraient défier la puissance du Fléau !', NULL, NULL, NULL, NULL, NULL, NULL, 9088, 1, 0, 0, 'kelthuzad SAY_SPECIAL1_MANA_DET');
INSERT INTO `script_texts` VALUES (-1533107, 'Enough! I grow tired of these distractions! ', NULL, 'Assez ! Je me lasse de ses passe-temps !', NULL, NULL, NULL, NULL, NULL, NULL, 9090, 1, 0, 0, 'kelthuzad SAY_SPECIAL3_MANA_DET');
INSERT INTO `script_texts` VALUES (-1533108, 'Fools, you have spread your powers too thin. Be free, my minions!', NULL, 'Imbéciles, vous avez éparpillés vos forces. Libérez-vous, mes serviteurs !', NULL, NULL, NULL, NULL, NULL, NULL, 9089, 1, 0, 0, 'kelthuzad SAY_SPECIAL2_DISPELL');
INSERT INTO `script_texts` VALUES (-1533118, 'Noo... o...', NULL, 'Noo... on...', NULL, NULL, NULL, NULL, NULL, NULL, 8828, 1, 0, 0, 'heigan SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1780127, 'Help! I\'ve only one hand to defend myself with.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'MIRAN_SAY_AMBUSH_ONE');
INSERT INTO `script_texts` VALUES (-1780128, 'Send them on! I\'m not afraid of some scrawny beasts!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'MIRAN_SAY_AMBUSH_TWO');
INSERT INTO `script_texts` VALUES (-1780129, 'Feel the power of the Dark Iron Dwarves!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DARK_IRON_RAIDER_SAY_AMBUSH');
INSERT INTO `script_texts` VALUES (-1780130, 'Ah, here at last! It\'s going to feel so good to get rid of these barrels.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'MIRAN_SAY_QUEST_END');
INSERT INTO `script_texts` VALUES (-1509028, '%s drains your mana and turns to stone.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'moam EMOTE_ENERGIZING');
INSERT INTO `script_texts` VALUES (-1000753, 'We must act quickly or shall be lost!', NULL, 'Nous devons agir rapidement, ou tout sera perdu !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_INTRO_1');
INSERT INTO `script_texts` VALUES (-1000754, 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', NULL, 'Mes forces ne peuvent pas percer les défenses des Qiraji. Nous ne pourrons pas nous approcher suffisamment pour poser votre chère barrière, dragon.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_FANDRAL_INTRO_2');
INSERT INTO `script_texts` VALUES (-1000755, 'There is a way...', NULL, 'Il existe un moyen...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 22, 'SAY_MERITHRA_INTRO_3');
INSERT INTO `script_texts` VALUES (-1000756, '%s nods knowingly.', NULL, '%s fait un signe de la tête d\'un air entendu.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_ARYGOS_NOD');
INSERT INTO `script_texts` VALUES (-1000757, 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', NULL, 'Aye, Fandral, souvenez vous de ces mots: ne laissez pas votre chagrin guider votre foi. Ces pensées que vous portez... vous conduirons à votre perte. L\'absolution ne peut être obtenue par la vengeance.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_CAELESTRASZ_INTRO_4');
INSERT INTO `script_texts` VALUES (-1000758, '%s glances at her compatriots.', NULL, '%s parcourt des yeux ses compagnons.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_MERITHRA_GLANCE');
INSERT INTO `script_texts` VALUES (-1000759, 'We will push them back, Anachronos. This I vow. Uphold your end of this task. Let not your hands falter as you seal our fates behind the barrier.', NULL, 'Nous le repousserons, Anachronos, j\'en fais le serment. Accomplissez votre part dans cette tâche. Que nos mains ne tremblent pas lorsque vous scellerez nos destins derrière la barrière !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_MERITHRA_INTRO_5');
INSERT INTO `script_texts` VALUES (-1000760, 'Succumb to the endless dream, little ones. Let it comsume you!', NULL, 'Laissez le rêve infini vous submerger, petits êtres. Laissez-le vous consumer !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 22, 'SAY_MERITHRA_ATTACK_1');
INSERT INTO `script_texts` VALUES (-1000761, 'Anachronos, this diversion will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', NULL, 'Anachronos, cette diversion vous donnera assez de temps, à vous et au jeune druide, pour sceller la porte. Ne faiblissez pas. Et maintenant, voyons comment ils supportent la magie chaotique.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_ARYGOS_ATTACK_2');
INSERT INTO `script_texts` VALUES (-1000762, 'Let them feel the wrath of the Blue Flight! May Malygos protect me!', NULL, 'Qu\'ils sentent la colère du Vol bleu ! Que Malygos me protège !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 22, 'SAY_ARYGOS_ATTACK_3');
INSERT INTO `script_texts` VALUES (-1000763, 'Do not forget the sacrifices we made on this day, night elf. We have all suffered immensely at the hands of these beasts.', NULL, 'N\'oubliez pas les sacrifices accomplis en ce jour, elfe de la nuit. Nous avons tous énormément souffert des mains de ces bêtes.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_CAELESTRASZ_ATTACK_4');
INSERT INTO `script_texts` VALUES (-1000764, 'Alexstrasza, give me the resolve to drive your enemies back.', NULL, 'Alexstrasza, donne moi la force de repousser tes ennemies.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 22, 'SAY_CAELESTRASZ_ATTACK_5');
INSERT INTO `script_texts` VALUES (-1000765, 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', NULL, 'MAINTENANT, FANDRAL ! ALLONS-Y ! Préparez votre magie !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 22, 'SAY_ANACHRONOS_SEAL_1');
INSERT INTO `script_texts` VALUES (-1000766, 'It is done dragon. Lead the way...', NULL, 'C\'est fait, dragon. Je vous suis !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'SAY_FANDRAL_SEAL_2');
INSERT INTO `script_texts` VALUES (-1000767, 'Stay close.', NULL, 'Restez prêt de moi.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_SEAL_3');
INSERT INTO `script_texts` VALUES (-1000768, 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', NULL, 'Les sables du temps s\'arrêteront, mais seulement pour un moment ! Je vais invoquer la barrière.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_SEAL_4');
INSERT INTO `script_texts` VALUES (-1000769, 'FINISH THE SPELL, STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', NULL, 'ACHEVEZ LE SORT, FORTEARMURE ! JE NE MAINTIENDRAI PAS LES GLYPHES DE SAUVEGARDE EN PLACE PLUS LONGTEMPS ! INVOQUEZ LES RACINES !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_SEAL_5');
INSERT INTO `script_texts` VALUES (-1000770, 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', NULL, 'Anciens, guidez ma main... Sortez de votre torpeur ! EVEILLEZ-VOUS ET SCELLEZ CET ENDROIT MAUDIT !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_FANDRAL_SEAL_6');
INSERT INTO `script_texts` VALUES (-1000771, '%s falls to one knee - exhausted.', NULL, '%s s\'effondre, à bout de souffle.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_FANDRAL_EXHAUSTED');
INSERT INTO `script_texts` VALUES (-1000772, 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', NULL, 'C\'est... C\'est terminé, seigneur Fortearmure. Nous sommes victorieux. Mais c\'est une victoire chèrement payée.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_1');
INSERT INTO `script_texts` VALUES (-1000773, 'There is but one duty that remains...', NULL, 'Alors, il ne reste qu\'un devoir à accomplir...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_2');
INSERT INTO `script_texts` VALUES (-1000774, 'Before I leave this place, I make one final offering to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', NULL, 'Avant de partir, laissez-moi vous faire une dernière don, seigneur Fortearmure. Un jour viendra ou vous aurez besoin de pénétrer dans cette forteresse maudite, utilisez le Sceptre des Sables Changeants sur le gong sacré. La magie qui maintient cette barrière se dissipera et les horreurs d\'Ahn\'Qiraj se déverseront à nouveau sur le monde.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_3');
INSERT INTO `script_texts` VALUES (-1000775, '%s hands the Scepter of the Shifting Sands to $N.', NULL, '%s tend le Sceptre des Sables Changeants à $N.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_ANACHRONOS_SCEPTER');
INSERT INTO `script_texts` VALUES (-1000776, 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', NULL, 'Après le carnage dont mon peuple a été temoin, vous pensez vraiment que je vais endosser un autre fardeau ? Vous avez perdu la raison.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_FANDRAL_EPILOGUE_4');
INSERT INTO `script_texts` VALUES (-1000777, 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', NULL, 'Je ne veux plus rien avoir à faire avec Silithus, les Qirajis et surtout, ces damnés dragons !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_FANDRAL_EPILOGUE_5');
INSERT INTO `script_texts` VALUES (-1000778, '%s hurls the Scepter of the Shifting Sands into the barrier, shattering it.', NULL, '%s lance le Sceptre des Sables Changeants contre la barrière, le brisant.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_FANDRAL_SHATTER');
INSERT INTO `script_texts` VALUES (-1000779, 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', NULL, 'Seigneur Fortearmure, que faites vous ? Vous seriez prêt à briser notre lien pour satisfaire votre orgueil ?', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_6');
INSERT INTO `script_texts` VALUES (-1000780, 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back. Though it takes a millenia, I WILL have my son back!', NULL, 'L\'âme de mon fils ne trouvera aucun récomfort dans cette victoire sans goût, dragon ! Je veux qu\'il revienne. Même si cela doit prendre un millénaire. JE VEUX que mon fils revienne !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'SAY_FANDRAL_EPILOGUE_7');
INSERT INTO `script_texts` VALUES (-1000781, '%s shakes his head in disappointment.', NULL, '%s hoche la tête de deception.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 25, 'EMOTE_ANACHRONOS_DISPPOINTED');
INSERT INTO `script_texts` VALUES (-1000782, '%s kneels down to pickup the fragments of the shattered scepter.', NULL, '%s se met à genoux et ramasse les fragments du sceptre brisé.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_ANACHRONOS_PICKUP');
INSERT INTO `script_texts` VALUES (-1000783, 'And now you know all that there is to know, mortal...', NULL, 'A présent, mortel, vous savez tout ce qu\'il y a à savoir...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_EPILOGUE_8');
INSERT INTO `script_texts` VALUES (-1000015, 'Good riddance!', '', 'Bon débarras !', '', '', '', '¡Hasta nunca!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000014, 'But your real punishment lies ahead.', '', 'Mais votre vraie punition reste à venir.', '', '', '', 'Pero tu verdadero castigo no ha comenzado aún.', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000013, 'Haha! I bet you thought you were done!', '', 'Hé hé ! Je parie que vous ne vous attendiez pas à ça !', '', '', '', '¡Ja, ja! ¡A que pensabas que eso era todo!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000012, 'Unleash the fury and let it be done!', '', 'Que toute fureur soit lâchée et que tout finisse !', '', '', '', '¡Desatad vuestra furia y castigad a los culpables!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1309025, 'Jeklik begins to cast a Great Heal!', '', 'Jeklik commence à lancer un sort de Soins exceptionnels !', '', '', '', '¡Jeklik comienza a lanzar una gran curación!', '', '', 0, 2, 0, 0, 'jeklik EMOTE_HEAL');
INSERT INTO `script_texts` VALUES (-1309026, 'Jeklik emits a deafening shriek, calling for help.', '', 'Jeklik pousse un hurlement assourdissant !', '', '', '', 'Jeklik emite un chillido ensordecedor, pidiendo ayuda.', '', '', 0, 2, 0, 0, 'jeklik EMOTE_SUMMON_BATS');
INSERT INTO `script_texts` VALUES (-1409019, 'Imprudent whelps! You\'ve rushed headlong to your own deaths! See now, the master stirs!', '', 'Imprudents imbéciles ! Vous vous êtes precipités vers vôtre propre mort ! Voyez, à présent, le Maitre remue !', '', '', '', '¡Cachorros imprudentes! ¡Te has precipitado precipitadamente hacia tu propia muerte! ¡Mira, el maestro se mueve!', '', '', 0, 1, 0, 0, 'ragnaros SAY_MAJ');
INSERT INTO `script_texts` VALUES (-1469034, '%s casts Destroy Egg', '', '%s lance Détruire l\'Oeuf.', '', '', '', '%s lanza Destruir Huevo', '', '', 0, 2, 0, 0, 'Razor EMOTE_DESTROY_EGG');
INSERT INTO `script_texts` VALUES (-1469035, 'Nefarian\'s troops flee as the power of the controlling power of the orb is drained.', '', 'Le pouvoir de contrôle de l\'orbe épuisé, les troupes de Nefarian s\'enfuient.', '', '', '', 'Las tropas de Nefarian huyen cuando se agota el poder de control del orbe.', '', '', 0, 2, 0, 0, 'Razorgore EMOTE_FLEE');
INSERT INTO `script_texts` VALUES (-1780148, 'FOR THE ALLIANCE!', NULL, 'POUR L\'ALLIANCE !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 7, 22, 'Silithus Field Duty Event - 8 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780147, 'For Ironforge!', NULL, 'Pour Ironforge !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 7, 22, 'Silithus Field Duty Event - 7 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780146, 'Sergeant, be ready! Nozzlespring, make sure you set this nasty insect on fire while I\'ll draw its attention. Janela, to my side!\r\n', NULL, 'Sergeant, tenez vous prêt ! Nozzlespring, faites donc rôtir cette infâme bestiole pendant que j\'attire son attention. Janela, avec moi !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 22, 'Silithus Field Duty Event - 7 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780145, 'Form the line! Everyone in position!', NULL, 'Formez les rangs ! Tout le monde à son poste !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 22, 'Silithus Field Duty Event - 6 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780144, 'Captain! Silithid wasp, straight ahead!', NULL, 'Capitaine ! Guêpe Silithide, droit devant !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 5, 'Silithus Field Duty Event - 5 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780143, 'As you can see over there, this is also here that Silithists are collected, and we...', NULL, 'Comme vous pouvez le voir là-bas, c\'est aussi ici que nous stockons les Silithistes collectées par l\'Alliance, et nous...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'Silithus Field Duty Event - 4 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780142, 'A swarming noise arising from the hive becomes louder and louder...', NULL, 'Un bruit de grouillement s\'élevant de la ruche se fait de plus en plus perçant...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'Silithus Field Duty Event - 3 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780141, 'More of these monstruous bugs come every day, and the recruits sent by the Cenarion Druids are barely enough for us to cope with the Silithids\' attacks. Your help is of paramount importance for us, and I will sign your papers if you serve the Alliance and the Cenarion Circle bravely.', NULL, 'Le nombre de ces monstrueux insectes croît sans cesse, et les recrues envoyées par les Druides Cénarien nous permettent à peine de faire face aux attaques des Silithides. Votre aide est d\'une importance capitale, et je signerai vos papiers si vous servez l\'Alliance et le Cercle Cénarien avec bravoure.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'Silithus Field Duty Event - 2 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780140, 'Welcome to Silithus, young recruit. The Ironforge Brigade fights in this region on behalf of the Alliance to repel the Qiraji invaders. We have been tasked by the Cenarion Circle to hold back the Silithid threat emanating from the Hive\'Zora. Alliance scouts are sent into the hive from this outpost on a regular basis, and the enemy\'s assaults on Cenarion Hold are to be intercepted here.', NULL, 'Bienvenue en Silithus, jeune recrue. La Brigade d\'Ironforge représente l\'Alliance dans sa lutte contre les envahisseurs Qiraji. Nous avons été chargés par le Cercle Cénarien de contenir la menace Silithide que constitue la Ruche Zora. Des éclaireurs de l\'Alliance sont envoyés régulièrement depuis cet avant poste, et les assaults de l\'enemi sur le Fort Cénarien sont interceptés ici.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, 'Silithus Field Duty Event - 1 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780139, 'FOR THE HORDE!', NULL, 'POUR LA HORDE !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 1, 22, 'Silithus Field Duty Event - 7 (Horde)');
INSERT INTO `script_texts` VALUES (-1780138, 'Strength and Honor!', NULL, 'Force et Honneur !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 1, 22, 'Silithus Field Duty Event - 9 (Horde)');
INSERT INTO `script_texts` VALUES (-1780137, 'Shai, Merok! To my side!', NULL, 'Shai, Merok ! À mes côtés !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 22, 'Silithus Field Duty Event - 6 (Horde)');
INSERT INTO `script_texts` VALUES (-1780136, 'Look! Something\'s coming!', NULL, 'Attention ! Quelque chose approche !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 5, 'Silithus Field Duty Event - 8 (Horde)');
INSERT INTO `script_texts` VALUES (-1780135, 'Krug suddenly halts, his eyes caught by a motion down in the Hive.', NULL, 'Krug s\'interrompt soudainement, le regard attiré par un mouvement en contrebas.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'Silithus Field Duty Event - 5 (Horde)');
INSERT INTO `script_texts` VALUES (-1780134, 'Be ready!  If I am to sign those papers, you\'re gonna have to...', NULL, 'Soyez sur vos gardes ! Si vous voulez que je signe ces papiers, il va falloir...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 4 (Horde)');
INSERT INTO `script_texts` VALUES (-1780133, 'Our scouts have reported that a Silithid attempt to take out this outpost is likely to occur very soon.', NULL, 'Les derniers rapports des éclaireurs nous laissent penser qu\'un assaut des Silithides sur cet avant-poste est imminent.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 3 (Horde)');
INSERT INTO `script_texts` VALUES (-1780132, 'Get into the ranks ! Fighting alongside the Orgrimmar Legion is no easy task. The recruits these elves from the Cenarion Circle keep sending us never last long.', NULL, 'Rentrez dans les rangs ! Se battre aux côtés de la Légion d\'Orgrimmar n\'est pas chose aisée. Les recrues que nous envoient ces elfes du Cercle Cénarien font rarement long feu.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 2 (Horde)');
INSERT INTO `script_texts` VALUES (-1780131, 'Lok\'tar Ogar !', NULL, 'Lok\'tar Ogar !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 22, 'Silithus Field Duty Event - 1 (Horde)');
INSERT INTO `script_texts` VALUES (-1780196, 'Ah freedom! Although brief, so sweet it is...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780197, 'Well duties call, yes? First, the bell... to give you vigor!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780198, 'Next, I\'ll place the wheel... to protect you from harm.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780199, 'And finally the candle... to burn those who could thwart you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780200, '$N, my duties are complete and I shall now take my leave. Luck to you my $gfellow:lady;, and remember to keep the Bell, Wheel and Candle working!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780201, 'Who dares steal my precious mount? You will pay for your insolence, mortal!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'DreadsteedRitualpnj14506');
INSERT INTO `script_texts` VALUES (-1780202, 'Xorothian Imp is pulled back to Xoroth!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'DreadsteedRitualpnj14482');
INSERT INTO `script_texts` VALUES (-1780203, 'Dread Guard is pulled back to Xoroth!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'DreadsteedRitualpnj14483');
INSERT INTO `script_texts` VALUES (-1000601, 'Sleepy... so sleepy...', NULL, 'Sommeil... tellement sommeil...', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lazy peon SAY_SPELL_HIT2');
INSERT INTO `script_texts` VALUES (-1000600, 'Ow! Ok, I\'ll get back to work, $N!', NULL, 'Aie! D\'accord, je retourne au travail, $N!', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lazy peon SAY_SPELL_HIT');
INSERT INTO `script_texts` VALUES (-1000011, 'The Sons of Thaurissan shall watch you perish in the Ring of the Law!', '', 'Les Fils de Thaurissan vous verront périr dans le Cercle de loi !', '', '', '', '¡Los hijos de Thaurissan os verán morir en el Círculo de la Ley!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000010, 'You have been sentenced to death for crimes against the Dark Iron nation!', '', 'Vous subirez la peine de mort pour vos crimes contre la nation Sombrefer !', '', '', '', '¡Se os ha sentenciado a muerte por delitos cometidos contra la nación Hierro Negro!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1001106, 'Don\'t forget to get my bell out of the chest here. And remember, if do happen to wander off, just ring it and I\'ll find you again.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'shay leafrunner SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001107, 'Are we taking the scenic route?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_1');
INSERT INTO `script_texts` VALUES (-1001108, 'Oh, what a beautiful flower over there...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_2');
INSERT INTO `script_texts` VALUES (-1001109, 'Are you sure this is the right way? Maybe we should go this way instead...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_3');
INSERT INTO `script_texts` VALUES (-1001110, 'Hmmm, I wonder what\'s over this way?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_4');
INSERT INTO `script_texts` VALUES (-1001111, 'This is quite an adventure!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_1');
INSERT INTO `script_texts` VALUES (-1001112, 'Oh, I wandered off again. I\'m sorry.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_2');
INSERT INTO `script_texts` VALUES (-1001113, 'The bell again, such a sweet sound.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_3');
INSERT INTO `script_texts` VALUES (-1001114, '%s begins to wander off.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'shay leafrunner EMOTE_WANDER');
INSERT INTO `script_texts` VALUES (-1001115, 'Oh, here you are, Rockbiter! I\'m sorry, I know I\'m not supposed to wander off.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'shay leafrunner SAY_EVENT_COMPLETE_1');
INSERT INTO `script_texts` VALUES (-1001116, 'I\'m so glad yer back Shay. Please, don\'t ever run off like that again! What would I tell yer parents if I lost ya?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'rockbiter SAY_EVENT_COMPLETE_2');
INSERT INTO `script_texts` VALUES (-1000784, 'Thanks $N. Now let\'s get out of here!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'melizza SAY_MELIZZA_START');
INSERT INTO `script_texts` VALUES (-1000785, 'We made it! Thanks again! I\'m going to run ahead!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'melizza SAY_MELIZZA_FINISH');
INSERT INTO `script_texts` VALUES (-1000786, 'Hey Hornizz! I\'m back! And there are some people behind me who helped me out of a jam.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'melizza SAY_MELIZZA_1');
INSERT INTO `script_texts` VALUES (-1000787, 'We\'re going to have to scratch the Maraudines off our list. Too hard to work with...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'melizza SAY_MELIZZA_2');
INSERT INTO `script_texts` VALUES (-1000788, 'Well, I\'m off to the Gelkis. They\'re not as dumb as the Maraudines, but they\'re more reasonable.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 3, 'melizza SAY_MELIZZA_3');
INSERT INTO `script_texts` VALUES (-1409020, 'You think you\'ve won already? Perhaps you\'ll need another lesson in pain!', NULL, 'Vous croyez avoir deja gagne? Vous avez sans doute besoin que je vous inflige quelques douleurs supplementaires.', NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'majordomo SAY_LAST_ADD');
INSERT INTO `script_texts` VALUES (-1001159, 'Please, help me to get through this cursed forest, $r.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001160, 'This creature suffers from the effect of the fel... We must end its misery.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ATTACK_IRONTREE');
INSERT INTO `script_texts` VALUES (-1001161, 'The corruption of the fel has not left any of the creatures of Felwood untouched, $N. Please, be on your guard.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ATTACK_TOXIC_HORROR');
INSERT INTO `script_texts` VALUES (-1001162, 'I sense the taint of corruption upon this $N. Help me detroy it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_EXIT_WOODS');
INSERT INTO `script_texts` VALUES (-1001163, 'That I must fight against my own kind deeply saddens me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_CLEAR_PATH');
INSERT INTO `script_texts` VALUES (-1001164, 'I can sense it now, $N. Ashenvale lies down this path.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ASHENVALE');
INSERT INTO `script_texts` VALUES (-1001165, 'I feel... something strange...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1001166, '$N my form has now changed! The true strength of my spirit is returing to me now... The cursed grasp of the forest is leaving me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_LIFT_CURSE');
INSERT INTO `script_texts` VALUES (-1001167, 'Thank you, $N. Now my spirit will finally be at peace.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ESCORT_COMPLETE');
INSERT INTO `script_texts` VALUES (-1001148, 'I am ready, $N. Let\'s find my equipment and get out of here. I think I know where it is.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'captured arko\'narin SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001149, 'Oh my! Look at this... all these candles. I\'m sure they\'re used for some terrible ritual or dark summoning. We best make haste.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 18, 'captured arko\'narin SAY_FIRST_STOP');
INSERT INTO `script_texts` VALUES (-1001150, 'There! Over there!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'captured arko\'narin SAY_SECOND_STOP');
INSERT INTO `script_texts` VALUES (-1001151, 'You will not stop me from escaping here, $N!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1001152, 'All I need now is a golden lasso.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_EQUIPMENT');
INSERT INTO `script_texts` VALUES (-1001153, 'DIE DEMON DOGS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_ESCAPE');
INSERT INTO `script_texts` VALUES (-1001154, 'Ah! Fresh air at last! I never thought I\'d see the day.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 4, 'captured arko\'narin SAY_FRESH_AIR');
INSERT INTO `script_texts` VALUES (-1001155, 'BETRAYER!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'spirit of trey lightforge SAY_BETRAYER');
INSERT INTO `script_texts` VALUES (-1001156, 'What was that?! Trey? TREY?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 22, 'captured arko\'narin SAY_TREY');
INSERT INTO `script_texts` VALUES (-1001157, 'You kept me in the cell for too long, monster!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_ATTACK_TREY');
INSERT INTO `script_texts` VALUES (-1001158, 'No! My friend... what\'s happened? This is all my fault...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 18, 'captured arko\'narin SAY_ESCORT_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000873, 'I know the way, insect. There is no need to prod me as if I were cattle.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_START');
INSERT INTO `script_texts` VALUES (-1000874, 'Surely you do not think that you will get away with this incursion. They will come for me and you shall pay for your insolence.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_PAY');
INSERT INTO `script_texts` VALUES (-1000875, 'RUN THEM THROUGH BROTHERS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'grark SAY_FIRST_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000876, 'I doubt you will be so lucky the next time you encounter my brethren.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_FIRST_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000877, 'They come for you, fool!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'grark SAY_SEC_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000878, 'What do you think you accomplish from this, fool? Even now, the Blackrock armies make preparations to destroy your world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_SEC_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000879, 'On darkest wing they fly. Prepare to meet your end!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'grark SAY_THIRD_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000880, 'The worst is yet to come!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_THIRD_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000881, '%s laughs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 11, 'grark EMOTE_LAUGH');
INSERT INTO `script_texts` VALUES (-1000882, 'Time to make your final stand, Insect.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'grark SAY_LAST_STAND');
INSERT INTO `script_texts` VALUES (-1000883, 'Kneel, Grark', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_1');
INSERT INTO `script_texts` VALUES (-1000884, 'Grark Lorkrub, you have been charged and found guilty of treason against Horde. How you plead is unimportant. High Executioner Nuzrak, step forward.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_2');
INSERT INTO `script_texts` VALUES (-1000885, '%s raises his massive axe over Grark.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 27, 'nuzark EMOTE_RAISE_AXE');
INSERT INTO `script_texts` VALUES (-1000886, '%s raises his hand and then lowers it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'lexlort EMOTE_LOWER_HAND');
INSERT INTO `script_texts` VALUES (-1000887, 'End him...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'lexlort SAY_LEXLORT_3');
INSERT INTO `script_texts` VALUES (-1000888, 'You, soldier, report back to Kargath at once!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_4');
INSERT INTO `script_texts` VALUES (-1000889, '%s submits.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'grark EMOTE_SUBMIT');
INSERT INTO `script_texts` VALUES (-1000890, 'You have come to play? Then let us play!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'grark SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1108999, 'It is true. The wheels of doom now turn, inevitably, toward the true return of Hakkar!', NULL, 'Il est vrai. La roue du destin tournent désormais , inévitablement , vers le vrai retour d\'Hakkar !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Yehkinya event quest');
INSERT INTO `script_texts` VALUES (-1108998, 'Blood and souls for Hakkar! HAHAHAH!', NULL, 'Du sang et des âmes pour Hakkar ! HAHAHAH !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Yehkinya event quest');
INSERT INTO `script_texts` VALUES (-1000650, '%s is immobilized.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'npc_solenor EMOTE_IMMOBILIZED');
INSERT INTO `script_texts` VALUES (-1000651, '%s is stricken by a virulent poison.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'npc_artorius EMOTE_POISON');
INSERT INTO `script_texts` VALUES (-1000652, '%s is silenced by the venemous sting.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'npc_simone EMOTE_SILENCE');
INSERT INTO `script_texts` VALUES (-1289010, 'You dare interfere with this being\'s testing? The battle must be fought alone! You shall all pay for this interference!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'the_cleaner SAY_CLEANER_AGGRO');
INSERT INTO `script_texts` VALUES (-1532033, 'This I promise... This I vow...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532032, ' Your death will not have been in vain, Taelan. A new Order is born on this day... an Order which will dedicate itself to extinguising the evil that plagues this world. An evil that cannot hide behind politics and pleasantries.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532031, 'Too long have I sat idle, gripped in this haze... this malaise, lamenting what could have been... what should have been.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532030, 'Look what they did to my boy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532029, 'A thousand more like him exist. Ten thousand. Should one fall, another will rise to take the seat of power.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532028, 'Then come, hermit!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532027, 'Face me, coward. Face the faith and strength that you once embodied.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532026, 'May your soul burn in anguish, Isillien! Light give me strength to battle this fiend.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532025, 'Tragic. The elder Fordring lives on... You are too late, old man. Retreat back to your cave, hermit, unless you wish to join your son in the Twisting Nether.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532024, 'What have you done, Isillien? You once fought with honor, for the good of our people... and now... you have murdered my boy...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532023, 'Did you really believe that you could defeat me? Your friends are soon to join you, Taelan.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532022, 'Enough!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532021, 'The end is now, Fordring.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532020, 'Grand Inquisitor Isillien calls for his guardsman.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532019, 'The Grand Crusader has charged me with destroying you and your newfound friends, Taelan, but know this: I do this for pleasure, not of obligation or duty.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532018, 'It is as they say; Like father, like son. You are as weak of will as Tirion... perhaps more so. I can only hope my assassins finally succeeded in ending his pitiful life.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532017, 'You disappoint me, Taelan. I had plans for you... grand plans. Alas, it was only a matter of time before your filthy bloodline would catch up with you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532016, 'You will not make it to the forest\'s edge, Fordring.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532015, 'This is not your fight, stranger. Protect yourself from the attacks of the Crimson Elite. I shall battle the Grand Inquisitor.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532014, 'Isillien!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532013, 'It\'s not much further. The main road is just up ahead', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532012, 'Highlord Taelan Fordring calls for his mount.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532011, 'Halt.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532010, 'Remove your disguise, lest you feel the bite of my blade when the fury has taken control.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532009, 'I will lead us through Hearthglen to the forest\'s edge. From there, you will take me to my father.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532008, 'NOOOOOOOOOOOO!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532007, 'There is no escape for you. The Crusade shall destroy all who carry the Scourge\'s taint.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532006, 'You carry the taint of the Scourge. Prepare to enter the twisting Nether!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532005, 'The Scarlet crusade shall smite the wicked, and drive evil from these lands!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532004, 'Something is wrong with the Highlord. Do something!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532003, 'Hey, what\'s going on over there? Sir, are you alright? Sir??', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532002, 'Highlord Fordring, are you alright?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532001, 'Sir?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
