INSERT INTO `script_texts` VALUES (-1230041, 'Violence! Property damage! None shall pass!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'phalanx SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1230048, 'Your efforts are utterly pointless, fools! You will never be able to defeat me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_1');
INSERT INTO `script_texts` VALUES (-1230049, 'They were just getting in the way anyways.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_2');
INSERT INTO `script_texts` VALUES (-1230050, 'Is that the best you can do? Do you really expect that you could defeat someone as awe inspiring as me?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_3');
INSERT INTO `script_texts` VALUES (-1230051, 'Ha! You can\'t even begin to imagine the futility of your efforts.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_4');
