INSERT INTO `script_texts` VALUES (-1900013, 'Yar, he\'s dead all right. That makes you da new king.. well, all of you! Gordok is yours now, boss! You should talk to me so you can learn everything there is about being da king! I was... is his assistant! Yeah, that\'s why I\'m called da crafty one!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'SMizzle the Crafty AY_KILL_KING_2');
INSERT INTO `script_texts` VALUES (-1900012, 'OH NOES! Da king is dead! Uh... hail to da new king! Yeah!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'Mizzle the Crafty SAY_KILL_KING_1');
INSERT INTO `script_texts` VALUES (-1900011, 'The king is dead - OH NOES! Summon Mizzle da Crafty! He knows what to do next!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'Cho\'Rush the Observer SAY_KILL_KING');
INSERT INTO `script_texts` VALUES (-1900047, 'OK Fengus, where you at?! You come call me a gnoll lover while I give you da hammer upside da head!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900046, 'Hey, who Fengus callin\' a gnoll lover?! Take da prisoners to da king; you smart to bring them with their weapons and show da king that they a threat. I\'ll go see if Fengus talk smack when I give him da beatdown! HAR!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900039, '%s begins to retaliate all attacks against him!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900038, 'No one get past me and threaten da king!  Ungh, take it!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900037, 'Help me crush these punys!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900045, 'You no challenge me, scrubs! I\'m da king now, and I stay king FOREVER!!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '11501');
INSERT INTO `script_texts` VALUES (-1000590, 'I can sense the SHADOW on your hearts. There can be no rest for the wicked!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'lethon SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000591, 'Your wicked souls shall feed my power!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'lethon SAY_CASTDRAW');
INSERT INTO `script_texts` VALUES (-1230039, 'Finally!! I can leave this dump.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Наконец-то! Теперь я могу покинуть эту дыру.', 0, 0, 0, 0, 'tobias SAY_FREE_2');
INSERT INTO `script_texts` VALUES (-1230038, 'Ograbisi needs new hat.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ограбиси нужна новая шляпка.', 0, 0, 0, 0, 'ograbisi SAY');
INSERT INTO `script_texts` VALUES (-1230037, 'Administering fists of fury on Shill Dinger!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Врежь ему!', 0, 0, 0, 0, 'reginald_windsor SAY_7_4');
INSERT INTO `script_texts` VALUES (-1230036, 'Where I come from, you get shanked for opening another inmate\'s cell door!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Там, откуда я родом, дают хорошего пинка за открывание камеры заключенного!', 0, 0, 0, 0, 'crest_killer SAY');
INSERT INTO `script_texts` VALUES (-1230035, 'Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ограбиси сделает маленькую шляпку из твоей головы!', 0, 0, 0, 0, 'shill_dinger SAY');
INSERT INTO `script_texts` VALUES (-1230034, 'Thank you! I will run for safety immediately!', NULL, 'Vous aurez à payer pour cette insulte, $c!', NULL, NULL, NULL, NULL, NULL, 'Спасибо! Я скроюсь в безопасном месте!', 0, 0, 0, 0, 'tobias SAY_FREE_1');
INSERT INTO `script_texts` VALUES (-1230033, 'Meet me at Maxwell\'s encampment. We\'ll go over the next stages of the plan there and figure out a way to decode my tablets without the decryption ring.', NULL, 'Au Final!! Je peux laisser ce dépotoir.', NULL, NULL, NULL, NULL, NULL, 'Встретимся в лагере Максвелла. Дальше действуй по плану и выясни способ расшифровки табличек.', 0, 0, 0, 0, 'reginald_windsor SAY_20_2');
INSERT INTO `script_texts` VALUES (-1230032, 'We made it!', NULL, 'Merci! Je vais courir pour la sécurité immédiatement!', NULL, NULL, NULL, NULL, NULL, 'У нас получилось!', 0, 0, 0, 0, 'reginald_windsor SAY_20_1');
INSERT INTO `script_texts` VALUES (-1230031, 'Excellent work, $N. Let\'s find the exit. I think I know the way. Follow me!', NULL, 'Rendez-vous au campement de Maxwell. Nous allons passer en revue les prochaines étapes du plan là et trouver un moyen pour décoder mes comprimés sans l\'anneau de décryptage.', NULL, NULL, NULL, NULL, NULL, 'Отличная работа, $N. Давай найдем выход. Мне кажется, я знаю дорогу. Следуй за мной!', 0, 0, 0, 0, 'reginald_windsor SAY_14_2');
INSERT INTO `script_texts` VALUES (-1230030, 'Get him out of there!', NULL, 'Nous l\'avons fait!', NULL, NULL, NULL, NULL, NULL, 'Помоги ему выбраться!', 0, 0, 0, 0, 'reginald_windsor SAY_14_1');
INSERT INTO `script_texts` VALUES (-1230029, 'He has to be in the last cell. Unless... they killed him.', NULL, 'Excellent travail, $N. Trouvons la sortie. Je pense que je connais le chemin. Suivez-moi!', NULL, NULL, NULL, NULL, NULL, 'Должно быть он в последней камере. Если они... не убили его.', 0, 0, 0, 0, 'reginald_windsor SAY_13_3');
INSERT INTO `script_texts` VALUES (-1230028, 'Administering fists of fury on Crest Killer!', NULL, 'Sortez-le de là!', NULL, NULL, NULL, NULL, NULL, 'Врежь ему!', 0, 0, 0, 0, 'reginald_windsor SAY_13_2');
INSERT INTO `script_texts` VALUES (-1230027, 'Open it. We need to hurry up. I can smell those Dark Irons coming a mile away and I can tell you one thing, they\'re COMING!', NULL, 'Il doit être dans la dernière cellule. A moins que... ils l\'ont tué.', NULL, NULL, NULL, NULL, NULL, 'Открой ее. Нам нужно поторопиться. Я за милю чувствую дух Черного Железа, и могу сказать, что они УЖЕ РЯДОМ!', 0, 0, 0, 0, 'reginald_windsor SAY_13_1');
INSERT INTO `script_texts` VALUES (-1230026, 'Alright, let\'s go.', NULL, 'Ouvrez-le. Nous avons besoin de se dépêcher. Je peux sentir les Sombrefers venir un mile loin et je peux vous dire une chose, ils vont venir!', NULL, NULL, NULL, NULL, NULL, 'Ладно, пойдем.', 0, 0, 0, 0, 'reginald_windsor SAY_7_3');
INSERT INTO `script_texts` VALUES (-1230025, 'That intolerant dirtbag finally got what was coming to him. Good riddance!', NULL, 'Bon, allons-y.', NULL, NULL, NULL, NULL, NULL, 'Он получил своей. Так ему и надо!', 0, 0, 0, 0, 'reginald_windsor SAY_7_2');
INSERT INTO `script_texts` VALUES (-1230024, 'Open it and be careful this time!', NULL, 'Ce dirtbag intolérants a finalement obtenu ce qu\'il méritait. Bon débarras!', NULL, NULL, NULL, NULL, NULL, 'Открывай, только в этот раз, будь осторожнее!', 0, 0, 0, 0, 'reginald_windsor SAY_7_1');
INSERT INTO `script_texts` VALUES (-1230023, 'I never did like those two. Let\'s get moving.', NULL, 'Ouvrez-le et soyez prudent cette fois!', NULL, NULL, NULL, NULL, NULL, 'Мне никогда не нравились эти двое. Пойдем дальше.', 0, 0, 0, 0, 'reginald_windsor SAY_5_2');
INSERT INTO `script_texts` VALUES (-1230022, 'Open it.', NULL, 'Je n\'ai jamais aimé les deux. Mettons-nous en mouvement.', NULL, NULL, NULL, NULL, NULL, 'Открой ее.', 0, 0, 0, 0, 'reginald_windsor SAY_5_1');
INSERT INTO `script_texts` VALUES (-1230021, 'Now we just have to free Tobias and we can get out of here. This way!', NULL, 'Ouvrez-le.', NULL, NULL, NULL, NULL, NULL, 'Осталось освободить Тобиаса и можно убираться отсюда. За мной!', 0, 0, 0, 0, 'reginald_windsor SAY_0_2');
INSERT INTO `script_texts` VALUES (-1230020, 'Can you feel the power, $N??? It\'s time to ROCK!', NULL, 'Maintenant nous devons juste pour libérer Tobias et nous pouvons sortir d\'ici. Par ici!', NULL, NULL, NULL, NULL, NULL, 'Чувствуешь силу, $N??? Пора веселиться!', 0, 0, 0, 0, 'reginald_windsor SAY_0_1');
INSERT INTO `script_texts` VALUES (-1230019, 'Ah, there it is!', NULL, 'Pouvez-vous sentir la puissance, $N??? Il est temps de Rock!', NULL, NULL, NULL, NULL, NULL, 'Ах, вот оно!', 0, 0, 0, 0, 'windsor SAY_9');
INSERT INTO `script_texts` VALUES (-1230018, 'This is it, $N. My stuff should be in that room. Cover me, I\'m going in!', NULL, 'Ah, ça y est!', NULL, NULL, NULL, NULL, NULL, 'Мои вещи должны быть в этой комнате. Прикрой меня $N, я захожу.', 0, 0, 0, 0, 'windsor SAY_6');
INSERT INTO `script_texts` VALUES (-1230017, 'Good work! We\'re almost there, $N. This way.', NULL, 'Ce est ça, $N. My stuff devrait être dans cette chambre. Couvrez-moi, je vais en!', NULL, NULL, NULL, NULL, NULL, 'Хорошая работа, $N! Мы почти пришли.', 0, 0, 0, 0, 'windsor SAY_4_3');
INSERT INTO `script_texts` VALUES (-1230016, 'Get him out of there!', NULL, 'Beau travail! Nous y sommes presque, $N. Par ici.', NULL, NULL, NULL, NULL, NULL, 'Вытащи его оттуда!', 0, 0, 0, 0, 'windsor SAY_4_2');
INSERT INTO `script_texts` VALUES (-1230015, 'Check that cell, $N. If someone is alive in there, we need to get them out.', NULL, 'Vérifiez cette cellule, $N. Si quelqu\'un est vivant là-bas, nous avons besoin de les faire sortir.', NULL, NULL, NULL, NULL, NULL, 'Проверь эту камеру, $N.  Если там есть живые, тогда мы должны вытащить их.', 0, 0, 0, 0, 'windsor SAY_4_1');
INSERT INTO `script_texts` VALUES (-1230014, 'Let\'s get a move on. My gear should be in the storage area up this way...', NULL, 'Mettons-nous un coup sur. Ma vitesse devrait être dans la zone de stockage de cette façon...', NULL, NULL, NULL, NULL, NULL, 'Пора убираться отсюда. Мое снаряжение должно быть в хранилище...', 0, 0, 0, 0, 'windsor SAY_1');
INSERT INTO `script_texts` VALUES (-1230013, 'You better hold me back $N or they are going to feel some prison house beatings.', NULL, 'Tu ferais mieux de me retenir ou $N va se sentir quelques coups maison de prison.', NULL, NULL, NULL, NULL, NULL, 'Тебе лучше сдерживать меня $N, либо я изобью их!', 0, 0, 0, 0, 'windsor SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1230012, 'I bet you\'re sorry now, aren\'t you !?!!', NULL, 'Je parie que vous êtes désolé maintenant, ne sont pas vous?', NULL, NULL, NULL, NULL, NULL, 'Бьюсь об заклад, сейчас вы сожалеете, не так ли?!', 0, 0, 0, 0, 'windsor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1230011, 'You locked up the wrong Marshal. Prepare to be destroyed!', NULL, 'Vous enfermé le mauvais maréchal, $N. Préparez-vous à être détruit!', NULL, NULL, NULL, NULL, NULL, 'Вы заперли не того Маршала. Приготовьтесь к смерти!', 0, 0, 0, 0, 'windsor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1230010, 'Thank you, $N! I\'m free!!!', NULL, 'Merci, $N! Je suis libre!!!', NULL, NULL, NULL, NULL, NULL, 'Спасибо тебе, $N! Теперь я свободен!!!', 0, 0, 0, 0, 'dughal SAY_FREE');
INSERT INTO `script_texts` VALUES (-1309027, '%s dies.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'thekal EMOTE_DIES');
INSERT INTO `script_texts` VALUES (-1309028, '%s is resurrected by a nearby ally!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'thekal EMOTE_RESURRECT');
INSERT INTO `script_texts` VALUES (-1230052, 'Emperor Thaurissan does not wish to be disturbed! Turn back now or face your doom, weak mortals!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'yell Magmus');
INSERT INTO `script_texts` VALUES (-1000020, 'Intruders! More pawns of the Argent Dawn, no doubt. I already count one of their number among my prisoners. Withdraw from my domain before she is executed!', NULL, 'Des intrus ! Encore des marionnettes de l\'Aube d\'argent, sans doute. J\'en ai déjà une parmi mes prisonniers. Hâtez-vous de quitter nom domaine, sans quoi elle sera exécutée !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_45MIN');
INSERT INTO `script_texts` VALUES (-1000021, 'You\'re still here? Your foolishness is amusing! The Argent Dawn wench needn\'t suffer in vain. Leave at once and she shall be spared!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_10MIN');
INSERT INTO `script_texts` VALUES (-1000022, 'I shall take great pleasure in taking this poor wretch\'s life! It\'s not too late, she needn\'t suffer in vain. Turn back and her death shall be merciful!', NULL, 'Je vais prendre beaucoup de plaisir à tuer cette pauvre femme ! Il n\'est pas trop tard, Il n\'est pas nécessaire qu\'elle souffre pour rien. Allez-vous-en et sa mort sera miséricordieuse !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_5MIN');
INSERT INTO `script_texts` VALUES (-1000023, 'Don\'t worry about me! Slay this dreadful beast and cleanse this world of his foul taint!', NULL, 'Ne vous inquiétez pas de moi ! Tuez cette bête odieuse ! Libérez le monde de sa vile corruption !', NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'ysida YELL_5MIN');
INSERT INTO `script_texts` VALUES (-1000024, 'May this prisoner\'s death serve as a warning. None shall defy the Scourge and live!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_FAILED');
INSERT INTO `script_texts` VALUES (-1000025, 'So you see fit to toy with the Lich King\'s creations? Ramstein, be sure to give the intruders a proper greeting.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_RAMMSTEIN');
INSERT INTO `script_texts` VALUES (-1000026, 'Ramstein hunger for flesh!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rammstein YELL_SPAWN');
INSERT INTO `script_texts` VALUES (-1000027, 'Who dares disturb our master?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'black_guard_sentry YELL_SPAWN');
INSERT INTO `script_texts` VALUES (-1000028, 'Time to take matters into my own hands. Come. Enter my domain and challenge the might of the Scourge!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_READY');
