INSERT INTO `script_texts` VALUES (-1900161, 'Prepare to be Malowned!', '이 말로운 님이 무서운 맛을 보여주마!', 'Préparez-vous au Malownage !', 'Gleich bekommt Ihr es mit Malown zu tun!', '准备被玛罗恩干掉吧。', '準備被瑪羅恩幹掉吧。', '¡Prepárate para recibir un machaque de Gassol!', '¡Prepárate para recibir un machaque de Gassol!', 'Сейчас тебя отмэлоунят!', 0, 0, 0, 0, 'Postmaster Malown - Aggro');
