INSERT INTO `script_texts` VALUES (-1780301, 'You know me to be the Supreme Commander of the Might of Kalimdor', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 1');
INSERT INTO `script_texts` VALUES (-1780302, 'An orc - a true orc warrior - wishes for one thing: To die in the glory of battle gainst a hated enemy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 2');
INSERT INTO `script_texts` VALUES (-1780303, 'Some of you have not fought in battles. Peace has been with us for many years. Many years we sat idle, but many years we battled.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 3');
INSERT INTO `script_texts` VALUES (-1780304, 'In those years - where strife filled the land as Legion and Scource sacked our homes, killed our families - these insects dwelled beneath us. Beneath our homes - waiting', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 4');
INSERT INTO `script_texts` VALUES (-1780305, 'Waiting to crush the life from our little ones. To slay all in their path. This they do for their god.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 5');
INSERT INTO `script_texts` VALUES (-1780306, 'And for our gods?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 6');
INSERT INTO `script_texts` VALUES (-1780307, 'We defend. We stand. We show that as one. United. We destroy. Their god will fall.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 7');
INSERT INTO `script_texts` VALUES (-1780308, 'To die today, on this field of battle, is to die an orcish death. To die today is to die for our little ones. Our old ones. Our... loved ones', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 8');
INSERT INTO `script_texts` VALUES (-1780309, 'Would any of you deny yourselves such a death? Such an honor?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 9');
INSERT INTO `script_texts` VALUES (-1780310, 'ONWARD! TO AHN\'QIRAJ!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'AQ Saurfang speech 10');
INSERT INTO `script_texts` VALUES (-1780311, 'The final battle for Kalimdor is upon us. The supreme commander of the Might of Kalimdor orders his troops to march upon Ahn\'Qiraj and destroy all in their path..', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'AQ Final battle world text');
INSERT INTO `script_texts` VALUES (-1780312, 'The Might of Kalimdor is victorious! The last of the Qiraji forces are defeated, retreating back to the ancient city-fortress of Ahn\'Qiraj. The Qiraji Resonating Crystals littering Kalimdor vanish as quickly as they appeared, leaving behind nothing but sundered earth.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'AQ War over');
