INSERT INTO `script_texts` VALUES (-1000002, '%s goes into a frenzy!', NULL, '%s est pris de frénésie !', NULL, NULL, NULL, 'se pone frenético.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES (-1000004, '%s goes into a berserker rage!', NULL, '%s entre dans une folie furieuse !', NULL, NULL, NULL, 'se pone frenético.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_GENERIC_BERSERK');
INSERT INTO `script_texts` VALUES (-1000005, '%s goes into a frenzy!', NULL, '%s est pris de frénésie !', NULL, NULL, NULL, 'se pone frenético.', NULL, NULL, 0, 3, 0, 0, 'EMOTE_BOSS_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES (-1000111, 'Our house is this way, through the thicket.', NULL, 'Notre maison est par ici, à travers le bosquet.', NULL, NULL, NULL, 'Nuestra casa es así, a través de la espesura.', NULL, NULL, 0, 0, 7, 0, 'magwin SAY_START');
INSERT INTO `script_texts` VALUES (-1000112, 'Help me!', NULL, 'Aidez-moi !', NULL, NULL, NULL, '¡Ayúdame!', NULL, NULL, 0, 0, 7, 0, 'magwin SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000113, 'My poor family. Everything has been destroyed.', NULL, 'Ma pauvre famille. Tout a été détruit.', NULL, NULL, NULL, 'Mi pobre familia. Todo ha sido destruido.', NULL, NULL, 0, 0, 7, 0, 'magwin SAY_PROGRESS');
INSERT INTO `script_texts` VALUES (-1000114, 'Father! Father! You\'re alive!', NULL, 'Père ! Père ! Tu es en vie !', NULL, NULL, NULL, '¡Padre! ¡Padre! ¡Estas vivo!', NULL, NULL, 0, 0, 7, 0, 'magwin SAY_END1');
INSERT INTO `script_texts` VALUES (-1000115, 'You can thank $N for getting me back here safely, father.', NULL, 'Tu peux remercier $N de m\'avoir ramené ici saint et sauf, père.', NULL, NULL, NULL, 'Puedes agradecerle a $N por traerme de regreso aquí sano y salvo, padre.', NULL, NULL, 0, 0, 7, 0, 'magwin SAY_END2');
INSERT INTO `script_texts` VALUES (-1000116, '%s hugs her father.', NULL, '%s serre son père dans ses bras.', NULL, NULL, NULL, '%s abraza a su padre.', NULL, NULL, 0, 2, 7, 0, 'magwin EMOTE_HUG');
INSERT INTO `script_texts` VALUES (-1000117, 'Thank you for agreeing to help. Now, let\'s get out of here $N.', NULL, 'Merci d\'avoir accepter de nous aider. Maintenant sortons d\'ici $N.', NULL, NULL, NULL, 'Gracias por aceptar ayudar. Ahora, salgamos de aquí $N.', NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_START');
INSERT INTO `script_texts` VALUES (-1000118, 'Over there! They\'re following us!', NULL, 'Par ici ! Ils nous suivent !', NULL, NULL, NULL, '¡Por ahí! ¡Nos están siguiendo!', NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `script_texts` VALUES (-1000119, 'Allow me a moment to rest. The journey taxes what little strength I have.', NULL, 'Accordez-moi un moment de repos. La journée a été difficile et j\'ai peu de force.', NULL, NULL, NULL, 'Permíteme un momento para descansar. El viaje pone a prueba las pocas fuerzas que tengo.', NULL, NULL, 0, 0, 1, 16, 'wounded elf SAY_ELF_RESTING');
INSERT INTO `script_texts` VALUES (-1000120, 'Did you hear something?', NULL, 'Vous avez entendu quelque chose ?', NULL, NULL, NULL, '¿Has oído algo?', NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `script_texts` VALUES (-1000121, 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has -', NULL, 'Le Guet de l\'Épervier, enfin ! Maintenant, où est mon... Oh non ! Mon paquet a disparu ! Où est...', NULL, NULL, NULL, 'Reloj de Halcón, ¡por fin! Ahora, ¿dónde está mi ...? ¡Oh, no! ¡Mi paquete, falta! Donde esta -', NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000122, 'You won\'t keep me from getting to Falcon Watch!', NULL, 'Vous ne m\'empêcherez pas d\'arriver au Guet de l\'Épervier !', NULL, NULL, NULL, '¡No me impedirá llegar a Reloj de Halcón!', NULL, NULL, 0, 0, 1, 0, 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `script_texts` VALUES (-1000123, 'Ready when you are, $c.', NULL, 'Je suis prêt, $c.', NULL, NULL, NULL, 'Listo cuando estés, $c.', NULL, NULL, 0, 0, 0, 15, 'big will SAY_BIG_WILL_READY');
INSERT INTO `script_texts` VALUES (-1000124, 'The Affray has begun. $n, get ready to fight!', NULL, 'La Dispute a commencé. $N, préparez-vous à combattre !', NULL, NULL, NULL, 'Ha comenzado el Enfrentamiento. $n, ¡prepárate para luchar!', NULL, NULL, 0, 0, 0, 0, 'twiggy SAY_TWIGGY_BEGIN');
INSERT INTO `script_texts` VALUES (-1000125, 'You! Enter the fray!', NULL, 'Vous ! Entrez dans le combat !', NULL, NULL, NULL, '¡Usted! ¡Entra en la refriega!', NULL, NULL, 0, 0, 0, 0, 'twiggy SAY_TWIGGY_FRAY');
INSERT INTO `script_texts` VALUES (-1000126, 'Challenger is down!', NULL, 'L’adversaire est vaincu !', NULL, NULL, NULL, '¡Ha caído el oponente!', NULL, NULL, 0, 0, 0, 0, 'twiggy SAY_TWIGGY_DOWN');
INSERT INTO `script_texts` VALUES (-1000127, 'The Affray is over.', NULL, 'La Dispute est finie !', NULL, NULL, NULL, 'La batalla ha terminado.', NULL, NULL, 0, 0, 0, 0, 'twiggy SAY_TWIGGY_OVER');
INSERT INTO `script_texts` VALUES (-1000128, 'We need you to send reinforcements to Manaforge Duro, Ardonis. This is not a request, it\'s an order.', NULL, 'Nous avons besoin de vous pour envoyer des renforts à la Manaforge Duro, Ardonis. Il ne s\'agit pas d\'une demande, c\'est un ordre.', NULL, NULL, NULL, 'Necesitamos que envíes refuerzos a Forja de Maná Duro, Ardonis. Esto no es una solicitud, es una orden.', NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `script_texts` VALUES (-1000129, 'You cannot be serious! We are severely understaffed and can barely keep this manaforge functional!', NULL, 'Vous ne pouvez pas être sérieux ! Nous sommes déjà à court de personnel et nous pouvons à peine garder la Manaforge fonctionnelle !', NULL, NULL, NULL, '¡Usted no puede ser serio! ¡Estamos muy faltos de personal y apenas podemos mantener funcional esta forja de maná!', NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT INTO `script_texts` VALUES (-1000130, 'You will do as ordered. Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control. Failure to comply will not be tolerated!', NULL, 'Vous ferez comme on vous l\'a ordonné. La Manaforge Duro subit déjà une lourde attaque par les créatures de mana et la situation est hors de contrôle. L\'échec ne sera pas toléré !', NULL, NULL, NULL, 'Hará lo que le ordene. Forja de Maná Duro ha sido objeto de un fuerte ataque de criaturas de maná y la situación está fuera de control. ¡No se tolerará el incumplimiento!', NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `script_texts` VALUES (-1000131, 'Indeed, it is not a request.', NULL, 'En effet, ce n\'est pas une demande.', NULL, NULL, NULL, 'De hecho, no es una solicitud.', NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT INTO `script_texts` VALUES (-1000132, 'My lord!', NULL, 'Mon seigneur !', NULL, NULL, NULL, '¡Mi señor!', NULL, NULL, 0, 0, 0, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `script_texts` VALUES (-1000133, 'Duro will be reinforced! Ultris was a complete disaster. I will NOT have that mistake repeated!', NULL, 'Duro recevra des renforts ! Ultris a été un désastre complet. Je ne veux pas répéter cette erreur !', NULL, NULL, NULL, '¡Duro será reforzado! Ultris fue un completo desastre. ¡NO permitiré que se repita ese error!', NULL, NULL, 0, 0, 0, 0, 'dawnforge PATHALEON_CULATOR_IMAGE_2');
INSERT INTO `script_texts` VALUES (-1000134, 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point... Prince Kael\'thas will tolerate no further delays. I will tolerate nothing other than complete success!', NULL, 'Nous avons eu un trop grand nombre de revers tout au long de notre route : la citadelle des Flammes infernales, la Crête Tombeciel, la Halte Aile-de-Feu... Le Prince Kael\'thas ne tolérera pas d\'autres retards. Je ne tolèrerai rien d\'autre qu\'une réussite totale !', NULL, NULL, NULL, 'Hemos tenido demasiados contratiempos en el camino: Ciudadela del Fuego Infernal, Cresta del Cielo Caído, Punto de Ala de Fuego ... El príncipe Kael\'thas no tolerará más demoras. ¡No toleraré nada más que el éxito total!', NULL, NULL, 0, 0, 0, 0, 'dawnforge PATHALEON_CULATOR_IMAGE_2_1');
INSERT INTO `script_texts` VALUES (-1000135, 'I am returning to Tempest Keep. See to it that I do not have reason to return!', NULL, 'Je vais retourner au Donjon de la Tempête. Veillez à ce que je n\'ai aucune raison de revenir !', NULL, NULL, NULL, 'Regreso a la Fortaleza de la Tempestad. ¡Ocúpate de que no tenga motivos para volver!', NULL, NULL, 0, 0, 0, 0, 'dawnforge PATHALEON_CULATOR_IMAGE_2_2');
INSERT INTO `script_texts` VALUES (-1000136, 'Yes, my lord.', NULL, 'Oui, mon seigneur !', NULL, NULL, NULL, 'Si mi señor.', NULL, NULL, 0, 0, 0, 0, 'dawnforge COMMANDER_DAWNFORGE_4 SAY_ARCANIST_ARDONIS_2');
INSERT INTO `script_texts` VALUES (-1000137, 'See to it, Ardonis!', NULL, 'Nous nous reverrons la-bas, Ardonis !', NULL, NULL, NULL, '¡Encárgate de ello, Ardonis!', NULL, NULL, 0, 0, 0, 0, 'dawnforge COMMANDER_DAWNFORGE_5');
INSERT INTO `script_texts` VALUES (-1000138, 'Avruu\'s magic... it still controls me. You must fight me, mortal. It\'s the only way to break the spell!', NULL, 'La magie d\'Avruu... elle me contrôle toujours. Vous devez me combattre, mortel. C\'est la seule façon de briser le charme !', NULL, NULL, NULL, 'La magia de Avruu ... todavía me controla. Debes luchar contra mí, mortal. ¡Es la única forma de romper el hechizo!', NULL, NULL, 0, 0, 0, 0, 'aeranas SAY_SUMMON');
INSERT INTO `script_texts` VALUES (-1000139, 'Avruu\'s magic is broken! I\'m free once again!', NULL, 'La magie d\'Avruu est cassée ! Je suis de nouveau libre !', NULL, NULL, NULL, '¡La magia de Avruu está rota! ¡Soy libre una vez más!', NULL, NULL, 0, 0, 0, 0, 'aeranas SAY_FREE');
INSERT INTO `script_texts` VALUES (-1000140, 'Let\'s go.', NULL, 'Allons-y.', NULL, NULL, NULL, 'Vamos.', NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_START');
INSERT INTO `script_texts` VALUES (-1000141, '$N, let\'s use the antechamber to the right.', NULL, '$N, nous allons utiliser l\'antichambre sur la droite.', NULL, NULL, NULL, '$N, usemos la antecámara de la derecha.', NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_PROGRESS1');
INSERT INTO `script_texts` VALUES (-1000142, 'I can see the light at the end of the tunnel!', NULL, 'Je peux voir la lumière au bout du tunnel !', NULL, NULL, NULL, '¡Puedo ver la luz al final del túnel!', NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_PROGRESS2');
INSERT INTO `script_texts` VALUES (-1000143, 'There\'s Farstrider Enclave now, $C. Not far to go... Look out! Troll ambush!!', NULL, 'Voici l\'Enclave des Péregrins, $C. Plus beaucoup de chemin à parcourir... Regardez ! Une embuscade de Trolls !', NULL, NULL, NULL, 'Ahora hay Enclave de Caminante Lejano, $C. No muy lejos ... ¡Cuidado! ¡¡Emboscada de trol !!', NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_PROGRESS3');
INSERT INTO `script_texts` VALUES (-1000144, 'Thank you for saving my life and bringing me back to safety, $N', NULL, 'Merci de m\'avoir sauvé la vie et de me ramener en lieu sur, $N.', NULL, NULL, NULL, 'Gracias por salvarme la vida y traerme de regreso a un lugar seguro, $N', NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_END1');
INSERT INTO `script_texts` VALUES (-1000145, 'Captain Helios, I\'ve been rescued from the Amani Catacombs. Reporting for duty, sir!', NULL, 'Capitaine Helios, j\'ai été sauvé des Catacombes Amani. J\'ai accompli mon devoir, Capitaine !', NULL, NULL, NULL, 'Capitán Helios, me rescataron de las catacumbas de Amani. ¡Reportándose para el deber, señor!', NULL, NULL, 0, 0, 1, 0, 'lilatha SAY_END2');
INSERT INTO `script_texts` VALUES (-1000146, 'Liatha, get someone to look at those injuries. Thank you for bringing her back safely.', NULL, 'Liatha, que quelqu\'un s\'occupe de vos blessures. Merci de l\'avoir ramenée en sécurité.', NULL, NULL, NULL, 'Liatha, haz que alguien mire esas heridas. Gracias por traerla de regreso sana y salva.', NULL, NULL, 0, 0, 1, 0, 'lilatha CAPTAIN_ANSWER');
INSERT INTO `script_texts` VALUES (-1000168, 'Who dares awaken Aquementas?', NULL, 'Qui ose réveiller Aquementas ?', NULL, NULL, NULL, '¿Quién osa despertar a Aquementas?', NULL, NULL, 0, 1, 0, 0, 'aquementas AGGRO_YELL_AQUE');
INSERT INTO `script_texts` VALUES (-1000169, 'Muahahahaha! You fool! You\'ve released me from my banishment in the interstices between space and time!', NULL, 'Muahahahaha ! Pauvre imbécile ! Vous m\'avez libéré de mon bannissement dans l\'interstice de l\'espace et du temps !', NULL, NULL, NULL, '¡Muahahahaha! ¡Tonto! ¡Me has liberado de mi destierro en los intersticios entre el espacio y el tiempo!', NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_1');
INSERT INTO `script_texts` VALUES (-1000170, 'All of Draenor shall quick beneath my feet! I will destroy this world and reshape it in my image!', NULL, 'Tout Draenor gisera rapidement sous mes pieds ! Je vais détruire ce monde et le remodeler à mon image !', NULL, NULL, NULL, '¡Todo Draenor se apresurará bajo mis pies! ¡Destruiré este mundo y lo remodelaré a mi imagen!', NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_2');
INSERT INTO `script_texts` VALUES (-1000171, 'Where shall I begin? I cannot bother myself with a worm such as yourself. There is a world to be conquered!', NULL, 'Par où vais-je commencer ? Je ne peux pas perdre de temps avec un ver comme vous. Il y a un monde à conquérir !', NULL, NULL, NULL, '¿Por dónde empezar? No puedo molestarme con un gusano como tú. ¡Hay un mundo que conquistar!', NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_3');
INSERT INTO `script_texts` VALUES (-1000172, 'No doubt the fools that banished me are long dead. I shall take wing survey my demense. Pray to whatever gods you hold dear that we do not meet again.', NULL, 'Sans doute que les imbéciles qui m\'ont exclu sont déjà morts. Je vais surveiller ma demeure depuis les airs. Priez votre dieu afin que nous ne nous rencontrions pas à nouveau.', NULL, NULL, NULL, 'Sin duda, los tontos que me desterraron hace tiempo que murieron. Tomaré vuelo para examinar mi demencia. Reza a los dioses que amas para que no nos volvamos a encontrar.', NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_4');
INSERT INTO `script_texts` VALUES (-1000173, 'NOOOOooooooo!', NULL, 'NOOOOooooooonnnn !', NULL, NULL, NULL, 'NOOOOooooooo!', NULL, NULL, 0, 1, 0, 0, 'nether_drake SAY_NIHIL_INTERRUPT');
INSERT INTO `script_texts` VALUES (-1000174, 'Good $N, you are under the spell\'s influence. I must analyze it quickly, then we can talk.', NULL, 'Bien $N, vous êtes sous l\'influence du sort. Je dois l\'analyser rapidement, alors nous pourrons parler.', NULL, NULL, NULL, 'Bien $N, estás bajo la influencia del hechizo. Debo analizarlo rápidamente, luego podemos hablar.', NULL, NULL, 0, 0, 7, 0, 'daranelle SAY_SPELL_INFLUENCE');
INSERT INTO `script_texts` VALUES (-1000175, 'Thank you, mortal.', NULL, 'Je vous remercie, mortel.', NULL, NULL, NULL, 'Gracias, mortal.', NULL, NULL, 0, 0, 11, 0, ' SAY_JUST_EATEN');
INSERT INTO `script_texts` VALUES (-1000176, 'The last thing I remember is the ship falling and us getting into the pods. I\'ll go see how I can help. Thank you!', NULL, 'La dernière chose dont je me souviens est la chute du vaisseau et notre fuite vers les nacelles. Je vais aller voir comment je peux aider. Merci !', NULL, NULL, NULL, 'Lo último que recuerdo es que el barco se cayó y nosotros nos metimos en las cápsulas. Iré a ver cómo puedo ayudar. ¡Gracias!', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL1');
INSERT INTO `script_texts` VALUES (-1000177, '$C, Where am I? Who are you? Oh no! What happened to the ship?', NULL, '$C, Où suis-je ? Qui êtes-vous ? Oh non ! Que s\'est-il passé sur le vaisseau ?', NULL, NULL, NULL, '$C, ¿Dónde estoy? ¿Quién eres tú? ¡Oh, no! ¿Qué pasó con el barco?', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL2');
INSERT INTO `script_texts` VALUES (-1000178, '$C You saved me! I owe you a debt that I can never repay. I\'ll go see if I can help the others.', NULL, '$C, vous m\'avez sauvé ! J\'ai une dette envers vous que je ne pourrais pas rembourser. Je vais voir si je peux aider les autres.', NULL, NULL, NULL, '$C ¡Me salvaste! Te debo una deuda que nunca podré pagar. Iré a ver si puedo ayudar a los demás.', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL3');
INSERT INTO `script_texts` VALUES (-1000179, 'Ugh... what is this place? Is that all that\'s left of the ship over there?', NULL, 'Ugh... quel est cet endroit ? Est-ce tout ce qui reste du vaisseau là-bas ?', NULL, NULL, NULL, 'Ugh ... ¿Qué es este lugar? ¿Eso es todo lo que queda del barco allí?', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HEAL4');
INSERT INTO `script_texts` VALUES (-1000180, 'Oh, the pain...', NULL, 'Oh, quelle douleur...', NULL, NULL, NULL, 'Oh, el dolor ...', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP1');
INSERT INTO `script_texts` VALUES (-1000181, 'Everything hurts, Please make it stop...', NULL, 'J\'ai mal partout. S\'il vous plaît, faites cesser la douleur...', NULL, NULL, NULL, 'Todo duele, por favor haz que pare ...', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP2');
INSERT INTO `script_texts` VALUES (-1000182, 'Ughhh... I hurt. Can you help me?', NULL, 'Ughhh... J\'ai mal. Pouvez-vous m\'aider ?', NULL, NULL, NULL, 'Ughhh ... me duele. ¿Me puedes ayudar?', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP3');
INSERT INTO `script_texts` VALUES (-1000183, 'I don\'t know if I can make it, please help me...', NULL, 'Je ne sais pas si je peux le faire, s\'il vous plaît, aidez-moi ...', NULL, NULL, NULL, 'No sé si podré hacerlo, por favor ayúdame ...', NULL, NULL, 0, 0, 7, 0, 'draenei_survivor SAY_HELP4');
INSERT INTO `script_texts` VALUES (-1000184, 'Yes Master, all goes along as planned.', NULL, 'Oui Maître, tout se passe comme prévu.', NULL, NULL, NULL, 'Sí Maestro, todo va según lo planeado.', NULL, NULL, 0, 0, 7, 0, 'engineer_spark SAY_TEXT');
INSERT INTO `script_texts` VALUES (-1000185, '%s puts the shell to his ear.', NULL, '%s colle le coquillage contre son oreille.', NULL, NULL, NULL, '%s se pone la cáscara en la oreja.', NULL, NULL, 0, 2, 7, 0, 'engineer_spark EMOTE_SHELL');
INSERT INTO `script_texts` VALUES (-1000186, 'Now I cut you!', NULL, 'Maintenant, je vais vous découper !', NULL, NULL, NULL, '¡Ahora te corto!', NULL, NULL, 0, 1, 7, 0, 'engineer_spark SAY_ATTACK');
INSERT INTO `script_texts` VALUES (-1000187, 'Thank you, dear $C, you just saved my life.', NULL, 'Merci, cher $C, vous venez de me sauver ma vie.', NULL, NULL, NULL, 'Gracias, $gquerido:querida; $C, acabas de salvarme la vida.', NULL, NULL, 0, 0, 7, 0, 'faulk SAY_HEAL');
INSERT INTO `script_texts` VALUES (-1000188, 'Deployment sucessful. Trespassers will be neutralized.', NULL, 'Déploiement terminé. Les intrus vont être neutralisés.', NULL, NULL, NULL, 'Implementación exitosa. Los intrusos serán neutralizados.', NULL, NULL, 0, 0, 0, 0, 'converted_sentry SAY_CONVERTED_1');
INSERT INTO `script_texts` VALUES (-1000189, 'Objective acquired. Initiating security routines.', NULL, 'Cible repérée. Lancement des mesures de sécurité.', NULL, NULL, NULL, 'Objetivo adquirido. Iniciar rutinas de seguridad.', NULL, NULL, 0, 0, 0, 0, 'converted_sentry SAY_CONVERTED_2');
INSERT INTO `script_texts` VALUES (-1000190, 'In Nagrand, food hunt ogre!', NULL, 'En Nagrand, la nourriture chasse les ogres !', NULL, NULL, NULL, '¡En Nagrand, caza ogro de comida!', NULL, NULL, 0, 0, 0, 0, ' SAY_LUMP_0');
INSERT INTO `script_texts` VALUES (-1000191, 'You taste good with maybe a little salt and pepper.', NULL, 'Vous avez peut-être un bon goût avec un peu de sel et de poivre.', NULL, NULL, NULL, 'Sabes bien con tal vez un poco de sal y pimienta.', NULL, NULL, 0, 0, 0, 0, ' SAY_LUMP_1');
INSERT INTO `script_texts` VALUES (-1000192, 'OK, OK! Lump give up!', NULL, 'OK, OK ! Lump renonce !', NULL, NULL, NULL, '¡BIEN BIEN! ¡Bulto ríndete!', NULL, NULL, 0, 0, 0, 0, ' SAY_LUMP_DEFEAT');
INSERT INTO `script_texts` VALUES (-1000193, 'Thank you, dear $C, you just saved my life.', NULL, 'Merci, cher $C, vous venez de me sauver ma vie.', NULL, NULL, NULL, 'Gracias, $gquerido:querida; $C, acabas de salvarme la vida.', NULL, NULL, 0, 0, 1, 0, 'stillblade SAY_HEAL');
INSERT INTO `script_texts` VALUES (-1000194, 'I give up! Please don\'t kill me!', NULL, 'Je renonce ! Ne me tuez pas s\'il vous plaît !', NULL, NULL, NULL, '¡Me rindo! ¡Por favor no me mates!', NULL, NULL, 0, 0, 0, 0, 'unkor SAY_SUBMIT');
INSERT INTO `script_texts` VALUES (-1000195, 'I choose the third option: KILLING YOU!', NULL, 'Je choisis la troisième option : VOUS TUER !', NULL, NULL, NULL, 'Elijo la tercera opción: ¡MATARTE!', NULL, NULL, 0, 0, 0, 0, 'floon SAY_FLOON_ATTACK');
INSERT INTO `script_texts` VALUES (-1000196, 'Belore...', NULL, 'Belore...', NULL, NULL, NULL, 'Belore ...', NULL, NULL, 0, 0, 1, 0, 'lady_sylvanas SAY_LAMENT_END');
INSERT INTO `script_texts` VALUES (-1000197, '%s kneels down and pick up the amulet.', NULL, '%s s\'agenouille et ramasse l\'amulette.', NULL, NULL, NULL, '%s se arrodilla y toma el amuleto.', NULL, NULL, 0, 2, 1, 0, 'lady_sylvanas EMOTE_LAMENT_END');
INSERT INTO `script_texts` VALUES (-1000198, 'Taste blade, mongrel!', NULL, 'Goûte à ma lame, bâtard !', NULL, NULL, NULL, '¡Siente el acero, chucho!', NULL, NULL, 0, 0, 0, 0, 'SAY_GUARD_SIL_AGGRO1');
INSERT INTO `script_texts` VALUES (-1000199, 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', NULL, 'Pitié, dites-moi que vous n\'avez pas fait ce que je crois que vous avez fait. Dites-moi que je ne vais pas devoir vous faire de mal...', NULL, NULL, NULL, 'Por favor, dime que no hiciste lo que creo que hiciste. Por favor, dime que no voy a tener que hacerte daño...', NULL, NULL, 0, 0, 0, 0, 'SAY_GUARD_SIL_AGGRO2');
INSERT INTO `script_texts` VALUES (-1000200, 'As if we don\'t have enough problems, you go and create more!', NULL, 'Comme si nous n\'avons pas assez de problèmes, vous allez en créer encore plus !', NULL, NULL, NULL, 'Como si no tuviéramos bastantes problemas, ¡vas tú y creas más!', NULL, NULL, 0, 0, 0, 0, 'SAY_GUARD_SIL_AGGRO3');
INSERT INTO `script_texts` VALUES (-1000201, 'I\'m saved! Thank you, doctor!', NULL, 'Me voilà sur pieds ! Merci docteur !', NULL, NULL, NULL, '¡Estoy salvado! ¡Gracias, doctor!', NULL, NULL, 0, 0, 0, 0, 'injured_patient SAY_DOC1');
INSERT INTO `script_texts` VALUES (-1000202, 'HOORAY! I AM SAVED!', NULL, 'HOURRA ! J\'AI SURVÉCU !', NULL, NULL, NULL, '¡HURRA! ¡ME HA SALVADO!', NULL, NULL, 0, 0, 0, 0, 'injured_patient SAY_DOC2');
INSERT INTO `script_texts` VALUES (-1000203, 'Sweet, sweet embrace... take me...', NULL, 'Adieu monde cruel... Je m\'en vais loin de toi mourir de douleur et d\'ennui... Adieu, adieu, adieu...', NULL, NULL, NULL, 'Dulce, dulce olvido... llévame contigo...', NULL, NULL, 0, 0, 0, 0, 'injured_patient SAY_DOC3');
INSERT INTO `script_texts` VALUES (-1000204, '%s looks up at you quizzically. Maybe you should inspect it?', NULL, '%s lève les yeux vers vous d\'un air interrogateur. Peut-être devriez vous l\'inspecter ?', NULL, NULL, NULL, 'te mira socarronamente. ¿Tal vez deberías examinarlo?', NULL, NULL, 0, 2, 0, 0, 'cluck EMOTE_A_HELLO');
INSERT INTO `script_texts` VALUES (-1000205, '%s looks at you unexpectadly.', NULL, '%s vous regarde avec impatience.', NULL, NULL, NULL, '%s te mira inesperadamente.', NULL, NULL, 0, 2, 0, 0, 'cluck EMOTE_H_HELLO');
INSERT INTO `script_texts` VALUES (-1000206, '%s starts pecking at the feed.', NULL, '%s commence à picorer la nourriture.', NULL, NULL, NULL, '%s comienza a picotear la alimentación.', NULL, NULL, 0, 2, 0, 0, 'cluck EMOTE_CLUCK_TEXT2');
INSERT INTO `script_texts` VALUES (-1000207, 'You have my blessing', NULL, 'Vous avez ma bénédiction.', NULL, NULL, NULL, 'Tienes mi bendición', NULL, NULL, 0, 0, 0, 0, 'ashyen_and_keleth SAY_REWARD_BLESS');
INSERT INTO `script_texts` VALUES (-1000208, 'Frenzyheart kill you if you come back. You no welcome here no more!', NULL, 'Frénécœur va vous tuer si vous revenez. On veut plus vous voir !', NULL, NULL, NULL, 'Corazón Frenético te matará si regresas. ¡Ya no eres bienvenido aquí!', NULL, NULL, 0, 0, 0, 0, 'vekjik SAY_TEXTID_VEKJIK1');
INSERT INTO `script_texts` VALUES (-1000209, 'Very well. Let\'s see what you have to show me, $N.', NULL, 'Très bien. Voyons voir ce que vous avez à me montrer, $N.', NULL, NULL, NULL, 'Muy bien. Veamos qué tienes que mostrarme, $N.', NULL, NULL, 0, 0, 1, 0, 'anvilward SAY_ANVIL1');
INSERT INTO `script_texts` VALUES (-1000210, 'What manner of trick is this, $R? If you seek to ambush me, I warn you I will not go down quietly!', NULL, 'Quel est le mode d\'emploi de ce truc, $R ? Si vous cherchez à me tendre une embuscade, je vous avertis que je ne me laisserai pas descendre si facilement !', NULL, NULL, NULL, '¿Qué truco es este, $R? Si buscas tenderme una emboscada, ¡te advierto que no bajaré tranquilamente!', NULL, NULL, 0, 0, 1, 0, 'anvilward SAY_ANVIL2');
INSERT INTO `script_texts` VALUES (-1000211, 'Warning! %s emergency shutdown process initiated by $N. Shutdown will complete in two minutes.', NULL, 'Attention ! Le processus d\'arrêt d\'urgence de la %s a été initialisé par $N. L\'arrêt sera effectif dans deux minutes.', NULL, NULL, NULL, '¡Advertencia!  %s proceso de apagado de emergencia iniciado por $N. El apagado se completará en dos minutos.', NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_START');
INSERT INTO `script_texts` VALUES (-1000212, 'Emergency shutdown will complete in one minute.', NULL, 'Le processus d\'arrêt d\'urgence prendra fin dans une minute.', NULL, NULL, NULL, 'El apagado de emergencia se completará en un minuto.', NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_60');
INSERT INTO `script_texts` VALUES (-1000213, 'Emergency shutdown will complete in thirty seconds.', NULL, 'Le processus d\'arrêt d\'urgence prendra fin dans trente secondes.', NULL, NULL, NULL, 'El apagado de emergencia se completará en treinta segundos.', NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_30');
INSERT INTO `script_texts` VALUES (-1000214, 'Emergency shutdown will complete in ten seconds.', NULL, 'Le processus d\'arrêt d\'urgence prendra fin dans dix secondes.', NULL, NULL, NULL, 'El apagado de emergencia se completará en diez segundos.', NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_10');
INSERT INTO `script_texts` VALUES (-1000215, 'Emergency shutdown complete.', NULL, 'Processus d\'arrêt d\'urgence terminé.', NULL, NULL, NULL, 'Apagado de emergencia completo.', NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000216, 'Emergency shutdown aborted.', NULL, 'Processus d\'arrêt d\'urgence annulé.', NULL, NULL, NULL, 'Apagado de emergencia abortado.', NULL, NULL, 0, 2, 0, 0, 'manaforge_control EMOTE_ABORT');
INSERT INTO `script_texts` VALUES (-1000217, 'Greetings, $N. I will guide you through the cavern. Please try and keep up.', NULL, 'Bonjour, $N. Je vais vous guider à travers la caverne. S\'il vous plaît, essayez de rester concentré.', NULL, NULL, NULL, 'Saludos, $N. Te guiaré a través de la caverna. Intenta seguir el ritmo.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_1');
INSERT INTO `script_texts` VALUES (-1000218, 'We do not know if the Caverns of Time have always been accessible to mortals. Truly, it is impossible to tell as the Timeless One is in perpetual motion, changing our timeways as he sees fit. What you see now may very well not exist tomorrow. You may wake up and have no memory of this place.', NULL, 'Nous ne savons pas si les Grottes du temps ont toujours été accessibles aux mortels. Vraiment, il est impossible de dire que le cours du temps un est en mouvement perpétuel, et si le temps peut choisir sa route comme bon lui semble. Ce que vous voyez maintenant peut très bien ne pas exister demain. Vous pouvez vous réveiller et ne pas avoir de souvenir de ce lieu.', NULL, NULL, NULL, 'No sabemos si las Cavernas del Tiempo siempre han sido accesibles para los mortales. En verdad, es imposible saberlo, ya que el Atemporal está en perpetuo movimiento, cambiando nuestros tiempos como mejor le parezca. Es muy posible que lo que ves ahora no exista mañana. Puede que te despiertes y no recuerdes este lugar.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_2');
INSERT INTO `script_texts` VALUES (-1000219, 'It is strange, I know... Most mortals cannot actually comprehend what they see here, as often, what they see is not anchored within their own perception of reality.', NULL, 'C\'est étrange, je sais... La plupart des mortels ne peuvent pas vraiment comprendre ce qu\'ils voient ici. Comme souvent, ce qu\'ils voyent n\'est pas ancré au sein de leur propre perception de la réalité.', NULL, NULL, NULL, 'Es extraño, lo sé ... La mayoría de los mortales en realidad no pueden comprender lo que ven aquí, ya que a menudo lo que ven no está anclado en su propia percepción de la realidad.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_3');
INSERT INTO `script_texts` VALUES (-1000220, 'Follow me, please.', NULL, 'Suivez-moi, s\'il vous plaît.', NULL, NULL, NULL, 'Sígame por favor.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_4');
INSERT INTO `script_texts` VALUES (-1000221, 'There are only two truths to be found here: First, that time is chaotic, always in flux, and completely malleable and second, perception does not dictate reality.', NULL, 'Il n\'y a que deux vérités qui se trouvent ici : Tout d\'abord, le temps est chaotique, toujours en flux, et complètement malléable. Deuxièmement, la perception ne dicte pas la réalité.', NULL, NULL, NULL, 'Aquí solo se pueden encontrar dos verdades: primero, que el tiempo es caótico, siempre cambiante y completamente maleable, y segundo, la percepción no dicta la realidad.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_5');
INSERT INTO `script_texts` VALUES (-1000222, 'As custodians of time, we watch over and care for Nozdormu\'s realm. The master is away at the moment, which means that attempts are being made to dramatically alter time. The master never meddles in the affairs of mortals but instead corrects the alterations made to time by others. He is reactionary in this regard.', NULL, 'En tant que gardiens du temps, nous prenons le plus grand soin du Royaume de Nozdormu. Le maître est absent pour le moment, ce qui signifie que des tentatives sont faites afin de modifier radicalement le temps. Le maître n\'interfère jamais dans les affaires des mortels, mais tente plutôt de corriger les modifications du temps apportées par d\'autres. Il est réactionnaire dans ce domaine.', NULL, NULL, NULL, 'Como custodios del tiempo, velamos y cuidamos el reino de Nozdormu. El maestro está ausente en este momento, lo que significa que se están haciendo intentos para alterar drásticamente el tiempo. El maestro nunca se entromete en los asuntos de los mortales, sino que corrige las alteraciones del tiempo hechas por otros. Es reaccionario a este respecto.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_6');
INSERT INTO `script_texts` VALUES (-1000223, 'For normal maintenance of time, the Keepers of Time are sufficient caretakers. We are able to deal with most ordinary disturbances. I speak of little things, such as rogue mages changing something in the past to elevate their status or wealth in the present.', NULL, 'Pour l\'entretien habituel du temps, les Gardiens du Temps sont suffisants. Nous sommes en mesure de faire face à la plupart des troubles. Je parle de petites choses, comme des mages rénégats qui veulent changer quelque chose dans le passé et élever leur statut ou richesse dans le présent.', NULL, NULL, NULL, 'Para el mantenimiento normal del tiempo, los Guardianes del Tiempo son suficientes guardianes. Podemos hacer frente a la mayoría de las perturbaciones ordinarias. Hablo de pequeñas cosas, como los magos rebeldes que cambian algo en el pasado para elevar su estatus o riqueza en el presente.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_7');
INSERT INTO `script_texts` VALUES (-1000224, 'These tunnels that you see are called timeways. They are infinite in number. The ones that currently exist in your reality are what the master has deemed as \'trouble spots.\' These trouble spots may differ completely in theme but they always share a cause. That is, their existence is a result of the same temporal disturbance. Remember that should you venture inside one...', NULL, 'Ces tunnels que vous voyez sont appelés couloirs du temps. Ils sont en nombre infini. Ceux qui existent actuellement dans la réalité sont ceux que le maître a estimé être des « points de troubles ». Ces points de troubles peuvent être complètement différents dans le thème, mais ils servent toujours la même cause. Leur existence est le résultat d\'une même perturbation du temps. Rappelez-vous en si vous vous aventurez dans un...', NULL, NULL, NULL, 'Estos túneles que ves se llaman vías del tiempo. Son infinitos en número. Los que existen actualmente en su realidad son lo que el maestro ha considerado como \"puntos conflictivos\". Estos puntos problemáticos pueden diferir completamente en el tema, pero siempre comparten una causa. Es decir, su existencia es el resultado de la misma perturbación temporal. Recuerda que si te adentras en uno ...', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_8');
INSERT INTO `script_texts` VALUES (-1000225, 'This timeway is in great disarray! We have agents inside right now attempting to restore order. What information I have indicates that Thrall\'s freedom is in jeopardy. A malevolent organization known as the Infinite Dragonflight is trying to prevent his escape. I fear without outside assistance, all will be lost.', NULL, 'Cette route du temps est en grand péril ! Nous avons maintenant des agents à l\'intérieur qui tentent de rétablir l\'ordre. D\'après mes sources, la liberté de Thrall est en danger. Une organisation maléfique connue sous le nom de Vol Infini tente d\'empêcher son évasion. Je crains que sans une aide extérieure, tout sera perdu.', NULL, NULL, NULL, '¡Esta vez está en un gran desorden! Tenemos agentes adentro ahora mismo intentando restablecer el orden. La información que tengo indica que la libertad de Thrall está en peligro. Una organización malévola conocida como Vuelo Infinito del Dragón está tratando de evitar su escape. Me temo que sin ayuda externa, todo estará perdido.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_9');
INSERT INTO `script_texts` VALUES (-1000226, 'We have very little information on this timeway. Sa\'at has been dispatched and is currently inside. The data we have gathered from his correspondence is that the Infinite Dragonflight are once again attempting to alter time. Could it be that the opening of the Dark Portal is being targeted for sabotage? Let us hope not...', NULL, 'Nous avons très peu d\'informations sur cette route du temps. Sa\'at a été envoyé et est actuellement à l\'intérieur. La seule information que nous avons collecté de ses rapports, est que le Vol Infini tente une nouvelle fois de modifier le temps. Serait-il possible que l\'ouverture de la Porte des ténèbres soit la cible d\'actes de sabotage ? Espérons que non...', NULL, NULL, NULL, 'Tenemos muy poca información sobre este horario. Sa\'at ha sido enviado y actualmente está dentro. Los datos que hemos recopilado de su correspondencia es que los Vuelo Infinito del Dragón están intentando una vez más alterar el tiempo. ¿Podría ser que la apertura del Portal Oscuro esté siendo objeto de sabotaje? Esperemos que no ...', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_10');
INSERT INTO `script_texts` VALUES (-1000227, 'This timeway is currently collapsing. What that may hold for the past, present and future is currently unknown...', NULL, 'Ce passage est effondré pour le moment. Ce qu\'il peut conserver pour notre passé, présent et avenir est actuellement inconnu...', NULL, NULL, NULL, 'Esta forma de tiempo se está derrumbando actualmente. Lo que eso puede deparar en el pasado, presente y futuro se desconoce actualmente ...', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_11');
INSERT INTO `script_texts` VALUES (-1000228, 'The timeways are currently ranked in order from least catastrophic to most catastrophic. Note that they are all classified as catastrophic, meaning that any single one of these timeways collapsing would mean that your world would end. We only classify them in such a way so that the heroes and adventurers that are sent here know which timeway best suits their abilities.', NULL, 'Les couloirs du temps sont actuellement classés par ordre de catastrophes. Notez qu\'ils sont tous classés comme catastrophiques, ce qui signifie que si l\'un de ces couloirs s\'effondre, le monde prendrait fin. Nous les avons classés de cette manière pour que les héros et les aventuriers qui sont envoyés ici savent quelle route convient le mieu à leurs capacités.', NULL, NULL, NULL, 'Las vías del tiempo están actualmente clasificadas en orden de menos catastrófico a más catastrófico. Tenga en cuenta que todos están clasificados como catastróficos, lo que significa que el colapso de cualquiera de estas vías del tiempo significaría que su mundo terminaría. Solo los clasificamos de tal manera que los héroes y aventureros que son enviados aquí sepan qué vía temporal se adapta mejor a sus habilidades.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_12');
INSERT INTO `script_texts` VALUES (-1000229, 'All we know of this timeway is that it leads to Mount Hyjal. The Infinite Dragonflight have gone to great lengths to prevent our involvement. We know next to nothing, mortal. Soridormi is currently attempting to break through the timeway\'s defenses but has thus far been unsuccessful. You might be our only hope of breaking through and resolving the conflict.', NULL, 'Tout ce que nous savons de ce passage est qu\'il mène au mont Hyjal. Les dragons du Vol Infini ont fait de grands efforts pour nous empêcher d\'y participer. Nous savons presque rien, mortel. Soridormi tente actuellement de percer les défenses du passage mais jusqu\'à présent ces tentatives ont été infructueuses. Vous êtes peut-être notre seul espoir de résoudre le conflit.', NULL, NULL, NULL, 'Todo lo que sabemos de esta vía temporal es que conduce al monte Hyjal. El Vuelo Infinito del Dragón ha hecho todo lo posible para evitar nuestra participación. No sabemos casi nada, mortal. Soridormi actualmente está intentando romper las defensas de la vía del tiempo, pero hasta ahora no ha tenido éxito. Puede que seas nuestra única esperanza de romper y resolver el conflicto.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_13');
INSERT INTO `script_texts` VALUES (-1000230, 'Our time is at an end $N. I would wish you luck, if such a thing existed.', NULL, 'C\'est ici que notre conversation prend fin $N. Je vous souhaite bonne chance, si une telle chose existe.', NULL, NULL, NULL, 'Nuestro tiempo ha llegado a su fin $N. Le deseo suerte, si tal cosa existiera.', NULL, NULL, 0, 3, 0, 0, 'WHISPER_CUSTODIAN_14');
INSERT INTO `script_texts` VALUES (-1000234, 'Follow me, stranger. This won\'t take long.', NULL, 'Suivez-moi. Ce ne sera pas long.', NULL, NULL, NULL, 'Sígueme, forastero. Esto no tardará mucho.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_SERV_0');
INSERT INTO `script_texts` VALUES (-1000235, 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', NULL, '« Shattrath fut jadis la capitale draeneï de ce monde. Son nom signifie : « demeure de lumière ». »', NULL, NULL, NULL, 'Shattrath fue una vez la capital draenei de este mundo. Su nombre significa \"morada de la luz\".', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_1');
INSERT INTO `script_texts` VALUES (-1000236, 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', NULL, 'Quand la Légion ardente retourna la Horde contre les draeneï, la bataille la plus terrible eut lieu ici. Les draeneï combattirent jusqu\'au dernier, mais la ville finit par tomber.', NULL, NULL, NULL, 'Cuando la Legión Ardiente volvió a los orcos contra los draenei, aquí se libró la batalla más feroz. Los draenei lucharon con uñas y dientes, pero al final la ciudad cayó.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_2');
INSERT INTO `script_texts` VALUES (-1000237, 'The city was left in ruins and darkness... until the Sha\'tar arrived.', NULL, 'La ville fut laissée en ruine, en proie aux ténèbres… jusqu\'à l\'arrivée des Sha\'tar.', NULL, NULL, NULL, 'La ciudad quedó en ruinas y oscuridad ... hasta que llegó el Sha\'tar.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_3');
INSERT INTO `script_texts` VALUES (-1000238, 'Let us go into the Lower City. I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', NULL, 'Allons dans la Ville basse. Je dois vous avertir que Shattrath est l\'un des seuls havres de paix de l\'Outreterre, et qu\'elle a donc attiré des foules de réfugiés de toutes les guerres, présentes et passées.', NULL, NULL, NULL, 'Vayamos a la Ciudad Baja. Te advertiré que, como uno de los únicos refugios seguros en Terrallende, Shattrath ha atraído a multitud de refugiados de todas las guerras, actuales y pasadas.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_4');
INSERT INTO `script_texts` VALUES (-1000239, 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', NULL, '« Les Sha\'tar, ou « nés de la lumière », sont les naaru venus en Outreterre pour combattre les démons de la Légion ardente. »', NULL, NULL, NULL, 'Los Sha\'tar, o \"nacidos de la luz\", son los naaru que llegaron a Terrallende para luchar contra los demonios de la Legión Ardiente.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_5');
INSERT INTO `script_texts` VALUES (-1000240, 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.', NULL, 'Ils furent attirés vers les ruines de Shattrath où un petit groupe de prêtres draeneï survivants continuait à exercer ses rites dans les vestiges de l\'un des temples, ici même.', NULL, NULL, NULL, 'Fueron atraídos a las ruinas de la ciudad de Shattrath, donde un pequeño remanente del sacerdocio draenei realizaba sus ritos dentro de un templo en ruinas en este mismo lugar.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_6');
INSERT INTO `script_texts` VALUES (-1000241, 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began. The ruined temple is now used as an infirmary for injured refugees.', NULL, 'Le groupe de prêtres, appelé l\'Aldor, reprit rapidement de l\'importance quand la nouvelle du retour des naaru se répandit, et la reconstruction commença peu après. Le temple en ruine sert maintenant d\'infirmerie pour les réfugiés blessés.', NULL, NULL, NULL, 'El sacerdocio, conocido como los Aldor, recuperó rápidamente su fuerza cuando se corrió la voz de que los naaru habían regresado y pronto comenzó la reconstrucción. El templo en ruinas ahora se utiliza como enfermería para refugiados heridos.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_7');
INSERT INTO `script_texts` VALUES (-1000242, 'It wouldn\'t be long, however, before the city came under attack once again. This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', NULL, 'Il ne fallut cependant pas attendre longtemps avant que la ville ne soit à nouveau assaillie. Cette fois, l\'attaque venait des armées d\'Illidan. Un important régiment d\'elfes de sang avait été envoyé par l\'allié d\'Illidan, Kael\'thas Haut-soleil, pour détruire la ville.', NULL, NULL, NULL, 'Sin embargo, no pasaría mucho tiempo antes de que la ciudad fuera atacada una vez más. Esta vez, el ataque provino de los ejércitos de Illidan. Un gran regimiento de elfos de sangre había sido enviado por el aliado de Illidan, Kael\'thas Sunstrider, para arrasar la ciudad.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_8');
INSERT INTO `script_texts` VALUES (-1000243, 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light. But then the unexpected happened.', NULL, 'Quand le bataillon d\'elfes de sang traversa ce pont, les exarques et redresseurs de torts de l\'Aldor prirent position pour défendre la Terrasse de la Lumière. C\'est alors que l\'inattendu arriva.', NULL, NULL, NULL, 'Cuando el regimiento de elfos de sangre cruzó este mismo puente, los exarcas y vindicadores de Aldor se alinearon para defender la Terraza de la Luz. Pero entonces sucedió lo inesperado.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_9');
INSERT INTO `script_texts` VALUES (-1000244, 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', NULL, 'Les elfes de sang déposèrent leurs armes devant les défenseurs de la ville. Leur chef, un ancien connu sous le nom de Voren\'thal, pénétra sur la Terrasse de la Lumière et exigea de parler à A\'dal.', NULL, NULL, NULL, 'Los elfos de sangre depusieron sus armas frente a los defensores de la ciudad; su líder, un anciano elfo de sangre conocido como Voren\'thal, irrumpió en la Terraza de la Luz y exigió hablar con A\'dal.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_10');
INSERT INTO `script_texts` VALUES (-1000245, 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', NULL, 'À l\'approche du naaru, Voren\'thal s\'agenouilla devant lui et prononça ces paroles : « Je vous ai vu en rêve, naaru. L\'unique espoir de survie de mon peuple est en vous. Mes fidèles et moi-même sommes venus pour vous servir. »', NULL, NULL, NULL, 'Cuando el naaru se acercó a él, Voren\'thal se arrodilló ante él y pronunció las siguientes palabras: \"Te he visto en una visión, naaru. La única esperanza de supervivencia de mi raza está en ti. Mis seguidores y yo estamos aquí para servirte. \"', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_11');
INSERT INTO `script_texts` VALUES (-1000246, 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves. Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.', NULL, 'La défection de Voren\'thal et de ses fidèles fut la plus grande perte jamais subie par les forces de Kael. Et il ne s\'agissait pas là de n\'importe quels elfes de sang. Nombre des plus brillants érudits et magistères de Kael avaient suivi l\'influence de Voren\'thal.', NULL, NULL, NULL, 'La deserción de Voren\'thal y sus seguidores fue la mayor pérdida jamás sufrida por las fuerzas de Kael. Y estos no eran cualquier elfo de sangre. Muchos de los mejores y más brillantes eruditos y magister de Kael habían sido influenciados por la influencia de Voren\'thal.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_12');
INSERT INTO `script_texts` VALUES (-1000247, 'The naaru accepted the defectors, who would become known as the Scryers; their dwelling lies in the platform above. Only those initiated with the Scryers are allowed there.', NULL, 'Les naaru accueillirent les renégats, qui prirent le nom de Clairvoyants. Leur demeure se trouve là-haut, sur l\'esplanade. Seuls les initiés y sont les bienvenus.', NULL, NULL, NULL, 'Los naaru aceptaron a los desertores, que serían conocidos como los Arúspices; su morada está en la plataforma de arriba. Solo los iniciados con los Arúspices pueden entrar allí.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_13');
INSERT INTO `script_texts` VALUES (-1000248, 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', NULL, 'L\'Aldor rassemble des fidèles de la Lumière, et le pardon et la rédemption sont des valeurs qu\'ils apprécient. Cependant, il fut difficile pour eux d\'oublier les exactions des elfes de sang commises sous les ordres de Kael.', NULL, NULL, NULL, 'Los Aldor son seguidores de la Luz y el perdón y la redención son valores que comprenden. Sin embargo, les resultó difícil olvidar las hazañas de los elfos de sangre mientras estaban bajo el mando de Kael.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_14');
INSERT INTO `script_texts` VALUES (-1000249, 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru. They were not happy to share the city with their former enemies.', NULL, 'Beaucoup de prêtres avaient été tués par les magistères qui juraient désormais de servir les naaru. Les prêtres n\'étaient pas enchantés de devoir cohabiter avec leurs anciens ennemis.', NULL, NULL, NULL, 'Muchos sacerdotes habían sido asesinados por los mismos magistrados que ahora prometían servir a los naaru. No estaban felices de compartir la ciudad con sus antiguos enemigos.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_15');
INSERT INTO `script_texts` VALUES (-1000250, 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above. As a holy site, only the initiated are welcome inside.', NULL, 'Le grand temple sacré de l\'Aldor et ses dépendances se trouvent sur la terrasse, là-haut. Il s\'agit d\'un site sacré et seuls les initiés y sont les bienvenus.', NULL, NULL, NULL, 'El templo más sagrado de Aldor y las viviendas circundantes se encuentran en la terraza de arriba. Como lugar sagrado, solo los iniciados son bienvenidos dentro.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_16');
INSERT INTO `script_texts` VALUES (-1000251, 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', NULL, 'Les attaques contre Shattrath continuèrent, mais la ville ne tomba pas, comme vous pouvez le voir. Bien au contraire, le naaru appelé Xi\'ri mena avec succès une incursion dans la vallée d\'Ombrelune, l\'entrée du domaine d\'Illidan.', NULL, NULL, NULL, 'Los ataques contra Shattrath continuaron, pero la ciudad no cayó, como puede ver. Por el contrario, el naaru conocido como Xi\'ri lideró una exitosa incursión en el Valle Sombraluna, el umbral de Illidan.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_17');
INSERT INTO `script_texts` VALUES (-1000252, 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers. The two factions have not given up on their old feuds, though.', NULL, 'Là-bas, il poursuit sa lutte contre Illidan avec l\'aide de l\'Aldor et des Clairvoyants. Ces deux camps n\'ont cependant pas abandonné leurs vieilles querelles.', NULL, NULL, NULL, 'Allí continúa librando la guerra contra Illidan con la ayuda de los Aldor y los Arúspices. Sin embargo, las dos facciones no han renunciado a sus antiguas enemistades.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_18');
INSERT INTO `script_texts` VALUES (-1000253, 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal. The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', NULL, 'L\'animosité entre eux est telle qu\'ils se disputent l\'honneur d\'aller aider les naaru là-bas. Chaque jour, la décision est prise ici par A\'dal. Les armées se rassemblent ici pour recevoir sa bénédiction avant de partir pour Ombrelune.', NULL, NULL, NULL, 'Tal es su animosidad que compiten por el honor de ser enviados para ayudar a los naaru allí. Cada día, esa decisión la toma A\'dal aquí. Los ejércitos se reúnen aquí para recibir la bendición de A\'dal antes de dirigirse a Sombraluna.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_19');
INSERT INTO `script_texts` VALUES (-1000254, 'Khadgar should be ready to see you again. Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.', NULL, 'Khadgar devrait être prêt à vous recevoir à nouveau. N\'oubliez pas que pour servir les Sha\'tar, il vous faudra vous allier à l\'Aldor ou aux Clairvoyants. Rechercher les faveurs de l\'un des deux groupes vous apportera l\'inimitié de l\'autre.', NULL, NULL, NULL, 'Khadgar debería estar listo para verte de nuevo. Solo recuerda que para servir a los Sha\'tar probablemente tendrás que aliarte con los Aldor o los Arúspices. Y buscar el favor de un grupo provocará el disgusto de los demás.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_20');
INSERT INTO `script_texts` VALUES (-1000255, 'Good luck stranger, and welcome to Shattrath City.', NULL, 'Bonne chance, étranger, et bienvenue à Shattrath.', NULL, NULL, NULL, 'Buena suerte, forastero, y bienvenido a la ciudad de Shattrath.', NULL, NULL, 0, 4, 0, 0, 'SAY_KHAD_SERV_21');
INSERT INTO `script_texts` VALUES (-1000274, 'Time to teach you a lesson in manners, little $Gboy:girl;!', NULL, 'Il est temps de vous donnez une leçon de savoir vivre, $gpetit gars:petite fille; !', NULL, NULL, NULL, 'Es hora de darte una lección de modales, pequeño $gniño:niña ;!', NULL, NULL, 0, 0, 0, 0, 'larry SAY_START');
INSERT INTO `script_texts` VALUES (-1000275, 'Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.', NULL, 'Maintenant, je vous laisse jusqu\'à \'3\' pour déguerpir d\'ici, avant que je ne lache mes chiens sur vous.', NULL, NULL, NULL, 'Ahora te voy a dar a la cuenta de \'3\' para que salgas de aquí antes de que te ponga los perros.', NULL, NULL, 0, 0, 0, 0, 'larry SAY_COUNT');
INSERT INTO `script_texts` VALUES (-1000276, '1...', NULL, '1...', NULL, NULL, NULL, '1...', NULL, NULL, 0, 0, 0, 0, 'larry SAY_COUNT_1');
INSERT INTO `script_texts` VALUES (-1000277, '2...', NULL, '2...', NULL, NULL, NULL, '2...', NULL, NULL, 0, 0, 0, 0, 'larry SAY_COUNT_2');
INSERT INTO `script_texts` VALUES (-1000278, 'Time to meet your maker!', NULL, 'Il est temps de rencontrer votre créateur !', NULL, NULL, NULL, '¡Hora de conocer a tu creador!', NULL, NULL, 0, 0, 0, 0, 'larry SAY_ATTACK_5');
INSERT INTO `script_texts` VALUES (-1000279, 'Alright, we give up! Don\'t hurt us!', NULL, 'D\'accord, on se rend ! Ne nous faites pas de mal !', NULL, NULL, NULL, '¡Muy bien, nos damos por vencidos! ¡No nos lastimes!', NULL, NULL, 0, 0, 0, 0, 'larry SAY_GIVEUP');
INSERT INTO `script_texts` VALUES (-1000280, 'A shadowy, sinister presence has invaded the Emerald Dream. Its power is poised to spill over into our world, $N. We must oppose it! That\'s why I cannot accompany you in person.', NULL, 'Un ténébreux, sinistre présence, a envahi le Rêve d\'Émeraude. Son pouvoir est sur le point de déborder sur notre monde, $N. Nous devons nous opposer à lui ! Raison pour laquelle je ne peux vous accompagner en personne.', NULL, NULL, NULL, 'Una presencia sombría y siniestra ha invadido el Sueño Esmeralda. Su poder está a punto de extenderse a nuestro mundo, $N. ¡Debemos oponernos a ella! Por eso no puedo acompañarte en persona.', NULL, NULL, 0, 0, 0, 1, 'clintar SAY_START');
INSERT INTO `script_texts` VALUES (-1000281, 'The Emerald Dream will never be yours!', NULL, 'Le Rêve d\'Émeraude ne sera jamais vôtre !', NULL, NULL, NULL, '¡El Sueño Esmeralda nunca será tuyo!', NULL, NULL, 0, 0, 0, 0, 'clintar SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1000282, 'Begone from this place!', NULL, 'Sortez de là !', NULL, NULL, NULL, '¡Aléjate de este lugar!', NULL, NULL, 0, 0, 0, 0, 'clintar SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1000283, 'That\'s the first relic, but there are still two more. Follow me, $N.', NULL, 'C\'est la première relique, mais il y en reste encore deux. Suivez-moi, $N.', NULL, NULL, NULL, 'Esa es la primera reliquia, pero todavía quedan dos más. Sígueme, $N.', NULL, NULL, 0, 0, 0, 0, 'clintar SAY_RELIC1');
INSERT INTO `script_texts` VALUES (-1000284, 'I\'ve recovered the second relic. Take a moment to rest, and then we\'ll continue to the last reliquary.', NULL, 'J\'ai récupéré la deuxième relique. Reposez vous un moment, et ensuite nous continuerons jusqu\'au dernier reliquaire.', NULL, NULL, NULL, 'He recuperado la segunda reliquia. Tómate un momento para descansar y luego continuaremos hasta el último relicario.', NULL, NULL, 0, 0, 0, 0, 'clintar SAY_RELIC2');
INSERT INTO `script_texts` VALUES (-1000285, 'We have all three of the relics, but my energy is rapidly fading. We must make our way back to Dreamwarden Lurosa! He will let you know what to do next.', NULL, 'Nous avons les trois reliques, mais mon énergie s\'estompe rapidement. Nous devons retourner chez Garderêve Lurosa ! Il vous dira que faire maintenant.', NULL, NULL, NULL, 'Tenemos las tres reliquias, pero mi energía se está desvaneciendo rápidamente. ¡Debemos regresar a Guardia de Sueños Lurosa! Él le hará saber qué hacer a continuación.', NULL, NULL, 0, 0, 0, 0, 'clintar SAY_RELIC3');
INSERT INTO `script_texts` VALUES (-1000286, 'Lurosa, I am entrusting the Relics of Aviana to $N, who will take them to Morthis Whisperwing. I must return completely to the Emerald Dream now. Do not let $N fail!', NULL, 'Lurosa, j\'ai confié les Reliques d\'Aviana à $N, qui va les emmener à Morthis Murmedaile. Je dois retourner dans le Rêve d\'Émeraude maintenant. Ne laissez pas $N faillir !', NULL, NULL, NULL, 'Lurosa, le estoy confiando las Reliquias de Aviana a $N, quien las llevará al Ala Susurrante de Morthis. Ahora debo regresar completamente al Sueño Esmeralda. ¡No dejes que $N falle!', NULL, NULL, 0, 0, 0, 1, 'clintar SAY_END');
INSERT INTO `script_texts` VALUES (-1000298, 'Alright, alright I think I can figure out how to operate this thing...', NULL, 'Bon, d\'accord. Je pense que je peux trouver comment faire fonctionner ce truc...', NULL, NULL, NULL, 'Está bien, está bien, creo que puedo averiguar cómo operar esta cosa ...', NULL, NULL, 0, 0, 0, 0, 'wizzlecrank SAY_START');
INSERT INTO `script_texts` VALUES (-1000299, 'Arrrgh! This isn\'t right!', NULL, 'Arrrgh ! Ce n\'est pas juste !', NULL, NULL, NULL, '¡Ajjjjj! ¡Esto no está bien!', NULL, NULL, 0, 0, 0, 0, 'wizzlecrank SAY_STARTUP1');
INSERT INTO `script_texts` VALUES (-1000300, 'Okay, I think I\'ve got it, now. Follow me, $n!', NULL, 'Bon, je pense que je l\'ai eu, maintenant. Suivez-moi, $N !', NULL, NULL, NULL, 'Vale, creo que ahora sí lo he logrado. ¡Sígueme, $n!', NULL, NULL, 0, 0, 0, 1, 'wizzlecrank SAY_STARTUP2');
INSERT INTO `script_texts` VALUES (-1000301, 'There\'s the stolen shredder! Stop it or Lugwizzle will have our hides!', NULL, 'Voilà la déchiqueteuse volée ! Arrêtez la ou Trimballast nous fera la peau !', NULL, NULL, NULL, '¡Ahí hay una trituradora robada! ¡Basta ya o Lugwizzle encontrará nuestro escondite!', NULL, NULL, 0, 1, 0, 0, 'wizzlecrank SAY_MERCENARY');
INSERT INTO `script_texts` VALUES (-1000302, 'Looks like we\'re out of woods, eh? Wonder what this does...', NULL, 'On dirait que nous sommes sortis des bois, hein ? Je me demande ce que cela...', NULL, NULL, NULL, 'Parece que estamos fuera de peligro, ¿eh? Me pregunto qué hace esto ...', NULL, NULL, 0, 0, 0, 0, 'wizzlecrank SAY_PROGRESS_1');
INSERT INTO `script_texts` VALUES (-1000303, 'Come on, don\'t break down on me now!', NULL, 'Venez, ne vous effondrez pas sur moi maintenant !', NULL, NULL, NULL, '¡Vamos, no te averíes ahora conmigo!', NULL, NULL, 0, 0, 0, 0, 'wizzlecrank SAY_PROGRESS_2');
INSERT INTO `script_texts` VALUES (-1000304, 'That was a close one! Well, let\'s get going, it\'s still a ways to Ratchet!', NULL, 'C\'est pas passé loin ! Bien, allons-y, il reste encore du chemin jusqu\'à Cabestan !', NULL, NULL, NULL, '¡Eso estuvo cerca! Bueno, sigamos, ¡todavía es una forma de Ratchet!', NULL, NULL, 0, 0, 0, 0, 'wizzlecrank SAY_PROGRESS_3');
INSERT INTO `script_texts` VALUES (-1000305, 'Hmm... I don\'t think this blinking red light is a good thing...', NULL, 'Hmm... Je ne pense pas que cette lumière rouge scintillante soit une bonne chose...', NULL, NULL, NULL, 'Hmm ... No creo que esta luz roja parpadeante sea algo bueno ...', NULL, NULL, 0, 0, 0, 0, 'wizzlecrank SAY_END');
INSERT INTO `script_texts` VALUES (-1000319, 'Let the trial begin, Bloodwrath, attack!', NULL, 'Que l\'épreuve commence, Hargnesang, à l\'attaque !', NULL, NULL, NULL, '¡Que comience el juicio, Ira de Sangre, ataca!', NULL, NULL, 0, 1, 1, 0, 'kelerun SayId1');
INSERT INTO `script_texts` VALUES (-1000320, 'Champion Lightrend, make me proud!', NULL, 'Champion Brise-Lumière, rends-moi fier !', NULL, NULL, NULL, 'Campeón Desgarro Ligero, ¡hazme sentir orgulloso!', NULL, NULL, 0, 1, 1, 0, 'kelerun SayId2');
INSERT INTO `script_texts` VALUES (-1000321, 'Show this upstart how a real Blood Knight fights, Swiftblade!', NULL, 'Montrez à cet arriviste comment un véritable Chevalier-de-Sang combat , Vivelame !', NULL, NULL, NULL, '¡Muéstrele a este advenedizo cómo lucha un verdadero Caballero de sangre, Cuchilla Rápida!', NULL, NULL, 0, 1, 1, 0, 'kelerun SayId3');
INSERT INTO `script_texts` VALUES (-1000322, 'Show $n the meaning of pain, Sunstriker!', NULL, 'Montrez à $N la signification du mot \"douleur\", Heurtesoleil !', NULL, NULL, NULL, '¡Muéstrale el significado del dolor, Golpe de Sol!', NULL, NULL, 0, 1, 1, 0, 'kelerun SayId4');
INSERT INTO `script_texts` VALUES (-1000340, '%s howls in delight at the sight of his lunch!', NULL, '%s hurle de joie à la vue de son déjeuner !', NULL, NULL, NULL, '¡%s aúlla de placer al ver su almuerzo!', NULL, NULL, 0, 2, 0, 0, 'kyle EMOTE_SEE_LUNCH');
INSERT INTO `script_texts` VALUES (-1000341, '%s eats his lunch.', NULL, '%s mange son déjeuner.', NULL, NULL, NULL, '%s come su almuerzo.', NULL, NULL, 0, 2, 0, 0, 'kyle EMOTE_EAT_LUNCH');
INSERT INTO `script_texts` VALUES (-1000342, '%s thanks you with a special dance.', NULL, '%s vous remercie par une danse spéciale.', NULL, NULL, NULL, '%s te agradece con un baile especial.', NULL, NULL, 0, 2, 0, 0, 'kyle EMOTE_DANCE');
INSERT INTO `script_texts` VALUES (-1000343, 'Is the way clear? Let\'s get out while we can, $N.', NULL, 'La voie est libre ? Sortons pendant que nous le pouvons, $N.', NULL, NULL, NULL, '¿Está despejado el camino? Salgamos mientras podamos, $N.', NULL, NULL, 0, 0, 0, 0, 'kayra SAY_START');
INSERT INTO `script_texts` VALUES (-1000344, 'Looks like we won\'t get away so easy. Get ready!', NULL, 'On dirait que nous n’arriverons pas à nous échapper si facilement. Tenez-vous prêt !', NULL, NULL, NULL, 'Parece que no nos escaparemos tan fácilmente. ¡Prepararse!', NULL, NULL, 0, 0, 0, 0, 'kayra SAY_AMBUSH1');
INSERT INTO `script_texts` VALUES (-1000345, 'Let\'s keep moving. We\'re not safe here!', NULL, 'Ne nous arrêtons pas. Nous ne sommes pas en sécurité ici !', NULL, NULL, NULL, 'Sigamos moviéndonos. ¡No estamos seguros aquí!', NULL, NULL, 0, 0, 0, 0, 'kayra SAY_PROGRESS');
INSERT INTO `script_texts` VALUES (-1000346, 'Look out, $N! Enemies ahead!', NULL, 'Attention, $N ! Il y a des ennemis devant !', NULL, NULL, NULL, '¡Cuidado, $N! ¡Enemigos por delante!', NULL, NULL, 0, 0, 0, 0, 'kayra SAY_AMBUSH2');
INSERT INTO `script_texts` VALUES (-1000347, 'We\'re almost to the refuge! Let\'s go.', NULL, 'Nous sommes presque arrivés au refuge ! Allons-y !', NULL, NULL, NULL, '¡Ya casi llegamos al refugio! Vamos.', NULL, NULL, 0, 0, 0, 0, 'kayra SAY_END');
INSERT INTO `script_texts` VALUES (-1000348, 'Ah...the wondrous sound of kodos. I love the way they make the ground shake... inspect the beast for me.', NULL, 'Ah... le son merveilleux des kodos. J\'aime leur façon de remuer la terre... contrôlez cette bête pour moi.', NULL, NULL, NULL, 'Ah ... el maravilloso sonido de los kodos. Me encanta la forma en que hacen temblar el suelo ... inspecciona la bestia por mí.', NULL, NULL, 0, 0, 0, 0, 'kodo round SAY_SMEED_HOME_1');
INSERT INTO `script_texts` VALUES (-1000349, 'Hey, look out with that kodo! You had better inspect that beast before i give you credit!', NULL, 'Hey, faites attention avec ce kodo ! Vous devriez mieux contrôler cette bête avant que je ne vous fasse crédit !', NULL, NULL, NULL, '¡Eh, cuidado con ese kodo! ¡Te convendría examinar a esa bestia antes de que te dé crédito!', NULL, NULL, 0, 0, 0, 0, 'kodo round SAY_SMEED_HOME_2');
INSERT INTO `script_texts` VALUES (-1000350, 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', NULL, 'Il est certain que ce kodo est une merveille. Attendez une minute, où sont mes lunettes à double foyer ? Peut-être devriez vous contrôler la bête pour moi.', NULL, NULL, NULL, 'Ese kodo seguro que es una belleza. Espera un minuto, ¿dónde están mis bifocales? Quizás deberías inspeccionar la bestia por mí.', NULL, NULL, 0, 0, 0, 0, 'kodo round SAY_SMEED_HOME_3');
INSERT INTO `script_texts` VALUES (-1000351, 'You, there! Hand over that moonstone and nobody gets hurt!', NULL, 'Vous, là ! Reposez cette Pierre de Lune et personne ne sera blessé !', NULL, NULL, NULL, '¡Tú allí! ¡Entregue esa piedra lunar y nadie saldrá herido!', NULL, NULL, 0, 1, 0, 0, 'sprysprocket SAY_START');
INSERT INTO `script_texts` VALUES (-1000352, '%s takes the Southfury moonstone and escapes into the river. Follow her!', NULL, '%s s\'empare de la Pierre de lune de la Furie-du-sud et s\'enfuit en direction de la rivière. Suivez-là !', NULL, NULL, NULL, '%s toma la piedra lunar Furia del Sur y escapa al río. ¡Síguela!', NULL, NULL, 0, 3, 0, 0, 'sprysprocket EMOTE_START');
INSERT INTO `script_texts` VALUES (-1000353, 'Just chill!', NULL, 'Reste relax !', NULL, NULL, NULL, '¡Solo relájate!', NULL, NULL, 0, 4, 0, 0, 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `script_texts` VALUES (-1000354, 'Stupid grenade picked a fine time to backfire! So much for high quality goblin engineering!', NULL, 'Cette stupide grenade a bien choisi son moment pour m\'ennuyer ! C\'en est fini de l\'ingénierie gobeline de haute qualité !', NULL, NULL, NULL, '¡La granada estúpida eligió un buen momento para ser contraproducente! ¡Hasta aquí la ingeniería goblin de alta calidad!', NULL, NULL, 0, 1, 0, 0, 'sprysprocket SAY_GRENADE_FAIL');
INSERT INTO `script_texts` VALUES (-1000355, 'All right, you win! I surrender! Just don\'t hurt me!', NULL, 'Ça va, vous avez gagné ! Je me rends ! Ne me frappez plus !', NULL, NULL, NULL, '¡Está bien, tú ganas! ¡Me rindo! ¡No me hagas daño!', NULL, NULL, 0, 1, 0, 0, 'sprysprocket SAY_END');
INSERT INTO `script_texts` VALUES (-1000360, 'The strands of LIFE have been severed! The Dreamers must be avenged!', NULL, 'Les liens de VIE ont été rompus ! Les Rêveurs doivent être vengés !', NULL, NULL, NULL, '¡Los hilos de la VIDA se han roto! ¡Tenemos que vengar a los Soñadores!', NULL, NULL, 0, 6, 0, 0, ' ysondre SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000361, 'Come forth, ye Dreamers – and claim your vengeance!', NULL, 'Venez, vous les Rêveurs.', NULL, NULL, NULL, '¡Adelante, soñadores, y reclamen su venganza!', NULL, NULL, 0, 6, 0, 0, ' ysondre SAY_SUMMONDRUIDS');
INSERT INTO `script_texts` VALUES (-1000362, 'Let\'s go $N. I am ready to reach Whitereach Post.', NULL, 'Allons-y $N. Je suis prêt à rejoindre Blanc-relais.', NULL, NULL, NULL, 'Vamos, $N. Estoy listo para llegar a Puesto de Alcance Blanco..', NULL, NULL, 0, 0, 1, 0, 'paoka SAY_START');
INSERT INTO `script_texts` VALUES (-1000363, 'Now this looks familiar. If we keep heading east, I think we can... Ahh, Wyvern on the attack!', NULL, 'Tout ça me paraît familier maintenant. Si nous continuons en direction de l\'est, je pense que nous pouvons...  Ahh, des Wyvernes nous attaquent !', NULL, NULL, NULL, 'Ahora esto parece familiar. Si seguimos hacia el este, creo que podemos ... ¡Ahh, Wyvern al ataque!', NULL, NULL, 0, 0, 1, 0, 'paoka SAY_WYVERN');
INSERT INTO `script_texts` VALUES (-1000364, 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', NULL, 'Merci beaucoup... Je peux retrouver le chemin du retour vers Blanc-relais à partir d\'ici. Assurez-vous de parler avec Motega Crin-de-feu. Peut-être pourrez-vous le persuader de me renvoyer à la maison.', NULL, NULL, NULL, 'Mil gracias... Puedo encontrar el camino de vuelta al Campamento del Tramo Blanco. Asegúrate de hablar con Motega Pirocabello; quizás puedas evitar que me envíe a casa.', NULL, NULL, 0, 0, 1, 0, 'paoka SAY_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000365, 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', NULL, NULL, NULL, NULL, NULL, 'En guardia... Arnak tiene un poder extraño sobre los Tótem Siniestro... no se alegrarán de verme huir.', NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_START');
INSERT INTO `script_texts` VALUES (-1000366, 'Look out, the Grimtotem are upon us!', NULL, NULL, NULL, NULL, NULL, '¡Cuidado, los Tótem Siniestro se nos echan encima!', NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_LOOK_OUT');
INSERT INTO `script_texts` VALUES (-1000367, 'Here they come.', NULL, NULL, NULL, NULL, NULL, 'Aquí vienen.', NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_HERE_COME');
INSERT INTO `script_texts` VALUES (-1000368, 'More Grimtotems are coming this way!', NULL, NULL, NULL, NULL, NULL, '¡Se acercan más Tótem Siniestro por este camino!', NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_MORE');
INSERT INTO `script_texts` VALUES (-1000369, 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', NULL, NULL, NULL, NULL, NULL, 'Al fin libre... debo irme, gracias por ayudarme a escapar. Puedo volver al Poblado Viento Libre por mí misma.', NULL, NULL, 0, 0, 1, 0, 'lakota SAY_LAKO_END');
INSERT INTO `script_texts` VALUES (-1209000, 'Oh no! Here they come!', NULL, 'Oh non! Ils sont la !', NULL, NULL, NULL, '¡Oh, no! ¡Aquí vienen!', NULL, NULL, 0, 0, 0, 0, 'Weegli Blastfuse SAY_WEEGLI_OHNO');
INSERT INTO `script_texts` VALUES (-1209001, 'Ok, here I go!', '자, 그럼 시작해볼까?', 'Voilà !', 'Gut, ich bin so weit!', '好了，我这就去！', '好了，我這就去!', '¡Vale, allá voy!', '¡Vale, allá voy!', 'Ага, я иду!', 0, 0, 0, 0, 'Weegli Blastfuse SAY_WEEGLI_OK_I_GO');
INSERT INTO `script_texts` VALUES (-1209002, 'What? How dare you say that to me?!?', NULL, 'Quoi? Comment osez-vous me dire cela ?!?', NULL, NULL, NULL, '¿Qué? ¿¡Cómo te atreves a decirme eso !?', NULL, NULL, 0, 0, 0, 0, 'Sergeant Bly SAY_1');
INSERT INTO `script_texts` VALUES (-1209003, 'After all we\'ve been through? Well, I didn\'t like you anyway!!', NULL, 'Après tout ce que nous avons traversé? Eh bien, je ne vous aimais pas non plus de toute façon!!', NULL, NULL, NULL, '¿Después de todo lo que hemos pasado? Bueno, ¡¡no me gustaste de todos modos !!', NULL, NULL, 0, 0, 0, 0, 'Sergeant Bly SAY_2');
INSERT INTO `script_texts` VALUES (-1000370, 'Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go!', NULL, 'Ne vous éloignez pas, $N. J\'aurai besoin de toute l\'aide possible pour sortir d\'ici. Allons-y !', NULL, NULL, NULL, 'No te alejes, $n. Voy a necesitar toda la ayuda posible para conseguir salir de aquí. ¡En marcha!', NULL, NULL, 0, 0, 1, 1, 'gilthares SAY_GIL_START');
INSERT INTO `script_texts` VALUES (-1000371, 'At last! Free from Northwatch Hold! I need a moment to catch my breath...', NULL, 'Enfin ! Me voici sorti du Fort du Nord ! Un moment, j\'ai besoin de reprendre un peu mon souffle.', NULL, NULL, NULL, '¡Al fin! ¡Libre del Bastión de la Guardia del Norte! Necesito un momento para recuperar el aliento ...', NULL, NULL, 0, 0, 1, 5, 'gilthares SAY_GIL_AT_LAST');
INSERT INTO `script_texts` VALUES (-1000372, 'Now I feel better. Let\'s get back to Ratchet. Come\r on, $n.', NULL, 'Maintenant je me sens mieux. Retournons à Cabestan. Venez, $N.', NULL, NULL, NULL, 'Ahora me siento mejor. Volvamos a Ratchet. Vamos, $n.', NULL, NULL, 0, 0, 1, 23, 'gilthares SAY_GIL_PROCEED');
INSERT INTO `script_texts` VALUES (-1000373, 'Looks like the Southsea Freebooters are heavily\r entrenched on the coast. This could get rough.', NULL, 'On dirait que les Flibustiers des mers du Sud sont fortement ancrés sur la côte. Ça pourrait s\'agiter.', NULL, NULL, NULL, 'Parece que los filibusteros de los mares del Sur están muy atrincherados en la costa. Esto podría complicarse.', NULL, NULL, 0, 0, 1, 25, 'gilthares SAY_GIL_FREEBOOTERS');
INSERT INTO `script_texts` VALUES (-1000374, 'Help! $C attacking!', NULL, 'À l\'aide ! Des $c attaquent !', NULL, NULL, NULL, '¡Ayudar! $C atacando!', NULL, NULL, 0, 0, 1, 0, 'gilthares SAY_GIL_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1000375, '$C heading this way fast! Time for revenge!', NULL, 'Des $c arrivent par ici rapidement ! C\'est l\'heure de la vengeance !', NULL, NULL, NULL, '¡$C se dirige hacia aquí rápido! ¡Tiempo de venganza!', NULL, NULL, 0, 0, 1, 0, 'gilthares SAY_GIL_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1000376, '$C coming right at us!', NULL, 'Des $c viennent droit sur nous !', NULL, NULL, NULL, '¡$C viene directo hacia nosotros!', NULL, NULL, 0, 0, 1, 0, 'gilthares SAY_GIL_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1000377, 'Get this $C off of me!', NULL, 'Éloignez ces $c de moi !', NULL, NULL, NULL, '¡Quítame estos $C!', NULL, NULL, 0, 0, 1, 0, 'gilthares SAY_GIL_AGGRO_4');
INSERT INTO `script_texts` VALUES (-1000378, 'Almost back to Ratchet! Let\'s keep up the pace...', NULL, 'Nous sommes presque à Cabestan ! Gardons le rythme...', NULL, NULL, NULL, '¡Queda poco para estar de vuelta en Trinquete! Mantengamos el paso…', NULL, NULL, 0, 0, 1, 0, 'gilthares SAY_GIL_ALMOST');
INSERT INTO `script_texts` VALUES (-1000379, 'Ah, the sweet salt air of Ratchet.', NULL, 'Ah, l\'air doux et salé de Cabestan.', NULL, NULL, NULL, 'Ah, me encanta el aire salobre de Trinquete.', NULL, NULL, 0, 0, 1, 0, 'gilthares SAY_GIL_SWEET');
INSERT INTO `script_texts` VALUES (-1000380, 'Captain Brightsun, $N here has freed me! $N, I am\r certain the Captain will reward your bravery.', NULL, 'Capitaine Brillesoleil, $N ici même m\'a libéré ! $N, je suis certain que le Capitaine va récompenser votre bravoure.', NULL, NULL, NULL, 'Capitán Brillosolar, ¡$N aquí me ha liberado! $N, estoy seguro de que el Capitán recompensará su valentía.', NULL, NULL, 0, 0, 1, 66, 'gilthares SAY_GIL_FREED');
INSERT INTO `script_texts` VALUES (-1000381, 'I sense the tortured spirits, $n. They are this way, come quickly!', NULL, 'Je sens les esprits torturés, $N. Ils sont par ici, venez vite !', NULL, NULL, NULL, 'Siento los espíritus torturados, $n. ¡Son así, ven pronto!', NULL, NULL, 0, 0, 0, 1, 'wilda SAY_WIL_START');
INSERT INTO `script_texts` VALUES (-1000382, 'Watch out!', NULL, 'Faîtes attention !', NULL, NULL, NULL, '¡Cuidado!', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_AGGRO1');
INSERT INTO `script_texts` VALUES (-1000383, 'Naga attackers! Defend yourself!', NULL, 'Des Nagas nous attaquent ! Défendez-vous !', NULL, NULL, NULL, '¡Atacantes naga! ¡Defiéndete!', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_AGGRO2');
INSERT INTO `script_texts` VALUES (-1000384, 'Grant me protection $n, i must break trough their foul magic!', NULL, 'Accordez-moi votre protection $N, je dois briser leur magie répugnante !', NULL, NULL, NULL, '¡Concédeme protección $n debo romper su magia repugnante!', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `script_texts` VALUES (-1000385, 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', NULL, 'Les Nagas de Glissentaille sont exceptionnellement cruels envers leurs prisonniers. C\'est un miracle si j\'ai survécu aussi longtemps à l\'intérieur de cette prison aquatique. Que notre Mère la Terre soit louée.', NULL, NULL, NULL, 'Los naga de Coilskar son excepcionalmente crueles con sus prisioneros. Es un milagro que haya sobrevivido dentro de esa prisión acuosa tanto tiempo como lo hice. Alabado sea Madre Tierra.', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `script_texts` VALUES (-1000386, 'Now we must find the exit.', NULL, 'Maintenant nous devons trouver la sortie.', NULL, NULL, NULL, 'Ahora debemos encontrar la salida.', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `script_texts` VALUES (-1000387, 'Lady Vashj must answer for these atrocities. She must be brought to justice!', NULL, 'Lady Vashj doît répondre de ces atrocités. Elle doît être trainée devant la justice !', NULL, NULL, NULL, 'Lady Vashj debe responder por estas atrocidades. ¡Debe ser llevada ante la justicia!', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `script_texts` VALUES (-1000388, 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', NULL, 'Le caractère tumultueux des grands cours d\'eau d\'Azeroth et de Draenor est la conséquence directe des actions des esprits aquatiques tourmentés.', NULL, NULL, NULL, 'La naturaleza tumultuosa de las grandes vías fluviales de Azeroth y Draenor es el resultado directo de los espíritus del agua atormentados.', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `script_texts` VALUES (-1000389, 'It shouldn\'t be much further, $n. The exit is just up ahead.', NULL, 'Il ne faut pas aller beaucoup plus loin, $N. La sortie est juste ici.', NULL, NULL, NULL, 'No debería estar mucho más lejos, $n. La salida está más adelante.', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `script_texts` VALUES (-1000390, 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', NULL, 'Merci, $N. S\'il vous plaît, retournez chez mes frères, à l\'autel de Damnation, près de la main de Gul\'dan, et dites-leur que Wilda est en lieu sûr. Que notre Mère la Terre veille sur vous...', NULL, NULL, NULL, 'Gracias, $n. Por favor, regresa con mis hermanos en el Altar de la Condena, cerca de la Mano de Gul\'dan, y diles que Wilda está a salvo. Que la Madre Tierra te cuide ...', NULL, NULL, 0, 0, 0, 0, 'wilda SAY_WIL_END');
INSERT INTO `script_texts` VALUES (-1000391, 'I\'m Thirsty.', NULL, 'J\'ai soif.', NULL, NULL, NULL, 'Tengo sed.', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TOOG_THIRST');
INSERT INTO `script_texts` VALUES (-1000392, 'Torta must be so worried.', NULL, 'Torta doit être si inquiète.', NULL, NULL, NULL, 'Thorta tiene que estar muy preocupada.', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TOOG_WORRIED');
INSERT INTO `script_texts` VALUES (-1000393, 'Torta, my love! I have returned at long last.', NULL, 'Torta, mon amour ! Je suis enfin revenu.', NULL, NULL, NULL, 'Thorta, ¡amor mío! Por fin he vuelto.', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TOOG_POST_1');
INSERT INTO `script_texts` VALUES (-1000394, 'You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it?', NULL, 'As-tu la moindre idée du temps que j\'ai attendu ici ? Et où est le dîner ? Tout ce temps loin d\'ici et c’est tout ce que tu as à me dire ?', NULL, NULL, NULL, '¿Te haces una idea del tiempo que llevo esperando aquí? ¿Y dónde está la cena? ¿Tanto tiempo fuera y no me tienes nada preparado?', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TORT_POST_2');
INSERT INTO `script_texts` VALUES (-1000395, 'My dearest Torta. I have been gone for so long. Finally we are reunited. At long last our love can blossom again.', NULL, 'Ma chère Torta, je suis parti si longtemps. Nous voilà enfin réunis. Notre amour peut enfin s\'épanouir à nouveau.', NULL, NULL, NULL, 'Mi queridísima Torta. Me he ido por tanto tiempo. Finalmente nos reunimos. Por fin nuestro amor puede florecer de nuevo.', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TOOG_POST_3');
INSERT INTO `script_texts` VALUES (-1000396, 'Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.', NULL, 'Assez de bavardages. Je meurs de faim ! Maintenant plonge ta carcasse dans cet océan et rapporte de quoi manger.', NULL, NULL, NULL, '¡Bueno, ya basta! ¡Estoy hambrienta! Mete ese polvoriento caparazón en el mar y trae a mamá algo rico.', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TORT_POST_4');
INSERT INTO `script_texts` VALUES (-1000397, 'Yes Torta. Whatever your heart desires...', NULL, 'Oui, Torta. Tout ce que tu voudras, mon cœur...', NULL, NULL, NULL, 'Sí Torta. Lo que sea que tu corazón desee ...', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TOOG_POST_5');
INSERT INTO `script_texts` VALUES (-1000398, 'And try not to get lost this time...', NULL, 'Essaie de ne pas te perdre, cette fois....', NULL, NULL, NULL, 'Y trata de no perderte esta vez ...', NULL, NULL, 0, 0, 0, 0, 'tooga SAY_TORT_POST_6');
INSERT INTO `script_texts` VALUES (-1000399, 'Peace is but a fleeting dream! Let the NIGHTMARE reign!', NULL, 'La paix est un rêve éphémère ! Que le CAUCHEMARD règne !', NULL, NULL, NULL, '¡La paz no es más que un sueño fugaz! ¡Que reine la PESADILLA!', NULL, NULL, 0, 6, 0, 0, 'taerar SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000400, 'Children of Madness - I release you upon this world!', NULL, 'Enfant de la Folie - Je vous libère de ce monde !', NULL, NULL, NULL, 'Hijos de la Locura, ¡yo os libero en este mundo!', NULL, NULL, 0, 6, 0, 0, 'taerar SAY_SUMMONSHADE');
INSERT INTO `script_texts` VALUES (-1000401, 'Hope is a DISEASE of the soul! This land shall wither and die!', NULL, 'L\'espoir est une MALADIE de l\'âme ! Ces terres vont flétrir et mourir !', NULL, NULL, NULL, '¡La esperanza es una ENFERMEDAD del alma! ¡Esta tierra se marchitará y morirá!', NULL, NULL, 0, 6, 0, 0, 'emeriss SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000402, 'Taste your world\'s corruption!', NULL, 'Goûtez le monde de la corruption !', NULL, NULL, NULL, '¡Probad la corrupción de vuestro mundo!', NULL, NULL, 0, 6, 0, 0, 'emeriss SAY_CASTCORRUPTION');
INSERT INTO `script_texts` VALUES (-1000411, 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak to you back in the tower.', NULL, 'Pourquoi ne traitons-nous pas avec vous maintenant, Hendel ? Dame Portvaillant va vouloir vous parler de votre retour dans la tour.', NULL, NULL, NULL, 'Hendel, ¿por qué no hacemos un trato contigo? Lady Valiente volverá a hablar contigo en la torre.', NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `script_texts` VALUES (-1000412, 'Please... please... Miss Proudmore. I didn\'t mean to...', NULL, 'S\'il vous plaît... s\'il vous plaît... Mademoiselle Portvaillant. Je ne voulais pas...', NULL, NULL, NULL, 'Por favor... por favor... Señorita Valiente. No tenía la intención de...', NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `script_texts` VALUES (-1000413, 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', NULL, 'Je suis désolé d\'avoir été si long. Je désirais que Dame Portvaillant soit également présente.', NULL, NULL, NULL, 'Pido disculpas por tardar tanto en llegar hasta aquí. Quería que Lady Valiente también estuviera presente.', NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `script_texts` VALUES (-1000414, 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', NULL, 'Nous devons retourner à la tour. Si vous souhaitez nous parler d\'avantage, vous devez nous retrouver là-bas.', NULL, NULL, NULL, 'Solo podemos quedarnos poco tiempo antes de regresar a la torre. Si queréis hablar más con nosotros, podréis encontrarnos aquí.', NULL, NULL, 0, 0, 7, 0, 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `script_texts` VALUES (-1000415, '%s, too injured, gives up the chase.', NULL, 'Ayant subi trop de blessures, %s renonce à la poursuite.', NULL, NULL, NULL, ', deja la persecución, ha recibido demasiado daño.', NULL, NULL, 0, 2, 0, 0, 'hendel EMOTE_SURRENDER');
INSERT INTO `script_texts` VALUES (-1000416, 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', NULL, 'Ma foi, je ne sais pas combien de temps je tiendrai dans cet état... Je me sens bien faible...', NULL, NULL, NULL, 'No sé hasta dónde llegaré en este estado... me siento muy débil...', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_START_1');
INSERT INTO `script_texts` VALUES (-1000417, 'Remember, if I faint again, the water that Spraggle gave you will revive me.', NULL, 'Souvenez-vous, si je m\'évanouis encore, l\'eau que Spraggle vous a donnée me rendra des forces.', NULL, NULL, NULL, 'Recuerda, si pierdo el conocimiento otra vez, el agua que te dio Spraggle me hará recuperar el sentido.', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_START_2');
INSERT INTO `script_texts` VALUES (-1000418, 'The heat... I can\'t take it...', NULL, 'La chaleur... Je ne la supporte pas...', NULL, NULL, NULL, 'El calor... no puedo soportarlo...', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_1');
INSERT INTO `script_texts` VALUES (-1000419, 'Maybe... you could carry me?', NULL, 'Peut-être... pourriez-vous me porter ?', NULL, NULL, NULL, 'Quizás... puedas llevarme.', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_2');
INSERT INTO `script_texts` VALUES (-1000420, 'Uuuuuuggggghhhhh....', NULL, 'Uuuuuuggggghhhhh....', NULL, NULL, NULL, 'Uuuuuuggggghhhhh...', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_3');
INSERT INTO `script_texts` VALUES (-1000421, 'I\'m not feeling so well...', NULL, 'Je ne me sens pas bien...', NULL, NULL, NULL, 'No me siento demasiado bien...', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_FAINT_4');
INSERT INTO `script_texts` VALUES (-1000422, 'Where... Where am I?', NULL, 'Où... Où suis-je ?', NULL, NULL, NULL, '¿Dónde... dónde estoy?', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_1');
INSERT INTO `script_texts` VALUES (-1000423, 'I am feeling a little better now, thank you.', NULL, 'Je me sens un peu mieux maintenant, merci.', NULL, NULL, NULL, 'Ya me siento un poco mejor, gracias.', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_2');
INSERT INTO `script_texts` VALUES (-1000424, 'Yes, I must go on.', NULL, 'Oui, je dois continuer.', NULL, NULL, NULL, 'Sí, debo continuar.', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_3');
INSERT INTO `script_texts` VALUES (-1000425, 'How am I feeling? Quite soaked, thank you.', NULL, 'Comment je me sens ? Tout trempé, merci.', NULL, NULL, NULL, '¿Cómo me siento? Empapado, gracias.', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_WAKE_4');
INSERT INTO `script_texts` VALUES (-1000426, 'Spraggle! I didn\'t think I\'d make it back!', NULL, 'Spraggle ! Je ne pensais pas que je m\'en sortirais !', NULL, NULL, NULL, '¡Spraggle! ¡Nunca creí que volvería!', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_END_1');
INSERT INTO `script_texts` VALUES (-1000427, 'Ringo! You\'re okay!', NULL, 'Ringo ! Tu vas bien !', NULL, NULL, NULL, '¡Ringo! ¿Estás bien?', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_SPR_END_2');
INSERT INTO `script_texts` VALUES (-1000428, 'Oh... I\'m feeling faint...', NULL, 'Oh... Je me sens si faible...', NULL, NULL, NULL, 'Oh... me siento débil...', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_END_3');
INSERT INTO `script_texts` VALUES (-1000429, '%s collapses onto the ground.', NULL, '%s s\'évanouit et tombe au sol.', NULL, NULL, NULL, '%s se derrumba en el suelo.', NULL, NULL, 0, 2, 0, 0, 'ringo EMOTE_RIN_END_4');
INSERT INTO `script_texts` VALUES (-1000430, '%s stands up after a short pause.', NULL, '%s se relève après une courte pause.', NULL, NULL, NULL, '%s se pone de pie después de una breve pausa.', NULL, NULL, 0, 2, 0, 0, 'ringo EMOTE_RIN_END_5');
INSERT INTO `script_texts` VALUES (-1000431, 'Ugh.', NULL, 'Ugh.', NULL, NULL, NULL, '¡Uf!', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_RIN_END_6');
INSERT INTO `script_texts` VALUES (-1000432, 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', NULL, 'Ringo ? Réveille-toi ! Ne te fais pas de souci, je vais m\'occuper de toi.', NULL, NULL, NULL, '¿Ringo? ¡Despierta! No te preocupes, me ocuparé de ti.', NULL, NULL, 0, 0, 0, 0, 'ringo SAY_SPR_END_7');
INSERT INTO `script_texts` VALUES (-1000433, '%s fades away after a long pause.', NULL, '%s s\'affaiblit après une longue pause.', NULL, NULL, NULL, '%s se desvanece después de una larga pausa.', NULL, NULL, 0, 2, 0, 0, 'ringo EMOTE_RIN_END_8');
INSERT INTO `script_texts` VALUES (-1000446, 'A-Me good. Good, A-Me. Follow... follow A-Me. Home. A-Me go home.', NULL, 'A-mi gentil. Gentil, A-mi. Suivez... suivez A-mi. Maison. A-mi retourne maison.', NULL, NULL, NULL, 'Memo bueno. Bueno, Memo. Seguir... seguir a Memo. Casa. Memo ir a casa.', NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_START');
INSERT INTO `script_texts` VALUES (-1000447, 'Good... good, A-Me. A-Me good. Home. Find home.', NULL, 'Gentil... gentil, A-mi. A-mi gentil. Maison. Retrouver maison.', NULL, NULL, NULL, 'Bueno... bueno, Memo. Memo bueno. Casa. Encontrar casa.', NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_PROGRESS');
INSERT INTO `script_texts` VALUES (-1000448, 'A-Me home! A-Me good! Good A-Me. Home. Home. Home.', NULL, 'A-mi maison ! A-mi gentil ! Gentil A-mi. Maison. Maison. Maison.', NULL, NULL, NULL, '¡A-Yo en casa! ¡A-yo bien! Bueno A-Me. Hogar. Hogar. Hogar.', NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_END');
INSERT INTO `script_texts` VALUES (-1000449, '$c, no hurt A-Me. A-Me good.', NULL, '$C, pas frapper A-mi. A-mi gentil.', NULL, NULL, NULL, '$c, no lastime A-Me. A-Yo bien.', NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_AGGRO1');
INSERT INTO `script_texts` VALUES (-1000450, 'Danger. Danger! $c try hurt A-Me.', NULL, 'Danger. Danger ! $C essai de frapper A-mi.', NULL, NULL, NULL, 'Peligro. ¡Peligro! $c trato de lastimar a A-Me.', NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_AGGRO2');
INSERT INTO `script_texts` VALUES (-1000451, 'Bad, $c. $c, bad!', NULL, 'Méchant, $c. $C, méchant !', NULL, NULL, NULL, 'Malo, $c. $c, ¡malo!', NULL, NULL, 0, 0, 0, 0, 'ame01 SAY_AME_AGGRO3');
INSERT INTO `script_texts` VALUES (-1000472, '1...', NULL, '1...', NULL, NULL, NULL, '1...', NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_1');
INSERT INTO `script_texts` VALUES (-1000473, '2...', NULL, '2...', NULL, NULL, NULL, '2...', NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_2');
INSERT INTO `script_texts` VALUES (-1000474, '3...', NULL, '3...', NULL, NULL, NULL, '3...', NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_3');
INSERT INTO `script_texts` VALUES (-1000475, '4...', NULL, '4...', NULL, NULL, NULL, '4...', NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_4');
INSERT INTO `script_texts` VALUES (-1000476, '5...', NULL, '5...', NULL, NULL, NULL, '5...', NULL, NULL, 0, 3, 0, 0, 'mana bomb SAY_COUNT_5');
INSERT INTO `script_texts` VALUES (-1000477, 'Let us leave this place. I\'ve had enough of these madmen!', NULL, 'Partons d\'ici. J\'en ai assez de ces fous !', NULL, NULL, NULL, 'Dejemos este lugar. ¡Ya tuve suficiente de estos locos!', NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_START');
INSERT INTO `script_texts` VALUES (-1000478, 'You\'ll go nowhere, fool!', NULL, 'Vous n\'irez nul part, abruti !', NULL, NULL, NULL, '¡No irás a ninguna parte, tonto!', NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_AMBUSH_A');
INSERT INTO `script_texts` VALUES (-1000479, 'Beware! More cultists come!', NULL, 'Attention ! D\'autres cultistes arrivent !', NULL, NULL, NULL, '¡Tener cuidado! ¡Vienen más cultistas!', NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_AMBUSH_B');
INSERT INTO `script_texts` VALUES (-1000480, 'You will not escape us so easily!', NULL, 'Tu ne t\'échapperas pas si facilement !', NULL, NULL, NULL, '¡No podrás escapar de nosotros tan fácilmente!', NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_AMBUSH_B_REPLY');
INSERT INTO `script_texts` VALUES (-1000481, 'I can find my way from here. My friend Mekeda will reward you for your kind actions.', NULL, 'Je trouverai mon chemin à partir d\'ici. Mekeda vous récompensera pour cette bonne action.', NULL, NULL, NULL, 'Puedo encontrar mi camino desde aquí. Mi amiga Mekeda te recompensará por tus amables acciones.', NULL, NULL, 0, 0, 0, 0, 'akuno SAY_AKU_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000482, 'Look out!', NULL, 'Faites attention !', NULL, NULL, NULL, '¡Cuidado!', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_START');
INSERT INTO `script_texts` VALUES (-1000483, 'Don\'t let them escape! Kill the strong one first!', NULL, 'Ne les laissez pas s\'échapper ! Tuez le plus fort en premier !', NULL, NULL, NULL, '¡No los dejes escapar! ¡Mata primero al fuerte!', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_NO_ESCAPE');
INSERT INTO `script_texts` VALUES (-1000484, 'More of them coming! Watch out!', NULL, 'Plusieurs d\'entre eux arrivent ! Faites attention !', NULL, NULL, NULL, '¡Vienen más! ¡Cuidado!', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_MORE');
INSERT INTO `script_texts` VALUES (-1000485, 'Where do you think you\'re going? Kill them all!', NULL, 'Où est-ce que vous pensez aller ? Tuez-les tous !', NULL, NULL, NULL, '¿A dónde crees que vas? ¡Matarlos a todos!', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_MORE_REPLY');
INSERT INTO `script_texts` VALUES (-1000486, 'Ride the lightning, filth!', NULL, 'Que la foudre s\'abatte sur toi, ordure !', NULL, NULL, NULL, '¡Monta el rayo, inmundicia!', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_LIGHTNING');
INSERT INTO `script_texts` VALUES (-1000487, 'FROST SHOCK!!!', NULL, 'HORION DE GIVRE !!!', NULL, NULL, NULL, 'CHOQUE DE HELADA !!!', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_SHOCK');
INSERT INTO `script_texts` VALUES (-1000488, 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell stranger.', NULL, 'Il vaut mieux nous séparer maintenant, au cas où ils en envoient encore plus à nos trousses. Espérons qu\'au moins un de nous puisse revenir vers Garrosh. Adieu étranger.', NULL, NULL, NULL, 'Es mejor que nos separemos ahora, en caso de que envíen más a por nosotros. Con suerte, alguno de nosotros regresará a Garrosh. Adiós forastero.', NULL, NULL, 0, 0, 0, 0, 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000489, 'Show our guest around Shattrath, will you? Keep an eye out for pickpockets in the lower city.', NULL, 'Faites visiter Shattrath à notre invité, voulez-vous ? Faites attention aux voleurs à la tire dans la Ville basse.', NULL, NULL, NULL, 'Muéstrale a nuestro invitado Shattrath, ¿quieres? Esté atento a los carteristas en la ciudad baja.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_START');
INSERT INTO `script_texts` VALUES (-1000490, 'A newcomer to Shattrath! Make sure to drop by later. We can always use a hand with the injured.', NULL, 'Un nouveau venu à Shattrath ! Revenez-nous voir plus tard. Nous tendons toujours une main amicale aux blessés.', NULL, NULL, NULL, '¡Un recién llegado a Shattrath! Asegúrate de pasar más tarde. Siempre podemos usar una mano con los heridos.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_INJURED');
INSERT INTO `script_texts` VALUES (-1000491, 'Be mindful of what you say, this one\'s being shown around by Khadgar\'s pet.', NULL, 'Surveille tes paroles, c\'est le familier de Khadgar qui lui fait visiter la ville.', NULL, NULL, NULL, 'Tenga en cuenta lo que dice, este lo muestra la mascota de Khadgar.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_MIND_YOU');
INSERT INTO `script_texts` VALUES (-1000492, 'Are you joking? I\'m a Scryer, I always watch what i say. More enemies than allies in this city, it seems.', NULL, 'Est-ce que vous plaisantez ? Je suis un Clairvoyant, je fais toujours attention à ce que je dis. On dirait qu\'il y a plus d\'ennemis que d\'alliés dans cette ville.', NULL, NULL, NULL, 'Estas bromeando Soy un Arúspide, siempre miro lo que digo. Parece que hay más enemigos que aliados en esta ciudad.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_MIND_ALWAYS');
INSERT INTO `script_texts` VALUES (-1000493, 'Light be with you, $n. And welcome to Shattrath.', NULL, 'Que la Lumière soit avec vous, $N. Et bienvenue à Shattrath.', NULL, NULL, NULL, 'La luz esté contigo, $n. Y bienvenido a Shattrath.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_ALDOR_GREET');
INSERT INTO `script_texts` VALUES (-1000494, 'We\'re rather selective of who we befriend, $n. You think you have what it takes?', NULL, 'Nous sommes plutôt sélectif avec ceux qui veulent être nos amis, $N. Vous croyez avoir ce qu\'il faut ?', NULL, NULL, NULL, 'Somos bastante selectivos en cuanto a con quién nos hacemos amigos, $n. ¿Crees que tienes lo que se necesita?', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_SCRYER_GREET');
INSERT INTO `script_texts` VALUES (-1000495, 'Khadgar himself is showing you around? You must have made a good impression, $n.', NULL, 'C’est Khadgar lui-même qui vous fait la visite ? Vous devez lui avoir fait une très bonne impression, $N.', NULL, NULL, NULL, '¿El mismo Khadgar te está mostrando los alrededores? Debes haber causado una buena impresión, $n.', NULL, NULL, 0, 0, 0, 0, 'SAY_KHAD_HAGGARD');
INSERT INTO `script_texts` VALUES (-1000496, '%s lifts its head into the air, as if listening for something.', NULL, '%s lève la tête en l\'air, comme s\'il écoutait quelque chose.', NULL, NULL, NULL, '%s levanta la cabeza en el aire, como si estuviera escuchando algo.', NULL, NULL, 0, 2, 0, 0, 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT INTO `script_texts` VALUES (-1000497, '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', NULL, '%s pousse un hurlement qui retentit à travers les montagnes du nord et vous fait signe de le suivre.', NULL, NULL, NULL, '%s deja escapar un aullido que resuena a través de las montañas hacia el norte y le indica que lo siga.', NULL, NULL, 0, 2, 0, 0, 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT INTO `script_texts` VALUES (-1000498, 'Welcome, kind spirit. What has brought you to us?', NULL, 'Bienvenue, esprit bienfaisant. Qu\'est ce qui vous amène ici ?', NULL, NULL, NULL, 'Bienvenido, espíritu amable. ¿Qué te ha traído hasta nosotros?', NULL, NULL, 0, 0, 0, 0, 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `script_texts` VALUES (-1000510, 'Please return to Zoram\'gar and report our success to the Warsong runner.', NULL, 'Je vous en prie, retournez à Zoram\'gar et faites un rapport de notre réussite au messager Chanteguerre.', NULL, NULL, NULL, 'Por favor, vete a Zoram\'gar e informa sobre nuestro éxito al corredor Grito de Guerra.', NULL, NULL, 0, 0, 1, 0, 'muglash SAY_MUG_RETURN');
INSERT INTO `script_texts` VALUES (-1000511, 'Aright, listen up! Form a circle around me and move out!', NULL, 'Bon, écoutez-moi ! Faites un cercle autour de moi et allez-y !', NULL, NULL, NULL, '¡Bien, escucha! ¡Forma un círculo a mi alrededor y muévete!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_START');
INSERT INTO `script_texts` VALUES (-1000512, 'Aright, $r, just keep us safe from harm while we work. We\'ll pay you when we return.', NULL, 'Très bien, $r, il suffira de nous protéger pendant notre travail. Nous vous paierons à votre retour.', NULL, NULL, NULL, 'Bien, $r, solo mantennos a salvo de daños mientras trabajamos. Te pagaremos cuando regresemos.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_KEEP_SAFE');
INSERT INTO `script_texts` VALUES (-1000513, 'The dig site is just north of here.', NULL, 'Le site de fouilles se trouve au nord.', NULL, NULL, NULL, 'El sitio de excavación está al norte de aquí.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NORTH');
INSERT INTO `script_texts` VALUES (-1000514, 'We\'re here! Start diggin\'!', NULL, 'Nous y sommes ! Commencez les fouilles !', NULL, NULL, NULL, '¡Estaban aquí! ¡Empieza a excavar!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_ARRIVE');
INSERT INTO `script_texts` VALUES (-1000515, 'I think there\'s somethin\' buried here, beneath the sand!', NULL, 'Je crois qu\'il y a quelque chose d\'enterré ici, sous le sable !', NULL, NULL, NULL, '¡Creo que hay algo enterrado aquí, debajo de la arena!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_BURIED');
INSERT INTO `script_texts` VALUES (-1000516, 'Almost got it!', NULL, 'J\'y suis presque !', NULL, NULL, NULL, '¡Casi lo tengo!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_ALMOST');
INSERT INTO `script_texts` VALUES (-1000517, 'By brann\'s brittle bananas! What is it!? It... It looks like a drum.', NULL, 'Par les bananes banales de Brann ! Qu\'est-ce que c\'est que ça ?! On dirait… un tambour.', NULL, NULL, NULL, '¡Por los plátanos quebradizos de Brann! ¿¡Qué es!? Es ... Parece un tambor.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DRUM');
INSERT INTO `script_texts` VALUES (-1000518, 'Wow... a drum.', NULL, 'Wahou… un tambour.', NULL, NULL, NULL, 'Vaya ... un tambor.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DRUM_REPLY');
INSERT INTO `script_texts` VALUES (-1000519, 'This discovery will surely rock the foundation of modern archaeology.', NULL, 'Cette découverte ébranlera sûrement les fondations de l\'archéologie moderne.', NULL, NULL, NULL, 'Este descubrimiento seguramente sacudirá los cimientos de la arqueología moderna.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DISCOVERY');
INSERT INTO `script_texts` VALUES (-1000520, 'Yea, great. Can we leave now? This desert is giving me hives.', NULL, 'Ouais, super. On peut y aller, maintenant ? Ce désert me donne des boutons.', NULL, NULL, NULL, 'Sí, genial. ¿Podemos irnos ahora? Este desierto me está dando urticaria.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_DISCOVERY_REPLY');
INSERT INTO `script_texts` VALUES (-1000521, 'Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir!', NULL, 'Mais qu\'est-ce que tu racontes ? Tu voudrais que j\'abandonne un tambour sans avoir tapé dessus d\'abord ? Nom d\'un fils de Forgefer, jamais ! Oh que non !', NULL, NULL, NULL, '¿Te has vuelto loco? ¿Esperas que deje un tambor sin antes golpearlo? ¡No este hijo de Forjaz! ¡No señor!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE');
INSERT INTO `script_texts` VALUES (-1000522, 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', NULL, 'Ça me rappelle la fois où tu nous as fait fouiller tout Silithus à la recherche de preuves de l\'existence des gnomes des sables.', NULL, NULL, NULL, 'Esto me recuerda aquella vez en la que nos hiciste buscar en Silithus pruebas de gnomos de arena.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY1');
INSERT INTO `script_texts` VALUES (-1000523, 'Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century?', NULL, 'Ou la fois où tu nous as expliqué que tu avais trouvé un remède à la peste du 20ème siècle. C\'est quoi, d\'abord, ce truc, le 20ème siècle ?', NULL, NULL, NULL, 'O esa vez en que nos dijiste que habías descubierto la cura para la plaga del siglo XX. ¿Qué es eso? ¿siglo 20?', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY2');
INSERT INTO `script_texts` VALUES (-1000524, 'I don\'t think it can top the one time where he told us that he\'d heard that Artha\'s \"cousin\'s\" skeleton was frozen beneath a glacier in Winterspring. I\'ll never forgive you for that one, Letoll. I mean honestly... Artha\'s cousin?', NULL, 'Je crois que ça ne pourra jamais dépasser la fois où il nous a dit que le squelette du « cousin » d\'Arthas était pris dans un glacier du Berceau-de-l\'hiver. Je ne te la pardonnerai jamais, celle-là, Leglas. Non mais sérieusement… Le cousin d\'Arthas ?', NULL, NULL, NULL, 'No creo que pueda superar la única vez que nos dijo que había escuchado que el esqueleto del \"primo\" de Artha estaba congelado debajo de un glaciar en Cuna del Invierno. Nunca te perdonaré por eso, Letoll. Quiero decir, honestamente ... ¿el primo de Artha?', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY3');
INSERT INTO `script_texts` VALUES (-1000525, 'I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', NULL, 'Je sais pas. Ça ne peut quand même pas battre la fois où il a essayé de nous convaincre qu\'en fait on n\'est tous que le produit de l\'imagination de quelqu\'un, et qu\'on sert juste à son amusement. Ça a été bien reçu, en plein dîner de famille.', NULL, NULL, NULL, 'No se. No puede superar el tiempo que intentó convencernos de que todos somos en realidad un producto de la imaginación de algún ser y que solo nos usan para su propia diversión personal. Eso fue bien durante la cena con la familia.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_NO_LEAVE_REPLY4');
INSERT INTO `script_texts` VALUES (-1000526, 'Shut yer yaps! I\'m gonna bang on this drum and that\'s that!', NULL, 'Fermez-la ! Je vais taper sur ce tambour un point c\'est tout !', NULL, NULL, NULL, '¡Cállate yaps! ¡Voy a tocar este tambor y eso es todo!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_SHUT');
INSERT INTO `script_texts` VALUES (-1000527, 'Say, do you guys hear that?', NULL, 'Eh, les gars, vous entendez ce que j\'entends ?', NULL, NULL, NULL, 'Dime, ¿escucháis eso?', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_REPLY_HEAR');
INSERT INTO `script_texts` VALUES (-1000528, 'IN YOUR FACE! I told you there was somethin\' here!', NULL, 'EN PLEIN DANS LES DENTS ! Je vous avais bien dit qu\'il y avait quelque chose !', NULL, NULL, NULL, '¡EN SU CARA! ¡Te dije que había algo aquí!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_IN_YOUR_FACE');
INSERT INTO `script_texts` VALUES (-1000529, 'Don\'t just stand there! Help him out!', NULL, 'Restez pas plantés là ! Aidez-le à sortir !', NULL, NULL, NULL, '¡No te quedes ahí parado! ¡Ayúdalo!', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_HELP_HIM');
INSERT INTO `script_texts` VALUES (-1000530, '%s picks up the drum.', NULL, '%s ramasse le tambour.', NULL, NULL, NULL, '%s levanta el tambor.', NULL, NULL, 0, 2, 0, 0, 'letoll EMOTE_LE_PICK_UP');
INSERT INTO `script_texts` VALUES (-1000531, 'You\'ve been a tremendous help, $r! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably.', NULL, 'Votre aide a vraiment été très précieuse, $r ! Sortons d\'ici avant qu\'il y en ait d\'autres qui arrivent ! Vous pourrez dire à Nanowitz que vous avez admirablement rempli votre mission.', NULL, NULL, NULL, '¡Has sido de gran ayuda, $r! ¡Salgamos de aquí antes de que aparezcan más de esas cosas! Le haré saber a Dwarfowitz que hizo el trabajo que se le pidió admirablemente.', NULL, NULL, 0, 0, 0, 0, 'letoll SAY_LE_THANKS');
INSERT INTO `script_texts` VALUES (-1000532, 'At your command, my liege...', NULL, 'À vos ordres, mon seigneur…', NULL, NULL, NULL, 'A sus órdenes, mi señor ...', NULL, NULL, 0, 0, 0, 0, 'torloth TORLOTH_DIALOGUE1');
INSERT INTO `script_texts` VALUES (-1000533, 'As you desire, Lord Illidan.', NULL, 'Comme vous le souhaitez, seigneur Illidan.', NULL, NULL, NULL, 'Como desee, Lord Illidan.', NULL, NULL, 0, 0, 0, 0, 'torloth TORLOTH_DIALOGUE2');
INSERT INTO `script_texts` VALUES (-1000534, 'Yes, Lord Illidan, I would sacrifice to you this magnificent physique. On this day you will fall - another victim of Torloth...', NULL, 'Pour le seigneur Illidan, je sacrifierais tout, même mon physique avantageux. Ce jour verra votre perte. Une nouvelle victime pour Torloth…', NULL, NULL, NULL, 'Sí, Lord Illidan, te sacrificaría este magnífico físico. En este día caerás, otra víctima de Torloth ...', NULL, NULL, 0, 0, 0, 0, 'torloth TORLOTH_DIALOGUE3');
INSERT INTO `script_texts` VALUES (-1000535, 'Destroy them, Torloth. Let lose their blood like a river upon this hallowed ground.', NULL, 'Détruis-les, Torloth. Que leur sang se répande tel une rivière sur ce sol sacré.', NULL, NULL, NULL, 'Destrúyelos, Torloth. Dejemos perder su sangre como un río sobre esta tierra sagrada.', NULL, NULL, 0, 0, 0, 0, 'lordillidan ILLIDAN_DIALOGUE');
INSERT INTO `script_texts` VALUES (-1000536, 'What manner of fool dares stand before Illidan Stormrage? Soldiers, destroy these insects!', NULL, 'Quel genre d\'idiot peut donc oser s\'opposer à Illidan Hurlorage ? Soldats, détruisez ces insectes !', NULL, NULL, NULL, '¿Qué clase de tonto se atreve a enfrentarse a Illidan Tempestira? ¡Soldados, destruyan estos insectos!', NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON1');
INSERT INTO `script_texts` VALUES (-1000537, 'You are no challenge for the Crimson Sigil. Mind breakers, end this nonsense.', NULL, 'Vous n\'êtes pas de taille contre le Cachet cramoisi. Briseurs d\'esprit, finissez-en avec ces inepties !', NULL, NULL, NULL, 'No eres un desafío para Crimson Sigil. Rompe mentes, acaben con esta tontería.', NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON2');
INSERT INTO `script_texts` VALUES (-1000538, 'Run while you still can. The highlords come soon...', NULL, 'Fuyez tant qu\'il en est encore temps. Voici qu\'arrivent les généralissimes…', NULL, NULL, NULL, 'Corre mientras puedas. Los altos señores llegarán pronto ...', NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON3');
INSERT INTO `script_texts` VALUES (-1000539, 'Torloth your master calls!', NULL, 'Torloth, ton maître t\'appelle !', NULL, NULL, NULL, '¡Torloth llama tu maestro!', NULL, NULL, 0, 1, 0, 0, 'lordillidan ILLIDAN_SUMMON4');
INSERT INTO `script_texts` VALUES (-1000540, 'So you have defeated the Crimson Sigil. You now seek to challenge my rule? Not even Arthas could defeat me, yet you dare to even harbor such thoughts? Then I say to you, come! Come $N! The Black Temple awaits...', NULL, 'Vous avez donc vaincu le Cachet cramoisi. Et vous voulez maintenant défier mon autorité ? Même Arthas n\'a pas pu me terrasser, et vous osez pourtant en rêver ? Très bien, venez ! Venez, $N, le Temple noir vous attend…', NULL, NULL, NULL, 'Así que has derrotado al Crimson Sigil. ¿Buscas ahora desafiar mi regla? Ni siquiera Arthas pudo derrotarme, pero ¿te atreves a albergar esos pensamientos? ¡Entonces te digo, ven! ¡Ven $N! El Templo Oscuro espera ...', NULL, NULL, 0, 1, 0, 0, 'lordillidan EVENT_COMPLETED');
INSERT INTO `script_texts` VALUES (-1000541, '%s jumps into the moonwell and goes underwater...', NULL, '%s plonge dans le puit de lune et va sous l\'eau...', NULL, NULL, NULL, '% s salta al pozo de la luna y se sumerge ...', NULL, NULL, 0, 2, 0, 0, 'kitten EMOTE_SAB_JUMP');
INSERT INTO `script_texts` VALUES (-1000542, '%s follows $n obediently.', NULL, '%s suit $N docilement.', NULL, NULL, NULL, 'sigue a $N con obediencia.', NULL, NULL, 0, 2, 0, 0, 'kitten EMOTE_SAB_FOLLOW');
INSERT INTO `script_texts` VALUES (-1000543, 'Why have you come here, outsider? You will only find pain! Our fate will be yours...', NULL, 'Pourquoi êtes-vous $gvenu:venue;, $gétranger:étrangères; ? Il n\'y a que de la souffrance ici. Vous partagerez notre destin...', NULL, NULL, NULL, '¿Por qué has venido aquí, forastero? ¡Solo encontrarás dolor! Nuestro destino será el tuyo ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_1');
INSERT INTO `script_texts` VALUES (-1000544, 'It was ... terrible... the demon...', NULL, 'C\'était... terrible... le démon...', NULL, NULL, NULL, 'Fue ... terrible ... el demonio ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_2');
INSERT INTO `script_texts` VALUES (-1000545, 'The darkness... the corruption... they came too quickly for anyone to know...', NULL, 'Les ténèbres… la corruption… ils sont venus si vite, personne n\'a eu le temps de s\'en aperçevoir…', NULL, NULL, NULL, 'La oscuridad ... la corrupción ... llegaron demasiado rápido para que nadie lo supiera ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_3');
INSERT INTO `script_texts` VALUES (-1000546, 'The darkness will consume all... all the living...', NULL, 'Les ténèbres… vont dévorer… tous les vivants…', NULL, NULL, NULL, 'La oscuridad consumirá a todos ... a todos los vivos ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_4');
INSERT INTO `script_texts` VALUES (-1000547, 'It is too late for us, living one. Take yourself and your friend away from here before you both are... claimed...', NULL, 'Il est trop tard pour nous, être vivant. Partez avec votre ami avant d\'être… pris tous les deux.', NULL, NULL, NULL, 'Es demasiado tarde para nosotros, viviendo uno. Llévate a ti y a tu amigo de aquí antes de que ambos sean ... reclamados ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_5');
INSERT INTO `script_texts` VALUES (-1000548, 'It is too late for Jarl... its hold is too strong...', NULL, 'C\'est trop tard pour Jarl... Cette cale est trop lourde...', NULL, NULL, NULL, 'Es demasiado tarde para Jarl ... su agarre es demasiado fuerte ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_6');
INSERT INTO `script_texts` VALUES (-1000549, 'Go away, whoever you are! Witch Hill is mine... mine!', NULL, 'Allez-vous-en, qui que vous soyez ! La Colline des sorcières est à moi et rien qu’à moi !', NULL, NULL, NULL, '¡Vete, quienquiera que seas! Witch Hill es mía ... ¡mía!', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_7');
INSERT INTO `script_texts` VALUES (-1000550, 'The manor... someone else... will soon be consumed...', NULL, 'Le manoir… quelqu\'un d\'autre… sera bientôt dévoré…', NULL, NULL, NULL, 'La mansión ... alguien más ... pronto será consumida ...', NULL, NULL, 0, 0, 0, 25, 'restless app SAY_RAND_8');
INSERT INTO `script_texts` VALUES (-1033015, '%s fumbles with the rusty lock on the courtyard door.', NULL, NULL, NULL, NULL, NULL, 'manosea con torpeza el oxidado cerrojo de la puerta del patio.', NULL, NULL, 0, 2, 0, 69, 'prisoner adamant EMOTE_UNLOCK_DOOR_AD');
INSERT INTO `script_texts` VALUES (-1036000, 'You there, check out that noise!', NULL, 'Vous là ! Occupez-vous de ce bruit bizarre.', NULL, NULL, NULL, '¡Tú, mira ese ruido!', NULL, NULL, 5775, 6, 7, 0, 'smite INST_SAY_ALARM1');
INSERT INTO `script_texts` VALUES (-1036001, 'We\'re under attack! A vast, ye swabs! Repel the invaders!', NULL, 'Nous sommes attaqués ! Repoussez les envahisseurs !', NULL, NULL, NULL, '¡Estamos bajo ataque! ¡Un vasto, hisopos! ¡Repele a los invasores!', NULL, NULL, 5777, 6, 7, 0, 'smite INST_SAY_ALARM2');
INSERT INTO `script_texts` VALUES (-1614998, 'I must take the necessary preparations before the awakening ritual can begin. You must protect me!', NULL, 'Je dois faire les préparatifs nécessaires avant de pouvoir commencer le rituel de réveil. Vous devez me protéger !', NULL, NULL, NULL, 'Debo hacer los preparativos necesarios antes de que pueda comenzar el ritual del despertar. ¡Debes protegerme!', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - begining');
INSERT INTO `script_texts` VALUES (-1614997, 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', NULL, 'Ces cavernes furent jadis un temple de résurrection dans les Tarides. Aujourd’hui, elles sont les salles des cauchemars.', NULL, NULL, NULL, 'Estas cavernas fueron en su día un templo de promisión para el resurgimiento en Los Baldíos. Ahora se han convertido en los corredores de las pesadillas.', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - after moving from 1 pnt');
INSERT INTO `script_texts` VALUES (-1614996, 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', NULL, 'Venez. Nous devons continuer ! Il y a encore beaucoup à faire avant de pouvoir tirer Naralex de son cauchemar.', NULL, NULL, NULL, 'Ven. Debemos continuar. Hay mucho que hacer antes de poder sacar a Naralex de su pesadilla.', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - first mobs slain');
INSERT INTO `script_texts` VALUES (-1614995, 'Within this circle of fire I must cast the spell to banish the spirits of slain Fanglords.', NULL, 'Au sein de ce cercle de feu, je dois lancer le sort qui bannira les esprits des seigneurs-Croc tombés au champ d’honneur.', NULL, NULL, NULL, 'Dentro de este círculo de fuego debo lanzar el hechizo para desterrar los espíritus de los Señores Colmillos asesinados.', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - circle of fire encounter begining');
INSERT INTO `script_texts` VALUES (-1614994, 'The caverns have been purified. To Naralex\'s chamber we go!', NULL, 'Les cavernes ont été purifiées. Allons dans les appartements de Naralex !', NULL, NULL, NULL, 'Las Cuevas han sido purificadas. ¡Nos vamos a la Cámara de Naralex!', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - after circle of fire encounter');
INSERT INTO `script_texts` VALUES (-1614993, 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', NULL, 'Au-delà de ce couloir, Naralex dort d\'un sommeil agité. Allons le réveiller avant qu’il ne soit trop tard.', NULL, NULL, NULL, 'Tras este pasillo, Naralex yace en un sueño intermitente. Vamos a despertarle antes de que sea demasiado tarde.', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - before turning to naralex chamber');
INSERT INTO `script_texts` VALUES (-1614992, 'Protect me brave souls as I delve into the Emerald Dream to rescue Naralex and put an end to this corruption!', NULL, 'Accordez-moi votre protection, âmes courageuses, pendant que je fouille dans les profondeurs du Rêve d\'émeraude pour secourir Naralex et mettre un terme à cette corruption !', NULL, NULL, NULL, '¡Protegedme, almas valientes, mientras escarbo en el Sueño Esmeralda para rescatar a Naralex y acabar con esta corrupción!', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex - ritual of awakening begining');
INSERT INTO `script_texts` VALUES (-1614991, '%s begins to perform the awakening ritual on Naralex.', NULL, NULL, NULL, NULL, NULL, 'empieza a realizar el ritual del despertar con Naralex.', NULL, NULL, 0, 2, 0, 0, 'Disciple of Naralex - Disciple emote');
INSERT INTO `script_texts` VALUES (-1614990, '%s tosses fitfully in troubled sleep.', NULL, NULL, NULL, NULL, NULL, 'da vueltas intermitentemente en un sueño agitado.', NULL, NULL, 0, 2, 0, 0, 'Naralex -  Naralex emote1');
INSERT INTO `script_texts` VALUES (-1614989, 'Attacked! Help get this $N off of me!', NULL, 'On m\'attaque ! Aidez-moi à me débarrasser de ce maudit $N !', NULL, NULL, NULL, '¡Me atacan! ¡Ayudadme a sacarme a $n de encima!', NULL, NULL, 0, 1, 0, 0, 'Disciple of Naralex -  attacked');
INSERT INTO `script_texts` VALUES (-1614987, '%s writhes in agony. The Disciple seems to be breaking through.', NULL, NULL, NULL, NULL, NULL, 'se retuerce de dolor. El Discípulo parece penetrar en la defensa enemiga.', NULL, NULL, 0, 2, 0, 0, 'Naralex -  emote2');
INSERT INTO `script_texts` VALUES (-1614986, 'This Mutanus the Devourer is a minion from Naralex\'s nightmare no doubt!', NULL, 'Mutanus le Dévoreur est sûrement au service du cauchemar de Naralex !', NULL, NULL, NULL, '¡Este Mutanus el Devorador es sin duda un esbirro de la pesadilla de Naralex!', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex -  after Mutaous spawn');
INSERT INTO `script_texts` VALUES (-1614985, 'I AM AWAKE, AT LAST!', NULL, 'ENFIN JE SUIS ÉVEILLÉ !', NULL, NULL, NULL, '¡HE DESPERTADO AL FIN!', NULL, NULL, 5789, 1, 0, 0, 'Naralex -  awakening after killing Mutanous');
INSERT INTO `script_texts` VALUES (-1614984, 'At last! Naralex awakes from the nightmare.', NULL, 'Enfin ! Naralex émerge de son cauchemar.', NULL, NULL, NULL, '¡Al fin! Naralex despierta de la pesadilla.', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex -  after N. awakening');
INSERT INTO `script_texts` VALUES (-1614983, 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', NULL, 'Ah, être enfin arraché aux griffes du redoutable cauchemar ! Je vous remercie, mon fidèle Disciple, ainsi que vos braves compagnons.', NULL, NULL, NULL, '¡Ah, al fin liberado de una pavorosa pesadilla! Te lo agradezco, mi leal Discípulo, a ti y a tus valientes compañeros.', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex -  after N. awakening1');
INSERT INTO `script_texts` VALUES (-1614982, 'We must go and gather with the other Disciples. There is much work to be done before I can make another attempt to restore the Barrens. Farewell, brave souls!', NULL, 'Nous devons aller rejoindre les autres Disciples. Il y a beaucoup de travail à faire avant que je ne puisse à nouveau tenter de rétablir les Tarides. Adieu, braves âmes !', NULL, NULL, NULL, 'Debemos ir y reunirnos con los otros Discípulos. Hay mucho trabajo que hacer antes de poder volver a intentar restaurar Los Baldíos. ¡Adiós mis valientes!', NULL, NULL, 0, 0, 0, 0, 'Disciple of Naralex -  after N. awakening2');
INSERT INTO `script_texts` VALUES (-1109000, 'The walls of the chamber tremble. Something is happening...', NULL, NULL, NULL, NULL, NULL, 'Las paredes de la cámara se estremecen. Algo está ocurriendo...', NULL, NULL, 0, 2, 0, 0, 'malfurion stormrage EMOTE_MALFURION');
INSERT INTO `script_texts` VALUES (-1109001, 'Be steadfast, champion. I know why it is that you are here and I know what it is that you seek. Eranikus will not give up the shard freely. He has been twisted... twisted by the same force that you seek to destroy.', NULL, NULL, NULL, NULL, NULL, 'Incorpórate, por favor. Sé por qué has venido y sé lo que andas buscando. Eranikus no te dará el fragmento de buen grado. Está corrupto... corrupto por las mismas fuerzas que tú pretendes destruir.', NULL, NULL, 0, 0, 0, 0, 'malfurion stormrge SAY_MALFURION1');
INSERT INTO `script_texts` VALUES (-1109002, 'Are you really surprised? Is it hard to believe that the power of an Old God could reach even inside the Dream? It is true - Eranikus, Tyrant of the Dream, wages a battle against us all. The Nightmare follows in his wake of destruction.', NULL, NULL, NULL, NULL, NULL, '¿De verdad te sorprende? ¿Es tan difícil de creer que el poder de un dios antiguo pudiera entrar en el Sueño? Pues así es. Eranikus, Tirano del Sueño, está enfrentado con todos nosotros. La Pesadilla continúa con su estela de destrucción.', NULL, NULL, 0, 0, 0, 0, 'malfurion stormrge SAY_MALFURION2');
INSERT INTO `script_texts` VALUES (-1109003, 'Understand this, Eranikus wants nothing more than to be brought to Azeroth from the Dream. Once he is out, he will stop at nothing to destroy my physical manifestation. This, however, is the only way in which you could recover the scepter shard.', NULL, NULL, NULL, NULL, NULL, 'Ten presente que lo que Eranikus desea es salir del Sueño para entrar en Azeroth. Cuando lo consiga, no se detendrá ante nada e intentará destruir mi manifestación física. Sin embargo, es el único modo que existe para que puedas recuperar el fragmento de cetro.', NULL, NULL, 0, 0, 0, 0, 'malfurion stormrge SAY_MAFLURION3');
INSERT INTO `script_texts` VALUES (-1109004, 'You will bring him back into this world, champion.', NULL, NULL, NULL, NULL, NULL, 'Lo traerás de vuelta a este mundo, valiente.', NULL, NULL, 0, 0, 0, 0, 'malfurion Stormrge SAY_MALFURION4');
INSERT INTO `script_texts` VALUES (-1189000, 'Ah, I have been waiting for a real challenge!', NULL, 'Ah, j\'espérais un véritable défi !', NULL, NULL, NULL, '¡Ah, estaba esperando un verdadero desafío!', NULL, NULL, 5830, 1, 0, 0, 'herod SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1189001, 'Blades of Light!', NULL, 'Lames de lumière !', NULL, NULL, NULL, '¡Espadas de luz!', NULL, NULL, 5832, 1, 0, 0, 'herod SAY_WHIRLWIND');
INSERT INTO `script_texts` VALUES (-1189002, 'Light, give me strength!', NULL, 'Lumière, donne-moi la force !', NULL, NULL, NULL, '¡Luz, dame fuerza!', NULL, NULL, 5833, 1, 0, 0, 'herod SAY_ENRAGE');
INSERT INTO `script_texts` VALUES (-1189003, 'Hah, is that all?', NULL, 'Ah ! C\'est tout ?', NULL, NULL, NULL, '¿Eso es todo?', NULL, NULL, 5831, 1, 0, 0, 'herod SAY_KILL');
INSERT INTO `script_texts` VALUES (-1189004, '%s becomes enraged!', NULL, '%s est pris de frénésie  !', NULL, NULL, NULL, 'se enfurece.', NULL, NULL, 0, 2, 0, 0, 'herod EMOTE_ENRAGE');
INSERT INTO `script_texts` VALUES (-1189005, 'Infidels! They must be purified!', NULL, 'Des infidèles ! Ils doivent être purifiés !', NULL, NULL, NULL, '¡Infieles! ¡Hay que purificarlos!', NULL, NULL, 5835, 1, 0, 0, 'mograine SAY_MO_AGGRO');
INSERT INTO `script_texts` VALUES (-1189006, 'Unworthy!', NULL, 'Indignes limaces.', NULL, NULL, NULL, 'Indigno', NULL, NULL, 5836, 1, 0, 0, 'mograine SAY_MO_KILL');
INSERT INTO `script_texts` VALUES (-1189007, 'At your side, milady!', NULL, 'À vos côtés, madame !', NULL, NULL, NULL, '¡A su lado, señora!', NULL, NULL, 5837, 1, 0, 0, 'mograine SAY_MO_RESSURECTED');
INSERT INTO `script_texts` VALUES (-1189008, 'What, Mograine has fallen? You shall pay for this treachery!', NULL, 'Mograine est tombé ? Vous paierez pour cette traîtrise !', NULL, NULL, NULL, '¿Qué, Mograine se ha caído? ¡Pagarás por esta traición!', NULL, NULL, 5838, 1, 0, 0, 'whitemane SAY_WH_INTRO');
INSERT INTO `script_texts` VALUES (-1189009, 'The Light has spoken!', NULL, 'La Lumière a parlé !', NULL, NULL, NULL, '¡La Luz ha hablado!', NULL, NULL, 5839, 1, 0, 0, 'whitemane SAY_WH_KILL');
INSERT INTO `script_texts` VALUES (-1189010, 'Arise, my champion!', NULL, 'À moi, mon champion !', NULL, NULL, NULL, '¡Álzate, campeón!', NULL, NULL, 5840, 1, 0, 0, 'whitemane SAY_WH_RESSURECT');
INSERT INTO `script_texts` VALUES (-1189011, 'Tell me... tell me everything!', NULL, 'Dites-moi... Dites-moi tout !', NULL, NULL, NULL, '¡Dímelo... dímelo todo!', NULL, NULL, 5847, 1, 0, 0, 'vishas SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1189012, 'Naughty secrets!', NULL, 'Vilains secrets...', NULL, NULL, NULL, '¡Secretos sucios!', NULL, NULL, 5849, 1, 0, 0, 'vishas SAY_HEALTH1');
INSERT INTO `script_texts` VALUES (-1189013, 'I\'ll rip the secrets from your flesh!', NULL, 'J\'arracherai les secrets de votre chair !', NULL, NULL, NULL, '¡Arrancaré los secretos de tus propias carnes!', NULL, NULL, 5850, 1, 0, 0, 'vishas SAY_HEALTH2');
INSERT INTO `script_texts` VALUES (-1189014, 'Purged by pain!', NULL, 'La douleur vous purifiera !', NULL, NULL, NULL, '¡Que el dolor te purgue!', NULL, NULL, 5848, 1, 0, 0, 'vishas SAY_KILL');
INSERT INTO `script_texts` VALUES (-1189015, 'The monster got what he deserved.', NULL, 'Le monstre a obtenu ce qu\'il méritait.', NULL, NULL, NULL, 'El monstruo obtuvo lo que se merecía.', NULL, NULL, 0, 0, 1, 0, 'vishas SAY_TRIGGER_VORREL');
INSERT INTO `script_texts` VALUES (-1189016, 'We hunger for vengeance.', NULL, 'Nous avons soif de vengeance !', NULL, NULL, NULL, 'Tenemos sed de venganza.', NULL, NULL, 5844, 1, 0, 0, 'thalnos SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1189017, 'No rest, for the angry dead.', NULL, 'Pas de repos... pour les morts en colère !', NULL, NULL, NULL, 'Sin descanso, para los muertos enojados.', NULL, NULL, 5846, 1, 0, 0, 'thalnos SAY_HEALTH');
INSERT INTO `script_texts` VALUES (-1189018, 'More... More souls.', NULL, 'Encore... encore des âmes.', NULL, NULL, NULL, 'Más ... Más almas.', NULL, NULL, 5845, 1, 0, 0, 'thalnos SAY_KILL');
INSERT INTO `script_texts` VALUES (-1189019, 'You will not defile these mysteries!', NULL, 'Vous ne profanerez pas ces mystères !', NULL, NULL, NULL, '¡No profanarás estos misterios!', NULL, NULL, 5842, 1, 0, 0, 'doan SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1189020, 'Burn in righteous fire!', NULL, 'Brûlez dans le feu purificateur !', NULL, NULL, NULL, '¡Arde en la hoguera de los justos!', NULL, NULL, 5843, 1, 0, 0, 'doan SAY_SPECIALAE');
INSERT INTO `script_texts` VALUES (-1189021, 'Release the hounds!', NULL, 'Lâchez les chiens !', NULL, NULL, NULL, '¡Suelta a los perros!', NULL, NULL, 5841, 1, 0, 0, 'loksey SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1230000, 'Ah, hits the spot!', NULL, 'Ah, frappe la cible !', NULL, NULL, NULL, '¡Ah, ha dado en el blanco!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_GOT_BEER');
INSERT INTO `script_texts` VALUES (-1230001, 'Come to aid the Throne!', NULL, 'Venez aider le Trône !', NULL, NULL, NULL, '¡Ven a ayudar al Trono!', NULL, NULL, 0, 1, 0, 0, 'dagran SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1230002, 'Hail to the king, baby!', NULL, 'Honneur au roi !', NULL, NULL, NULL, '¡Viva el rey, nena!', NULL, NULL, 0, 1, 0, 0, 'dagran SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1230003, 'You have challenged the Seven, and now you will die!', NULL, 'Vous avez défié les Sept, et maintenant vous allez mourir !', NULL, NULL, NULL, '¡Has desafiado a los Siete y ahora morirás!', NULL, NULL, 0, 0, 0, 0, 'doomrel SAY_DOOMREL_START_EVENT');
INSERT INTO `script_texts` VALUES (-1309000, 'Let the coils of hate unfurl!', NULL, 'Que se déroulent les anneaux de la haine !', NULL, NULL, NULL, '¡Que se despliegue la potencia del odio!', NULL, NULL, 8421, 1, 0, 0, 'venoxis SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1309001, 'Ssserenity..at lassst!', NULL, 'La ssssérénité. Enfffffin !', NULL, NULL, NULL, 'La serenidad ... al último!', NULL, NULL, 0, 1, 0, 0, 'venoxis SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309002, 'Lord Hir\'eek, grant me wings of vengance!', NULL, 'Seigneur Hir’eek, accorde-moi les ailes de la vengeance !', NULL, NULL, NULL, '¡Lord Hir\'eek, concédeme alas de venganza!', NULL, NULL, 8417, 1, 0, 0, 'jeklik SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309003, 'I command you to rain fire down upon these invaders!', NULL, 'Qu\'une pluie de feu s\'abatte sur les envahisseurs !', NULL, NULL, NULL, '¡Te ordeno que cubras de fuego a los invasores!', NULL, NULL, 0, 1, 0, 0, 'jeklik SAY_RAIN_FIRE');
INSERT INTO `script_texts` VALUES (-1309004, 'Finally ...death. Curse you Hakkar! Curse you!', NULL, 'Enfin... la mort ! Sois maudit, Hakkar ! Sois maudit !', NULL, NULL, NULL, 'Finalmente ... muerte. ¡Maldito seas Hakkar! ¡Te maldigo!', NULL, NULL, 8422, 1, 0, 0, 'jeklik SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309005, 'Draw me to your web mistress Shadra. Unleash your venom!', NULL, 'Entraine moi dans ta toile dame Shadra ! Déchaine ton venin !', NULL, NULL, NULL, 'Llévame a tu amante de la telaraña Shadra. ¡Desata tu veneno!', NULL, NULL, 8418, 1, 0, 0, 'marli SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309006, 'Shadra, make of me your avatar!', NULL, 'Shadra, fais de moi ton avatar !', NULL, NULL, NULL, '¡Shadra, conviérteme en tu avatar!', NULL, NULL, 0, 1, 0, 0, 'marli SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1309007, 'Aid me my brood!', NULL, 'Aidez-moi, mes enfants !', NULL, NULL, NULL, '¡Ayudadme, hijos míos!', NULL, NULL, 0, 1, 0, 0, 'marli SAY_SPIDER_SPAWN');
INSERT INTO `script_texts` VALUES (-1309008, 'Bless you mortal for this release. Hakkar controls me no longer...', NULL, 'Soyez bénis, mortels, pour m\'avoir libérée… Hakkar ne me contrôle plus !', NULL, NULL, NULL, 'Bendito seas mortal por esta liberación. Hakkar ya no me controla ...', NULL, NULL, 8423, 1, 0, 0, 'marli SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309009, 'Shirvallah, fill me with your RAGE!', NULL, 'Shirvallah, que ta RAGE m’envahisse !', NULL, NULL, NULL, 'Shirvallah, ¡lléname de tu IRA!', NULL, NULL, 8419, 1, 0, 0, 'thekal SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309010, 'Hakkar binds me no more! Peace at last!', NULL, 'Hakkar ne me domine plus ! Je connais enfin la paix !', NULL, NULL, NULL, '¡Hakkar ya no me ata! ¡Paz al fin!', NULL, NULL, 8424, 1, 0, 0, 'thekal SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309011, 'Bethekk, your priestess calls upon your might!', NULL, 'Bethekk, ta prêtresse en appelle à ta puissance !', NULL, NULL, NULL, 'Bethekk, ¡tu sacerdotisa invoca tu poder!', NULL, NULL, 8416, 1, 0, 0, 'arlokk SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309012, 'Feast on $n, my pretties!', NULL, 'Dévorez $N, mes jolies !', NULL, NULL, NULL, '¡Daos un festín con $n, mis pequeños!', NULL, NULL, 0, 1, 0, 0, 'arlokk SAY_FEAST_PANTHER');
INSERT INTO `script_texts` VALUES (-1309013, 'At last, I am free of the Soulflayer!', NULL, 'Enfin ! Je suis enfin libérée de l\'Écorcheur d\'âmes !', NULL, NULL, NULL, '¡Por fin soy libre del Cazador de Almas!', NULL, NULL, 8412, 1, 0, 0, 'arlokk SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1309015, 'I\'ll feed your souls to Hakkar himself!', NULL, 'Je jetterai vos âmes en pâture à Hakkar lui-même !', NULL, NULL, NULL, '¡Serviré en bandeja vuestras almas al mismísimo Hakkar!', NULL, NULL, 8413, 1, 0, 0, 'mandokir SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309016, 'DING!', NULL, 'DING !', NULL, NULL, NULL, '¡DING!', NULL, NULL, 0, 1, 0, 0, 'mandokir SAY_DING_KILL');
INSERT INTO `script_texts` VALUES (-1309017, 'GRATS!', NULL, 'GRATS!', NULL, NULL, NULL, '¡Enhorabuena!', NULL, NULL, 0, 6, 0, 0, 'mandokir SAY_GRATS_JINDO');
INSERT INTO `script_texts` VALUES (-1309018, '$N! I\'m watching you!', NULL, '$N, je vous ai à l\'œil !', NULL, NULL, NULL, '¡$n! ¡Te estoy vigilando!', NULL, NULL, 0, 1, 0, 0, 'mandokir SAY_WATCH');
INSERT INTO `script_texts` VALUES (-1309019, 'Don\'t make me angry. You won\'t like it when I\'m angry.', NULL, 'Ne me mettez pas en colère. Vous n\'allez pas aimer quand je suis colère.', NULL, NULL, NULL, 'No me hagas enojar. No te gustará cuando estoy enojado.', NULL, NULL, 0, 4, 0, 0, 'mandokir SAY_WATCH_WHISPER');
INSERT INTO `script_texts` VALUES (-1309020, 'PRIDE HERALDS THE END OF YOUR WORLD. COME, MORTALS! FACE THE WRATH OF THE SOULFLAYER!', NULL, 'L’ORGUEIL ANNONCE LA FIN DE VOTRE MONDE. AVANCEZ, MORTELS ! AFFRONTEZ LA COLÈRE DE L’ÉCORCHEUR D’ÂMES !', NULL, NULL, NULL, 'HERALDAS DEL ORGULLO EL FIN DE TU MUNDO. ¡VEN MORTALES! ¡ENFRENTE A LA IRA DEL DESOLLADOR DE ALMAS!', NULL, NULL, 8414, 1, 0, 0, 'hakkar SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1309021, 'Fleeing will do you no good, mortals!', NULL, 'Fuir ne vous servira à rien, mortels !', NULL, NULL, NULL, '¡Huir no os solucionará nada, mortales!', NULL, NULL, 0, 1, 0, 0, 'hakkar SAY_FLEEING');
INSERT INTO `script_texts` VALUES (-1309022, 'You dare set foot upon Hakkari holy ground? Minions of Hakkar, destroy the infidels!', NULL, 'Vous osez poser le pied sur le sol sacré des Hakkari ? Serviteurs d’Hakkar, détruisez les infidèles !', NULL, NULL, NULL, '¿Te atreves a poner un pie en la tierra sagrada de Hakkari? ¡Esbirros de Hakkar, destruyan a los infieles!', NULL, NULL, 0, 1, 0, 0, 'hakkar SAY_MINION_DESTROY');
INSERT INTO `script_texts` VALUES (-1309023, 'Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all!', NULL, 'Serviteurs d’Hakkar, écoutez votre dieu. Son temple a été profané. Des envahisseurs ont pénétré sur son sol sacré ! Protégez l’Autel de sang ! Tuez-les tous !', NULL, NULL, NULL, 'Esbirros de Hakkar, escuchad a vuestro dios. La santidad de este templo se ha visto comprometida. ¡Hay invasores en la tierra santa! ¡Debemos proteger el Altar de la Sangre! ¡Matadlos a todos!', NULL, NULL, 0, 1, 0, 0, 'hakkar SAY_PROTECT_ALTAR');
INSERT INTO `script_texts` VALUES (-1309024, '%s goes into a rage after seeing his raptor fall in battle!', NULL, '%s devient fou furieux en voyant son raptor mourir durant le combat !', NULL, NULL, NULL, 'monta en cólera tras ver a su raptor caer en combate.', NULL, NULL, 0, 2, 0, 0, 'mandokir EMOTE_RAGE');
INSERT INTO `script_texts` VALUES (-1329000, 'Thanks to Egan', NULL, 'Remerciez Egan.', NULL, NULL, NULL, 'Gracias a Egan', NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED0');
INSERT INTO `script_texts` VALUES (-1329001, 'Rivendare must die', NULL, 'Vaillefendre doit mourir.', NULL, NULL, NULL, 'Rivendare debe morir', NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED1');
INSERT INTO `script_texts` VALUES (-1329002, 'Who you gonna call?', NULL, 'Qui allez vous appeler ?', NULL, NULL, NULL, '¿A quien vas a llamar?', NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED2');
INSERT INTO `script_texts` VALUES (-1329003, 'Don\'t cross those beams!', NULL, 'Ne croisez pas les faisceaux !', NULL, NULL, NULL, '¡No cruce esos rayos!', NULL, NULL, 0, 0, 0, 0, 'freed_soul SAY_ZAPPED3');
INSERT INTO `script_texts` VALUES (-1409000, '%s performs one last service for Ragnaros.', NULL, '%s accomplit un dernier service pour Ragnaros...', NULL, NULL, NULL, '%s realiza un último servicio para Ragnaros.', NULL, NULL, 0, 2, 0, 0, 'geddon EMOTE_SERVICE');
INSERT INTO `script_texts` VALUES (-1409001, '%s forces one of his Firesworn Minions to erupt!', NULL, NULL, NULL, NULL, NULL, 'obliga a uno de sus esbirros de fuego a estallar.', NULL, NULL, 0, 2, 0, 0, 'garr EMOTE_MASSIVE_ERUPTION');
INSERT INTO `script_texts` VALUES (-1409002, '%s refuses to die while its master is in trouble.', NULL, '%s refuse de mourir tant que son maître est en danger !', NULL, NULL, NULL, '%s se niega a morir mientras su maestro está en problemas.', NULL, NULL, 0, 2, 0, 0, 'core rager EMOTE_LOWHP');
INSERT INTO `script_texts` VALUES (-1409003, 'Reckless mortals, none may challenge the sons of the living flame!', NULL, 'Imprudents mortels ! Nul ne défie les Fils de la Flamme vivante !', NULL, NULL, NULL, '¡Mortales imprudentes, nadie puede desafiar a los hijos de la llama viviente!', NULL, NULL, 8035, 1, 0, 0, 'majordomo SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1409004, 'The runes of warding have been destroyed! Hunt down the infidels, my brethren!', NULL, 'Les runes de sauvegarde ont été détruites ! Traquez les infidèles, mes frères !', NULL, NULL, NULL, '¡Las runas de protección han sido destruidas! ¡Dad caza a los infieles, hermanos míos!', NULL, NULL, 8039, 6, 0, 0, 'majordomo SAY_SPAWN');
INSERT INTO `script_texts` VALUES (-1409005, 'Ashes to Ashes!', NULL, 'Des cendres aux cendres.', NULL, NULL, NULL, '¡Cenizas a las cenizas!', NULL, NULL, 8037, 1, 0, 0, 'majordomo SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1409006, 'Burn mortals! Burn for this transgression!', NULL, 'Brûlez mortels ! Brûlez pour ce sacrilège !', NULL, NULL, NULL, '¡Quema a los mortales! ¡Arde por esta transgresión!', NULL, NULL, 8036, 1, 0, 0, 'majordomo SAY_SPECIAL');
INSERT INTO `script_texts` VALUES (-1409007, 'Impossible! Stay your attack, mortals... I submit! I submit! Brashly, you have come to wrest the secrets of the Living Flame! You will soon regret the recklessness of your quest. I go now to summon the lord whose house this is. Should you seek an audience with him, your paltry lives will surely be forfeit! Nevertheless, seek out his lair, if you dare!', NULL, 'Impossible ! Arrêtez votre attaque, mortels... Je me rends ! Je me rends ! Vous avez eu l\'audace de venir affronter les secrets de la Flamme vivante ! Vous regretterez bien vite cette quête ! Je vais invoquer le seigneur dont c\'est la demeure. Si vous cherchez à obtenir une audience auprès de lui, vous y perdrez sans doute votre misérable existence ! Mais entrez donc dans son repaire, si vous l\'osez !', NULL, NULL, NULL, '¡Imposible! Detengan su ataque, mortales ... ¡Me someto! ¡me someto! ¡Con descaro, has venido a arrancar los secretos de la Llama Viviente! Pronto te arrepentirás de la imprudencia de tu búsqueda. Voy ahora a llamar al señor cuya casa es esta. Si busca una audiencia con él, ¡seguramente perderá su miserable vida! Sin embargo, busque su guarida, si se atreve.', NULL, NULL, 8038, 1, 0, 0, 'majordomo SAY_DEFEAT');
INSERT INTO `script_texts` VALUES (-1409008, 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', NULL, 'Voyez Ragnaros, le seigneur du Feu ! Il était ancien lorsque ce monde était jeune. Inclinez-vous devant lui, mortels ! Inclinez-vous avant votre fin !', NULL, NULL, NULL, '¡Mira a Ragnaros, el Señor del Fuego! ¡El que era anciano cuando este mundo era joven! ¡Inclinaos ante él, mortales! ¡Inclínate ante tu final!', NULL, NULL, 8040, 1, 0, 0, 'ragnaros SAY_SUMMON_MAJ');
INSERT INTO `script_texts` VALUES (-1409009, 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION???', NULL, 'TROP TÔT ! VOUS M\'AVEZ RÉVEILLÉ TROP TÔT, EXECUTUS ! QUE SIGNIFIE CETTE INTRUSION ???', NULL, NULL, NULL, '¡DEMASIADO PRONTO! ¡ME HAS DESPERTADO DEMASIADO PRONTO, EXECUTUS! ¿¿¿QUÉ SIGNIFICA ESTA INTROMISIÓN???', NULL, NULL, 8043, 1, 0, 0, 'ragnaros SAY_ARRIVAL1_RAG');
INSERT INTO `script_texts` VALUES (-1409010, 'These mortal infidels, my lord! They have invaded your sanctum and seek to steal your secrets!', NULL, 'Ces mortels infidèles, monseigneur ! Ils ont envahi votre sanctuaire et cherchent à voler vos secrets !', NULL, NULL, NULL, '¡Estos infieles mortales, señor! ¡Han invadido vuestro santuario y pretenden robar vuestros secretos!', NULL, NULL, 8041, 1, 0, 0, 'ragnaros SAY_ARRIVAL2_MAJ');
INSERT INTO `script_texts` VALUES (-1409011, 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE, AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', NULL, 'IMBÉCILE ! VOUS AVEZ PERMIS À CES INSECTES DE RAMPER JUSQU\'AU SAINT DES SAINTS ? ET MAINTENANT VOUS LES MENEZ JUSQU\'À MON REPAIRE ? VOUS M\'AVEZ TRAHI, EXECUTUS ! JUSTICE SERA FAITE, EN VÉRITÉ !', NULL, NULL, NULL, '¡TONTO! ¿HAS PERMITIDO QUE ESTOS INSECTOS CORRERAS RAMPANTES A TRAVÉS DEL NÚCLEO SAGRADO, Y AHORA LOS LLEVAS A MI MUY GUARDA? ¡ME HAS FALLADO, EJECUTADLO! ¡DE VERDAD SE CUMPLIRÁ LA JUSTICIA!', NULL, NULL, 8044, 1, 0, 0, 'ragnaros SAY_ARRIVAL3_RAG');
INSERT INTO `script_texts` VALUES (-1409012, 'NOW FOR YOU, INSECTS! BOLDLY, YOU SOUGHT THE POWER OF RAGNAROS. NOW YOU SHALL SEE IT FIRSTHAND!', NULL, 'ET MAINTENANT À VOUS, INSECTES ! VOUS CHERCHIEZ EFFRONTÉMENT LE POUVOIR DE RAGNAROS. VOUS ALLEZ LE SENTIR DE PRÈS !', NULL, NULL, NULL, '¡AHORA PARA USTED, INSECTOS! Audazmente, buscaste el poder de ragnaros. ¡AHORA LO VERÁS DE PRIMERA MANO!', NULL, NULL, 8045, 1, 0, 0, 'ragnaros SAY_ARRIVAL5_RAG');
INSERT INTO `script_texts` VALUES (-1409013, 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', NULL, 'VENEZ ! Ô MES SERVITEURS ! VENEZ DÉFENDRE VOTRE MAÎTRE !', NULL, NULL, NULL, '¡SIERVOS, A MÍ! ¡DEFENDED A VUESTRO MAESTRO!', NULL, NULL, 8049, 1, 0, 0, 'ragnaros SAY_REINFORCEMENTS1');
INSERT INTO `script_texts` VALUES (-1409014, 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH YOU CREATURES OF HATE! YOUR MASTER CALLS!', NULL, 'VOUS NE POUVEZ PAS DÉFAIRE LA FLAMME VIVANTE. VENEZ SUPPÔTS DU FEU ! VENEZ CRÉATURES DE LA HAINE ! VOTRE MAÎTRE VOUS APPELLE !', NULL, NULL, NULL, '¡NO PUEDES VENCER LA LLAMA VIVA! ¡VEN, SÚBDITOS DE FUEGO! ¡VEN POR USTEDES CRIATURAS DEL ODIO! ¡SUS LLAMADAS MAESTRAS!', NULL, NULL, 8050, 1, 0, 0, 'ragnaros SAY_REINFORCEMENTS2');
INSERT INTO `script_texts` VALUES (-1409015, 'BY FIRE BE PURGED!', NULL, 'PAR LE FEU, SOYEZ PURIFIÉS !', NULL, NULL, NULL, '¡EL FUEGO TE PURGARÁ!', NULL, NULL, 8046, 1, 0, 0, 'ragnaros SAY_HAND');
INSERT INTO `script_texts` VALUES (-1409016, 'TASTE THE FLAMES OF SULFURON!', NULL, 'GOÛTEZ AUX FLAMMES DE SULFURON !', NULL, NULL, NULL, '¡PRUEBA LAS LLAMAS DE SULFURON!', NULL, NULL, 8047, 1, 0, 0, 'ragnaros SAY_WRATH');
INSERT INTO `script_texts` VALUES (-1409017, 'DIE INSECT!', NULL, 'MEURS INSECTE !', NULL, NULL, NULL, '¡MUERE INSECTO!', NULL, NULL, 8051, 1, 0, 0, 'ragnaros SAY_KILL');
INSERT INTO `script_texts` VALUES (-1409018, 'MY PATIENCE IS DWINDLING! COME GNATS TO YOUR DEATH!', NULL, 'MA PATIENCE S\'ÉPUISE... VENEZ VOUS FAIRE TUER MOUCHERONS !', NULL, NULL, NULL, '¡MI PACIENCIA SE ESTÁ DISMINUYENDO! ¡VEN GNATS A TU MUERTE!', NULL, NULL, 8048, 1, 0, 0, 'ragnaros SAY_MAGMABURST');
INSERT INTO `script_texts` VALUES (-1469000, 'None of your kind should be here! You\'ve doomed only yourselves!', NULL, 'Aucun membre de votre espèce ne devrait être ici ! Vous vous êtes condamnés vous-mêmes !', NULL, NULL, NULL, '¡Ninguno de los de tu clase debería estar aquí! ¡Se han condenado sólo a ustedes mismos!', NULL, NULL, 8286, 1, 0, 0, 'broodlord SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1469001, 'Clever Mortals but I am not so easily lured away from my sanctum!', NULL, 'Bien tenté, mortels, mais il n\'est pas si facile de m\'éloigner de mon sanctum !', NULL, NULL, NULL, '¡Mortales inteligentes, pero no soy tan fácil de alejarme de mi santuario!', NULL, NULL, 8287, 1, 0, 0, 'broodlord SAY_LEASH');
INSERT INTO `script_texts` VALUES (-1469002, 'REUSE ME', NULL, '%s est pris de frénésie !', NULL, NULL, NULL, 'REUTILIZARME', NULL, NULL, 0, 0, 0, 0, 'REUSE ME');
INSERT INTO `script_texts` VALUES (-1469003, '%s flinches as its skin shimmers.', NULL, '%s grimace lorsque sa peau se met à briller.', NULL, NULL, NULL, 'se estremece mientras su piel empieza a brillar.', NULL, NULL, 0, 2, 0, 0, 'chromaggus EMOTE_SHIMMER');
INSERT INTO `script_texts` VALUES (-1469004, 'In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say...', NULL, 'Dans ce monde où le temps est un ennemi, il est mon plus grand allié. Vous pensez jouer au grand jeu de la vie mais en fait c\'est vous le jouet. Et je dis...', NULL, NULL, NULL, 'En este mundo donde el tiempo es tu enemigo, es mi mayor aliado. Este gran juego de la vida que crees jugar, en realidad juega contigo. Y yo digo...', NULL, NULL, 0, 0, 0, 0, 'victor_nefarius SAY_GAMESBEGIN_1');
INSERT INTO `script_texts` VALUES (-1469005, 'Let the games begin!', NULL, 'Que les jeux commencent !', NULL, NULL, NULL, '¡Que comiencen los juegos!', NULL, NULL, 8280, 1, 0, 0, 'victor_nefarius SAY_GAMESBEGIN_2');
INSERT INTO `script_texts` VALUES (-1469006, 'Ah...the heroes. You are persistent, aren\'t you? Your ally here attempted to match his power against mine - and paid the price. Now he shall serve me...by slaughtering you.', NULL, 'Ah, les fameux héros. Vous êtez tenaces, n\'est-ce pas. Votre allié a tenté de m\'affronter et en a payé le prix. Il est maintenant mon serviteur, et il va vous tuer. Debout mon petit dragon rouge. Détruis les !', NULL, NULL, NULL, 'Ah... Los héroes. Eres muy persistente, ¿eh? Tu aliado intentó medir su poder contra el mío... Y pagó el precio. Ahora me servirá... Matándote.', NULL, NULL, 8279, 1, 0, 0, 'victor_nefarius SAY_VAEL_INTRO');
INSERT INTO `script_texts` VALUES (-1469007, 'Well done, my minions. The mortals\' courage begins to wane! Now, let\'s see how they contend with the true Lord of Blackrock Spire!', NULL, 'Beau travail. Le courage des mortels commence a faiblir ! Voyons maintenant, s\'ils peuvent affronter le véritable seigneur du Pic de Blackrock !', NULL, NULL, NULL, 'Bien hecho, mis secuaces. ¡El coraje de los mortales comienza a menguar! ¡Ahora, veamos cómo se enfrentan al verdadero Señor de Aguja Roca Negra!', NULL, NULL, 8288, 1, 0, 0, 'nefarian SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1469008, 'Enough! Now you vermin shall feel the force of my birthright, the fury of the earth itself.', NULL, 'Assez, vermines ! Vous allez maintenant sentir la puissance de mon héritage ! La fureur de la terre elle même !', NULL, NULL, NULL, '¡Suficiente! Ahora ustedes, alimañas, sentirán la fuerza de mi derecho de nacimiento, la furia de la tierra misma.', NULL, NULL, 8289, 1, 0, 0, 'nefarian SAY_XHEALTH');
INSERT INTO `script_texts` VALUES (-1469009, 'BURN! You wretches! BURN!', NULL, 'Brûlez misérables ! Brûlez !', NULL, NULL, NULL, '¡QUEMAD! ¡Malditos! ¡QUEMAD!', NULL, NULL, 8290, 1, 0, 0, 'nefarian SAY_SHADOWFLAME');
INSERT INTO `script_texts` VALUES (-1469010, 'Impossible! Rise my minions! Serve your master once more!', NULL, 'C\'est impossible ! Relevez-vous, serviteurs ! Servez une nouvelle fois votre maître !', NULL, NULL, NULL, '¡Imposible! ¡Levántense mis secuaces! ¡Sirve a tu amo una vez más!', NULL, NULL, 8291, 1, 0, 0, 'nefarian SAY_RAISE_SKELETONS');
INSERT INTO `script_texts` VALUES (-1469011, 'Worthless $N! Your friends will join you soon enough!', NULL, 'Pitoyable créature, tes amis vont bientot te rejoindre...', NULL, NULL, NULL, '¡Sin valor $Y! ¡Tus amigos se unirán a ti muy pronto!', NULL, NULL, 8293, 1, 0, 0, 'nefarian SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1469012, 'This cannot be!  I am the master here!  You mortals are nothing to my kind!  Do you hear me? Nothing!', NULL, 'C\'est impossible ! Je suis le maître ici ! Vous, mortels, n\'êtes rien pour nous ! Vous entendez ? Rien !', NULL, NULL, NULL, '¡¡No puede ser!! ¡Yo soy el maestro aquí! ¡Mortales, no significáis nada para los míos! ¿Me oís? ¡Nada!', NULL, NULL, 8292, 1, 0, 0, 'nefarian SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1469013, 'Mages too? You should be more careful when you play with magic...', NULL, 'Les mages aussi ? Vous devriez être plus prudents lorsque vous jouez avec la magie.', NULL, NULL, NULL, '¿Magos también? Deberíais tener más cuidado cuando jugáis con la magia...', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_MAGE');
INSERT INTO `script_texts` VALUES (-1469014, 'Warriors, I know you can hit harder than that! Let\'s see it!', NULL, 'Guerriers, je sais que vous pouvez frapper plus fort que ça ! Voyons ça !', NULL, NULL, NULL, 'Guerreros, sé que pueden golpear más fuerte que eso. ¡Vamos a verlo!', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_WARRIOR');
INSERT INTO `script_texts` VALUES (-1469015, 'Druids and your silly shapeshifting. Let\'s see it in action!', NULL, 'Les druides et leur stupides changements de forme. Voyons ce qu\'ils donnent en vrai…', NULL, NULL, NULL, 'Druidas y tu tonto cambio de forma. ¡Veámoslo en acción!', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_DRUID');
INSERT INTO `script_texts` VALUES (-1469016, 'Priests! If you\'re going to keep healing like that, we might as well make it a little more interesting!', NULL, 'Prêtres ! Si vous continuez à soigner comme ça, nous pourrions rendre le processus plus intéressant !', NULL, NULL, NULL, '¡Sacerdotes! Si vais a seguir curando de esa forma, ¡podíamos hacerlo más interesante!', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_PRIEST');
INSERT INTO `script_texts` VALUES (-1469017, 'Paladins, I\'ve heard you have many lives. Show me.', NULL, 'Les paladins… J\'ai entendu dire que vous aviez de nombreuses vies… Montrez-moi.', NULL, NULL, NULL, 'Paladines, he oído que tienes muchas vidas. Muéstrame.', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_PALADIN');
INSERT INTO `script_texts` VALUES (-1469018, 'Shamans, show me what your totems can do!', NULL, 'Chamans, montrez-moi ce que vos totems peuvent faire !', NULL, NULL, NULL, '¡Chamanes, mostradme lo que pueden hacer vuestros tótems!', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_SHAMAN');
INSERT INTO `script_texts` VALUES (-1469019, 'Warlocks, you shouldn\'t be playing with magic you don\'t understand. See what happens?', NULL, 'Démonistes, vous ne devriez pas jouer avec une magie qui vous dépasse. Vous voyez ce qui arrive ?', NULL, NULL, NULL, 'Brujos... No deberíais estar jugando con magia que no comprendéis. ¿Veis lo que pasa?', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_WARLOCK');
INSERT INTO `script_texts` VALUES (-1469020, 'Hunters and your annoying pea-shooters!', NULL, 'Ah, les chasseurs et les stupides sarbacanes !', NULL, NULL, NULL, '¡Cazadores y vuestras molestas cerbatanas!', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_HUNTER');
INSERT INTO `script_texts` VALUES (-1469021, 'Rogues? Stop hiding and face me!', NULL, 'Voleurs, arrêtez de vous cacher et affrontez-moi !', NULL, NULL, NULL, '¿Pícaros? ¡Dejad de esconderos y enfrentaos a mí!', NULL, NULL, 0, 1, 0, 0, 'nefarian SAY_ROGUE');
INSERT INTO `script_texts` VALUES (-1469022, 'You\'ll pay for forcing me to do this!', NULL, 'Vous allez payer pour m\'avoir forcé à faire ça !', NULL, NULL, NULL, '¡Pagarás por obligarme a hacer esto!', NULL, NULL, 8275, 1, 0, 0, 'razorgore SAY_EGGS_BROKEN1');
INSERT INTO `script_texts` VALUES (-1469023, 'Fools! These eggs are more precious than you know!', NULL, 'Imbéciles ! Vous n\'avez pas idée de la valeur de ces œufs !', NULL, NULL, NULL, '¡Idiotas! ¡Esos huevos son más valiosos que los vuestros!', NULL, NULL, 8276, 1, 0, 0, 'razorgore SAY_EGGS_BROKEN2');
INSERT INTO `script_texts` VALUES (-1469024, 'No - not another one! I\'ll have your heads for this atrocity!', NULL, 'Non, pas un autre ! J\'aurai vos têtes pour venger cette atrocité !', NULL, NULL, NULL, '¡No, otro no! ¡Haré que os corten la cabeza por esta atrocidad!', NULL, NULL, 8277, 1, 0, 0, 'razorgore SAY_EGGS_BROKEN3');
INSERT INTO `script_texts` VALUES (-1469025, 'If I fall into the abyss I\'ll take all of you mortals with me...', NULL, 'Si je tombe dans les abysses, je vous entrainerai tous avec moi mortels...', NULL, NULL, NULL, 'Si caigo al abismo, los llevaré a todos ustedes, mortales, conmigo ...', NULL, NULL, 8278, 1, 0, 0, 'razorgore SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1469026, 'Too late, friends! Nefarius\' corruption has taken hold...I cannot...control myself.', NULL, 'Trop tard, mes amis ! La corruption de Nefarius s\'empare de moi… Je ne peux plus… me contrôler.', NULL, NULL, NULL, '¡Demasiado tarde, amigos! La corrupción de Nefarius se ha apoderado ... no puedo ... controlarme.', NULL, NULL, 8281, 1, 0, 1, 'vaelastrasz SAY_LINE1');
INSERT INTO `script_texts` VALUES (-1469027, 'I beg you, mortals - FLEE! Flee before I lose all sense of control! The black fire rages within my heart! I MUST- release it!', NULL, 'Je vous en supplie, mortels, FUYEZ ! Fuyez avant que je perde tout contrôle ! Le feu noir se déchaîne dans mon cœur ! JE DOIS le libérer !', NULL, NULL, NULL, 'Os lo suplico, mortales, ¡HUID! ¡Huid antes de que pierda el control por completo! ¡El fuego negro inunda mi corazón! ¡DEBO liberarlo!', NULL, NULL, 8282, 1, 0, 1, 'vaelastrasz SAY_LINE2');
INSERT INTO `script_texts` VALUES (-1469028, 'FLAME! DEATH! DESTRUCTION! Cower, mortals before the wrath of Lord...NO - I MUST fight this! Alexstrasza help me, I MUST fight it!', NULL, 'FLAMMES ! MORT ! DESTRUCTION ! Tremblez, mortels, devant la colère du seigneur… NON ! JE DOIS lutter ! Qu\'Alexstrasza me vienne en aide, JE DOIS me battre !', NULL, NULL, NULL, '¡LLAMAS! ¡MUERTE! ¡DESTRUCCIÓN! Mortales, temblad ante la ira del Señor... ¡NO, DEBO luchar contra esto! ¡Que Alexstrasza me ayude, DEBO luchar contra esto!', NULL, NULL, 8283, 1, 0, 15, 'vaelastrasz SAY_LINE3');
INSERT INTO `script_texts` VALUES (-1469029, 'Nefarius\' hate has made me stronger than ever before! You should have fled while you could, mortals! The fury of Blackrock courses through my veins!', NULL, 'La haine de Nefarius me rend plus fort que jamais ! Vous auriez dû fuir tant qu\'il en était encore temps, mortels ! La fureur de Rochenoire coule dans mes veines !', NULL, NULL, NULL, '¡El odio de Nefarius me ha hecho más fuerte que nunca! ¡Deberían haber huido mientras pudieron, mortales! ¡La furia de Roca Negra corre por mis venas!', NULL, NULL, 8285, 1, 0, 0, 'vaelastrasz SAY_HALFLIFE');
INSERT INTO `script_texts` VALUES (-1469030, 'Forgive me, $N! Your death only adds to my failure!', NULL, 'Pardonnez-moi, $N ! Votre mort ne fait qu\'ajouter à mon échec !', NULL, NULL, NULL, '¡Perdóname, $n! ¡Tu muerte solo aumenta mi fracaso!', NULL, NULL, 8284, 1, 0, 0, 'vaelastrasz SAY_KILLTARGET');
INSERT INTO `script_texts` VALUES (-1469031, 'Looking for this: Red Scepter Shard? Come and get it...', NULL, NULL, 'Sucht ihr das hier: Roter Szepter Splitter? Kommt und holt es...', '在找红色节杖碎片吗？过来啊，到这里来拿啊。。。', NULL, 'Buscando esto: ¿Fragmento de cetro rojo? Ven y cógelo...', NULL, NULL, 0, 1, 0, 0, 'Nefarius\' yell at start of Red Scepter Shard Run');
INSERT INTO `script_texts` VALUES (-1509000, '%s senses your fear.', NULL, '%s ressent votre peur.', NULL, NULL, NULL, 'siente tu miedo.', NULL, NULL, 0, 2, 0, 0, 'moam EMOTE_AGGRO');
INSERT INTO `script_texts` VALUES (-1509001, '%s bristles with energy!', NULL, '%s luit avec énergie !', NULL, NULL, NULL, '¡%s se eriza de energía!', NULL, NULL, 0, 2, 0, 0, 'moan EMOTE_MANA_FULL');
INSERT INTO `script_texts` VALUES (-1509002, '%s sets eyes on $N!', NULL, '%s pose son regard sur $N !', NULL, NULL, NULL, 'mira a $n!', NULL, NULL, 0, 2, 0, 0, 'buru EMOTE_TARGET');
INSERT INTO `script_texts` VALUES (-1509004, 'Remember, Rajaxx, when I said I\'d kill you last?', NULL, 'Tu te rappelles, Rajaxx, quand je disais que je te tuerai en dernier ?', NULL, NULL, NULL, 'Rajaxx, ¿recuerdas que te dije que serías el último en morir?', NULL, NULL, 0, 1, 0, 0, 'andorov SAY_ANDOROV_ATTACK');
INSERT INTO `script_texts` VALUES (-1509003, 'They come now. Try not to get yourself killed, young blood.', NULL, 'Ils arrivent. Essayez de ne pas vous faire tuer, bleusaille.', NULL, NULL, NULL, 'Ahí vienen. Intenta que no te maten, $gchico:chica;.', NULL, NULL, 0, 1, 0, 0, 'andorov SAY_ANDOROV_INTRO');
INSERT INTO `script_texts` VALUES (-1509005, 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', NULL, 'L\'heure de notre vengeance sonne enfin ! Que les ténèbres règnent dans le coeur de nos ennemis !', NULL, NULL, NULL, '¡Se acerca la hora de imponer nuestro castigo!\n ¡Que la oscuridad reine en los corazones de nuestros enemigos!', NULL, NULL, 8612, 1, 0, 0, 'rajaxx SAY_WAVE3');
INSERT INTO `script_texts` VALUES (-1509006, 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!', NULL, 'C\'en est fini d\'attendre derrière des portes fermées et des murs de pierre. Nous ne serons pas privés de notre vengeance. Les dragons eux-mêmes trembleront devant notre courroux !', NULL, NULL, NULL, '¡Ya no esperaremos detrás de puertas enrejadas y muros de piedra! ¡Ya no se negará nuestra venganza! ¡Los dragones mismos temblarán ante nuestra ira!', NULL, NULL, 8610, 1, 0, 0, 'rajaxx SAY_WAVE4');
INSERT INTO `script_texts` VALUES (-1509007, 'Fear is for the enemy! Fear and death!', NULL, 'La peur est pour l\'ennemi ! La peur et la mort !', NULL, NULL, NULL, '¡El miedo es para el enemigo! ¡El miedo y la muerte!', NULL, NULL, 8608, 1, 0, 0, 'rajaxx SAY_WAVE5');
INSERT INTO `script_texts` VALUES (-1509008, 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!', NULL, 'Forteramure pleurnichera pour avoir la vie sauve, comme la fait son morveux de fils. En ce jour mille ans d\'injustice s\'achèvent !', NULL, NULL, NULL, 'Yelmo de Ciervo gimoteará y suplicará por su vida, ¡tal como lo hizo el cachorro de su hijo! ¡Mil años de injusticia terminarán este día!', NULL, NULL, 8611, 1, 0, 0, 'rajaxx SAY_WAVE6');
INSERT INTO `script_texts` VALUES (-1509009, 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!', NULL, 'Fandral ! Ton heure est venue ! Va te cacher dans le rêve d\'Emeraude et prie pour que nous ne te trouvions jamais !', NULL, NULL, NULL, '¡Fandral! ¡Ha llegado tu hora! ¡Ve y escóndete en el Sueño Esmeralda y reza para que nunca te encontremos!', NULL, NULL, 8607, 1, 0, 0, 'rajaxx SAY_WAVE7');
INSERT INTO `script_texts` VALUES (-1509010, 'Impudent fool! I will kill you myself!', NULL, 'Imbécile imprudent ! Je vais te tuer moi-même !', NULL, NULL, NULL, '¡Idiota insolente! ¡Te mataré yo mismo!', NULL, NULL, 8609, 1, 0, 0, 'rajaxx SAY_INTRO');
INSERT INTO `script_texts` VALUES (-1509011, 'Attack and make them pay dearly!', NULL, 'À l\'attaque ! Qu\'ils payent !', NULL, NULL, NULL, '¡Ataca y hazlos pagar caro!', NULL, NULL, 8603, 1, 0, 0, 'rajaxx SAY_UNK1');
INSERT INTO `script_texts` VALUES (-1509012, 'Crush them! Drive them out!', NULL, 'Écrasez les ! Chassez les !', NULL, NULL, NULL, '¡Aplastalos! ¡Échadlos fuera!', NULL, NULL, 8605, 1, 0, 0, 'rajaxx SAY_UNK2');
INSERT INTO `script_texts` VALUES (-1509013, 'Do not hesitate! Destroy them!', NULL, 'Détruisez les ! N\'hésitez pas !', NULL, NULL, NULL, '¡No lo dudes! ¡Destruyelos!', NULL, NULL, 8606, 1, 0, 0, 'rajaxx SAY_UNK3');
INSERT INTO `script_texts` VALUES (-1509014, 'Warriors! Captains! Continue the fight!', NULL, 'Guerriers ! Capitaines ! Continuez le combat !', NULL, NULL, NULL, 'Guerreros! ¡Capitanes! ¡Continúa la lucha!', NULL, NULL, 8613, 1, 0, 0, 'rajaxx SAY_UNK4');
INSERT INTO `script_texts` VALUES (-1509015, 'You are not worth my time $N!', NULL, 'Je n\'ai pas de temps à perdre avec toi !', NULL, NULL, NULL, '¡No vales mi tiempo $N!', NULL, NULL, 8614, 1, 0, 0, 'rajaxx SAY_DEAGGRO');
INSERT INTO `script_texts` VALUES (-1509016, 'Breath your last!', NULL, 'Éxalte ton dernier souffle !', NULL, NULL, NULL, '¡Respira tu última!', NULL, NULL, 8604, 1, 0, 0, 'rajaxx SAY_KILLS_ANDOROV');
INSERT INTO `script_texts` VALUES (-1509017, 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will cease!', NULL, 'Bientôt vous allez connaître le prix de votre ingérence, mortels... Le maître est bientôt de retour... Et quand il va se relever, votre monde cessera !', NULL, NULL, NULL, 'Pronto sabrán el precio de su intromisión, mortales ... El maestro está casi completo ... ¡Y cuando se levante, su mundo cesará!', NULL, NULL, 0, 1, 0, 0, 'rajaxx SAY_COMPLETE_QUEST');
INSERT INTO `script_texts` VALUES (-1509018, 'I am rejuvinated!', NULL, 'Je reprends des forces !', NULL, NULL, NULL, 'Estoy rejuvenecido!', NULL, NULL, 8593, 1, 0, 0, 'ossirian SAY_SURPREME1');
INSERT INTO `script_texts` VALUES (-1509019, 'My powers are renewed!', NULL, 'Mes pouvoirs reviennent !', NULL, NULL, NULL, '¡Mis poderes se renuevan!', NULL, NULL, 8595, 1, 0, 0, 'ossirian SAY_SURPREME2');
INSERT INTO `script_texts` VALUES (-1509020, 'My powers return!', NULL, 'Mes pouvoirs sont de retour !', NULL, NULL, NULL, '¡Mis poderes regresan!', NULL, NULL, 8596, 1, 0, 0, 'ossirian SAY_SURPREME3');
INSERT INTO `script_texts` VALUES (-1509025, 'Sands of the desert rise and block out the sun!', NULL, 'Sables du désert à moi ! Cachez le soleil !', NULL, NULL, NULL, '¡Las arenas del desierto se elevan y bloquean el sol!', NULL, NULL, 8598, 1, 0, 0, 'ossirian SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1509026, 'You are terminated.', NULL, 'Vous avez été liquidés.', NULL, NULL, NULL, 'Estáis acabados.', NULL, NULL, 8602, 1, 0, 0, 'ossirian SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1509027, 'I...have...failed.', NULL, 'J\'ai... échoué...', NULL, NULL, NULL, 'He... he... fallado.', NULL, NULL, 8594, 1, 0, 0, 'ossirian SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531000, 'Are you so eager to die? I would be happy to accomodate you.', NULL, NULL, NULL, NULL, NULL, '¿Estás tan ansioso por morir? Estaría feliz de poder acomodarte.', NULL, NULL, 8615, 1, 0, 0, 'skeram SAY_AGGRO1');
INSERT INTO `script_texts` VALUES (-1531001, 'Cower mortals! The age of darkness is at hand.', NULL, NULL, NULL, NULL, NULL, '¡Atemorizados mortales! La era de las tinieblas se acerca.', NULL, NULL, 8616, 1, 0, 0, 'skeram SAY_AGGRO2');
INSERT INTO `script_texts` VALUES (-1531002, 'Tremble! The end is upon you.', NULL, NULL, NULL, NULL, NULL, '¡Temblad! El fin está sobre ti.', NULL, NULL, 8621, 1, 0, 0, 'skeram SAY_AGGRO3');
INSERT INTO `script_texts` VALUES (-1531003, 'Let your death serve as an example!', NULL, NULL, NULL, NULL, NULL, '¡Que tu muerte sirva de ejemplo!', NULL, NULL, 8617, 1, 0, 0, 'skeram SAY_SLAY1');
INSERT INTO `script_texts` VALUES (-1531004, 'Spineless wretches! You will drown in rivers of blood!', NULL, NULL, NULL, NULL, NULL, '¡Miserables cobardes! ¡Te ahogarás en ríos de sangre!', NULL, NULL, 8619, 1, 0, 0, 'skeram SAY_SLAY2');
INSERT INTO `script_texts` VALUES (-1531005, 'The screams of the dying will fill the air. A symphony of terror is about to begin!', NULL, NULL, NULL, NULL, NULL, 'Los gritos de los moribundos llenarán el aire. ¡Una sinfonía de terror está a punto de comenzar!', NULL, NULL, 8620, 1, 0, 0, 'skeram SAY_SLAY3');
INSERT INTO `script_texts` VALUES (-1531006, 'Prepare for the return of the ancient ones!', NULL, NULL, NULL, NULL, NULL, '¡Prepárate para el regreso de los antiguos!', NULL, NULL, 8618, 1, 0, 0, 'skeram SAY_SPLIT');
INSERT INTO `script_texts` VALUES (-1531007, 'You only delay... the inevetable.', NULL, NULL, NULL, NULL, NULL, 'Solo retrasas ... lo inevitable.', NULL, NULL, 8622, 1, 0, 0, 'skeram SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531008, 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!', NULL, NULL, NULL, NULL, NULL, '¡Serás juzgado por profanar estos terrenos sagrados! ¡Las leyes de los Antiguos no serán desafiadas! ¡Los intrusos serán aniquilados!', NULL, NULL, 8646, 1, 0, 0, 'sartura SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1531009, 'I sentence you to death!', NULL, NULL, NULL, NULL, NULL, '¡Yo te condeno a muerte!', NULL, NULL, 8647, 1, 0, 0, 'sartura SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1531010, 'I serve to the last!', NULL, NULL, NULL, NULL, NULL, '¡Yo cumplo hasta el final!', NULL, NULL, 8648, 1, 0, 0, 'sartura SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531011, '%s is weakened!', NULL, NULL, NULL, NULL, NULL, 'está débil!', NULL, NULL, 0, 2, 0, 0, 'cthun EMOTE_WEAKENED');
INSERT INTO `script_texts` VALUES (-1531012, 'The massive floating eyeball in the center of the chamber turns its gaze upon you. You stand before a god.', NULL, NULL, NULL, NULL, NULL, 'El enorme ojo que flota en el centro de la cámara dirige su mirada hacia ti. Te encuentras ante un dios.', NULL, NULL, 0, 3, 0, 0, 'eye cthun EMOTE_INTRO');
INSERT INTO `script_texts` VALUES (-1531013, 'Only flesh and bone. Mortals are such easy prey...', NULL, NULL, NULL, NULL, NULL, 'Solo carne y hueso. Los mortales sois una presa fácil...', NULL, NULL, 0, 1, 0, 0, 'veklor SAY_INTRO_1');
INSERT INTO `script_texts` VALUES (-1531014, 'Where are your manners, brother. Let us properly welcome our guests.', NULL, NULL, NULL, NULL, NULL, '¿Dónde están tus modales, hermano? Recibamos a nuestros invitados como se merecen.', NULL, NULL, 0, 1, 0, 0, 'veknilash SAY_INTRO_2');
INSERT INTO `script_texts` VALUES (-1533106, 'Your petty magics are no challenge to the might of the Scourge! ', NULL, 'Vos magies insignifiantes ne sauraient défier la puissance du Fléau !', NULL, NULL, NULL, '¡Tu mísera magia no supone ningún desafío para la Plaga!', NULL, NULL, 9088, 1, 0, 0, 'kelthuzad SAY_SPECIAL1_MANA_DET');
INSERT INTO `script_texts` VALUES (-1533107, 'Enough! I grow tired of these distractions! ', NULL, 'Assez ! Je me lasse de ses passe-temps !', NULL, NULL, NULL, '¡Suficiente! ¡Me canso de estas distracciones!', NULL, NULL, 9090, 1, 0, 0, 'kelthuzad SAY_SPECIAL3_MANA_DET');
INSERT INTO `script_texts` VALUES (-1533108, 'Fools, you have spread your powers too thin. Be free, my minions!', NULL, 'Imbéciles, vous avez éparpillés vos forces. Libérez-vous, mes serviteurs !', NULL, NULL, NULL, 'Tontos, habéis extendido demasiado vuestros poderes. ¡Sean libres, mis secuaces!', NULL, NULL, 9089, 1, 0, 0, 'kelthuzad SAY_SPECIAL2_DISPELL');
INSERT INTO `script_texts` VALUES (-1533118, 'Noo... o...', NULL, 'Noo... on...', NULL, NULL, NULL, 'Noo ... o ...', NULL, NULL, 8828, 1, 0, 0, 'heigan SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1090000, 'With your help, I can evaluate these tunnels.', NULL, NULL, NULL, NULL, NULL, 'Con tu ayuda, podré evaluar estos túneles.', NULL, NULL, 0, 0, 0, 1, 'emi shortfuse SAY_START');
INSERT INTO `script_texts` VALUES (-1090001, 'Let\'s see if we can find out where these Troggs are coming from.... and put a stop to the invasion!', NULL, NULL, NULL, NULL, NULL, 'Veamos si podemos descubrir de dónde vienen estos troggs... ¡y detener esta invasión!', NULL, NULL, 0, 0, 0, 1, 'emi shortfuse SAY_INTRO_1');
INSERT INTO `script_texts` VALUES (-1090002, 'Such devastation... what a horrible mess...', NULL, NULL, NULL, NULL, NULL, 'Qué devastación... qué desastre espantoso...', NULL, NULL, 0, 0, 0, 5, 'emi shortfuse SAY_INTRO_2');
INSERT INTO `script_texts` VALUES (-1090003, 'It\'s quiet here....', NULL, NULL, NULL, NULL, NULL, 'Casi hemos llegado...', NULL, NULL, 0, 0, 0, 1, 'emi shortfuse SAY_INTRO_3');
INSERT INTO `script_texts` VALUES (-1090004, '...too quiet.', NULL, NULL, NULL, NULL, NULL, '... hay demasiado silencio.', NULL, NULL, 0, 0, 0, 1, 'emi shortfuse SAY_INTRO_4');
INSERT INTO `script_texts` VALUES (-1090005, 'Look! Over there at the tunnel wall!', NULL, NULL, NULL, NULL, NULL, '¡Mira! ¡Allí, en la pared del túnel!', NULL, NULL, 0, 0, 0, 25, 'emi shortfuse SAY_LOOK_1');
INSERT INTO `script_texts` VALUES (-1090006, 'Trogg incursion! Defend me while I blast the hole closed!', NULL, NULL, NULL, NULL, NULL, '¡Una incursión de troggs! ¡Defiéndeme mientras tapo el agujero con una explosión!', NULL, NULL, 0, 0, 0, 5, 'emi shortfuse SAY_HEAR_1');
INSERT INTO `script_texts` VALUES (-1090007, 'Get this, $n off of me!', NULL, NULL, NULL, NULL, NULL, '¡Quítame a $n de encima!', NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1090008, 'I don\'t think one charge is going to cut it. Keep fending them off!', NULL, NULL, NULL, NULL, NULL, 'No creo que una carga baste para bloquearlo. ¡Sigue combatiéndolos!', NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_CHARGE_1');
INSERT INTO `script_texts` VALUES (-1090009, 'The charges are set. Get back before they blow!', NULL, NULL, NULL, NULL, NULL, 'Las cargas están colocadas. ¡Retrocede antes de que estallen!', NULL, NULL, 0, 0, 0, 5, 'emi shortfuse SAY_CHARGE_2');
INSERT INTO `script_texts` VALUES (-1090010, 'Incoming blast in 10 seconds!', NULL, NULL, NULL, NULL, NULL, '¡Explosión prevista en 10 segundos!', NULL, NULL, 0, 1, 0, 5, 'emi shortfuse SAY_BLOW_1_10');
INSERT INTO `script_texts` VALUES (-1090011, 'Incoming blast in 5 seconds. Clear the tunnel! Stay back!', NULL, NULL, NULL, NULL, NULL, '¡Explosión prevista en 5 segundos! ¡Despejad el túnel! ¡Atrás!', NULL, NULL, 0, 1, 0, 5, 'emi shortfuse SAY_BLOW_1_5');
INSERT INTO `script_texts` VALUES (-1090012, 'FIRE IN THE HOLE!', NULL, NULL, NULL, NULL, NULL, '¡FUEGO EN EL ORIFICIO!', NULL, NULL, 0, 1, 0, 25, 'emi shortfuse SAY_BLOW_1');
INSERT INTO `script_texts` VALUES (-1090013, 'Well done! Without your help I would have never been able to thwart that wave of troggs.', NULL, NULL, NULL, NULL, NULL, '¡Bien hecho! Sin tu ayuda jamás habría podido burlar la horda de troggs.', NULL, NULL, 0, 0, 0, 4, 'emi shortfuse SAY_FINISH_1');
INSERT INTO `script_texts` VALUES (-1090014, 'Did you hear something?', NULL, NULL, NULL, NULL, NULL, '¿Has oído algo?', NULL, NULL, 0, 0, 0, 6, 'emi shortfuse SAY_LOOK_2');
INSERT INTO `script_texts` VALUES (-1090015, 'I heard something over there.', NULL, NULL, NULL, NULL, NULL, 'Yo he oído algo por allí.', NULL, NULL, 0, 0, 0, 25, 'emi shortfuse SAY_HEAR_2');
INSERT INTO `script_texts` VALUES (-1090016, 'More troggs! Ward them off as I prepare the explosives!', NULL, NULL, NULL, NULL, NULL, '¡Más troggs! ¡Recházalos mientras preparo los explosivos!', NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_CHARGE_3');
INSERT INTO `script_texts` VALUES (-1090017, 'The final charge is set. Stand back!', NULL, NULL, NULL, NULL, NULL, 'La carga final está colocada. ¡Atrás!', NULL, NULL, 0, 0, 0, 1, 'emi shortfuse SAY_CHARGE_4');
INSERT INTO `script_texts` VALUES (-1090018, '10 seconds to blast! Stand back!!!', NULL, NULL, NULL, NULL, NULL, '¡10 segundos para la explosión! ¡¡¡Atrás!!!', NULL, NULL, 0, 1, 0, 5, 'emi shortfuse SAY_BLOW_2_10');
INSERT INTO `script_texts` VALUES (-1090019, '5 seconds until detonation!!!!!', NULL, NULL, NULL, NULL, NULL, '¡¡¡5 segundos para la explosión!!!', NULL, NULL, 0, 1, 0, 5, 'emi shortfuse SAY_BLOW_2_5');
INSERT INTO `script_texts` VALUES (-1090020, 'Nice work! I\'ll set off the charges to prevent any more troggs from making it to the surface.', NULL, NULL, NULL, NULL, NULL, '¡Buen trabajo! Activaré las cargas para evitar que más troggs lleguen a la superficie.', NULL, NULL, 0, 0, 0, 1, 'emi shortfuse SAY_BLOW_SOON');
INSERT INTO `script_texts` VALUES (-1090024, 'Usurpers! Gnomeregan is mine!', NULL, NULL, NULL, NULL, NULL, '¡Usurpadores! ¡Gnomeregan es mío!', NULL, NULL, 5807, 1, 0, 0, 'thermaplugg SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1090025, 'My machines are the future! They\'ll destroy you all! ', NULL, NULL, NULL, NULL, NULL, '¡Mis máquinas son el futuro! ¡Os destruirán a todos!', NULL, NULL, 5808, 1, 0, 0, 'thermaplugg  SAY_PHASE');
INSERT INTO `script_texts` VALUES (-1090026, 'Explosions! MORE explosions! I\'ve got to have more explosions! ', NULL, NULL, NULL, NULL, NULL, '¡Explosiones! ¡MÁS explosiones! ¡Tengo que tener más explosiones!', NULL, NULL, 5809, 1, 0, 0, 'thermaplugg  SAY_BOMB');
INSERT INTO `script_texts` VALUES (-1090027, '...and stay dead! He got served', NULL, NULL, NULL, NULL, NULL, '... ¡y quédate muerto! El fue servido', NULL, NULL, 5810, 1, 0, 0, 'thermaplugg  SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1780127, 'Help! I\'ve only one hand to defend myself with.', NULL, NULL, NULL, NULL, NULL, '¡Socorro! Solo tengo una mano para defenderme.', NULL, NULL, 0, 0, 0, 0, 'MIRAN_SAY_AMBUSH_ONE');
INSERT INTO `script_texts` VALUES (-1780128, 'Send them on! I\'m not afraid of some scrawny beasts!', NULL, NULL, NULL, NULL, NULL, '¡Traédmelos! ¡No me asustan esas bestias esqueléticas!', NULL, NULL, 0, 0, 0, 0, 'MIRAN_SAY_AMBUSH_TWO');
INSERT INTO `script_texts` VALUES (-1780129, 'Feel the power of the Dark Iron Dwarves!', NULL, NULL, NULL, NULL, NULL, '¡Siente el poder de los enanos Hierro Negro!', NULL, NULL, 0, 0, 0, 0, 'DARK_IRON_RAIDER_SAY_AMBUSH');
INSERT INTO `script_texts` VALUES (-1780130, 'Ah, here at last! It\'s going to feel so good to get rid of these barrels.', NULL, NULL, NULL, NULL, NULL, '¡Ah, al fin hemos llegado! Qué alivio poder librarnos de todos esos cañones.', NULL, NULL, 0, 0, 0, 0, 'MIRAN_SAY_QUEST_END');
INSERT INTO `script_texts` VALUES (-1509028, '%s drains your mana and turns to stone.', NULL, NULL, NULL, NULL, NULL, 'se bebe tu maná y se convierte en piedra.', NULL, NULL, 0, 2, 0, 0, 'moam EMOTE_ENERGIZING');
INSERT INTO `script_texts` VALUES (-1000566, 'Stand back! Stand clear! The infernal will need to be given a wide berth!', NULL, NULL, NULL, NULL, NULL, '¡Atrás! ¡Atrás! Tenemos que dejarle mucho espacio al inferno.', NULL, NULL, 0, 0, 0, 0, 'SAY_NIBY_1');
INSERT INTO `script_texts` VALUES (-1000567, 'BOW DOWN TO THE ALMIGHTY! BOW DOWN BEFORE MY INFERNAL DESTRO... chicken?', NULL, NULL, NULL, NULL, NULL, '¡INCLINÁOS ANTE EL TODOPODEROSO INCLINÁOS ANTE MI INFERNO DESTRUC... ¿Gallina?', NULL, NULL, 0, 0, 0, 0, 'SAY_NIBY_2');
INSERT INTO `script_texts` VALUES (-1000568, '%s rolls on the floor laughing.', NULL, NULL, NULL, NULL, NULL, 'rueda por el suelo riéndose sin parar.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_IMPSY_1');
INSERT INTO `script_texts` VALUES (-1000569, 'Niby, you\' re an idiot.', NULL, NULL, NULL, NULL, NULL, 'Niby, eres un idiota.', NULL, NULL, 0, 0, 0, 0, 'SAY_IMPSY_1');
INSERT INTO `script_texts` VALUES (-1000570, 'Silence, servant! Vengeance will be mine! Death to Stormwind! Death by chicken!', NULL, NULL, NULL, NULL, NULL, '¡Silencio, siervo! ¡La venganza será mía! ¡Muerte a Ventormenta! ¡Muerte por sobredosis de gallina!', NULL, NULL, 0, 0, 0, 0, 'SAY_NIBY_3');
INSERT INTO `script_texts` VALUES (-1000753, 'We must act quickly or shall be lost!', NULL, 'Nous devons agir rapidement, ou tout sera perdu !', NULL, NULL, NULL, '¡Debemos actuar rápidamente o nos perderemos!', NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_INTRO_1');
INSERT INTO `script_texts` VALUES (-1000754, 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', NULL, 'Mes forces ne peuvent pas percer les défenses des Qiraji. Nous ne pourrons pas nous approcher suffisamment pour poser votre chère barrière, dragon.', NULL, NULL, NULL, 'Mis tropas no pueden superar las defensas qiraji. No seremos capaces de acercarnos lo suficiente para que puedas colocar tu preciada barrera, dragón.', NULL, NULL, 0, 0, 0, 0, 'SAY_FANDRAL_INTRO_2');
INSERT INTO `script_texts` VALUES (-1000755, 'There is a way...', NULL, 'Il existe un moyen...', NULL, NULL, NULL, 'Hay un modo...', NULL, NULL, 0, 0, 0, 22, 'SAY_MERITHRA_INTRO_3');
INSERT INTO `script_texts` VALUES (-1000756, '%s nods knowingly.', NULL, '%s fait un signe de la tête d\'un air entendu.', NULL, NULL, NULL, 'hace un gesto de complicidad.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_ARYGOS_NOD');
INSERT INTO `script_texts` VALUES (-1000757, 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', NULL, 'Aye, Fandral, souvenez vous de ces mots: ne laissez pas votre chagrin guider votre foi. Ces pensées que vous portez... vous conduirons à votre perte. L\'absolution ne peut être obtenue par la vengeance.', NULL, NULL, NULL, 'Fandral, recuerda estas palabras: no permitas que el dolor guíe tu fe. Estos pensamientos que albergas... los lugares aciagos a los que te diriges, elfo de la noche. La absolución no se alcanza mediante la errada venganza.', NULL, NULL, 0, 0, 0, 1, 'SAY_CAELESTRASZ_INTRO_4');
INSERT INTO `script_texts` VALUES (-1000758, '%s glances at her compatriots.', NULL, '%s parcourt des yeux ses compagnons.', NULL, NULL, NULL, 'mira a sus compatriotas.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_MERITHRA_GLANCE');
INSERT INTO `script_texts` VALUES (-1000759, 'We will push them back, Anachronos. This I vow. Uphold your end of this task. Let not your hands falter as you seal our fates behind the barrier.', NULL, 'Nous le repousserons, Anachronos, j\'en fais le serment. Accomplissez votre part dans cette tâche. Que nos mains ne tremblent pas lorsque vous scellerez nos destins derrière la barrière !', NULL, NULL, NULL, 'Haremos que retrocedan, Anachronos. Lo prometo. Completa tu cometido. No vaciles a la hora de encerrar nuestros destinos tras la barrera.', NULL, NULL, 0, 0, 0, 1, 'SAY_MERITHRA_INTRO_5');
INSERT INTO `script_texts` VALUES (-1000760, 'Succumb to the endless dream, little ones. Let it comsume you!', NULL, 'Laissez le rêve infini vous submerger, petits êtres. Laissez-le vous consumer !', NULL, NULL, NULL, 'Sucumbir al sueño sin fin, pequeños. ¡Deja que te consuma!', NULL, NULL, 0, 1, 0, 22, 'SAY_MERITHRA_ATTACK_1');
INSERT INTO `script_texts` VALUES (-1000761, 'Anachronos, this diversion will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', NULL, 'Anachronos, cette diversion vous donnera assez de temps, à vous et au jeune druide, pour sceller la porte. Ne faiblissez pas. Et maintenant, voyons comment ils supportent la magie chaotique.', NULL, NULL, NULL, 'Anachronos, esta estrategia de distracción os proporcionará a ti y al joven druida tiempo suficiente para cerrar la puerta. No falléis. Ahora veremos cómo se defienden de la magia caótica.', NULL, NULL, 0, 0, 0, 1, 'SAY_ARYGOS_ATTACK_2');
INSERT INTO `script_texts` VALUES (-1000762, 'Let them feel the wrath of the Blue Flight! May Malygos protect me!', NULL, 'Qu\'ils sentent la colère du Vol bleu ! Que Malygos me protège !', NULL, NULL, NULL, '¡Que sientan la cólera del Vuelo Azul! ¡Que Malygos me proteja!', NULL, NULL, 0, 1, 0, 22, 'SAY_ARYGOS_ATTACK_3');
INSERT INTO `script_texts` VALUES (-1000763, 'Do not forget the sacrifices we made on this day, night elf. We have all suffered immensely at the hands of these beasts.', NULL, 'N\'oubliez pas les sacrifices accomplis en ce jour, elfe de la nuit. Nous avons tous énormément souffert des mains de ces bêtes.', NULL, NULL, NULL, 'No olvides los sacrificios que hicimos en este día, elfo de la noche. Todos hemos sufrido inmensamente a manos de estas bestias.', NULL, NULL, 0, 0, 0, 1, 'SAY_CAELESTRASZ_ATTACK_4');
INSERT INTO `script_texts` VALUES (-1000764, 'Alexstrasza, give me the resolve to drive your enemies back.', NULL, 'Alexstrasza, donne moi la force de repousser tes ennemies.', NULL, NULL, NULL, 'Alexstrasza, dame la determinación de hacer retroceder a tus enemigos.', NULL, NULL, 0, 1, 0, 22, 'SAY_CAELESTRASZ_ATTACK_5');
INSERT INTO `script_texts` VALUES (-1000765, 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', NULL, 'MAINTENANT, FANDRAL ! ALLONS-Y ! Préparez votre magie !', NULL, NULL, NULL, '¡AHORA, CORZOCELADA! ¡ALLÁ VAMOS! ¡Prepara tu magia!', NULL, NULL, 0, 0, 0, 22, 'SAY_ANACHRONOS_SEAL_1');
INSERT INTO `script_texts` VALUES (-1000766, 'It is done dragon. Lead the way...', NULL, 'C\'est fait, dragon. Je vous suis !', NULL, NULL, NULL, 'Está hecho dragón. Lidera el camino ...', NULL, NULL, 0, 0, 0, 25, 'SAY_FANDRAL_SEAL_2');
INSERT INTO `script_texts` VALUES (-1000767, 'Stay close.', NULL, 'Restez prêt de moi.', NULL, NULL, NULL, 'Quédate cerca.', NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_SEAL_3');
INSERT INTO `script_texts` VALUES (-1000768, 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', NULL, 'Les sables du temps s\'arrêteront, mais seulement pour un moment ! Je vais invoquer la barrière.', NULL, NULL, NULL, 'Las tierras del tiempo se detendrán, ¡pero solo por un instante! Haré aparecer la barrera.', NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_SEAL_4');
INSERT INTO `script_texts` VALUES (-1000769, 'FINISH THE SPELL, STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', NULL, 'ACHEVEZ LE SORT, FORTEARMURE ! JE NE MAINTIENDRAI PAS LES GLYPHES DE SAUVEGARDE EN PLACE PLUS LONGTEMPS ! INVOQUEZ LES RACINES !', NULL, NULL, NULL, '¡CONCLUYE EL HECHIZO, CORZOCELADA! ¡NO PUEDO CONTENER A LOS GLIFOS QUE PROTEGEN EL LUGAR POR MUCHO MÁS TIEMPO! ¡INVOCA A LAS RAÍCES!', NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_SEAL_5');
INSERT INTO `script_texts` VALUES (-1000770, 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', NULL, 'Anciens, guidez ma main... Sortez de votre torpeur ! EVEILLEZ-VOUS ET SCELLEZ CET ENDROIT MAUDIT !', NULL, NULL, NULL, 'Ancestros, guiad mi mano... ¡Despertad de vuestro sueño! ¡DESPERTAD Y SELLAD ESTE LUGAR MALDITO!', NULL, NULL, 0, 0, 0, 0, 'SAY_FANDRAL_SEAL_6');
INSERT INTO `script_texts` VALUES (-1000771, '%s falls to one knee - exhausted.', NULL, '%s s\'effondre, à bout de souffle.', NULL, NULL, NULL, 'hinca exhausto una rodilla.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_FANDRAL_EXHAUSTED');
INSERT INTO `script_texts` VALUES (-1000772, 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', NULL, 'C\'est... C\'est terminé, seigneur Fortearmure. Nous sommes victorieux. Mais c\'est une victoire chèrement payée.', NULL, NULL, NULL, 'Se... Se ha acabado, Lord Corzocelada. ¡Hemos vencido! Si bien el precio que hemos pagado por esta victoria ha sido alto.', NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_1');
INSERT INTO `script_texts` VALUES (-1000773, 'There is but one duty that remains...', NULL, 'Alors, il ne reste qu\'un devoir à accomplir...', NULL, NULL, NULL, 'Tan solo queda una obligación que cumplir...', NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_2');
INSERT INTO `script_texts` VALUES (-1000774, 'Before I leave this place, I make one final offering to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', NULL, 'Avant de partir, laissez-moi vous faire une dernière don, seigneur Fortearmure. Un jour viendra ou vous aurez besoin de pénétrer dans cette forteresse maudite, utilisez le Sceptre des Sables Changeants sur le gong sacré. La magie qui maintient cette barrière se dissipera et les horreurs d\'Ahn\'Qiraj se déverseront à nouveau sur le monde.', NULL, NULL, NULL, 'Antes de abandonar este lugar, te daré un último consejo, Lord Corzocelada. Si en algún momento tienes que entrar en esta fortaleza maldita, usa el cetro del Mar de Dunas para hacer sonar el gong sagrado. La magia que sustenta la barrera desaparecerá y los horrores de Ahn\'Qiraj se cernirán de nuevo sobre el mundo.', NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_3');
INSERT INTO `script_texts` VALUES (-1000775, '%s hands the Scepter of the Shifting Sands to $N.', NULL, '%s tend le Sceptre des Sables Changeants à $N.', NULL, NULL, NULL, '%s le entrega el cetro de las arenas movedizas a $N.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_ANACHRONOS_SCEPTER');
INSERT INTO `script_texts` VALUES (-1000776, 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', NULL, 'Après le carnage dont mon peuple a été temoin, vous pensez vraiment que je vais endosser un autre fardeau ? Vous avez perdu la raison.', NULL, NULL, NULL, 'Tras la violencia que mi pueblo ha presenciado y de la cual ha sido víctima, ¿esperas que acepte otra carga, dragón? Sin duda, estás loco.', NULL, NULL, 0, 0, 0, 1, 'SAY_FANDRAL_EPILOGUE_4');
INSERT INTO `script_texts` VALUES (-1000777, 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', NULL, 'Je ne veux plus rien avoir à faire avec Silithus, les Qirajis et surtout, ces damnés dragons !', NULL, NULL, NULL, '¡No quiero tener nada que ver con Silithus, los qiraji y, sobre todo, con ningún condenado dragón!', NULL, NULL, 0, 0, 0, 1, 'SAY_FANDRAL_EPILOGUE_5');
INSERT INTO `script_texts` VALUES (-1000778, '%s hurls the Scepter of the Shifting Sands into the barrier, shattering it.', NULL, '%s lance le Sceptre des Sables Changeants contre la barrière, le brisant.', NULL, NULL, NULL, 'arroja el cetro del Mar de Dunas contra la barrera, haciéndolo añicos.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_FANDRAL_SHATTER');
INSERT INTO `script_texts` VALUES (-1000779, 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', NULL, 'Seigneur Fortearmure, que faites vous ? Vous seriez prêt à briser notre lien pour satisfaire votre orgueil ?', NULL, NULL, NULL, 'Lord Staghelm, ¿a dónde vas? ¿Romperías nuestro vínculo por el orgullo?', NULL, NULL, 0, 0, 0, 1, 'SAY_ANACHRONOS_EPILOGUE_6');
INSERT INTO `script_texts` VALUES (-1000780, 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back. Though it takes a millenia, I WILL have my son back!', NULL, 'L\'âme de mon fils ne trouvera aucun récomfort dans cette victoire sans goût, dragon ! Je veux qu\'il revienne. Même si cela doit prendre un millénaire. JE VEUX que mon fils revienne !', NULL, NULL, NULL, 'El alma de mi hijo no encontrará consuelo en esta victoria vacía, dragón. Lo tendré de vuelta. Aunque se necesitan milenios, ¡tendré a mi hijo de vuelta!', NULL, NULL, 0, 0, 0, 1, 'SAY_FANDRAL_EPILOGUE_7');
INSERT INTO `script_texts` VALUES (-1000781, '%s shakes his head in disappointment.', NULL, '%s hoche la tête de deception.', NULL, NULL, NULL, 'sacude la cabeza en un gesto de decepción.', NULL, NULL, 0, 2, 0, 25, 'EMOTE_ANACHRONOS_DISPPOINTED');
INSERT INTO `script_texts` VALUES (-1000782, '%s kneels down to pickup the fragments of the shattered scepter.', NULL, '%s se met à genoux et ramasse les fragments du sceptre brisé.', NULL, NULL, NULL, '%s se arrodilla para recoger los fragmentos del cetro destrozado.', NULL, NULL, 0, 2, 0, 0, 'EMOTE_ANACHRONOS_PICKUP');
INSERT INTO `script_texts` VALUES (-1000783, 'And now you know all that there is to know, mortal...', NULL, 'A présent, mortel, vous savez tout ce qu\'il y a à savoir...', NULL, NULL, NULL, 'Y ahora ya sabes todo lo que hay que saber, mortal...', NULL, NULL, 0, 0, 0, 0, 'SAY_ANACHRONOS_EPILOGUE_8');
INSERT INTO `script_texts` VALUES (-1000015, 'Good riddance!', '', 'Bon débarras !', '', '', '', '¡Hasta nunca!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000014, 'But your real punishment lies ahead.', '', 'Mais votre vraie punition reste à venir.', '', '', '', 'Pero tu verdadero castigo no ha comenzado aún.', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000013, 'Haha! I bet you thought you were done!', '', 'Hé hé ! Je parie que vous ne vous attendiez pas à ça !', '', '', '', '¡Ja, ja! ¡A que pensabas que eso era todo!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000012, 'Unleash the fury and let it be done!', '', 'Que toute fureur soit lâchée et que tout finisse !', '', '', '', '¡Desatad vuestra furia y castigad a los culpables!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1309025, 'Jeklik begins to cast a Great Heal!', '', 'Jeklik commence à lancer un sort de Soins exceptionnels !', '', '', '', '¡Jeklik comienza a lanzar una gran curación!', '', '', 0, 2, 0, 0, 'jeklik EMOTE_HEAL');
INSERT INTO `script_texts` VALUES (-1309026, 'Jeklik emits a deafening shriek, calling for help.', '', 'Jeklik pousse un hurlement assourdissant !', '', '', '', 'Jeklik emite un chillido ensordecedor, pidiendo ayuda.', '', '', 0, 2, 0, 0, 'jeklik EMOTE_SUMMON_BATS');
INSERT INTO `script_texts` VALUES (-1409019, 'Imprudent whelps! You\'ve rushed headlong to your own deaths! See now, the master stirs!', '', 'Imprudents imbéciles ! Vous vous êtes precipités vers vôtre propre mort ! Voyez, à présent, le Maitre remue !', '', '', '', '¡Cachorros imprudentes! ¡Te has precipitado precipitadamente hacia tu propia muerte! ¡Mira, el maestro se mueve!', '', '', 0, 1, 0, 0, 'ragnaros SAY_MAJ');
INSERT INTO `script_texts` VALUES (-1469032, '%s begins to cast Wing Buffet', '', '%s commence à lancer Frappe des ailes.', '', '', '', '% s comienza a lanzar Buffet de Alas', '', '', 0, 2, 0, 0, 'EMOTE_GENERIC_WING_BUFFET');
INSERT INTO `script_texts` VALUES (-1469033, '%s begins to cast Shadow Flame.', '', '%s commence à lancer Flamme d\'ombre.', '', '', '', '%s comienza a lanzar Llama de las Sombras.', '', '', 0, 2, 0, 0, 'EMOTE_GENERIC_SHADOW_FLAME');
INSERT INTO `script_texts` VALUES (-1469034, '%s casts Destroy Egg', '', '%s lance Détruire l\'Oeuf.', '', '', '', '%s lanza Destruir Huevo', '', '', 0, 2, 0, 0, 'Razor EMOTE_DESTROY_EGG');
INSERT INTO `script_texts` VALUES (-1469035, 'Nefarian\'s troops flee as the power of the controlling power of the orb is drained.', '', 'Le pouvoir de contrôle de l\'orbe épuisé, les troupes de Nefarian s\'enfuient.', '', '', '', 'Las tropas de Nefarian huyen cuando se agota el poder de control del orbe.', '', '', 0, 2, 0, 0, 'Razorgore EMOTE_FLEE');
INSERT INTO `script_texts` VALUES (-1109005, 'The shield be down! Rise up Atal\'ai! Rise up!', '', 'Que le bouclier s\'abaisse ! Debout, Atal\'ai, debout !', '', '', '', '¡Que baje el escudo! ¡Álzate Atal\'ai! ¡Álzate!', '', '', 5861, 6, 0, 0, 'Jammal\'an YELL_SHIELD');
INSERT INTO `script_texts` VALUES (-1109006, 'I draw closer to your world...', '', 'J\'approche de votre monde...', '', '', '', 'Me acerco a tu mundo ...', '', '', 5867, 1, 0, 0, 'AvatarOfHakkar YELL_BRAZIER01');
INSERT INTO `script_texts` VALUES (-1109007, 'I taste the blood of life...', '', 'Je goûterai le sang de la vie...', '', '', '', 'Pruebo la sangre de la vida ...', '', '', 5868, 1, 0, 0, 'AvatarOfHakkar YELL_BRAZIER02');
INSERT INTO `script_texts` VALUES (-1109008, 'I... am near...', '', 'Je... suis proche...', '', '', '', 'Yo estoy cerca...', '', '', 5869, 1, 0, 0, 'AvatarOfHakkar YELL_BRAZIER03');
INSERT INTO `script_texts` VALUES (-1109009, 'Hakkar... lives !', '', 'Hakkar... vit !', '', '', '', 'Hakkar ... ¡vive!', '', '', 5870, 1, 0, 0, 'AvatarOfHakkar YELL_BRAZIER04');
INSERT INTO `script_texts` VALUES (-1109010, 'Die, mortals !', '', 'Mourrez, mortels !', '', '', '', '¡Mueran, mortales!', '', '', 5871, 1, 0, 0, 'AvatarOfHakkar YELL_AGGRO01');
INSERT INTO `script_texts` VALUES (-1109011, 'You know not what you do! We must destroy you for your own good!', '', 'Vous ne savez pas ce que vous faites ! Nous devons vous détruire pour votre propre bien.', '', '', '', '¡No sabes lo que haces! ¡Debemos destruirte por tu propio bien!', '', '', 0, 1, 0, 0, 'Dreamscythe YELL_INTRO');
INSERT INTO `script_texts` VALUES (-1109012, 'Turn back! Do not wake the dreamer!', '', 'Demi-tour ! Ne réveillez pas le rêveur !', '', '', '', '¡Vuelve! ¡No despiertes al soñador!', '', '', 5866, 6, 0, 0, 'Dreamscythe YELL_AGGRO');
INSERT INTO `script_texts` VALUES (-1109014, 'My banishment is ended! Let the blood flow!', '이제 자유다! 피의 복수를 해 주겠다!', 'Mon exil a pris fin. Que coule le sang!', 'Meine Verbannung ist vorbei! Lasst Blut fließen!', '我的流放生涯结束了！让鲜血尽情流淌吧！', '我所遭受的懲罰終於結束了!讓鮮血在大地上流淌吧!', '¡Mi destierro ha terminado! ¡Que corra la sangre!', '¡Mi destierro ha terminado! ¡Que corra la sangre!', 'Мой брат оскорблен! Да прольется кровь!', 5859, 1, 14, 0, 'Atalalarion - YELL_SPAWN');
INSERT INTO `script_texts` VALUES (-1780148, 'FOR THE ALLIANCE!', NULL, 'POUR L\'ALLIANCE !', NULL, NULL, NULL, '¡Por la Alianza!', NULL, NULL, 0, 1, 7, 22, 'Silithus Field Duty Event - 8 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780147, 'For Ironforge!', NULL, 'Pour Ironforge !', NULL, NULL, NULL, '¡Por Forjaz!', NULL, NULL, 0, 1, 7, 22, 'Silithus Field Duty Event - 7 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780146, 'Sergeant, be ready! Nozzlespring, make sure you set this nasty insect on fire while I\'ll draw its attention. Janela, to my side!\r\n', NULL, 'Sergeant, tenez vous prêt ! Nozzlespring, faites donc rôtir cette infâme bestiole pendant que j\'attire son attention. Janela, avec moi !', NULL, NULL, NULL, 'Sargento, ¡prepárese! Boquilla de Resorte, asegúrate de prender fuego a este repugnante insecto mientras yo llamo su atención. Janela, a mi lado!', NULL, NULL, 0, 0, 7, 22, 'Silithus Field Duty Event - 7 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780145, 'Form the line! Everyone in position!', NULL, 'Formez les rangs ! Tout le monde à son poste !', NULL, NULL, NULL, '¡Forma la línea! ¡Todos en posición!', NULL, NULL, 0, 0, 7, 22, 'Silithus Field Duty Event - 6 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780144, 'Captain! Silithid wasp, straight ahead!', NULL, 'Capitaine ! Guêpe Silithide, droit devant !', NULL, NULL, NULL, '¡Capitán! Avispa silítida, ¡adelante!', NULL, NULL, 0, 0, 7, 5, 'Silithus Field Duty Event - 5 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780143, 'As you can see over there, this is also here that Silithists are collected, and we...', NULL, 'Comme vous pouvez le voir là-bas, c\'est aussi ici que nous stockons les Silithistes collectées par l\'Alliance, et nous...', NULL, NULL, NULL, 'Como puede ver allí, esto también es aquí donde se recolectan los silitistas, y nosotros ...', NULL, NULL, 0, 0, 7, 0, 'Silithus Field Duty Event - 4 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780142, 'A swarming noise arising from the hive becomes louder and louder...', NULL, 'Un bruit de grouillement s\'élevant de la ruche se fait de plus en plus perçant...', NULL, NULL, NULL, 'Un enjambre que surge de la colmena se hace cada vez más fuerte ...', NULL, NULL, 0, 2, 0, 0, 'Silithus Field Duty Event - 3 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780141, 'More of these monstruous bugs come every day, and the recruits sent by the Cenarion Druids are barely enough for us to cope with the Silithids\' attacks. Your help is of paramount importance for us, and I will sign your papers if you serve the Alliance and the Cenarion Circle bravely.', NULL, 'Le nombre de ces monstrueux insectes croît sans cesse, et les recrues envoyées par les Druides Cénarien nous permettent à peine de faire face aux attaques des Silithides. Votre aide est d\'une importance capitale, et je signerai vos papiers si vous servez l\'Alliance et le Cercle Cénarien avec bravoure.', NULL, NULL, NULL, 'Cada día llegan más de estos monstruosos bichos, y los reclutas enviados por los Druidas Cenarion apenas nos alcanzan para hacer frente a los ataques de los Silítidos. Su ayuda es de suma importancia para nosotros, y firmaré sus papeles si sirven valientemente a la Alianza y al Círculo Cenarion.', NULL, NULL, 0, 0, 7, 0, 'Silithus Field Duty Event - 2 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780140, 'Welcome to Silithus, young recruit. The Ironforge Brigade fights in this region on behalf of the Alliance to repel the Qiraji invaders. We have been tasked by the Cenarion Circle to hold back the Silithid threat emanating from the Hive\'Zora. Alliance scouts are sent into the hive from this outpost on a regular basis, and the enemy\'s assaults on Cenarion Hold are to be intercepted here.', NULL, 'Bienvenue en Silithus, jeune recrue. La Brigade d\'Ironforge représente l\'Alliance dans sa lutte contre les envahisseurs Qiraji. Nous avons été chargés par le Cercle Cénarien de contenir la menace Silithide que constitue la Ruche Zora. Des éclaireurs de l\'Alliance sont envoyés régulièrement depuis cet avant poste, et les assaults de l\'enemi sur le Fort Cénarien sont interceptés ici.', NULL, NULL, NULL, 'Bienvenido a Silithus, joven recluta. La Brigada Forjaz lucha en esta región en nombre de la Alianza para repeler a los invasores qiraji. El Círculo Cenarion nos ha encomendado la tarea de contener la amenaza silítida que emana de la Colmena\'Zora. Los exploradores de la Alianza son enviados a la colmena desde este puesto de avanzada de forma regular, y los asaltos del enemigo en Bastión Cenarion deben ser interceptados aquí.', NULL, NULL, 0, 0, 7, 0, 'Silithus Field Duty Event - 1 (Alliance)');
INSERT INTO `script_texts` VALUES (-1780139, 'FOR THE HORDE!', NULL, 'POUR LA HORDE !', NULL, NULL, NULL, '¡Por la Horda!', NULL, NULL, 0, 1, 1, 22, 'Silithus Field Duty Event - 7 (Horde)');
INSERT INTO `script_texts` VALUES (-1780138, 'Strength and Honor!', NULL, 'Force et Honneur !', NULL, NULL, NULL, '¡Fuerza y honor!', NULL, NULL, 0, 1, 1, 22, 'Silithus Field Duty Event - 9 (Horde)');
INSERT INTO `script_texts` VALUES (-1780137, 'Shai, Merok! To my side!', NULL, 'Shai, Merok ! À mes côtés !', NULL, NULL, NULL, '¡Shai, Merok! ¡A mi lado!', NULL, NULL, 0, 0, 1, 22, 'Silithus Field Duty Event - 6 (Horde)');
INSERT INTO `script_texts` VALUES (-1780136, 'Look! Something\'s coming!', NULL, 'Attention ! Quelque chose approche !', NULL, NULL, NULL, '¡Mirar! ¡Algo viene!', NULL, NULL, 0, 0, 1, 5, 'Silithus Field Duty Event - 8 (Horde)');
INSERT INTO `script_texts` VALUES (-1780135, 'Krug suddenly halts, his eyes caught by a motion down in the Hive.', NULL, 'Krug s\'interrompt soudainement, le regard attiré par un mouvement en contrebas.', NULL, NULL, NULL, 'Krug se detiene de repente, sus ojos captados por un movimiento hacia abajo en la Colmena.', NULL, NULL, 0, 2, 0, 0, 'Silithus Field Duty Event - 5 (Horde)');
INSERT INTO `script_texts` VALUES (-1780134, 'Be ready!  If I am to sign those papers, you\'re gonna have to...', NULL, 'Soyez sur vos gardes ! Si vous voulez que je signe ces papiers, il va falloir...', NULL, NULL, NULL, '¡Estar listo! Si voy a firmar esos papeles, tendrás que ...', NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 4 (Horde)');
INSERT INTO `script_texts` VALUES (-1780133, 'Our scouts have reported that a Silithid attempt to take out this outpost is likely to occur very soon.', NULL, 'Les derniers rapports des éclaireurs nous laissent penser qu\'un assaut des Silithides sur cet avant-poste est imminent.', NULL, NULL, NULL, 'Nuestros exploradores han informado de que muy pronto se producirá un intento de los silítidos de acabar con este puesto de avanzada.', NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 3 (Horde)');
INSERT INTO `script_texts` VALUES (-1780132, 'Get into the ranks ! Fighting alongside the Orgrimmar Legion is no easy task. The recruits these elves from the Cenarion Circle keep sending us never last long.', NULL, 'Rentrez dans les rangs ! Se battre aux côtés de la Légion d\'Orgrimmar n\'est pas chose aisée. Les recrues que nous envoient ces elfes du Cercle Cénarien font rarement long feu.', NULL, NULL, NULL, '¡Entra en las filas! Luchar junto a la Legión de Orgrimmar no es una tarea fácil. Los reclutas que estos elfos del Círculo Cenarion siguen enviándonos nunca duran mucho.', NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 2 (Horde)');
INSERT INTO `script_texts` VALUES (-1780131, 'Lok\'tar Ogar !', NULL, 'Lok\'tar Ogar !', NULL, NULL, NULL, '¡Lok\'tar Ogar!', NULL, NULL, 0, 0, 1, 22, 'Silithus Field Duty Event - 1 (Horde)');
INSERT INTO `script_texts` VALUES (-1780149, 'Remember, I need your help to properly channel.I will ask you to aid me sevral times on our path, so please be ready.', NULL, ' Souvenez-vous, je vais avoir besoin de votre aide pour canaliser correctement. Tenez-vous $gprêt:prête; vous devrez m\'aider à plusieurs reprises au cours de notre chemin.', NULL, NULL, NULL, 'Recuerda, necesito tu ayuda para canalizar adecuadamente. Te pediré que me ayudes varias veces en nuestro camino, así que prepárate.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780150, 'This blue light... It\'s strange. What do you think it means?', NULL, 'Cette lumière bleue... C\'est étrange. Qu\'est-ce que cela peut bien signifier ?', NULL, NULL, NULL, 'Esta luz azul ... es extraña. ¿Qué crees que significa?', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780151, 'Ok, let\'s get going!', NULL, 'C\'est bon, allons-y!', NULL, NULL, NULL, '¡Muy bien, pongámonos en marcha!', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780152, 'We must continue now.', NULL, 'Nous devons poursuivre à présent.', NULL, NULL, NULL, 'Debemos continuar ahora.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780153, 'Let\'s find the next one.', NULL, 'Trouvons la suivante.', NULL, NULL, NULL, 'Vamos a encontrar el siguiente.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780154, 'It is time for the final step; we must activate the altar.', NULL, 'On est arrivé à la dernière étape ; nous devons activer l\'autel.', NULL, NULL, NULL, 'Es hora de dar el paso final; debemos activar el altar.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780155, 'I will read the word carved into the stone, and you will find a way to light it.', NULL, 'Je vais lire les mots gravés dans la pierre, et vous allez trouver une manière de l\'allumer.', NULL, NULL, NULL, 'Leeré la palabra grabada en la piedra y encontrarás la manera de iluminarla.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780157, 'The altar is glowing! We have done it!', NULL, 'L\'autel s\'illumine ! Nous avons réussi !', NULL, NULL, NULL, '¡El altar está brillando! ¡Lo hemos conseguido!', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780158, 'What is happening? Look!', NULL, 'Que se passe-t-il ? Regardez !', NULL, NULL, NULL, '¿Qué ocurre? ¡Mira!', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780159, 'It has been many years...', NULL, 'Tant d\'années se sont écoulées', NULL, NULL, NULL, 'Han sido muchos años...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780160, 'Who has disturbed the altar of the goddess?', NULL, 'Qui a dérangé l\'autel de la déesse?', NULL, NULL, NULL, '¿Quién ha perturbado el altar de la diosa?', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780161, 'Please, priestesses, forgive us for our intrusion. We do not wish any harm here.', NULL, 'Pardonnez-nous notre intrusion, prêtresses. Nous ne cherchons pas à faire du mal ici.', NULL, NULL, NULL, 'Por favor, sacerdotisa, perdona nuestra intromisión. No queremos causar ningún problema.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780162, 'We only wish to know why the wildkin guard this area...', NULL, 'Nous souhaitons uniquement comprendre pourquoi les Indomptables gardent ce lieu', NULL, NULL, NULL, 'Solo queremos saber por qué los lechúcicos guardan esta zona...', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780163, 'Enu thora\'serador. This is a sacred place.', NULL, 'Enu thora\'serador. Cet endroit est sacré.', NULL, NULL, NULL, 'Enu thora\'serador. Este es un lugar sagrado.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780164, 'We will show you...', NULL, 'Nous allons vous montrer...', NULL, NULL, NULL, 'Nosotros te mostraremos ...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780165, 'Look above you; thara dormil dorah...', NULL, 'Regardez au-dessus de vous ; thara dormil dorah...', NULL, NULL, NULL, 'Mira ahí arriba; thara dormil dorah...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780166, 'This gem once allowed direct communication with Elune, herself.', NULL, 'Cette gemme permettait autrefois la communication directe avec Elune elle-même.', NULL, NULL, NULL, 'Antaño esta gema permitía comunicarse directamente con la propia Elune.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780167, 'Through the gem, Elune channeled her infinite wisdom...', NULL, 'A travers la gemme, Elune insufflait sa sagesse infinie...', NULL, NULL, NULL, 'A través de la gema, Elune canalizaba su infinita sabiduría...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780168, 'Realizing that the gem needed to be protected, we turned to the goddess herself.', NULL, 'Réalisant que la gemme devait être protégée, nous nous sommes tournées vers la déesse en personne.', NULL, NULL, NULL, 'Al comprender que la gema debía ser protegida, recurrimos a la propia diosa.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780169, 'Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear...', NULL, 'Peu après, nous avons commencé à avoir des visions d\'une créature... Une créature dotée des plumes d\'une chouette, mais avec la volonté et la puissance d\'un ours...', NULL, NULL, NULL, 'Poco después comenzamos a tener visiones de una criatura... Una criatura con plumas de búho, pero con la voluntad y la fuerza del oso...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780170, 'It was on that day that the wildkin were given to us. Fierce guardians, the goddess assigned the wildkin to protect all of her sacred places.', NULL, 'C\'est en ce jour que les Indomptables nous ont été donnés. De furieux gardiens ! La déesse les a affectés à la protection de tous ses lieux saints ', NULL, NULL, NULL, 'Fue en ese día que nos entregaron los parientes salvajes. Guardianes feroces, la diosa asignó a los salvajes para proteger todos sus lugares sagrados.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780171, 'Anu\'dorini Talah, Ru shallora enudoril.', NULL, NULL, NULL, NULL, NULL, 'Anu\'dorini Talah, Ru shallora enudoril.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780172, 'But now, many years later, the wildkin have grown more feral, and without the guidance of the goddess, they are confused...', NULL, 'Mais maintenant, de nombreuses années plus tard, les Indomptables sont devenus plus sauvages, et sans leur déesse pour les guider, ils sont confus...', NULL, NULL, NULL, 'Pero ahora, muchos años después, los salvajes se han vuelto más salvajes y sin la guía de la diosa, están confundidos ...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780173, 'Without a purpose, they wander... But many find their way back to the sacred areas that they once were sworn to protect.', NULL, 'Sans raison d\'être, ils errent... Mais beaucoup retrouvent leur chemin vers les lieux sacrées qu\'ils avaient autrefois juré de protéger.', NULL, NULL, NULL, 'Sin un propósito, vagan ... Pero muchos encuentran el camino de regreso a las áreas sagradas que una vez juraron proteger.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780174, 'Wildkin are inherently magical; this power was bestowed upon them by the goddess.', NULL, 'Les Indomptables sont magiques par essence ; ce pouvoir leur fut accordé par la déesse.', NULL, NULL, NULL, 'Los lechúcicos son mágicos intrínsecamente; un poder que les fue otorgado por la diosa.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780175, 'Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago.', NULL, 'Sachez qu\'où que vous puissiez les trouver sur ce monde, ils protègent quelque chose d\'éminemment important, puisque la tâche leur a été confié il y a si longtemps.', NULL, NULL, NULL, 'Tienes que saber que donde quiera que combatas con ellos, estarán protegiendo algo importante, como se les encargó hace tantísimo tiempo.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780176, 'Please, remember what we have shown you...', NULL, 'S\'il vous plait, souvenez vous de ce que nous vous avons montré...', NULL, NULL, NULL, 'Por favor, recuerda lo que te he enseñado...', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780178, 'Farewell.', NULL, 'Adieu.', NULL, NULL, NULL, 'Despedida.', NULL, NULL, 0, 0, 0, 0, 'quest4901');
INSERT INTO `script_texts` VALUES (-1780179, 'Thank you for you help, $N. I wish you well in your adventures.', NULL, 'Merci de votre aide $N. Je vous souhaite le meilleur pour la suite de vos aventures.', NULL, NULL, NULL, 'Gracias por tu ayuda, $N. Te deseo lo mejor en tus aventuras.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780180, 'I want to stay here and reflect on what we have seen. Please see Erelas and tell him what we have learned.', NULL, 'Je souhaite rester ici et méditer sur ce que nous avons vu. S\'il vous plaît allez voir Erelas et dites-lui ce que nous avons appris.', NULL, NULL, NULL, 'Quiero quedarme aquí y meditar sobre lo que he visto. Por favor, ve a ver a Eralas y dile lo que hemos aprendido.', NULL, NULL, 0, 0, 0, 68, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780181, 'This is the place. Let\'s light it.', NULL, 'Nous y sommes. Allumons-la.', NULL, NULL, NULL, 'Aquí es. Vamos a iluminarlo.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780182, 'This is the place. Let\'s light it.', NULL, 'Nous y sommes. Allumons-le.', NULL, NULL, NULL, 'Aquí es. Vamos a iluminarlo.', NULL, NULL, 0, 0, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1780183, 'Ranshalla begins chanting a strange spell.', NULL, 'Ranshalla commence à incanter un sort étrange.', NULL, NULL, NULL, 'Ranshalla comienza a cantar un extraño hechizo.', NULL, NULL, 0, 2, 0, 0, 'quest4901pnj10300');
INSERT INTO `script_texts` VALUES (-1509029, 'I lied...', NULL, 'J\'ai menti...', NULL, NULL, NULL, 'Mentí...', NULL, NULL, 0, 1, 0, 0, 'andorov SAY_ANDOROV_INTRO');
INSERT INTO `script_texts` VALUES (-1509030, 'Kill first, then ask for questions... they\'re coming !', NULL, 'Tuez d\'abord, posez des questions ensuite... Ils arrivent !', NULL, NULL, NULL, 'Mata primero, luego haz preguntas ... ¡ya vienen!', NULL, NULL, 0, 1, 0, 0, 'andorov SAY_ANDOROV_INTRO');
INSERT INTO `script_texts` VALUES (-1780190, 'Ok, let\'s get started.', NULL, NULL, NULL, NULL, NULL, 'Vale, empecemos.', NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780191, 'Nope, not here.', NULL, NULL, NULL, NULL, NULL, 'No, aquí no.', NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780192, 'Ah! There\'s one!', NULL, NULL, NULL, NULL, NULL, '¡Ah! ¡Hay uno!', NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780193, 'Come, $N! Let\'s go over there and collect it!', NULL, NULL, NULL, NULL, NULL, '¡Ven, $N! ¡Vayamos allí y recójalo!', NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780194, 'Ok, now let\'s get out of here!', NULL, NULL, NULL, NULL, NULL, 'Vale, ¡ahora salgamos de aquí!', NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780195, 'I can make it from here. Thanks $N! And talk to my employer about a reward!', NULL, NULL, NULL, NULL, NULL, 'Puedo hacerlo desde aquí. ¡Gracias $N! ¡Y hablar con mi empleador sobre una recompensa!', NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780196, 'Ah freedom! Although brief, so sweet it is...', NULL, NULL, NULL, NULL, NULL, '¡Ah, libertad! Aunque breve, tan dulce es ...', NULL, NULL, 0, 1, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780197, 'Well duties call, yes? First, the bell... to give you vigor!', NULL, NULL, NULL, NULL, NULL, 'Bueno, los deberes llaman, ¿no? Primero, la campana ... ¡para darte vigor!', NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780198, 'Next, I\'ll place the wheel... to protect you from harm.', NULL, NULL, NULL, NULL, NULL, 'Después, colocaré la rueda... ¡Para protegerte del peligro!', NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780199, 'And finally the candle... to burn those who could thwart you!', NULL, NULL, NULL, NULL, NULL, 'Y finalmente la vela ... ¡para quemar a los que podrían frustrarte!', NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780200, '$N, my duties are complete and I shall now take my leave. Luck to you my $gfellow:lady;, and remember to keep the Bell, Wheel and Candle working!', NULL, NULL, NULL, NULL, NULL, '$N, mis deberes están completos y ahora me iré. ¡Suerte, mi $g compañero : señora;, y recuerde mantener funcionando la Campana, la Rueda y la Vela!', NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780201, 'Who dares steal my precious mount? You will pay for your insolence, mortal!', NULL, NULL, NULL, NULL, NULL, '¿Quién se atreve a robar mi preciosa montura? ¡Pagarás por tu insolencia, mortal!', NULL, NULL, 0, 1, 0, 0, 'DreadsteedRitualpnj14506');
INSERT INTO `script_texts` VALUES (-1780202, 'Xorothian Imp is pulled back to Xoroth!', NULL, NULL, NULL, NULL, NULL, '¡El diablillo xorothiano regresa a Xoroth!', NULL, NULL, 0, 2, 0, 0, 'DreadsteedRitualpnj14482');
INSERT INTO `script_texts` VALUES (-1780203, 'Dread Guard is pulled back to Xoroth!', NULL, NULL, NULL, NULL, NULL, '¡Guardia aterrador regresa a Xoroth!', NULL, NULL, 0, 2, 0, 0, 'DreadsteedRitualpnj14483');
INSERT INTO `script_texts` VALUES (-1000601, 'Sleepy... so sleepy...', NULL, 'Sommeil... tellement sommeil...', NULL, NULL, NULL, 'Somnoliento ... tan somnoliento ...', NULL, NULL, 0, 0, 1, 0, 'lazy peon SAY_SPELL_HIT2');
INSERT INTO `script_texts` VALUES (-1000600, 'Ow! Ok, I\'ll get back to work, $N!', NULL, 'Aie! D\'accord, je retourne au travail, $N!', NULL, NULL, NULL, '¡Ay! Ok, volveré a trabajar, $N!', NULL, NULL, 0, 0, 1, 0, 'lazy peon SAY_SPELL_HIT');
INSERT INTO `script_texts` VALUES (-1047000, 'Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble.', NULL, NULL, NULL, NULL, NULL, '¡Ju, ju! Finalmente hemos conseguido salir de aquí. De todas maneras, las cosas no vienen fáciles. Mantened los ojos abiertos por si tenemos problemas.', NULL, NULL, 0, 0, 0, 0, 'willix SAY_READY');
INSERT INTO `script_texts` VALUES (-1047001, 'Up there is where Charlga Razorflank resides. Blasted old crone.', NULL, NULL, NULL, NULL, NULL, 'Allí es donde reside Charlga Filonavaja. Maldita bruja vieja.', NULL, NULL, 0, 0, 0, 25, 'willix SAY_1');
INSERT INTO `script_texts` VALUES (-1047002, 'There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you!', NULL, NULL, NULL, NULL, NULL, '¡En esta zanja hay una trufa hojazul! ¡Es como oro esperando a ser extraído, créeme!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_2');
INSERT INTO `script_texts` VALUES (-1047003, 'There could be danger around every corner here.', NULL, NULL, NULL, NULL, NULL, 'Aquí acecha el peligro en cada esquina.', NULL, NULL, 0, 0, 0, 0, 'willix SAY_3');
INSERT INTO `script_texts` VALUES (-1047004, 'I don\'t see how these foul animals live in this place... sheesh it smells!', NULL, NULL, NULL, NULL, NULL, 'No puedo entender que estos nauseabundos animales vivan en este lugar... ¡Puaj! ¡Apesta!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_4');
INSERT INTO `script_texts` VALUES (-1047005, 'I think I see a way for us to get out of this big twisted mess of a bramble.', NULL, NULL, NULL, NULL, NULL, 'Creo que veo un modo de salir de este enorme y enredado zarzal.', NULL, NULL, 0, 0, 0, 0, 'willix SAY_5');
INSERT INTO `script_texts` VALUES (-1047006, 'Glad to be out of that wretched trench. Not much nicer up here though!', NULL, NULL, NULL, NULL, NULL, 'Me alegro de haber salido de aquella espantosa trinchera. ¡Aunque aquí arriba tampoco se está mucho mejor!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_6');
INSERT INTO `script_texts` VALUES (-1047007, 'Finally! I\'ll be glad to get out of this place.', NULL, NULL, NULL, NULL, NULL, '¡Por fin! Qué alegría cuando salgamos de este lugar.', NULL, NULL, 0, 0, 0, 0, 'willix SAY_7');
INSERT INTO `script_texts` VALUES (-1047008, 'I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help!', NULL, NULL, NULL, NULL, NULL, 'Creo que descansaré un momento y cogeré un poco de aire antes de regresar a Trinquete. ¡Gracias por tu ayuda!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_END');
INSERT INTO `script_texts` VALUES (-1047009, '$N heading this way fast! To arms!', NULL, NULL, NULL, NULL, NULL, '¡$n viene en esta dirección a gran velocidad! ¡Coged las armas!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1047010, 'Eek! $N coming right at us!', NULL, NULL, NULL, NULL, NULL, '¡Uyy! ¡$n viene directo hacia nosotros!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1047011, 'Egads! $N on me!', NULL, NULL, NULL, NULL, NULL, '¡Pardiez! ¡Tengo a $n sobre mí!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1047012, 'Help! Get this $N off of me!', NULL, NULL, NULL, NULL, NULL, '¡Socorro! ¡Sacadme a $n de encima!', NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES (-1189022, 'The master has fallen! Avenge him my brethren!', NULL, 'Le maître est tombé! Vengez-le mes frères!', NULL, NULL, NULL, '¡El maestro ha caído! ¡Venganlo, hermanos míos!', NULL, NULL, 5834, 1, 0, 0, 'trainee SAY_TRAINEE_SPAWN');
INSERT INTO `script_texts` VALUES (-1090021, 'FIRE IN THE HOLE!', NULL, NULL, NULL, NULL, NULL, '¡FUEGO EN EL ORIFICIO!', NULL, NULL, 0, 1, 0, 0, 'emi shortfuse SAY_BLOW_2');
INSERT INTO `script_texts` VALUES (-1090022, 'Superb! Because of your help, my people stand a chance of re-taking our beloved city. Three cheers to you!', NULL, NULL, NULL, NULL, NULL, '¡Excelente! Gracias a tu ayuda, mi gente tiene una oportunidad de recuperar su amada ciudad. ¡Tres hurras para ti!', NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_FINISH_2');
INSERT INTO `script_texts` VALUES (-1090023, 'We come from below! You can never stop us!', NULL, NULL, NULL, NULL, NULL, '¡Venimos de abajo! ¡Nunca nos podrás detener!', NULL, NULL, 0, 1, 0, 1, 'grubbis SAY_GRUBBIS_SPAWN');
INSERT INTO `script_texts` VALUES (-1090028, '$n attacking! Help!', NULL, NULL, NULL, NULL, NULL, '¡$n me ataca! ¡Socorro!', NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1780204, 'Get me out of here!', NULL, NULL, NULL, NULL, NULL, '¡Sacadme de aquí!', NULL, NULL, 0, 0, 0, 0, 'q2904npc7850');
INSERT INTO `script_texts` VALUES (-1780205, 'Self-destruct sequence activated.', NULL, NULL, NULL, NULL, NULL, 'Secuencia de autodestrucción activada.', NULL, NULL, 0, 1, 0, 0, 'q2904npc7897');
INSERT INTO `script_texts` VALUES (-1780206, 'Anti-escape countermeasures activated. Self-destruct in t-minus 20 seconds...', NULL, NULL, NULL, NULL, NULL, 'Contramedidas anti-escape activadas. Autodestrucción en t-menos 20 segundos ...', NULL, NULL, 0, 0, 0, 0, 'q2904npc7897');
INSERT INTO `script_texts` VALUES (-1780207, 'I see the exit! Hurry, hurry!', NULL, NULL, NULL, NULL, NULL, '¡Veo la salida! ¡Deprisa, deprisa!', NULL, NULL, 0, 0, 0, 0, 'q2904npc7850');
INSERT INTO `script_texts` VALUES (-1780208, 'Thank you for rescuing me! Word of your deed will not go unnoticed. ', NULL, NULL, NULL, NULL, NULL, '¡Gracias por rescatarme! Tus actos no pasarán inadvertidos.', NULL, NULL, 0, 0, 0, 0, 'q2904npc7850');
INSERT INTO `script_texts` VALUES (-1129005, 'All right, stay close. These fiends will jump right out of the shadows at you if you let your guard down.', NULL, NULL, NULL, NULL, NULL, 'Muy bien, mantente cerca. Estos demonios saltarán directamente de las sombras hacia ti si bajas la guardia.', NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_READY');
INSERT INTO `script_texts` VALUES (-1129006, 'Okay, here we go. It\'s going to take about five minutes to shut this thing down through the ritual. Once I start, keep the vermin off of me or it will be the end of us all!', NULL, NULL, NULL, NULL, NULL, 'Bien, aquí vamos. Tomará unos cinco minutos apagar esta cosa a través del ritual. Una vez que empiece, ¡mantén las alimañas fuera de mí o será el fin de todos nosotros!', NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_START_RIT');
INSERT INTO `script_texts` VALUES (-1129007, 'You\'ll rue the day you crossed me, $N', NULL, NULL, NULL, NULL, NULL, 'Lamentarás el día que me cruzaste, $N', NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1129008, 'Incoming $N - look sharp, friends!', NULL, NULL, NULL, NULL, NULL, '$N entrante. ¡Atentos, amigos!', NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1129009, 'Three minutes left -- I can feel the energy starting to build! Keep up the solid defense!', NULL, NULL, NULL, NULL, NULL, 'Quedan tres minutos, ¡puedo sentir que la energía comienza a acumularse! ¡Mantén la defensa sólida!', NULL, NULL, 0, 1, 0, 0, 'belnistrasz SAY_3_MIN');
INSERT INTO `script_texts` VALUES (-1129010, 'Just two minutes to go! We\'re half way there, but don\'t let your guard down!', NULL, NULL, NULL, NULL, NULL, '¡Solo faltan dos minutos! Estamos a mitad de camino, ¡pero no bajes la guardia!', NULL, NULL, 0, 1, 0, 0, 'belnistrasz SAY_2_MIN');
INSERT INTO `script_texts` VALUES (-1129011, 'One more minute! Hold on now, the ritual is about to take hold!', NULL, NULL, NULL, NULL, NULL, '¡Un minuto más! ¡Espera ahora, el ritual está a punto de comenzar!', NULL, NULL, 0, 1, 0, 0, 'belnistrasz SAY_1_MIN');
INSERT INTO `script_texts` VALUES (-1129012, 'That\'s it -- we made it! The ritual is set in motion, and idol fires are about to go out for good! You truly are the heroes I thought you would be!', NULL, NULL, NULL, NULL, NULL, 'Eso es todo, ¡lo logramos! ¡El ritual se pone en marcha y los fuegos de los ídolos están a punto de apagarse para siempre! ¡Realmente son los héroes que pensé que serían!', NULL, NULL, 0, 1, 0, 4, 'belnistrasz SAY_FINISH');
INSERT INTO `script_texts` VALUES (-1000011, 'The Sons of Thaurissan shall watch you perish in the Ring of the Law!', '', 'Les Fils de Thaurissan vous verront périr dans le Cercle de loi !', '', '', '', '¡Los hijos de Thaurissan os verán morir en el Círculo de la Ley!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000010, 'You have been sentenced to death for crimes against the Dark Iron nation!', '', 'Vous subirez la peine de mort pour vos crimes contre la nation Sombrefer !', '', '', '', '¡Se os ha sentenciado a muerte por delitos cometidos contra la nación Hierro Negro!', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1001106, 'Don\'t forget to get my bell out of the chest here. And remember, if do happen to wander off, just ring it and I\'ll find you again.', NULL, NULL, NULL, NULL, NULL, 'No olvides sacar mi campana del arcón. Y recuerda, si me desvío, tócala y te volveré a encontrar.', NULL, NULL, 0, 0, 0, 1, 'shay leafrunner SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001107, 'Are we taking the scenic route?', NULL, NULL, NULL, NULL, NULL, '¿Vamos por la ruta pintoresca?', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_1');
INSERT INTO `script_texts` VALUES (-1001108, 'Oh, what a beautiful flower over there...', NULL, NULL, NULL, NULL, NULL, 'Oh, qué flor más bella esa que está allí...', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_2');
INSERT INTO `script_texts` VALUES (-1001109, 'Are you sure this is the right way? Maybe we should go this way instead...', NULL, NULL, NULL, NULL, NULL, '¿Seguro que vamos por el camino correcto? Quizás debamos mejor ir por aquí...', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_3');
INSERT INTO `script_texts` VALUES (-1001110, 'Hmmm, I wonder what\'s over this way?', NULL, NULL, NULL, NULL, NULL, 'Mmmm, me pregunto qué es lo que hay por este otro camino...', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_4');
INSERT INTO `script_texts` VALUES (-1001111, 'This is quite an adventure!', NULL, NULL, NULL, NULL, NULL, '¡Vaya aventura!', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_1');
INSERT INTO `script_texts` VALUES (-1001112, 'Oh, I wandered off again. I\'m sorry.', NULL, NULL, NULL, NULL, NULL, 'Oh, me alejé de nuevo. Lo siento.', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_2');
INSERT INTO `script_texts` VALUES (-1001113, 'The bell again, such a sweet sound.', NULL, NULL, NULL, NULL, NULL, 'Otra vez la campana, qué sonido más hermoso.', NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_3');
INSERT INTO `script_texts` VALUES (-1001114, '%s begins to wander off.', NULL, NULL, NULL, NULL, NULL, 'empieza a desviarse.', NULL, NULL, 0, 2, 0, 0, 'shay leafrunner EMOTE_WANDER');
INSERT INTO `script_texts` VALUES (-1001115, 'Oh, here you are, Rockbiter! I\'m sorry, I know I\'m not supposed to wander off.', NULL, NULL, NULL, NULL, NULL, '¡Vaya, aquí estás, Muerdepiedras! Lo siento, sé que no debería desviarme de la ruta.', NULL, NULL, 0, 0, 0, 1, 'shay leafrunner SAY_EVENT_COMPLETE_1');
INSERT INTO `script_texts` VALUES (-1001116, 'I\'m so glad yer back Shay. Please, don\'t ever run off like that again! What would I tell yer parents if I lost ya?', NULL, NULL, NULL, NULL, NULL, 'Estoy tan contenta de que hayas respaldado a Shay. ¡Por favor, no vuelvas a salir corriendo así! ¿Qué les diría a tus padres si los perdiera?', NULL, NULL, 0, 0, 0, 1, 'rockbiter SAY_EVENT_COMPLETE_2');
INSERT INTO `script_texts` VALUES (-1000784, 'Thanks $N. Now let\'s get out of here!', NULL, NULL, NULL, NULL, NULL, 'Gracias $N. ¡Ahora salgamos de aquí!', NULL, NULL, 0, 0, 0, 0, 'melizza SAY_MELIZZA_START');
INSERT INTO `script_texts` VALUES (-1000785, 'We made it! Thanks again! I\'m going to run ahead!', NULL, NULL, NULL, NULL, NULL, '¡Lo hicimos! ¡Gracias de nuevo! ¡Voy a correr adelante!', NULL, NULL, 0, 0, 0, 0, 'melizza SAY_MELIZZA_FINISH');
INSERT INTO `script_texts` VALUES (-1000786, 'Hey Hornizz! I\'m back! And there are some people behind me who helped me out of a jam.', NULL, NULL, NULL, NULL, NULL, '¡Hola Hornizz! ¡Volví! Y hay algunas personas detrás de mí que me ayudaron a salir de un apuro.', NULL, NULL, 0, 0, 0, 1, 'melizza SAY_MELIZZA_1');
INSERT INTO `script_texts` VALUES (-1000787, 'We\'re going to have to scratch the Maraudines off our list. Too hard to work with...', NULL, NULL, NULL, NULL, NULL, 'Tendremos que tachar a los Maraudines de nuestra lista. Demasiado difícil trabajar con ...', NULL, NULL, 0, 0, 0, 1, 'melizza SAY_MELIZZA_2');
INSERT INTO `script_texts` VALUES (-1000788, 'Well, I\'m off to the Gelkis. They\'re not as dumb as the Maraudines, but they\'re more reasonable.', NULL, NULL, NULL, NULL, NULL, 'Bueno, me voy a los Gelkis. No son tan tontos como los Maraudines, pero son más razonables.', NULL, NULL, 0, 0, 0, 3, 'melizza SAY_MELIZZA_3');
INSERT INTO `script_texts` VALUES (-1409020, 'You think you\'ve won already? Perhaps you\'ll need another lesson in pain!', NULL, 'Vous croyez avoir deja gagne? Vous avez sans doute besoin que je vous inflige quelques douleurs supplementaires.', NULL, NULL, NULL, '¿Crees que ya has ganado? ¡Pues yo creo que necesitas otra lección de dolor!', NULL, NULL, 0, 1, 0, 0, 'majordomo SAY_LAST_ADD');
INSERT INTO `script_texts` VALUES (-1001159, 'Please, help me to get through this cursed forest, $r.', NULL, NULL, NULL, NULL, NULL, 'Por favor, ayúdame a atravesar este bosque maldito, $r.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001160, 'This creature suffers from the effect of the fel... We must end its misery.', NULL, NULL, NULL, NULL, NULL, 'Esta criatura sufre del efecto del fel ... Debemos acabar con su miseria.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_ATTACK_IRONTREE');
INSERT INTO `script_texts` VALUES (-1001161, 'The corruption of the fel has not left any of the creatures of Felwood untouched, $N. Please, be on your guard.', NULL, NULL, NULL, NULL, NULL, 'La descomposición del inferi no ha dejado intactas a las criaturas de Frondavil, $N. Por favor, permanece en guardia.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_ATTACK_TOXIC_HORROR');
INSERT INTO `script_texts` VALUES (-1001162, 'I sense the taint of corruption upon this $N. Help me detroy it.', NULL, NULL, NULL, NULL, NULL, 'Siento la mancha de corrupción en este $N. Ayúdame a destruirlo.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_EXIT_WOODS');
INSERT INTO `script_texts` VALUES (-1001163, 'That I must fight against my own kind deeply saddens me.', NULL, NULL, NULL, NULL, NULL, 'Tener que luchar contra los míos me entristece profundamente.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_CLEAR_PATH');
INSERT INTO `script_texts` VALUES (-1001164, 'I can sense it now, $N. Ashenvale lies down this path.', NULL, NULL, NULL, NULL, NULL, 'Puedo sentirlo ahora, $n. Vallefresno está por este camino.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_ASHENVALE');
INSERT INTO `script_texts` VALUES (-1001165, 'I feel... something strange...', NULL, NULL, NULL, NULL, NULL, 'Me siento... un poco extraño...', NULL, NULL, 0, 0, 0, 0, 'arei SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1001166, '$N my form has now changed! The true strength of my spirit is returing to me now... The cursed grasp of the forest is leaving me.', NULL, NULL, NULL, NULL, NULL, '$N mi forma ahora ha cambiado! La verdadera fuerza de mi espíritu está regresando a mí ahora ... El dominio maldito del bosque me está abandonando.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_LIFT_CURSE');
INSERT INTO `script_texts` VALUES (-1001167, 'Thank you, $N. Now my spirit will finally be at peace.', NULL, NULL, NULL, NULL, NULL, 'Gracias, $n. Ahora mi espíritu podrá descansar en paz.', NULL, NULL, 0, 0, 0, 0, 'arei SAY_ESCORT_COMPLETE');
INSERT INTO `script_texts` VALUES (-1001148, 'I am ready, $N. Let\'s find my equipment and get out of here. I think I know where it is.', NULL, NULL, NULL, NULL, NULL, 'Estoy listo, $N. Busquemos mi equipo y salgamos de aquí. Creo que sé dónde está.', NULL, NULL, 0, 0, 0, 1, 'captured arko\'narin SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001149, 'Oh my! Look at this... all these candles. I\'m sure they\'re used for some terrible ritual or dark summoning. We best make haste.', NULL, NULL, NULL, NULL, NULL, '¡Oh mi! Mira esto ... todas estas velas. Estoy seguro de que se utilizan para algún ritual terrible o invocación oscura. Es mejor que nos demos prisa.', NULL, NULL, 0, 0, 0, 18, 'captured arko\'narin SAY_FIRST_STOP');
INSERT INTO `script_texts` VALUES (-1001150, 'There! Over there!', NULL, NULL, NULL, NULL, NULL, '¡Allí! ¡Por allí!', NULL, NULL, 0, 0, 0, 25, 'captured arko\'narin SAY_SECOND_STOP');
INSERT INTO `script_texts` VALUES (-1001151, 'You will not stop me from escaping here, $N!', NULL, NULL, NULL, NULL, NULL, '¡No evitarás que me escape de aquí, $N!', NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1001152, 'All I need now is a golden lasso.', NULL, NULL, NULL, NULL, NULL, 'Lo único que necesito es un lazo de oro.', NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_EQUIPMENT');
INSERT INTO `script_texts` VALUES (-1001153, 'DIE DEMON DOGS!', NULL, NULL, NULL, NULL, NULL, '¡MUERE PERROS DEMONIOS!', NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_ESCAPE');
INSERT INTO `script_texts` VALUES (-1001154, 'Ah! Fresh air at last! I never thought I\'d see the day.', NULL, NULL, NULL, NULL, NULL, '¡Ah! ¡Aire fresco por fin! Nunca pensé que vería el día.', NULL, NULL, 0, 0, 0, 4, 'captured arko\'narin SAY_FRESH_AIR');
INSERT INTO `script_texts` VALUES (-1001155, 'BETRAYER!', NULL, NULL, NULL, NULL, NULL, '¡TRAIDOR!', NULL, NULL, 0, 1, 0, 0, 'spirit of trey lightforge SAY_BETRAYER');
INSERT INTO `script_texts` VALUES (-1001156, 'What was that?! Trey? TREY?', NULL, NULL, NULL, NULL, NULL, '¡¿Qué fue eso?! Trey? TREY?', NULL, NULL, 0, 0, 0, 22, 'captured arko\'narin SAY_TREY');
INSERT INTO `script_texts` VALUES (-1001157, 'You kept me in the cell for too long, monster!', NULL, NULL, NULL, NULL, NULL, '¡Me mantuviste en la celda demasiado tiempo, monstruo!', NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_ATTACK_TREY');
INSERT INTO `script_texts` VALUES (-1001158, 'No! My friend... what\'s happened? This is all my fault...', NULL, NULL, NULL, NULL, NULL, '¡Oh no! ¿Qué ha pasado? Es todo culpa mía...', NULL, NULL, 0, 0, 0, 18, 'captured arko\'narin SAY_ESCORT_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000873, 'I know the way, insect. There is no need to prod me as if I were cattle.', NULL, NULL, NULL, NULL, NULL, 'Conozco el camino, gusano. No tienes que empujarme como si fuera una res.', NULL, NULL, 0, 0, 0, 1, 'grark SAY_START');
INSERT INTO `script_texts` VALUES (-1000874, 'Surely you do not think that you will get away with this incursion. They will come for me and you shall pay for your insolence.', NULL, NULL, NULL, NULL, NULL, 'No pensaréis que os vais a salir con la vuestra con esta incursión. Vendrán a buscarme y pagaréis por vuestra insolencia.', NULL, NULL, 0, 0, 0, 1, 'grark SAY_PAY');
INSERT INTO `script_texts` VALUES (-1000875, 'RUN THEM THROUGH BROTHERS!', NULL, NULL, NULL, NULL, NULL, '¡ATRAVESADLOS CON LAS ESPADAS, HERMANOS!', NULL, NULL, 0, 0, 0, 5, 'grark SAY_FIRST_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000876, 'I doubt you will be so lucky the next time you encounter my brethren.', NULL, NULL, NULL, NULL, NULL, 'Dudo que tengas tanta suerte la próxima vez que te encuentres con mis hermanos.', NULL, NULL, 0, 0, 0, 1, 'grark SAY_FIRST_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000877, 'They come for you, fool!', NULL, NULL, NULL, NULL, NULL, '¡Vienen a por ti, idiota!', NULL, NULL, 0, 0, 0, 5, 'grark SAY_SEC_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000878, 'What do you think you accomplish from this, fool? Even now, the Blackrock armies make preparations to destroy your world.', NULL, NULL, NULL, NULL, NULL, '¿Qué crees que vas a lograr con esto, idiota? Incluso ahora, los ejércitos Roca Negra están preparándose para destruir tu mundo.', NULL, NULL, 0, 0, 0, 1, 'grark SAY_SEC_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000879, 'On darkest wing they fly. Prepare to meet your end!', NULL, NULL, NULL, NULL, NULL, 'Su vuelo es el más oscuro. ¡Prepárate para tu final!', NULL, NULL, 0, 0, 0, 5, 'grark SAY_THIRD_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000880, 'The worst is yet to come!', NULL, NULL, NULL, NULL, NULL, '¡Lo peor está por venir!', NULL, NULL, 0, 0, 0, 1, 'grark SAY_THIRD_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000881, '%s laughs.', NULL, NULL, NULL, NULL, NULL, 'ríe.', NULL, NULL, 0, 2, 0, 11, 'grark EMOTE_LAUGH');
INSERT INTO `script_texts` VALUES (-1000882, 'Time to make your final stand, Insect.', NULL, NULL, NULL, NULL, NULL, 'Es hora de hacer tu última aparición, miserable.', NULL, NULL, 0, 0, 0, 0, 'grark SAY_LAST_STAND');
INSERT INTO `script_texts` VALUES (-1000883, 'Kneel, Grark', NULL, NULL, NULL, NULL, NULL, 'Arrodíllate, Grark', NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_1');
INSERT INTO `script_texts` VALUES (-1000884, 'Grark Lorkrub, you have been charged and found guilty of treason against Horde. How you plead is unimportant. High Executioner Nuzrak, step forward.', NULL, NULL, NULL, NULL, NULL, 'Grark Lorkrub, ha sido acusado y declarado culpable de traición a la Horda. La forma en que suplica no es importante. Sumo verdugo Nuzrak, da un paso adelante.', NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_2');
INSERT INTO `script_texts` VALUES (-1000885, '%s raises his massive axe over Grark.', NULL, NULL, NULL, NULL, NULL, 'eleva su hacha gigantesca sobre Grark.', NULL, NULL, 0, 2, 0, 27, 'nuzark EMOTE_RAISE_AXE');
INSERT INTO `script_texts` VALUES (-1000886, '%s raises his hand and then lowers it.', NULL, NULL, NULL, NULL, NULL, 'levanta la mano y luego la baja.', NULL, NULL, 0, 2, 0, 0, 'lexlort EMOTE_LOWER_HAND');
INSERT INTO `script_texts` VALUES (-1000887, 'End him...', NULL, NULL, NULL, NULL, NULL, '¡Acaba con él!', NULL, NULL, 0, 0, 0, 0, 'lexlort SAY_LEXLORT_3');
INSERT INTO `script_texts` VALUES (-1000888, 'You, soldier, report back to Kargath at once!', NULL, NULL, NULL, NULL, NULL, '¡Tú, Soldado, preséntate ante Kargath de inmediato!', NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_4');
INSERT INTO `script_texts` VALUES (-1000889, '%s submits.', NULL, NULL, NULL, NULL, NULL, 'se somete.', NULL, NULL, 0, 2, 0, 0, 'grark EMOTE_SUBMIT');
INSERT INTO `script_texts` VALUES (-1000890, 'You have come to play? Then let us play!', NULL, NULL, NULL, NULL, NULL, '¿Has venido a jugar? ¡Juguemos, pues!', NULL, NULL, 0, 0, 0, 0, 'grark SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1780211, 'Beware, $N! Look to the west!', NULL, NULL, NULL, NULL, NULL, '¡Cuidado, $N! ¡Mira hacia el oeste!', NULL, NULL, 0, 0, 1, 0, 'q4021npc3389 BEWARE');
INSERT INTO `script_texts` VALUES (-1780212, 'A defender has fallen!', NULL, NULL, NULL, NULL, NULL, '¡Ha caído un defensor!', NULL, NULL, 0, 0, 1, 0, 'q4021npc3389 DEFENDER_FALLLEN');
INSERT INTO `script_texts` VALUES (-1780213, 'Kolkar Invader charges!', NULL, NULL, NULL, NULL, NULL, '¡El invasor Kolkar carga!', NULL, NULL, 0, 2, 0, 0, 'q4021npc9524 CHARGE');
INSERT INTO `script_texts` VALUES (-1780214, 'Half way there...', NULL, NULL, NULL, NULL, NULL, 'Estás a medio camino...', NULL, NULL, 0, 1, 1, 0, 'q4021npc9990 HALF_WAY');
INSERT INTO `script_texts` VALUES (-1780215, 'Defend the bunkers!', NULL, NULL, NULL, NULL, NULL, '¡Defended los búnkeres!', NULL, NULL, 0, 0, 1, 0, 'q4021npc9458 DEFEND');
INSERT INTO `script_texts` VALUES (-1780216, 'Our foes will fall!', NULL, NULL, NULL, NULL, NULL, '¡Nuestros enemigos caerán!', NULL, NULL, 0, 0, 1, 0, 'q4021npc9458 FOES');
INSERT INTO `script_texts` VALUES (-1780217, 'For the Horde!', NULL, NULL, NULL, NULL, NULL, '¡Por la Horda!', NULL, NULL, 0, 0, 1, 0, 'q4021npc9458 HORDE');
INSERT INTO `script_texts` VALUES (-1780218, 'The Kolkar are the strongest!', NULL, NULL, NULL, NULL, NULL, '¡Los Kolkar son los más fuertes!', NULL, NULL, 0, 1, 0, 0, 'q4021npc9456 KOLKAR_STRONGEST');
INSERT INTO `script_texts` VALUES (-1780219, 'The Kolkar invaders are retreating!', NULL, NULL, NULL, NULL, NULL, '¡Los invasores Kolkar se retiran!', NULL, NULL, 0, 1, 1, 0, 'q4021npc3389 RETREATING');
INSERT INTO `script_texts` VALUES (-1108999, 'It is true. The wheels of doom now turn, inevitably, toward the true return of Hakkar!', NULL, 'Il est vrai. La roue du destin tournent désormais , inévitablement , vers le vrai retour d\'Hakkar !', NULL, NULL, NULL, 'Es verdad. ¡Las ruedas de la fatalidad ahora giran, inevitablemente, hacia el verdadero regreso de Hakkar!', NULL, NULL, 0, 0, 0, 0, 'Yehkinya event quest');
INSERT INTO `script_texts` VALUES (-1108998, 'Blood and souls for Hakkar! HAHAHAH!', NULL, 'Du sang et des âmes pour Hakkar ! HAHAHAH !', NULL, NULL, NULL, '¡Sangre y almas para Hakkar! ¡JAJAJA!', NULL, NULL, 0, 0, 0, 0, 'Yehkinya event quest');
INSERT INTO `script_texts` VALUES (-1000650, '%s is immobilized.', NULL, NULL, NULL, NULL, NULL, 'está $ginmovilizado:inmovilizada;.', NULL, NULL, 0, 2, 0, 0, 'npc_solenor EMOTE_IMMOBILIZED');
INSERT INTO `script_texts` VALUES (-1000651, '%s is stricken by a virulent poison.', NULL, NULL, NULL, NULL, NULL, 'es $gabatido:abatida; por un veneno virulento.', NULL, NULL, 0, 2, 0, 0, 'npc_artorius EMOTE_POISON');
INSERT INTO `script_texts` VALUES (-1000652, '%s is silenced by the venemous sting.', NULL, NULL, NULL, NULL, NULL, '%s es silenciado por la picadura venenosa.', NULL, NULL, 0, 2, 0, 0, 'npc_simone EMOTE_SILENCE');
INSERT INTO `script_texts` VALUES (-1289010, 'You dare interfere with this being\'s testing? The battle must be fought alone! You shall all pay for this interference!', NULL, NULL, NULL, NULL, NULL, '¿Te atreves a interferir con las pruebas de este ser? ¡La batalla debe librarse solo! ¡Todos pagarán por esta interferencia!', NULL, NULL, 0, 0, 0, 0, 'the_cleaner SAY_CLEANER_AGGRO');
INSERT INTO `script_texts` VALUES (-1532033, 'This I promise... This I vow...', NULL, NULL, NULL, NULL, NULL, 'Lo prometo... Lo juro...', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532032, ' Your death will not have been in vain, Taelan. A new Order is born on this day... an Order which will dedicate itself to extinguising the evil that plagues this world. An evil that cannot hide behind politics and pleasantries.', NULL, NULL, NULL, NULL, NULL, 'Tu muerte no habrá sido en vano, Taelan. Una nueva Orden nace en este día ... una Orden que se dedicará a extinguir el mal que asola este mundo. Un mal que no puede esconderse detrás de la política y las bromas.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532031, 'Too long have I sat idle, gripped in this haze... this malaise, lamenting what could have been... what should have been.', NULL, NULL, NULL, NULL, NULL, 'He estado demasiado tiempo sin hacer nada, atado a esta bruma... a este malestar, lamentando lo que podría haber sido... lo que debiera haber sido.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532030, 'Look what they did to my boy.', NULL, NULL, NULL, NULL, NULL, 'Mira lo que hicieron a mi chico.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532029, 'A thousand more like him exist. Ten thousand. Should one fall, another will rise to take the seat of power.', NULL, NULL, NULL, NULL, NULL, 'Hay mil más como él. Diez mil. Si cayera uno, otros se levantarían para tomar la sede del poder.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532028, 'Then come, hermit!', NULL, NULL, NULL, NULL, NULL, '¡Entonces ven, ermitaño!', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532027, 'Face me, coward. Face the faith and strength that you once embodied.', NULL, NULL, NULL, NULL, NULL, 'Enfréntate a mí, cobarde. Enfréntate a la fe y a la fuerza que un día encarnaste.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532026, 'May your soul burn in anguish, Isillien! Light give me strength to battle this fiend.', NULL, NULL, NULL, NULL, NULL, '¡Que tu alma arda de angustia, Isillien! ¡Luz, dame fuerza para enfrentarme a este demonio!', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532025, 'Tragic. The elder Fordring lives on... You are too late, old man. Retreat back to your cave, hermit, unless you wish to join your son in the Twisting Nether.', NULL, NULL, NULL, NULL, NULL, 'Trágico. El mayor de los Fordring sigue con vida... Llegas demasiado tarde, anciano. Regresa a tu cueva, ermitaño, si no quieres reunirte con tu hijo en El Vacío Abisal.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532024, 'What have you done, Isillien? You once fought with honor, for the good of our people... and now... you have murdered my boy...', NULL, NULL, NULL, NULL, NULL, '¿Qué has hecho, Isillien? Una vez luchaste con honor, por el bien de nuestra gente ... y ahora ... has asesinado a mi hijo ...', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532023, 'Did you really believe that you could defeat me? Your friends are soon to join you, Taelan.', NULL, NULL, NULL, NULL, NULL, '¿De verdad creías que podrías vencerme? Tus amigos pronto se reunirán contigo, Taelan.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532022, 'Enough!', NULL, NULL, NULL, NULL, NULL, '¡Ya es suficiente!', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532021, 'The end is now, Fordring.', NULL, NULL, NULL, NULL, NULL, 'Este es el fin, Fordring.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532020, 'Grand Inquisitor Isillien calls for his guardsman.', NULL, NULL, NULL, NULL, NULL, 'El gran inquisidor Isillien llama a su guardia.', NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532019, 'The Grand Crusader has charged me with destroying you and your newfound friends, Taelan, but know this: I do this for pleasure, not of obligation or duty.', NULL, NULL, NULL, NULL, NULL, 'El Gran Cruzado me ha encargado destruirte a ti y a tus nuevos amigos, Taelan, pero has de saber algo: lo hago por placer, no por obligación ni por deber.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532018, 'It is as they say; Like father, like son. You are as weak of will as Tirion... perhaps more so. I can only hope my assassins finally succeeded in ending his pitiful life.', NULL, NULL, NULL, NULL, NULL, 'Es como dicen; De tal palo tal astilla. Eres tan débil de voluntad como Tirion ... quizás más. Solo puedo esperar que mis asesinos finalmente logren terminar con su lamentable vida.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532017, 'You disappoint me, Taelan. I had plans for you... grand plans. Alas, it was only a matter of time before your filthy bloodline would catch up with you.', NULL, NULL, NULL, NULL, NULL, 'Me has decepcionado, Taelan. Tenía planes para ti... grandes planes. Desgraciadamente era solo una cuestión de tiempo que tu sucia estirpe te recuperase.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532016, 'You will not make it to the forest\'s edge, Fordring.', NULL, NULL, NULL, NULL, NULL, 'No lo harás en el extremo del bosque, Fordring.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532015, 'This is not your fight, stranger. Protect yourself from the attacks of the Crimson Elite. I shall battle the Grand Inquisitor.', NULL, NULL, NULL, NULL, NULL, 'Esta no es tu lucha, $gforastero:forastera;. Protégete de los ataques de la Élite carmesí. Yo lucharé con el Gran inquisidor.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532014, 'Isillien!', NULL, NULL, NULL, NULL, NULL, '¡Isillien!', NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532013, 'It\'s not much further. The main road is just up ahead', NULL, NULL, NULL, NULL, NULL, 'No es mucho más. La carretera principal está más adelante.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532012, 'Highlord Taelan Fordring calls for his mount.', NULL, NULL, NULL, NULL, NULL, 'El alto señor Taelan Fordring llama a su montura.', NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532011, 'Halt.', NULL, NULL, NULL, NULL, NULL, 'Alto.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532010, 'Remove your disguise, lest you feel the bite of my blade when the fury has taken control.', NULL, NULL, NULL, NULL, NULL, 'Quítate el disfraz, no vaya a ser que sientas el mordisco de mi espada cuando la furia se haga con el control.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532009, 'I will lead us through Hearthglen to the forest\'s edge. From there, you will take me to my father.', NULL, NULL, NULL, NULL, NULL, 'Nos guiaré a través de Cañada Hogar hasta el Borde del Bosque. De ahí me llevarás con mi padre.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532008, 'NOOOOOOOOOOOO!', NULL, NULL, NULL, NULL, NULL, '¡NOOOOOOOOOOOO!', NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532007, 'There is no escape for you. The Crusade shall destroy all who carry the Scourge\'s taint.', NULL, NULL, NULL, NULL, NULL, 'No hay escapatoria para ti. La Cruzada destruirá a todos los que portan la mancha de la Plaga.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532006, 'You carry the taint of the Scourge. Prepare to enter the twisting Nether!', NULL, NULL, NULL, NULL, NULL, 'Llevas la mancha de la Plaga. ¡Prepárate para entrar en el retorcido Nether!', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532005, 'The Scarlet crusade shall smite the wicked, and drive evil from these lands!', NULL, NULL, NULL, NULL, NULL, '¡La cruzada escarlata golpeará a los malvados y echará al mal de estas tierras!', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532004, 'Something is wrong with the Highlord. Do something!', NULL, NULL, NULL, NULL, NULL, 'Algo va mal con el Alto Señor. ¡Haz algo!', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532003, 'Hey, what\'s going on over there? Sir, are you alright? Sir??', NULL, NULL, NULL, NULL, NULL, 'Eh, ¿qué está pasando? Señor, ¿está bien? ¿¿Señor??', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532002, 'Highlord Fordring, are you alright?', NULL, NULL, NULL, NULL, NULL, 'Alto Señor Fordring, ¿se encuentra bien?', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532001, 'Sir?', NULL, NULL, NULL, NULL, NULL, '¡Dí, señor?', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532000, 'What the...', NULL, NULL, NULL, NULL, NULL, 'Qué demo...', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1469037, 'Get up, little red wyrm...and destroy them!', NULL, NULL, NULL, NULL, NULL, '¡Levántate, pequeño wyrm rojo ... y destrúyelos!', NULL, NULL, 0, 1, 0, 1, 'victor_nefarius SAY_NEFARIUS_CORRUPT_2');
INSERT INTO `script_texts` VALUES (-1388004, '%s is devoured!', NULL, NULL, NULL, NULL, NULL, 'es $gdevorado:devorada;!', NULL, NULL, 0, 2, 0, 0, 'trio EMOTE_DEVOUR');
INSERT INTO `script_texts` VALUES (-1531015, 'There will be pain...', NULL, NULL, NULL, NULL, NULL, 'Habrá sufrimiento...', NULL, NULL, 0, 1, 0, 0, 'veklor SAY_INTRO_3');
INSERT INTO `script_texts` VALUES (-1531016, 'Oh so much pain...', NULL, NULL, NULL, NULL, NULL, 'Mucho sufrimiento...', NULL, NULL, 0, 1, 0, 0, 'veknilash SAY_INTRO_4');
INSERT INTO `script_texts` VALUES (-1531017, 'Come, little ones.', NULL, NULL, NULL, NULL, NULL, 'Vengan, pequeños.', NULL, NULL, 0, 1, 0, 0, 'veklor SAY_INTRO_5');
INSERT INTO `script_texts` VALUES (-1531018, 'The feast of souls begins now...', NULL, NULL, NULL, NULL, NULL, 'Comienza el festín de almas...', NULL, NULL, 0, 1, 0, 0, 'veknilash SAY_INTRO_6');
INSERT INTO `script_texts` VALUES (-1531019, 'It\'s too late to turn away.', NULL, NULL, NULL, NULL, NULL, 'Es demasiado tarde para alejarse.', NULL, NULL, 8623, 1, 0, 0, 'veklor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1531020, 'Prepare to embrace oblivion!', NULL, NULL, NULL, NULL, NULL, '¡Prepárate para abrazar el olvido!', NULL, NULL, 8626, 1, 0, 0, 'veklor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1531021, 'Like a fly to the web.', NULL, NULL, NULL, NULL, NULL, 'Como una mosca en la red.', NULL, NULL, 8624, 1, 0, 0, 'veklor SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1388101, '%s shares his powers with his brethren.', NULL, NULL, NULL, NULL, NULL, 'comparte sus poderes con sus hermanos.', NULL, NULL, 0, 2, 0, 0, 'aq40 EMOTE_SHARE');
INSERT INTO `script_texts` VALUES (-1230069, 'Get away from those kegs!', NULL, NULL, NULL, NULL, NULL, '¡Apártate de esos barriles!', NULL, NULL, 0, 1, 0, 0, 'Hurley Blackbreath YELL_HURLEY_SPAWN');
INSERT INTO `script_texts` VALUES (-1230070, 'You\'ll pay for that!', NULL, NULL, NULL, NULL, NULL, '¡Pagarás por eso!', NULL, NULL, 0, 0, 0, 0, 'Hurley Blackbreath SAY_HURLEY_AGGRO');
INSERT INTO `script_texts` VALUES (-1230057, 'That\'s it! You\'re going down!', NULL, NULL, NULL, NULL, NULL, '¡Eso es! ¡Vas hacia abajo!', NULL, NULL, 0, 1, 0, 0, 'Plugger Spazzring YELL_AGRRO_1');
INSERT INTO `script_texts` VALUES (-1230058, 'That\'s it! No more beer until this mess is sorted out!', NULL, NULL, NULL, NULL, NULL, '¡Eso es! ¡No más cerveza hasta que se solucione este lío!', NULL, NULL, 0, 1, 0, 0, 'Plugger Spazzring YELL_AGRRO_2');
INSERT INTO `script_texts` VALUES (-1230059, 'Hey, my pockets were picked!', NULL, NULL, NULL, NULL, NULL, '¡Eh, me han robado la cartera!', NULL, NULL, 0, 1, 0, 0, 'Plugger Spazzring YELL_PICKPOCKETED');
INSERT INTO `script_texts` VALUES (-1230060, 'Ah, hits the spot!', NULL, NULL, NULL, NULL, NULL, '¡Ah, ha dado en el blanco!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_GOT_BEER');
INSERT INTO `script_texts` VALUES (-1230061, 'I want more ale! Give me more ale!', NULL, NULL, NULL, NULL, NULL, '¡Quiero más cerveza! ¡Dame más cerveza!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_MORE_BEER');
INSERT INTO `script_texts` VALUES (-1230062, 'That one\'s empty!!', NULL, NULL, NULL, NULL, NULL, '¡¡Está vacío!!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_BARREL_1');
INSERT INTO `script_texts` VALUES (-1230063, 'Ah, empty again!', NULL, NULL, NULL, NULL, NULL, '¡Ah, vacío de nuevo!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_BARREL_2');
INSERT INTO `script_texts` VALUES (-1230064, 'ALE!', NULL, NULL, NULL, NULL, NULL, '¡CERVEZA!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_BARREL_3');
INSERT INTO `script_texts` VALUES (-1230056, 'No stealing the goods!', NULL, NULL, NULL, NULL, NULL, '¡No robar cosas!', NULL, NULL, 0, 1, 0, 0, 'Plugger Spazzring YELL_STOLEN_3');
INSERT INTO `script_texts` VALUES (-1230055, 'Hey! Get away from that!', NULL, NULL, NULL, NULL, NULL, '¡Oye! ¡Aléjate de eso!', NULL, NULL, 0, 1, 0, 0, 'Plugger Spazzring YELL_STOLEN_2');
INSERT INTO `script_texts` VALUES (-1230054, 'What are you doing over there?', NULL, NULL, NULL, NULL, NULL, '¿Qué estáis haciendo ahí?', NULL, NULL, 0, 1, 0, 0, 'Plugger Spazzring YELL_STOLEN_1');
INSERT INTO `script_texts` VALUES (-1230065, 'Try the boar! It\'s my new recipe!', NULL, NULL, NULL, NULL, NULL, '¡Prueba el jabalí! ¡Es mi nueva receta!', NULL, NULL, 0, 0, 0, 0, 'Plugger Spazzring SAY_OOC_1');
INSERT INTO `script_texts` VALUES (-1230066, 'Enjoy! You won\'t find better ale anywhere!', NULL, NULL, NULL, NULL, NULL, '¡Disfrutala! ¡No encontrarás mejor cerveza en ningún lado!', NULL, NULL, 0, 0, 0, 0, 'Plugger Spazzring SAY_OOC_2');
INSERT INTO `script_texts` VALUES (-1230067, 'Drink up! There\'s more where that came from!', NULL, NULL, NULL, NULL, NULL, '¡Bebelo todo! ¡Hay más de donde vino eso!', NULL, NULL, 0, 0, 0, 0, 'Plugger Spazzring SAY_OOC_3');
INSERT INTO `script_texts` VALUES (-1230068, 'Have you tried the Dark Iron Ale? It\'s the best!', NULL, NULL, NULL, NULL, NULL, '¿Has probado la cerveza Ale de Hierro Oscuro? ¡Es el mejor!', NULL, NULL, 0, 0, 0, 0, 'Plugger Spazzring SAY_OOC_4');
INSERT INTO `script_texts` VALUES (-1230041, 'Violence! Property damage! None shall pass!!', NULL, NULL, NULL, NULL, NULL, '¡Violencia! ¡Daño a la propiedad! ¡¡Ninguno pasará!!', NULL, NULL, 0, 1, 0, 0, 'phalanx SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1230048, 'Your efforts are utterly pointless, fools! You will never be able to defeat me!', NULL, NULL, NULL, NULL, NULL, '¡Tus esfuerzos son completamente inútiles, tontos! ¡Nunca podrás vencerme!', NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_1');
INSERT INTO `script_texts` VALUES (-1230049, 'They were just getting in the way anyways.', NULL, NULL, NULL, NULL, NULL, 'De todas manera, interferían con mis propósitos.', NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_2');
INSERT INTO `script_texts` VALUES (-1230050, 'Is that the best you can do? Do you really expect that you could defeat someone as awe inspiring as me?', NULL, NULL, NULL, NULL, NULL, '¿Es lo mejor que puedes hacer? ¿De verdad esperas poder derrotar a alguien tan inspirador como yo?', NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_3');
INSERT INTO `script_texts` VALUES (-1230051, 'Ha! You can\'t even begin to imagine the futility of your efforts.', NULL, NULL, NULL, NULL, NULL, '¡Ja! No podéis siquiera imaginar la futilidad de vuestros esfuerzos.', NULL, NULL, 0, 1, 0, 0, 'Emperor Thaurissan YELL_SENATOR_4');
INSERT INTO `script_texts` VALUES (-1230042, 'You\'ll pay for that!', NULL, NULL, NULL, NULL, NULL, '¡Pagarás por eso!', NULL, NULL, 0, 0, 0, 0, 'Grim Patron SAY_PISSED_PATRON_1');
INSERT INTO `script_texts` VALUES (-1230040, 'No!', NULL, NULL, NULL, NULL, NULL, '¡No!', NULL, NULL, 0, 0, 0, 0, 'Grim Patron SAY_PISSED_PATRON_2');
INSERT INTO `script_texts` VALUES (-1230044, 'Oh! Now I\'m pissed!', NULL, NULL, NULL, NULL, NULL, '¡Oh! ¡Ahora estoy cabreado!', NULL, NULL, 0, 0, 0, 0, 'Grim Patron SAY_PISSED_PATRON_3');
INSERT INTO `script_texts` VALUES (-1230045, 'I\'m out of here!', NULL, NULL, NULL, NULL, NULL, '¡Me voy de aquí!', NULL, NULL, 0, 0, 0, 0, 'rocknot SAY_ROCKNOT_DESPAWN');
INSERT INTO `script_texts` VALUES (-1230046, 'HALT! There were...reports...of a...disturbance.', NULL, NULL, NULL, NULL, NULL, '¡ALTO! Hubo ... informes ... de un ... disturbio.', NULL, NULL, 0, 1, 0, 0, 'Fireguard Destroyer YELL_PATROL_1');
INSERT INTO `script_texts` VALUES (-1230047, 'We...are...investigating.', NULL, NULL, NULL, NULL, NULL, 'Estamos... investigando.', NULL, NULL, 0, 1, 0, 0, 'Fireguard Destroyer YELL_PATROL_2');
INSERT INTO `script_texts` VALUES (-1230071, 'Hey, Rocknot!', NULL, NULL, NULL, NULL, NULL, '¡Oye, Sinroca!', NULL, NULL, 0, 0, 0, 0, 'Nagmara SAY_NAGMARA_1');
INSERT INTO `script_texts` VALUES (-1230072, 'Let\'s go, honey.', NULL, NULL, NULL, NULL, NULL, 'Vamos, amor mío.', NULL, NULL, 0, 0, 0, 0, 'Nagmara SAY_NAGMARA_2');
INSERT INTO `script_texts` VALUES (-1230073, '%s kisses her lover.', NULL, NULL, NULL, NULL, NULL, 'besa a su amante.', NULL, NULL, 0, 2, 0, 0, 'Nagmara TEXTEMOTE_NAGMARA');
INSERT INTO `script_texts` VALUES (-1230074, '%s kisses Mistress Nagmara', NULL, NULL, NULL, NULL, NULL, 'besa a la maestra Nagmara.', NULL, NULL, 0, 2, 0, 0, 'Rocknot TEXTEMOTE_ROCKNOT');
INSERT INTO `script_texts` VALUES (-1230043, 'You can\'t hide from us. Prepare to burn!', NULL, NULL, NULL, NULL, NULL, 'No puedes esconderte de nosotros. ¡Prepárate para arder!', NULL, NULL, 0, 1, 0, 0, 'anvilrage guardsman SAY_GUARD_AGGRO');
INSERT INTO `script_texts` VALUES (-1900040, '%s lets out a deep roar, alerting nearby allies and becoming enraged!', NULL, NULL, NULL, NULL, NULL, 'deja salir un gruñido profundo que alerta a los aliados cercanos y se enfurece.', NULL, NULL, 0, 2, 0, 0, 'Dire Maul Guards');
INSERT INTO `script_texts` VALUES (-1900013, 'Yar, he\'s dead all right. That makes you da new king.. well, all of you! Gordok is yours now, boss! You should talk to me so you can learn everything there is about being da king! I was... is his assistant! Yeah, that\'s why I\'m called da crafty one!', NULL, NULL, NULL, NULL, NULL, 'Sí, está muerto. Eso los convierte en un nuevo rey ... bueno, ¡todos ustedes! ¡Gordok es tuyo ahora, jefe! ¡Deberías hablar conmigo para que puedas aprender todo sobre ser un rey! Yo era ... es su asistente! ¡Sí, por eso me llaman el astuto!', NULL, NULL, 0, 0, 0, 0, 'SMizzle the Crafty AY_KILL_KING_2');
INSERT INTO `script_texts` VALUES (-1900012, 'OH NOES! Da king is dead! Uh... hail to da new king! Yeah!', NULL, NULL, NULL, NULL, NULL, '¡OH NOES! ¡Da King está muerto! Uh ... ¡salve al nuevo rey! ¡Si!', NULL, NULL, 0, 6, 0, 0, 'Mizzle the Crafty SAY_KILL_KING_1');
INSERT INTO `script_texts` VALUES (-1900011, 'The king is dead - OH NOES! Summon Mizzle da Crafty! He knows what to do next!', NULL, NULL, NULL, NULL, NULL, 'El rey ha muerto - ¡OH NO! ¡Invoca a Mizzle el Astuto! ¡Él sabe qué hacer a continuación!', NULL, NULL, 0, 6, 0, 0, 'Cho\'Rush the Observer SAY_KILL_KING');
INSERT INTO `script_texts` VALUES (-1900047, 'OK Fengus, where you at?! You come call me a gnoll lover while I give you da hammer upside da head!', NULL, NULL, NULL, NULL, NULL, 'OK Fengus, ¿dónde estás? ¡Ven a llamarme amante de los gnolls mientras te doy un martillo en la cabeza!', NULL, NULL, 0, 1, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900046, 'Hey, who Fengus callin\' a gnoll lover?! Take da prisoners to da king; you smart to bring them with their weapons and show da king that they a threat. I\'ll go see if Fengus talk smack when I give him da beatdown! HAR!', NULL, NULL, NULL, NULL, NULL, 'Oye, ¿a quién llama Fengus amante de los gnolls? Lleva a los prisioneros al rey; es inteligente traerlos con sus armas y mostrarle al rey que son una amenaza. ¡Iré a ver si Fengus habla mal cuando le dé una paliza! ¡HAR!', NULL, NULL, 0, 1, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900039, '%s begins to retaliate all attacks against him!', NULL, NULL, NULL, NULL, NULL, 'empieza a responder a todos los ataques!', NULL, NULL, 0, 2, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900038, 'No one get past me and threaten da king!  Ungh, take it!!', NULL, NULL, NULL, NULL, NULL, '¡Nadie pasar por mí y amenazar $gal rey:a la reina;! ¡Agh, toma!', NULL, NULL, 0, 0, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900037, 'Help me crush these punys!', NULL, NULL, NULL, NULL, NULL, '¡Ayúdame a aplastar a estos impresentables!', NULL, NULL, 0, 1, 0, 0, '14325');
INSERT INTO `script_texts` VALUES (-1900045, 'You no challenge me, scrubs! I\'m da king now, and I stay king FOREVER!!!', NULL, NULL, NULL, NULL, NULL, '¡No me desafíes, matorrales! ¡Soy el rey ahora, y seguiré siendo rey PARA SIEMPRE!', NULL, NULL, 0, 0, 0, 0, '11501');
INSERT INTO `script_texts` VALUES (-1289000, 'School is in session!', NULL, NULL, NULL, NULL, NULL, '¡Estamos dando clase!', NULL, NULL, 0, 1, 0, 0, 'gandling SAY_GANDLING_SPAWN');
INSERT INTO `script_texts` VALUES (-1289001, 'Class...dismissed.', NULL, NULL, NULL, NULL, NULL, 'Clase ... despedida.', NULL, NULL, 0, 1, 0, 0, 'gandling SAY_GANDLING_DEATH');
INSERT INTO `script_texts` VALUES (-1000590, 'I can sense the SHADOW on your hearts. There can be no rest for the wicked!', NULL, NULL, NULL, NULL, NULL, 'Puedo sentir la SOMBRA en vuestros corazones. ¡No puede haber descanso para los malos!', NULL, NULL, 0, 6, 0, 0, 'lethon SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1000591, 'Your wicked souls shall feed my power!', NULL, NULL, NULL, NULL, NULL, '¡Vuestras malvadas almas alimentan mi poder!', NULL, NULL, 0, 6, 0, 0, 'lethon SAY_CASTDRAW');
INSERT INTO `script_texts` VALUES (-1230039, 'Finally!! I can leave this dump.', NULL, NULL, NULL, NULL, NULL, '¡Por fin! Puedo irme de este vertedero.', NULL, 'Наконец-то! Теперь я могу покинуть эту дыру.', 0, 0, 0, 0, 'tobias SAY_FREE_2');
INSERT INTO `script_texts` VALUES (-1230038, 'Ograbisi needs new hat.', NULL, NULL, NULL, NULL, NULL, 'Ograbisi necesita un sombrero nuevo.', NULL, 'Ограбиси нужна новая шляпка.', 0, 0, 0, 0, 'ograbisi SAY');
INSERT INTO `script_texts` VALUES (-1230037, 'Administering fists of fury on Shill Dinger!', NULL, NULL, NULL, NULL, NULL, '¡Administrando puños de furia en Complice Dinger!', NULL, 'Врежь ему!', 0, 0, 0, 0, 'reginald_windsor SAY_7_4');
INSERT INTO `script_texts` VALUES (-1230036, 'Where I come from, you get shanked for opening another inmate\'s cell door!', NULL, NULL, NULL, NULL, NULL, '¡En mi tierra te colgarían de un pie si abres la puerta de la celda de un interno!', NULL, 'Там, откуда я родом, дают хорошего пинка за открывание камеры заключенного!', 0, 0, 0, 0, 'crest_killer SAY');
INSERT INTO `script_texts` VALUES (-1230035, 'Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you.', NULL, NULL, NULL, NULL, NULL, 'Quizás Ograbisi use tu cabeza como pequeño sombrero cuando acabe contigo.', NULL, 'Ограбиси сделает маленькую шляпку из твоей головы!', 0, 0, 0, 0, 'shill_dinger SAY');
INSERT INTO `script_texts` VALUES (-1230034, 'Thank you! I will run for safety immediately!', NULL, 'Vous aurez à payer pour cette insulte, $c!', NULL, NULL, NULL, '¡Gracias! ¡Correré por seguridad de inmediato!', NULL, 'Спасибо! Я скроюсь в безопасном месте!', 0, 0, 0, 0, 'tobias SAY_FREE_1');
INSERT INTO `script_texts` VALUES (-1230033, 'Meet me at Maxwell\'s encampment. We\'ll go over the next stages of the plan there and figure out a way to decode my tablets without the decryption ring.', NULL, 'Au Final!! Je peux laisser ce dépotoir.', NULL, NULL, NULL, 'Nos vemos en el campamento de Maxwell. Una vez allí, atravesaremos las siguientes etapas del plan y averiguaremos la manera de descodificar mis tablillas sin el anillo de descodificación.', NULL, 'Встретимся в лагере Максвелла. Дальше действуй по плану и выясни способ расшифровки табличек.', 0, 0, 0, 0, 'reginald_windsor SAY_20_2');
INSERT INTO `script_texts` VALUES (-1230032, 'We made it!', NULL, 'Merci! Je vais courir pour la sécurité immédiatement!', NULL, NULL, NULL, '¡Lo hemos conseguido!', NULL, 'У нас получилось!', 0, 0, 0, 0, 'reginald_windsor SAY_20_1');
INSERT INTO `script_texts` VALUES (-1230031, 'Excellent work, $N. Let\'s find the exit. I think I know the way. Follow me!', NULL, 'Rendez-vous au campement de Maxwell. Nous allons passer en revue les prochaines étapes du plan là et trouver un moyen pour décoder mes comprimés sans l\'anneau de décryptage.', NULL, NULL, NULL, 'Excelente trabajo, $n. Encontremos la salida. Creo que conozco el camino. ¡Seguidme!', NULL, 'Отличная работа, $N. Давай найдем выход. Мне кажется, я знаю дорогу. Следуй за мной!', 0, 0, 0, 0, 'reginald_windsor SAY_14_2');
INSERT INTO `script_texts` VALUES (-1230030, 'Get him out of there!', NULL, 'Nous l\'avons fait!', NULL, NULL, NULL, '¡Sacadle de ahí!', NULL, 'Помоги ему выбраться!', 0, 0, 0, 0, 'reginald_windsor SAY_14_1');
INSERT INTO `script_texts` VALUES (-1230029, 'He has to be in the last cell. Unless... they killed him.', NULL, 'Excellent travail, $N. Trouvons la sortie. Je pense que je connais le chemin. Suivez-moi!', NULL, NULL, NULL, 'Tiene que estar en la última celda. A no ser... que le hayan matado.', NULL, 'Должно быть он в последней камере. Если они... не убили его.', 0, 0, 0, 0, 'reginald_windsor SAY_13_3');
INSERT INTO `script_texts` VALUES (-1230028, 'Administering fists of fury on Crest Killer!', NULL, 'Sortez-le de là!', NULL, NULL, NULL, '¡Administrando puños de furia en Asesino de Cresta!', NULL, 'Врежь ему!', 0, 0, 0, 0, 'reginald_windsor SAY_13_2');
INSERT INTO `script_texts` VALUES (-1230027, 'Open it. We need to hurry up. I can smell those Dark Irons coming a mile away and I can tell you one thing, they\'re COMING!', NULL, 'Il doit être dans la dernière cellule. A moins que... ils l\'ont tué.', NULL, NULL, NULL, 'Ábrela. Tenemos que darnos prisa. Puedo oler a esos Hierro Negro a un kilómetro de distancia y puedo decirte una cosa: ¡SE ACERCAN!', NULL, 'Открой ее. Нам нужно поторопиться. Я за милю чувствую дух Черного Железа, и могу сказать, что они УЖЕ РЯДОМ!', 0, 0, 0, 0, 'reginald_windsor SAY_13_1');
INSERT INTO `script_texts` VALUES (-1230026, 'Alright, let\'s go.', NULL, 'Ouvrez-le. Nous avons besoin de se dépêcher. Je peux sentir les Sombrefers venir un mile loin et je peux vous dire une chose, ils vont venir!', NULL, NULL, NULL, 'De acuerdo, vámonos.', NULL, 'Ладно, пойдем.', 0, 0, 0, 0, 'reginald_windsor SAY_7_3');
INSERT INTO `script_texts` VALUES (-1230025, 'That intolerant dirtbag finally got what was coming to him. Good riddance!', NULL, 'Bon, allons-y.', NULL, NULL, NULL, 'Ese maldito intolerante finalmente recibió su merecido. ¡Adiós y hasta nunca!', NULL, 'Он получил своей. Так ему и надо!', 0, 0, 0, 0, 'reginald_windsor SAY_7_2');
INSERT INTO `script_texts` VALUES (-1230024, 'Open it and be careful this time!', NULL, 'Ce dirtbag intolérants a finalement obtenu ce qu\'il méritait. Bon débarras!', NULL, NULL, NULL, '¡Ábrela y ten cuidado esta vez!', NULL, 'Открывай, только в этот раз, будь осторожнее!', 0, 0, 0, 0, 'reginald_windsor SAY_7_1');
INSERT INTO `script_texts` VALUES (-1230023, 'I never did like those two. Let\'s get moving.', NULL, 'Ouvrez-le et soyez prudent cette fois!', NULL, NULL, NULL, 'Nunca hice lo que esos dos. Vámonos de aquí.', NULL, 'Мне никогда не нравились эти двое. Пойдем дальше.', 0, 0, 0, 0, 'reginald_windsor SAY_5_2');
INSERT INTO `script_texts` VALUES (-1230022, 'Open it.', NULL, 'Je n\'ai jamais aimé les deux. Mettons-nous en mouvement.', NULL, NULL, NULL, 'Ábrela.', NULL, 'Открой ее.', 0, 0, 0, 0, 'reginald_windsor SAY_5_1');
INSERT INTO `script_texts` VALUES (-1230021, 'Now we just have to free Tobias and we can get out of here. This way!', NULL, 'Ouvrez-le.', NULL, NULL, NULL, 'Ahora solo tenemos que liberar a Tobías y podremos salir de aquí. ¡Por aquí!', NULL, 'Осталось освободить Тобиаса и можно убираться отсюда. За мной!', 0, 0, 0, 0, 'reginald_windsor SAY_0_2');
INSERT INTO `script_texts` VALUES (-1230020, 'Can you feel the power, $N??? It\'s time to ROCK!', NULL, 'Maintenant nous devons juste pour libérer Tobias et nous pouvons sortir d\'ici. Par ici!', NULL, NULL, NULL, '¿¿¿Sientes el poder, $n??? ¡Es hora de JUGAR!', NULL, 'Чувствуешь силу, $N??? Пора веселиться!', 0, 0, 0, 0, 'reginald_windsor SAY_0_1');
INSERT INTO `script_texts` VALUES (-1230019, 'Ah, there it is!', NULL, 'Pouvez-vous sentir la puissance, $N??? Il est temps de Rock!', NULL, NULL, NULL, 'Ah, ahí está.', NULL, 'Ах, вот оно!', 0, 0, 0, 0, 'windsor SAY_9');
INSERT INTO `script_texts` VALUES (-1230018, 'This is it, $N. My stuff should be in that room. Cover me, I\'m going in!', NULL, 'Ah, ça y est!', NULL, NULL, NULL, 'Aquí es, $n. Mis cosas tienen que estar en esa habitación. ¡Cúbreme, voy a entrar!', NULL, 'Мои вещи должны быть в этой комнате. Прикрой меня $N, я захожу.', 0, 0, 0, 0, 'windsor SAY_6');
INSERT INTO `script_texts` VALUES (-1230017, 'Good work! We\'re almost there, $N. This way.', NULL, 'Ce est ça, $N. My stuff devrait être dans cette chambre. Couvrez-moi, je vais en!', NULL, NULL, NULL, '¡Buen trabajo! Estamos a punto de llegar, $N. Por aquí.', NULL, 'Хорошая работа, $N! Мы почти пришли.', 0, 0, 0, 0, 'windsor SAY_4_3');
INSERT INTO `script_texts` VALUES (-1230016, 'Get him out of there!', NULL, 'Beau travail! Nous y sommes presque, $N. Par ici.', NULL, NULL, NULL, '¡Sacadle de ahí!', NULL, 'Вытащи его оттуда!', 0, 0, 0, 0, 'windsor SAY_4_2');
INSERT INTO `script_texts` VALUES (-1230015, 'Check that cell, $N. If someone is alive in there, we need to get them out.', NULL, 'Vérifiez cette cellule, $N. Si quelqu\'un est vivant là-bas, nous avons besoin de les faire sortir.', NULL, NULL, NULL, 'Comprueba esa celda, $n. Si todavía hay vivos dentro, tendremos que sacarlos de ahí.', NULL, 'Проверь эту камеру, $N.  Если там есть живые, тогда мы должны вытащить их.', 0, 0, 0, 0, 'windsor SAY_4_1');
INSERT INTO `script_texts` VALUES (-1230014, 'Let\'s get a move on. My gear should be in the storage area up this way...', NULL, 'Mettons-nous un coup sur. Ma vitesse devrait être dans la zone de stockage de cette façon...', NULL, NULL, NULL, 'Démonos prisa. Mis herramientas deberían estar en el almacén por aquí...', NULL, 'Пора убираться отсюда. Мое снаряжение должно быть в хранилище...', 0, 0, 0, 0, 'windsor SAY_1');
INSERT INTO `script_texts` VALUES (-1230013, 'You better hold me back $N or they are going to feel some prison house beatings.', NULL, 'Tu ferais mieux de me retenir ou $N va se sentir quelques coups maison de prison.', NULL, NULL, NULL, 'Será mejor que me detengas $N o van a sentir algunas palizas en la prisión.', NULL, 'Тебе лучше сдерживать меня $N, либо я изобью их!', 0, 0, 0, 0, 'windsor SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1230012, 'I bet you\'re sorry now, aren\'t you !?!!', NULL, 'Je parie que vous êtes désolé maintenant, ne sont pas vous?', NULL, NULL, NULL, 'Apuesto a que lo sientes ahora, ¿no? !!', NULL, 'Бьюсь об заклад, сейчас вы сожалеете, не так ли?!', 0, 0, 0, 0, 'windsor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1230011, 'You locked up the wrong Marshal. Prepare to be destroyed!', NULL, 'Vous enfermé le mauvais maréchal, $N. Préparez-vous à être détruit!', NULL, NULL, NULL, 'Encerraste al mariscal equivocado. ¡Prepárate para ser destruido!', NULL, 'Вы заперли не того Маршала. Приготовьтесь к смерти!', 0, 0, 0, 0, 'windsor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1230010, 'Thank you, $N! I\'m free!!!', NULL, 'Merci, $N! Je suis libre!!!', NULL, NULL, NULL, '¡Gracias, $n! ¡¡¡Soy libre!!!', NULL, 'Спасибо тебе, $N! Теперь я свободен!!!', 0, 0, 0, 0, 'dughal SAY_FREE');
INSERT INTO `script_texts` VALUES (-1309027, '%s dies.', NULL, NULL, NULL, NULL, NULL, 'muere.', NULL, NULL, 0, 2, 0, 0, 'thekal EMOTE_DIES');
INSERT INTO `script_texts` VALUES (-1309028, '%s is resurrected by a nearby ally!', NULL, NULL, NULL, NULL, NULL, 'resucita gracias a un aliado cercano.', NULL, NULL, 0, 2, 0, 0, 'thekal EMOTE_RESURRECT');
INSERT INTO `script_texts` VALUES (-1230052, 'Emperor Thaurissan does not wish to be disturbed! Turn back now or face your doom, weak mortals!', NULL, NULL, NULL, NULL, NULL, '¡El emperador Thaurissan no desea ser perturbado! ¡Regresad ahora o enfrentaos a vuestro fin, débiles mortales!', NULL, NULL, 0, 6, 0, 0, 'yell Magmus');
INSERT INTO `script_texts` VALUES (-1000020, 'Intruders! More pawns of the Argent Dawn, no doubt. I already count one of their number among my prisoners. Withdraw from my domain before she is executed!', NULL, 'Des intrus ! Encore des marionnettes de l\'Aube d\'argent, sans doute. J\'en ai déjà une parmi mes prisonniers. Hâtez-vous de quitter nom domaine, sans quoi elle sera exécutée !', NULL, NULL, NULL, '¡Intrusos! Más peones del Alba Argenta, sin duda. Ya cuento a uno de ellos entre mis prisioneros. ¡Retírate de mi dominio antes de que sea ejecutada!', NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_45MIN');
INSERT INTO `script_texts` VALUES (-1000021, 'You\'re still here? Your foolishness is amusing! The Argent Dawn wench needn\'t suffer in vain. Leave at once and she shall be spared!', NULL, NULL, NULL, NULL, NULL, '¿Sigues aquí? ¡Tu necedad es divertida! La moza del Alba Argenta no tiene por qué sufrir en vano. ¡Vete de inmediato y ella se salvará!', NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_10MIN');
INSERT INTO `script_texts` VALUES (-1000022, 'I shall take great pleasure in taking this poor wretch\'s life! It\'s not too late, she needn\'t suffer in vain. Turn back and her death shall be merciful!', NULL, 'Je vais prendre beaucoup de plaisir à tuer cette pauvre femme ! Il n\'est pas trop tard, Il n\'est pas nécessaire qu\'elle souffre pour rien. Allez-vous-en et sa mort sera miséricordieuse !', NULL, NULL, NULL, '¡Me complacerá mucho quitarle la vida a este pobre infeliz! No es demasiado tarde, no tiene por qué sufrir en vano. ¡Vuelve y su muerte será misericordiosa!', NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_5MIN');
INSERT INTO `script_texts` VALUES (-1000023, 'Don\'t worry about me! Slay this dreadful beast and cleanse this world of his foul taint!', NULL, 'Ne vous inquiétez pas de moi ! Tuez cette bête odieuse ! Libérez le monde de sa vile corruption !', NULL, NULL, NULL, '¡No te preocupes por mí! ¡Mata a esta terrible bestia y limpia este mundo de su inmunda mancha!', NULL, NULL, 0, 6, 0, 0, 'ysida YELL_5MIN');
INSERT INTO `script_texts` VALUES (-1000024, 'May this prisoner\'s death serve as a warning. None shall defy the Scourge and live!', NULL, NULL, NULL, NULL, NULL, 'Que la muerte de este prisionero sirva de advertencia. ¡Nadie desafiará la Plaga y vivirá!', NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_FAILED');
INSERT INTO `script_texts` VALUES (-1000025, 'So you see fit to toy with the Lich King\'s creations? Ramstein, be sure to give the intruders a proper greeting.', NULL, NULL, NULL, NULL, NULL, '¿Así que crees que es adecuado juguetear con las creaciones del Rey Exánime? Ramstein, asegúrate de dar a los intrusos la bienvenida que se merecen.', NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_RAMMSTEIN');
INSERT INTO `script_texts` VALUES (-1000026, 'Ramstein hunger for flesh!', NULL, NULL, NULL, NULL, NULL, '¡Ramstein tiene hambre de carne!', NULL, NULL, 0, 6, 0, 0, 'rammstein YELL_SPAWN');
INSERT INTO `script_texts` VALUES (-1000027, 'Who dares disturb our master?', NULL, NULL, NULL, NULL, NULL, '¿Quién osa molestar a nuestro maestro?', NULL, NULL, 0, 6, 0, 0, 'black_guard_sentry YELL_SPAWN');
INSERT INTO `script_texts` VALUES (-1000028, 'Time to take matters into my own hands. Come. Enter my domain and challenge the might of the Scourge!', NULL, NULL, NULL, NULL, NULL, 'Ya es hora de tomar el asunto en mis propias manos. Ven. ¡Entra en mis dominios y desafía el poder de la Plaga!', NULL, NULL, 0, 6, 0, 0, 'rivendare YELL_READY');
INSERT INTO `script_texts` VALUES (-1000029, 'My death means nothing.... light... will... prevail!', NULL, NULL, NULL, NULL, NULL, '¡Mi muerte no cambia nada... la luz... seguirá... imperando!', NULL, NULL, 0, 6, 0, 0, 'ysida YELL_FAILED');
INSERT INTO `script_texts` VALUES (-1000030, 'You did it... you\'ve slain Baron Rivendare! The Argent Dawn shall hear of your valiant deeds!', NULL, NULL, NULL, NULL, NULL, 'Lo hiciste ... ¡has matado al barón Rivendare! ¡El Alba Argenta se enterará de tus valientes hazañas!', NULL, NULL, 0, 0, 0, 0, 'ysida SAY_REWARD');
INSERT INTO `script_texts` VALUES (-1780223, '%s nervously crawls from the crate.', NULL, NULL, '%s kriecht vorsichting aus der Kiste.', '%s紧张兮兮地爬出了箱子', '%s緊張兮兮地爬出了箱子', 'se arrastra tembloroso desde el cajón.', '%s se gatea nervosamente del cajón.', NULL, 0, 2, 0, 0, 'Snufflenose emerges from crate');
INSERT INTO `script_texts` VALUES (-1780224, '%s wiggles its whiskers at $N.', NULL, NULL, '%s shaut $N an und wackelt kurz mit den Schnurrhaaren.', '%s朝着$N动了动胡须', '%s朝著$N動了動胡須', '%s se mira a $N y menea con sus bigotes.', '%s se mira a $N y menea con sus bigotes.', NULL, 0, 2, 0, 0, 'Snufflenose received command');
INSERT INTO `script_texts` VALUES (-1780225, '%s sniffs at the ground.', NULL, NULL, '%s schnuppert am Boden.', '%s冲着地面闻了起来', '%s沖著地面聞了起來', '%s huele al suelo.', '%s huele al suelo.', NULL, 0, 2, 0, 0, 'Snufflenose found tubers');
INSERT INTO `script_texts` VALUES (-1469038, 'I wonder, heroes, what will you do when I manage to destroy this shard? Will the world be beyond Salvation?', NULL, NULL, 'Ich frage mich, Helden, was werdet ihr tun wenn ich diesen Splitter zerstöre? Wird die Welt nicht mehr zu retten sein?', '我在想，英雄们啊，如果我把这碎片给毁了，你们会怎么办呢？这世界就无法被拯救了吧？', NULL, 'Me pregunto, valientes, qué haréis cuando consiga destruir este fragmento. ¿No habrá salvación para el mundo?', NULL, NULL, 0, 1, 0, 0, 'Nefarius\' Scepter Taunt ');
INSERT INTO `script_texts` VALUES (-1469039, 'Lord Victor Nefarius\'s laughter echoes through the halls of Blackwing.', NULL, NULL, 'Lord Victor Nefarius\' Gelächter shallt durch die Hallen des Pechschwingenhortes', '维克多·耐法里奥斯的笑声回响在整个黑翼大厅里', NULL, 'La risa de Lord Victor Nefarius resuena por los pasillos de Ala Negra.', NULL, NULL, 0, 3, 0, 11, 'Lord Victor Nefarius\'s laughter echoes through the halls of Blackwing.');
INSERT INTO `script_texts` VALUES (-1469040, 'What\'s this? A chip in the shard? Finally, a weakness exposed... It won\'t be long now.', NULL, NULL, 'Was ist das? Ein Sprung im Splitter? Endlich, eine ausgesetze Schwäche... Es ist bald soweit.', '这是什么？碎片里有个缺口？终于，弱点出现了。。。用不了多久了', NULL, '¿Qué es esto? ¿Una muesca en el fragmento? Por fin un punto débil... No tardaré mucho.', NULL, NULL, 0, 1, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1469041, 'Would it not be glorious if you somehow managed to defeat me, but not before I could destroy the only hope Kalimdor may have? I am giddy with anticipation. Soon... Soon it will be dust.', NULL, NULL, 'Wäre es nicht glorreich wenn ihr mich irgendwie besiegen würdet, aber nicht bevor ich die einzige Hoffnung Kalimdor\'s zerstöre? Mir wird Schwindelig vor Vorfreude. Bald... bald wird eure Hoffnung zu Staub verfallen.', '如果你们在我摧毁这拯救卡利姆多唯一的希望之后才将我打败，那这份荣耀是不是会变得暗淡无光许多了呢。这将会发生一切真是让我太兴奋了。很快了。。。很快，一切都会化为灰烬。', NULL, '¿No sería glorioso si de alguna manera lograras derrotarme, pero no antes de que pudiera destruir la única esperanza que Kalimdor puede tener? Estoy mareado de anticipación. Pronto ... Pronto será polvo.', NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1469042, 'Not even my father could have planned such tragedy... such chaos. You will never make it! Time is running out!', NULL, NULL, 'Nicht einmal mein Vater hätte so eine Tragödie... solch ein Chaos bewirken können. Ihr werdet es nie schaffen! Die Zeit wird knapp!', '就连我父亲当初都没能计划出这样的悲剧。。。这样彻底的混沌。你们不会成功的！时间快没了！', NULL, 'Ni siquiera mi padre podría haber planeado semejante tragedia ... semejante caos. ¡Nunca lo lograrás! ¡Se acaba el tiempo!', NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1469043, 'Ah, the protective layer is nearly gone. Do you hear that, heroes? That... That is the sound of inevitability. Of your hopes and dreams being crushed under the weight of my being.', NULL, NULL, 'Ah, die Schutzschicht is bald aufgebrochen. Hört ihr das, Helden? Das ist der Klang der Unvermeidlichkeit. Der Klang eurer Hoffnungen und Träume, wie sie unter dem Gewicht meines Wesens zermalmt werden.', '哎呀，保护层快没了。你们听到吗，英雄们？那个。。。那是你们逃不掉的声音，是你们的希望和梦想被我摧毁的声音。', NULL, '¡Ah! La capa protectora casi ha desaparecido. ¿Oís eso, valientes? Eso... es el sonido de lo inevitable. De vuestras esperanzas y sueños truncados por el peso de mi ser.', NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1469044, 'Lord Victor Nefarius\'s laughter can once more be heard through the halls of Blackwing.', NULL, NULL, 'Lord Victor Nefarius\' Gelächter kann noch einmal durch die Hallen des Pechschwingenhortes gehört werden.', '耐法里奥斯的笑声再次响彻这个黑翼大厅。', NULL, 'La risa de Lord Victor Nefarius se puede escuchar una vez más a través de los pasillos de Ala Negra ...', NULL, NULL, 0, 3, 0, 11, NULL);
INSERT INTO `script_texts` VALUES (-1469045, 'The scepter shard is no more! Your beloved Kalimdor shall soon be a desolate wasteland. I will, of course, turn the Eastern Kingdoms into an ashen, lifeless void...', NULL, NULL, 'Der Szeptersplitter ist nicht mehr! Euer beliebtes Kalimdor wird bald eine trostlose Wüste sein. Ich werde, natürlich, die Östlichen Königreiche zu Asche machen, zu einer leblosen Leere...', '碎片不复存在了！你们挚爱的卡利姆多马上就会成为一块荒芜的废地。而我，当然了，就会把东部王国变成一片苍白的，毫无生气的空虚。。。', NULL, '¡El fragmento de cetro es historia! Vuestra amada Kalimdor pronto se convertirá en un lugar desolado. Y, por supuesto, haré de los Reinos del Este un lugar triste y sin vida.', NULL, NULL, 0, 1, 0, 0, 'Nefarius\' Final Say on Scepter Run fail');
INSERT INTO `script_texts` VALUES (-1000007, '%s, Champion of the Bronze Dragonflight, has rung the Scarab Gong. The ancient gates of Ahn\'Qiraj open, revealing the horrors of a forgotten war...', NULL, NULL, NULL, NULL, NULL, '%s, Campeón del Vuelo de Dragones de Bronce, ha tocado el Scarab Gong. Las antiguas puertas de Ahn\'Qiraj se abren, revelando los horrores de una guerra olvidada ...', NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1000008, 'Massive Qiraji resonating crystals break through the earthen crust of Kalimdor. Colossal creatures made of obsidian stone breach the freshly broken land.', NULL, NULL, NULL, NULL, NULL, 'Enormes obeliscos de cristal resonador surgen de la corteza terrestre de Kalimdor. Criaturas colosales hechas de piedra obsidiana se abren paso a través de la quebrada tierra.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1000009, 'Colossus of Ashi breaks free of its cocoon beneath Hive\'Ashi.', NULL, NULL, NULL, NULL, NULL, 'Coloso de Ashi se libera de su capullo debajo de Hive\'Ashi.', NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1000017, 'Colossus of Zora casts its massive shadow upon the whole of Silithus.', NULL, NULL, NULL, NULL, NULL, 'Coloso de Zora proyecta su enorme sombra sobre todo Silithus.', NULL, NULL, 0, 3, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1000016, 'Colossus of Regal hears the call to battle and rises to serve its master.', NULL, NULL, NULL, NULL, NULL, 'Coloso de Regal escucha la llamada a la batalla y se levanta para servir a su amo.', NULL, NULL, 0, 3, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1531022, 'Your brash arrogance!', NULL, NULL, NULL, NULL, NULL, '¡Tu descarada arrogancia!', NULL, NULL, 8628, 1, 0, 0, 'veklor SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES (-1531023, 'You will not escape death!', NULL, NULL, NULL, NULL, NULL, '¡No escaparás a la muerte!', NULL, NULL, 8629, 1, 0, 0, 'veklor SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1531024, 'My brother...NO!', NULL, NULL, NULL, NULL, NULL, 'Mi hermano ... ¡NO!', NULL, NULL, 8625, 1, 0, 0, 'veklor SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531025, 'To decorate our halls!', NULL, NULL, NULL, NULL, NULL, '¡Para decorar nuestros salones!', NULL, NULL, 8627, 1, 0, 0, 'veklor SAY_SPECIAL');
INSERT INTO `script_texts` VALUES (-1531026, 'Ah, lambs to the slaughter!', NULL, NULL, NULL, NULL, NULL, '¡Ah, corderos al matadero!', NULL, NULL, 8630, 1, 0, 0, 'veknilash SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1531027, 'Let none survive!', NULL, NULL, NULL, NULL, NULL, '¡Que nadie sobreviva!', NULL, NULL, 8632, 1, 0, 0, 'veknilash SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1531028, 'Join me brother, there is blood to be shed!', NULL, NULL, NULL, NULL, NULL, '¡Únete a mí hermano, hay sangre que derramar!', NULL, NULL, 8631, 1, 0, 0, 'veknilash SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1531029, 'Look brother, fresh blood!', NULL, NULL, NULL, NULL, NULL, '¡Mira hermano, sangre fresca!', NULL, NULL, 8633, 1, 0, 0, 'veknilash SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES (-1531030, 'Your fate is sealed!', NULL, NULL, NULL, NULL, NULL, '¡Tu destino está escrito!', NULL, NULL, 8635, 1, 0, 0, 'veknilash SAY_SLAY');
INSERT INTO `script_texts` VALUES (-1531031, 'Vek\'lor, I feel your pain!', NULL, NULL, NULL, NULL, NULL, '¡Vek\'lor, puedo sentir tu miedo!', NULL, NULL, 8636, 1, 0, 0, 'veknilash SAY_DEATH');
INSERT INTO `script_texts` VALUES (-1531032, 'Shall be your undoing!', NULL, NULL, NULL, NULL, NULL, '¡Será tu perdición!', NULL, NULL, 8634, 3, 0, 0, 'veknilash SAY_SPECIAL');
INSERT INTO `script_texts` VALUES (-1531033, 'Death is close...', NULL, NULL, NULL, NULL, NULL, 'La muerte está cerca ...', NULL, NULL, 8580, 4, 0, 0, 'cthun SAY_WHISPER_1');
INSERT INTO `script_texts` VALUES (-1531034, 'You are already dead.', NULL, NULL, NULL, NULL, NULL, 'Ya estas muerto', NULL, NULL, 8581, 4, 0, 0, 'cthun SAY_WHISPER_2');
INSERT INTO `script_texts` VALUES (-1531035, 'Your courage will fail.', NULL, NULL, NULL, NULL, NULL, 'Tu coraje fallará.', NULL, NULL, 8582, 4, 0, 0, 'cthun SAY_WHISPER_3');
INSERT INTO `script_texts` VALUES (-1531036, 'Your friends will abandon you.', NULL, NULL, NULL, NULL, NULL, 'Tus amigos te abandonarán.', NULL, NULL, 8583, 4, 0, 0, 'cthun SAY_WHISPER_4');
INSERT INTO `script_texts` VALUES (-1531037, 'You will betray your friends.', NULL, NULL, NULL, NULL, NULL, 'Traicionarás a tus amigos.', NULL, NULL, 8584, 4, 0, 0, 'cthun SAY_WHISPER_5');
INSERT INTO `script_texts` VALUES (-1531038, 'You will die.', NULL, NULL, NULL, NULL, NULL, 'Morirás.', NULL, NULL, 8585, 4, 0, 0, 'cthun SAY_WHISPER_6');
INSERT INTO `script_texts` VALUES (-1531039, 'You are weak.', NULL, NULL, NULL, NULL, NULL, 'Eres débil.', NULL, NULL, 8586, 4, 0, 0, 'cthun SAY_WHISPER_7');
INSERT INTO `script_texts` VALUES (-1531040, 'Your heart will explode.', NULL, NULL, NULL, NULL, NULL, 'Tu corazón explotará.', NULL, NULL, 8587, 4, 0, 0, 'cthun SAY_WHISPER_8');
INSERT INTO `script_texts` VALUES (-1531041, '%s begins to slow!', NULL, NULL, NULL, NULL, NULL, '¡%s empieza a ralentizarse!', NULL, NULL, 0, 3, 0, 0, 'viscidus EMOTE_SLOW');
INSERT INTO `script_texts` VALUES (-1531042, '%s is freezing up!', NULL, NULL, NULL, NULL, NULL, '¡%s se está congelando!', NULL, NULL, 0, 3, 0, 0, 'viscidus EMOTE_FREEZE');
INSERT INTO `script_texts` VALUES (-1531043, '%s is frozen solid!', NULL, NULL, NULL, NULL, NULL, '%s está congelado sólidamente!', NULL, NULL, 0, 3, 0, 0, 'viscidus EMOTE_FROZEN');
INSERT INTO `script_texts` VALUES (-1531044, '%s begins to crack!', NULL, NULL, NULL, NULL, NULL, '¡%s comienza a agrietarse!', NULL, NULL, 0, 3, 0, 0, 'viscidus EMOTE_CRACK');
INSERT INTO `script_texts` VALUES (-1531045, '%s looks ready to shatter!', NULL, NULL, NULL, NULL, NULL, '%s parece listo para romperse!', NULL, NULL, 0, 3, 0, 0, 'viscidus EMOTE_SHATTER');
INSERT INTO `script_texts` VALUES (-1900048, 'Ah, here we are! Well let\'s get to work, shall we...?', NULL, NULL, NULL, NULL, NULL, '¡Ah, aquí estamos! Bueno, pongámonos a trabajar, ¿de acuerdo ...?', NULL, NULL, 0, 0, 0, 0, 'J\'eevee Scholomance 1');
INSERT INTO `script_texts` VALUES (-1900049, 'Oh, right! Over here now...', NULL, NULL, NULL, NULL, NULL, '¡Correcto! Por aquí ahora ...', NULL, NULL, 0, 0, 0, 0, 'J\'eevee Scholomance 2');
INSERT INTO `script_texts` VALUES (-1900050, 'And now... the final step!', NULL, NULL, NULL, NULL, NULL, 'Y ahora... ¡El último paso!', NULL, NULL, 0, 0, 0, 0, 'J\'eevee Scholomance 3');
INSERT INTO `script_texts` VALUES (-1900051, 'I\'m finished. The parchment is made. Now, return to Gorzeeki...', NULL, NULL, NULL, NULL, NULL, 'Terminé. El pergamino está hecho. Ahora, regresa a Gorzeeki ...', NULL, NULL, 0, 0, 0, 0, 'J\'eevee Scholomance 4');
INSERT INTO `script_texts` VALUES (-1999901, 'Farewell. We shall speak again. I\'m sure.', NULL, NULL, NULL, NULL, NULL, 'Despedida. Hablaremos de nuevo. Estoy seguro.', NULL, NULL, 0, 0, 7, 0, 'Tervosh SAY_FAREWELL');
INSERT INTO `script_texts` VALUES (-1999911, 'It\'s Proudmoore! Get out of here!', NULL, NULL, NULL, NULL, NULL, '¡Es Valiente! ¡Fuera de aquí!', NULL, NULL, 0, 0, 7, 0, 'SAY_PROGRESS_5_SEN');
INSERT INTO `script_texts` VALUES (-1999912, 'Run!', NULL, NULL, NULL, NULL, NULL, '¡Corre!', NULL, NULL, 0, 0, 7, 0, 'SAY_PROGRESS_6_SEN');
INSERT INTO `script_texts` VALUES (-1001019, 'You are an agent of their wicked god, fool.  I will see you destroyed!', '그들의 사악한 신을 섬기는군. 어리석은 것. 널 없애버리고 말겠다!', 'Vous êtes un agent de leur dieu pernicieux, espèce de fou. Je vais vous détruire !', 'Ihr seid ein Agent ihres tückischen Gottes, Dummkopf! Ich werde Euch vernichten lassen!', '你是邪恶之神的奴仆，愚蠢的家伙。我要见证你的毁灭！', '你是邪惡之神的奴僕，愚蠢的傢伙。我要見證你的毀滅!', 'No eres $gun:una; representante de su malvado dios, idiota. ¡Contemplaré tu destrucción!', 'No eres $gun:una; representante de su malvado dios, idiota. ¡Contemplaré tu destrucción!', 'Ты – слуга их безумного бога! Сейчас я увижу твою смерть!', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` VALUES (-1229020, 'Intruders are destroying our eggs!  Stop!!', '침입자들이 우리 알을 파괴하고 있다! 저지하라!!', 'Des intrus détruisent nos œufs ! Arrêtez !', 'Eindringlinge vernichten unsere Eier! Halt!!!', '入侵者正在破坏我们的蛋！住手！！', '入侵者正在破壞我們的蛋!住手!!', '¡Unos intrusos están destruyendo nuestros huevos! ¡Basta!', '¡Unos intrusos están destruyendo nuestros huevos! ¡Basta!', 'Враги уничтожают нашу кладку! Остановить!', 0, 1, 0, 0, 'rookery hatcher - SAY_ROOKERY_EVENT_START');
INSERT INTO `script_texts` VALUES (-1900100, 'Let the Fishing Tournament BEGIN!', '낚시왕 선발대회를 시작합니다!', 'Que le concours de pêche COMMENCE !', 'Lasst den Angelwettbewerb BEGINNEN!', '钓鱼大赛开始啦！', '釣魚大賽開始啦!', '¡Que dé comienzo el torneo de pesca!', '¡Que dé comienzo el torneo de pesca!', 'Состязание рыбаков НАЧАЛОСЬ!', 0, 1, 0, 0, 'Fishing Tournament - Begin');
INSERT INTO `script_texts` VALUES (-1900101, 'We have a winner!  $n has won FIRST PLACE in the tournament!', '승자가 나왔습니다! $n|1이;가; 이번 주 낚시왕 선발대회에서 대상을 받았습니다!', 'Nous avons $gun gagnant:une gagnante; ! $n a remporté la PREMIÈRE PLACE du tournoi !', 'Wir haben einen Gewinner! $n hat den ERSTEN PLATZ im Turnier erreicht!', '决出优胜者了！钓鱼大赛的第一名是：$n！', '優勝者出現了!$n是大賽中的第一名!', '¡Tenemos $gun ganador:una ganadora;! ¡$n se ha hecho con el PRIMER PUESTO del torneo!', '¡Tenemos $gun ganador:una ganadora;! ¡$n se ha hecho con el PRIMER PUESTO del torneo!', 'А вот и наш победитель! Первое место $gзанял:заняла; $n!', 0, 1, 0, 0, 'Fishing Tournament - Winner');
INSERT INTO `script_texts` VALUES (-1900102, 'And the Tastyfish have gone for the week!   I will remain for another hour to allow you to turn in your fish!', '이번 주 맛둥어는 모두 잡혔습니다! 앞으로 단 한 시간 동안만 여기서 물고기를 접수하오니 서둘러 오십시오!', 'Toutes les courbines sont parties pour une semaine. Je reste encore une heure pour vous permettre de vous débarrasser de vos prises.', 'Das war\'s für diese Woche mit Leckerfischen! Ich bleibe noch eine weitere Stunde hier, damit Ihr Eure Fische abliefern könnt!', '本周的钓鱼比赛已经结束！在我逗留的一小时内，你们可以用可口鱼跟我交换物品！', '本週的釣魚比賽已經結束!我會在這裡再留一小時來購買你們的可口魚!', '¡Y los peces se han acabado! ¡Me quedaré otra hora más para que podáis entregar vuestros peces!', '¡Y los peces se han acabado! ¡Me quedaré otra hora más para que puedan entregar sus peces!', 'Все, вкуснорыбы не будет как минимум неделю! Я задержусь еще на часок, чтобы у вас была возможность принести мне рыбу.', 0, 1, 0, 0, 'Fishing Tournament - Over');
INSERT INTO `script_texts` VALUES (-1900114, 'The Scourge have broken into the Bastion!  Redouble your efforts!  We must not fail!', '스컬지가 성채 안으로 침입했다! 힘을 내라! 절대로 져서는 안 돼!', 'Le Fléau s\'est introduit dans le Bastion ! Redoublez d\'efforts ! Nous ne devons pas échouer !', 'Die Geißel ist in die Bastion eingedrungen! Verdoppelt eure Bemühungen! Wir dürfen nicht versagen!', '亡灵天灾打进堡垒了！坚守阵地！我们绝对不能失败！', '天譴軍團被迫退守堡壘!加倍努力!我們不能失敗!', '¡La Plaga ha irrumpido en el bastión! ¡Redoblad vuestros esfuerzos! ¡No podemos fallar!', '¡La Plaga ha irrumpido en el bastión! ¡Redoblen sus esfuerzos! ¡No podemos fallar!', 'Плеть ворвалась в бастион! Удвойте усилия! Не сдаваться!', 0, 6, 0, 0, 'stratholme event');
INSERT INTO `script_texts` VALUES (-1900115, 'The Ash\'ari Crystals have been destroyed! The Slaughterhouse is vulnerable!', '애쉬아리 수정이 파괴되었다! 도살장이 위험하다!', 'Les Cristaux d\'Ash\'ari ont été détruits ! L\'Abattoir est vulnérable !', 'Die Kristalle von Ash\'ari wurden zerstört! Das Schlachthaus ist jetzt angreifbar!', '阿莎利水晶已经被摧毁了！屠宰场危险！', '阿莎利水晶已經被摧毀了!屠宰房危險!', '¡Han destruido los Cristales de Ash\'ari! ¡El Degolladero es vulnerable!', '¡Han destruido los Cristales de Ash\'ari! ¡El Degolladero es vulnerable!', 'Кристаллы Аш\'ари разрушены! Бойня в опасности!', 0, 6, 0, 0, 'stratholme all crystals destroyed');
INSERT INTO `script_texts` VALUES (-1900116, 'An Ash\'ari Crystal has been toppled! Restore the ziggurat before the Slaughterhouse is vulnerable!', '애쉬아리 수정 하나가 무너졌다! 도살장이 공격받기 전에 지구라트를 복원하라!', 'Un cristal d\'Ash\'ari vient d\'être renversé ! Restaurez la ziggourat avant que l\'abattoir ne soit vulnérable !', 'Ein Kristall von Ash\'ari wurde umgestürzt! Stellt die Ziggurat wieder her, ehe das Schlachthaus verwundbar wird!', '一块阿莎利水晶已经被摧毁了！趁屠宰场处于易受攻击状态，赶快修复通灵塔！', '阿莎利水晶已經被摧毀了!在屠宰房遭到攻擊之前修復通靈塔!', '¡Un Cristal de Ash\'ari se ha venido abajo! ¡Restaurad el zigurat antes de que El Degolladero sea vulnerable!', '¡Un Cristal de Ash\'ari se ha venido abajo! ¡Restaurad el zigurat antes de que El Degolladero sea vulnerable!', 'Кристалл Аш\'ари сброшен! Восстановите зиккурат, пока Бойня не осталась без защиты!', 0, 6, 0, 0, 'stratholme crystal destroyed');
INSERT INTO `script_texts` VALUES (-1900117, 'Kill $n!', '$n|1을;를; 해치워라!', 'Tuez $n !', 'Töte $n!', '杀了$n！', '殺吧，$n!', '¡Mata a $n!', '¡Mata a $n!', 'Убить |3-3($n)!', 0, 1, 0, 0, 'Captain Tuubid - Say Kill');
INSERT INTO `script_texts` VALUES (-1900160, 'TIMMY!', '티미!', 'TIMMY !', 'TIMMY!', '提米！', '提米!', '¡TIMMY!', '¡TIMMY!', 'ТИММИ!', 0, 1, 0, 0, 'Timmy the Cruel');
INSERT INTO `script_texts` VALUES (-1900161, 'Prepare to be Malowned!', '이 말로운 님이 무서운 맛을 보여주마!', 'Préparez-vous au Malownage !', 'Gleich bekommt Ihr es mit Malown zu tun!', '准备被玛罗恩干掉吧。', '準備被瑪羅恩幹掉吧。', '¡Prepárate para recibir un machaque de Gassol!', '¡Prepárate para recibir un machaque de Gassol!', 'Сейчас тебя отмэлоунят!', 0, 0, 0, 0, 'Postmaster Malown - Aggro');
INSERT INTO `script_texts` VALUES (-1900162, 'You\'ve been MALOWNED!', '이 말로운 님의 무서운 맛을 잘 보았느냐?', 'Le MALOWNAGE vous écrase !', 'Das kommt davon, wenn man sich mit Malown anlegt!', '你已经被玛罗恩搞定了！', '你已經被瑪羅恩搞定了!', '¡Recibes un MACHAQUE de Gassol!', '¡Recibes un MACHAQUE de Gassol!', 'Тебя ОТМЭЛОУНИЛИ!', 0, 0, 0, 25, 'Postmaster Malown - Killed Victim');
INSERT INTO `script_texts` VALUES (-1209004, 'Who dares step into my domain!  Come!  Come, and be consumed!', '감히 누가 내 땅에 발을 들여놓는 것이냐! 덤벼봐! 집어삼켜 버리겠어!', 'Qui ose entrer dans mon domaine ? Venez ! Venez et soyez consumé !', 'Wer wagt es, mein Domizil zu betreten! Kommt! Kommt und werdet verzehrt!', '谁敢踏入我的领地！来！来被消灭吧！', '誰敢踏入我的領地!來!來被消滅吧!', '¡Quién osa adentrarse en mis dominios! ¡Ven! ¡Ven para ser consumido!', '¡Quién osa adentrarse en mis dominios! ¡Ven! ¡Ven para ser consumido!', 'Кто осмелился вторгнуться в мои владения?! Идите же, идите на свою погибель!', 0, 6, 0, 0, 'Chief Ukorz Sandscalp - Yell on Door Open');
INSERT INTO `script_texts` VALUES (-1900166, '%s begins to summon in a Blackhand Dreadweaver!', '%s|1이;가; 검은손부대 공포술사를 소환하기 시작합니다!', '%s commence à invoquer un Tisseur d\'effroi Main-noire !', '%s beginnt, einen Schreckenswirker der Schwarzfaustlegion zu beschwören!', '%s开始召唤黑手恐法师！', '%s開始召喚一個黑手織懼者!', 'comienza a invocar a un tejedor de tinieblas Puño Negro.', '%s comienza a invocar a un tejedor de tinieblas Puño Negro.', '%s начинает призывать ткача ужаса из легиона Чернорука!', 0, 2, 0, 0, 'Blackhand Summoner - Begin cast Summon Blackhand Dreadweaver');
INSERT INTO `script_texts` VALUES (-1900167, '%s begins to summon in a Blackhand Veteran!', '%s|1이;가; 검은손부대 고참병을 소환하기 시작합니다!', '%s commence à invoquer un vétéran Main-noire !', '%s beginnt, einen Veteranen der Schwarzfaustlegion zu beschwören!', '%s开始召唤黑手老兵！', '%s開始召喚一個黑手老兵!', 'comienza a invocar a un veterano Puño Negro.', '%s comienza a invocar a un veterano Puño Negro.', '%s начинает призывать ветерана легиона Чернорука!', 0, 2, 0, 0, 'Blackhand Summoner - Begin cast Summon Blackhand Veteran');
INSERT INTO `script_texts` VALUES (-1900168, 'You do not belong here!  Ancients, rise against these intruders!', '너희는 이곳의 존재가 아니구나! 고대 정령들이여, 일어나서 침입자들을 해치워라!', 'Votre place n\'est pas ici ! Anciens, affrontez les intrus !', 'Ihr gehört nicht hierher! Ihr Uralten, erhebt euch gegen diese elenden Eindringlinge!', '你并不属于这里！远古的守卫，醒来吧！消灭入侵者！', '你不屬於這裡!上古之人，為對抗這些入侵者而起!', '¡Este no es sitio para ti! ¡Ancestros, alzaos contra los intrusos!', '¡Este no es sitio para ti! ¡Ancestros, alzaos contra los intrusos!', 'Вам здесь не место! Древа, восстаньте против этих чужаков!', 0, 1, 0, 0, 'Tendris Warpwood - Aggro');
INSERT INTO `script_texts` VALUES (-1109013, 'I\'ll feast on your bones!', '네놈들을 제물로 바치겠다.', 'Je festoierai de vos os!', 'Ich fresse Eure Knochen!', '我要吃掉你的骨头！', '我要吃掉你的骨頭!', '¡Devoraré tus huesos!', '¡Me daré un festín con tus huesos!', 'Я спляшу на ваших костях!', 5860, 1, 14, 0, 'Atalalarion - YELL_AGGRO');
INSERT INTO `script_texts` VALUES (-1900169, '%s marches around, roaring and making a ruckus.', '%s|1이;가; 이리저리 돌아다니면서 큰 소리를 내고 소동을 피웁니다.', '%s marche en rond, en rugissant et en faisant du grabuge.', '%s marschiert herum, brüllt und macht Rabatz.', '%s到处乱跑，大声喊叫，制造骚乱。', '%s到處亂跑，大聲喊叫，製造騷亂。', 'camina con paso firme, haciendo ruido y armando jaleo.', '%s camina con paso firme, haciendo ruido y armando jaleo.', '%s ходит кругами, ревет и шумит.', 0, 2, 0, 0, 'Umi\'s Mechanical Yeti - Emote on spawn.');
INSERT INTO `script_texts` VALUES (-1900170, 'RAAAAAAAR!', '우워어어어어!', 'RAAAAAAAR !', 'RAAAAAAAR!', '啊啊啊啊啊啊！', '啊啊啊啊啊啊!', '¡RAAAAAAAR!', '¡RAAAAAAAR!', 'РРРРЫЫЫЫЫ!!!', 0, 0, 0, 0, 'Umi\'s Mechanical Yeti - Say on spawn.');
INSERT INTO `script_texts` VALUES (-1900171, 'Oh!!! Get that thing away from me!', '으악!!! 제발 저것 좀 떨어뜨려 줘!', 'Oh !!! Je ne veux pas voir cette chose !', 'Oh!!! Nehmt das Ding da weg!', '啊呀！把那东西从我这儿拿走！', '啊呀!把那東西從我這兒拿走!', '¡¡¡Oh!!! ¡Aleja esa cosa de mí!', '¡¡¡Oh!!! ¡Aleja esa cosa de mí!', 'А-а-а! Уберите это от меня!', 0, 0, 0, 0, 'Quixxil - Say on Umi\'s Mechanical Yeti spawn.');
INSERT INTO `script_texts` VALUES (-1900172, '%s jumps in fright!', '%s|1이;가; 무서워하며 흠칫 놀랍니다!', '%s sursaute d’effroi !', '%s zuckt erschreckt zusammen.', '%s惊骇地跳起来！', '%s驚駭地跳起來!', 'salta del susto.', '%s salta del susto.', '%s подпрыгивает от испуга!', 0, 2, 0, 0, 'Sprinkle - Emote on Umi\'s Mechanical Yeti spawn.');
