INSERT INTO `script_texts` VALUES (-1999914, 'Go with grace, and may the Lady\'s magic protect you.', NULL, NULL, NULL, NULL, NULL, 'Que la gracia te acompañe y que la magia de la Dama te proteja.', NULL, NULL, 0, 0, 7, 0, 'TERVOSH_SAY_ON_QUEST_MD_PT14');
INSERT INTO `script_texts` VALUES (-1533134, 'A Guardian of Icecrown enters the fight!', NULL, NULL, NULL, NULL, NULL, '¡Un guardián de la Corona de Hielo entra en la lucha!', NULL, NULL, 0, 3, 0, 0, 'kelthuzad EMOTE_GUARDIAN');
INSERT INTO `script_texts` VALUES (-1533136, '%s teleports and begins to channel a spell!', NULL, NULL, NULL, NULL, NULL, '¡%s se teletransporta y comienza a canalizar un hechizo!', NULL, NULL, 0, 3, 0, 0, 'heigan EMOTE_TELEPORT');
INSERT INTO `script_texts` VALUES (-1533137, '%s rushes to attack once more!', NULL, NULL, NULL, NULL, NULL, '¡%s se apresura a atacar una vez más!', NULL, NULL, 0, 3, 0, 0, 'heigan EMOTE_RETURN');
INSERT INTO `script_texts` VALUES (-1533138, '%s teleports into the fray!', NULL, NULL, NULL, NULL, NULL, '¡%s se teletransporta a la refriega!', NULL, NULL, 0, 3, 0, 0, 'gothik EMOTE_TO_FRAY');
INSERT INTO `script_texts` VALUES (-1533139, 'The central gate opens!', NULL, NULL, NULL, NULL, NULL, '¡Se abre la puerta central!', NULL, NULL, 0, 3, 0, 0, 'gothik EMOTE_GATE');
INSERT INTO `script_texts` VALUES (-1533156, '%s casts Unyielding Pain on everyone!', NULL, NULL, NULL, NULL, NULL, '¡%s lanza un dolor inquebrantable a todo el mundo!', NULL, NULL, 0, 3, 0, 0, 'lady_blaumeux EMOTE_UNYIELDING_PAIN');
INSERT INTO `script_texts` VALUES (-1533157, '%s casts Condemation on everyone!', NULL, NULL, NULL, NULL, NULL, '%s lanza condena a todos!', NULL, NULL, 0, 3, 0, 0, 'sir_zeliek EMOTE_CONDEMATION');
INSERT INTO `script_texts` VALUES (-1533158, '%s injects you with a mutagen!', NULL, NULL, NULL, NULL, NULL, '¡%s te inyecta un mutágeno!', NULL, NULL, 0, 5, 0, 0, 'grobbulus EMOTE_INJECTION');
INSERT INTO `script_texts` VALUES (-1999927, 'Have you come to save the world? To cleanse it?', NULL, NULL, NULL, NULL, NULL, '¿Has venido a salvar el mundo? ¿Para limpiarlo?', NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999919, 'Take me with you, Sir.', NULL, NULL, NULL, NULL, NULL, 'Llévame contigo, señor.', NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1780300, 'Sandstorm coming from the south, take co... That\'s no sandstorm! INCOMING QIRAJI ARMY!', NULL, NULL, NULL, NULL, NULL, 'Tormenta de arena viniendo del sur, toma co ... ¡Eso no es una tormenta de arena! ¡EL EJÉRCITO QIRAJI!', NULL, NULL, 0, 6, 0, 93, 'AQ Saurfang CH attack');
INSERT INTO `script_texts` VALUES (-1780301, 'You know me to be the Supreme Commander of the Might of Kalimdor', NULL, NULL, NULL, NULL, NULL, 'Sabes que soy el Comandante Supremo del Poder de Kalimdor', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 1');
INSERT INTO `script_texts` VALUES (-1780302, 'An orc - a true orc warrior - wishes for one thing: To die in the glory of battle gainst a hated enemy.', NULL, NULL, NULL, NULL, NULL, 'Un orco, un verdadero guerrero orco, desea una cosa: morir en la gloria de la batalla contra un enemigo odiado.', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 2');
INSERT INTO `script_texts` VALUES (-1780303, 'Some of you have not fought in battles. Peace has been with us for many years. Many years we sat idle, but many years we battled.', NULL, NULL, NULL, NULL, NULL, 'Algunos de ustedes no han peleado en batallas. La paz ha estado con nosotros durante muchos años. Muchos años estuvimos sentados ociosos, pero muchos años luchamos.', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 3');
INSERT INTO `script_texts` VALUES (-1780304, 'In those years - where strife filled the land as Legion and Scource sacked our homes, killed our families - these insects dwelled beneath us. Beneath our homes - waiting', NULL, NULL, NULL, NULL, NULL, 'En esos años, donde la lucha llenó la tierra cuando Legion y Scource saquearon nuestras casas y mataron a nuestras familias, estos insectos habitaban debajo de nosotros. Debajo de nuestras casas - esperando', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 4');
INSERT INTO `script_texts` VALUES (-1780305, 'Waiting to crush the life from our little ones. To slay all in their path. This they do for their god.', NULL, NULL, NULL, NULL, NULL, 'Esperando aplastar la vida de nuestros pequeños. Para matar a todos a su paso. Esto lo hacen por su dios.', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 5');
INSERT INTO `script_texts` VALUES (-1780306, 'And for our gods?', NULL, NULL, NULL, NULL, NULL, '¿Y por nuestros dioses?', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 6');
INSERT INTO `script_texts` VALUES (-1780307, 'We defend. We stand. We show that as one. United. We destroy. Their god will fall.', NULL, NULL, NULL, NULL, NULL, 'Defendemos. Permanecemos. Lo mostramos como uno. Unido. Destruimos. Su dios caerá.', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 7');
INSERT INTO `script_texts` VALUES (-1780308, 'To die today, on this field of battle, is to die an orcish death. To die today is to die for our little ones. Our old ones. Our... loved ones', NULL, NULL, NULL, NULL, NULL, 'Morir hoy, en este campo de batalla, es morir como un orco. Morir hoy es morir por nuestros pequeños. Nuestros viejos. Nuestros seres queridos', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 8');
INSERT INTO `script_texts` VALUES (-1780309, 'Would any of you deny yourselves such a death? Such an honor?', NULL, NULL, NULL, NULL, NULL, '¿Alguno de ustedes se negaría a sí mismo tal muerte? ¿Qué honor?', NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 9');
INSERT INTO `script_texts` VALUES (-1780310, 'ONWARD! TO AHN\'QIRAJ!', NULL, NULL, NULL, NULL, NULL, '¡ADELANTE! ¡A AHN\'QIRAJ!', NULL, NULL, 0, 6, 0, 0, 'AQ Saurfang speech 10');
INSERT INTO `script_texts` VALUES (-1780311, 'The final battle for Kalimdor is upon us. The supreme commander of the Might of Kalimdor orders his troops to march upon Ahn\'Qiraj and destroy all in their path..', NULL, NULL, NULL, NULL, NULL, 'La batalla final por Kalimdor está sobre nosotros. El comandante supremo del Poder de Kalimdor ordena a sus tropas que marchen sobre Ahn\'Qiraj y destruyan a todos a su paso.', NULL, NULL, 0, 0, 0, 0, 'AQ Final battle world text');
INSERT INTO `script_texts` VALUES (-1780312, 'The Might of Kalimdor is victorious! The last of the Qiraji forces are defeated, retreating back to the ancient city-fortress of Ahn\'Qiraj. The Qiraji Resonating Crystals littering Kalimdor vanish as quickly as they appeared, leaving behind nothing but sundered earth.', NULL, NULL, NULL, NULL, NULL, '¡El poder de Kalimdor ha vencido! La última de las tropas qiraji ha sido derrotada y se está replegando de vuelta a la antigua ciudad-fortaleza de Ahn\'Qiraj. Los cristales resonadores qiraji que ensombrecen Kalimdor se desvanecen con la misma facilidad con la que surgieron, dejando tras de sí la marca de su ubicación.', NULL, NULL, 0, 0, 0, 0, 'AQ War over');
